// src/App.js - Working Full Version V2.5
// Built on the successful minimal version

import React from 'react';
import { Auth } from '@supabase/auth-ui-react';
import { ThemeSupa } from '@supabase/auth-ui-shared';
import { supabase } from './services';
import { AppProvider, useApp } from './contexts';

// Import components one by one to ensure they work
import Sidebar from './components/shared/Sidebar';
import EnhancedDashboard from './components/pages/EnhancedDashboard';
import MyTeamEnhanced from './components/pages/MyTeamEnhanced';
import ManagerReview from './components/pages/ManagerReview';
import MyReviews from './components/pages/MyReviews';
import MyDevelopmentCenterEnhanced from './components/pages/MyDevelopmentCenterEnhanced';
import Assessment from './components/pages/Assessment';
import Admin from './components/pages/Admin';
import FeedbackWall from './components/pages/FeedbackWall';
import ManagerPlaybook from './components/pages/ManagerPlaybook';
import EmployeeHelpPage from './components/pages/EmployeeHelpPage';
import ManagerHelpPage from './components/pages/ManagerHelpPage';
import AdminHelpPage from './components/pages/AdminHelpPage';

// Import modals
import StartReviewCycleModal from './components/modals/StartReviewCycleModal';
import CreateReviewCycleModal from './components/modals/CreateReviewCycleModal';
import CreateEmployeeModal from './components/modals/CreateEmployeeModal';
import EditEmployeeModal from './components/modals/EditEmployeeModal';
import GiveKudoModal from './components/modals/GiveKudoModal';
import GiveFeedbackModal from './components/modals/GiveFeedbackModal';
import UATFeedbackModal from './components/modals/UATFeedbackModal';


// Main App Component
const MainApp = () => {
  const { 
    user, 
    userRole, 
    userName, 
    userDataLoading, 
    activePage, 
    modal, 
    setActivePage, 
    openModal, 
    closeModal, 
    signOut 
  } = useApp();

  // Show loading screen while checking authentication
  if (userDataLoading && user) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-cyan-400 mb-4">EDGE</h1>
          <p className="text-gray-400">Loading your profile...</p>
          <div className="mt-4 w-8 h-8 border-2 border-cyan-400 border-t-transparent rounded-full animate-spin mx-auto"></div>
        </div>
      </div>
    );
  }

  // Show login screen if not authenticated
  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-900">
        <div className="w-full max-w-sm">
          <div className="text-center mb-6">
            <h1 className="text-3xl font-bold text-cyan-400">EDGE</h1>
            <p className="text-gray-400 text-sm">Employee Development & Growth Engine</p>
          </div>
          <div className="bg-gray-800 p-6 rounded-lg">
            <Auth
              supabaseClient={supabase}
              appearance={{ 
                theme: ThemeSupa,
                style: {
                  button: { 
                    background: '#0891b2', 
                    color: 'white',
                    borderRadius: '0.5rem'
                  },
                  anchor: { color: '#67e8f9' },
                  input: { 
                    background: '#374151',
                    border: '1px solid #4b5563',
                    borderRadius: '0.5rem',
                    color: 'white'
                  }
                }
              }}
              providers={[]}
              view="sign_in"
            />
            
          </div>
        </div>
      </div>
    );
  }

  // Page renderer function
  const PageRenderer = ({ page }) => {
  try {
    switch (page.name) {
      case 'Dashboard':
        return <EnhancedDashboard />;
      case 'My Team':
        return <MyTeamEnhanced />;
      case 'Manager Review':
        return <ManagerReview pageProps={page.props} />;
      case 'My Reviews':
        return <MyReviews />;
      case 'My Development':
        return <MyDevelopmentCenterEnhanced />;
      case 'Admin':
        return <Admin />;
      case 'Assessment':
        return <Assessment pageProps={page.props} />;
      case 'Feedback Wall':
        return <FeedbackWall />;
      case 'Manager Playbook':
        return <ManagerPlaybook />;
      case 'Help':
        // Return role-specific help page
        if (userRole === 'admin') {
          return <AdminHelpPage />;
        } else if (userRole === 'manager') {
          return <ManagerHelpPage />;
        } else {
          return <EmployeeHelpPage />;
        }
      default:
        return <EnhancedDashboard />;
    }
  } catch (error) {
    console.error('Error rendering page:', error);
    return (
      <div className="p-8">
        <div className="bg-red-900 border border-red-700 rounded-lg p-4">
          <h2 className="text-red-200 font-bold mb-2">Page Error</h2>
          <p className="text-red-300">Error loading {page.name}: {error.message}</p>
          <button 
            onClick={() => setActivePage({ name: 'Dashboard', props: {} })}
            className="mt-4 bg-red-700 hover:bg-red-600 text-white px-4 py-2 rounded"
          >
            Return to Dashboard
          </button>
        </div>
      </div>
    );
  }
};


  // MAIN APP STRUCTURE
  return (
    <div className="min-h-screen bg-gray-900 text-gray-100">
      {/* SIDEBAR - Fixed positioned */}
      <Sidebar />
      
      {/* MAIN CONTENT - With left margin to account for fixed sidebar */}
      <main className="ml-64 min-h-screen">
        <PageRenderer page={activePage} />
      </main>

      {/* MODALS - Only render when modal.isOpen is true and modal.name matches */}
      {modal.isOpen && modal.name === 'startReviewCycle' && (
        <StartReviewCycleModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: () => {
              if (modal.props?.onComplete) {
                modal.props.onComplete();
              }
              closeModal();
            }
          }} 
        />
      )}

      {modal.isOpen && modal.name === 'createReviewCycle' && (
        <CreateReviewCycleModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: () => {
              if (modal.props?.onComplete) {
                modal.props.onComplete();
              }
              closeModal();
            }
          }} 
        />
      )}

      {modal.isOpen && modal.name === 'createEmployee' && (
        <CreateEmployeeModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: () => {
              if (modal.props?.onComplete) {
                modal.props.onComplete();
              }
              closeModal();
            }
          }} 
        />
      )}

      {modal.isOpen && modal.name === 'editEmployee' && (
        <EditEmployeeModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: () => {
              if (modal.props?.onComplete) {
                modal.props.onComplete();
              }
              closeModal();
            }
          }} 
        />
      )}

      {modal.isOpen && modal.name === 'giveKudo' && (
        <GiveKudoModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: () => {
              if (modal.props?.onComplete) {
                modal.props.onComplete();
              }
              closeModal();
            }
          }} 
        />
      )}

      {modal.isOpen && modal.name === 'giveFeedback' && (
        <GiveFeedbackModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: () => {
              if (modal.props?.onComplete) {
                modal.props.onComplete();
              }
              closeModal();
            }
          }} 
        />
      )}


      {/* DEBUG INFO (only in development) */}
      {process.env.NODE_ENV === 'development' && (
        <div className="fixed bottom-0 left-0 right-0 bg-black bg-opacity-75 text-xs text-gray-400 p-2 border-t border-gray-700">
          Debug: User: {user?.email} | Role: {userRole} | Page: {activePage.name} | Modal: {modal.isOpen ? modal.name : 'none'}
        </div>
      )}
    </div>
  );
};

// App wrapper with provider
export default function App() {
  return (
    <AppProvider>
      <MainApp />
    </AppProvider>
  );
}

// src/AppWithRouter.tsx - Main App with React Router integration
import React from 'react';
import { AppProvider } from './contexts';
import AuthenticatedApp from './components/routing/AuthenticatedApp';

// New App wrapper with React Router integration
export default function AppWithRouter() {
  return (
    <AppProvider>
      <AuthenticatedApp />
    </AppProvider>
  );
}

// src/components/admin/PulseQuestionsManager.tsx - Admin pulse questions management
import React, { useState, useEffect } from 'react';
import { 
  Plus, 
  Trash2, 
  Edit3, 
  BarChart3, 
  Eye, 
  Save, 
  X,
  AlertTriangle,
  TrendingUp,
  TrendingDown,
  Activity
} from 'lucide-react';
import { supabase } from '../../services/supabaseClient';

interface PulseQuestion {
  id: string;
  question_id: string;
  question_text: string;
  category: 'satisfaction' | 'workload' | 'support' | 'engagement';
  type: 'scale' | 'boolean' | 'choice';
  options?: string[];
  is_active: boolean;
  sort_order: number;
  created_at: string;
}

interface QuestionStats {
  question_id: string;
  question_text: string;
  category: string;
  total_responses: number;
  avg_response: number;
  response_1_count: number;
  response_2_count: number;
  response_3_count: number;
  response_4_count: number;
  response_5_count: number;
  satisfaction_percentage: number;
  last_response: string;
}

export default function PulseQuestionsManager() {
  const [questions, setQuestions] = useState<PulseQuestion[]>([]);
  const [questionStats, setQuestionStats] = useState<QuestionStats[]>([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingQuestion, setEditingQuestion] = useState<PulseQuestion | null>(null);
  const [selectedQuestion, setSelectedQuestion] = useState<string | null>(null);
  const [viewMode, setViewMode] = useState<'manage' | 'statistics'>('manage');
  const [error, setError] = useState<string | null>(null);

  // New question form state
  const [newQuestion, setNewQuestion] = useState({
    question_id: '',
    question_text: '',
    category: 'satisfaction' as const,
    type: 'scale' as const,
    options: [] as string[]
  });

  // Edit question form state
  const [editForm, setEditForm] = useState({
    question_text: '',
    category: 'satisfaction' as 'satisfaction' | 'workload' | 'support' | 'engagement',
    type: 'scale' as 'scale' | 'boolean' | 'choice',
    options: [] as string[]
  });

  useEffect(() => {
    loadQuestions();
    if (viewMode === 'statistics') {
      loadQuestionStats();
    }
  }, [viewMode]);

  // Populate edit form when editing question changes
  useEffect(() => {
    if (editingQuestion) {
      setEditForm({
        question_text: editingQuestion.question_text,
        category: editingQuestion.category,
        type: editingQuestion.type,
        options: editingQuestion.options || []
      });
    }
  }, [editingQuestion]);

  const loadQuestions = async () => {
    try {
      setError(null);
      const { data, error } = await supabase
        .from('pulse_questions')
        .select('*')
        .order('sort_order');

      if (error) throw error;
      setQuestions(data || []);
    } catch (error: any) {
      console.error('Error loading questions:', error);
      setError(error.message || 'Failed to load questions. Make sure the pulse_questions table exists.');
    } finally {
      setLoading(false);
    }
  };

  const loadQuestionStats = async () => {
    try {
      const { data, error } = await supabase
        .rpc('get_question_statistics', { days_back: 30 });

      if (error) {
        console.warn('Statistics function not available:', error.message);
        setQuestionStats([]);
        return;
      }
      setQuestionStats(data || []);
    } catch (error) {
      console.error('Error loading question statistics:', error);
      setQuestionStats([]);
    }
  };

  const handleAddQuestion = async () => {
    if (!newQuestion.question_text.trim()) {
      alert('Please fill in the question text');
      return;
    }

    try {
      // Auto-generate unique question ID
      const timestamp = Date.now();
      const categoryPrefix = newQuestion.category.substring(0, 3);
      const autoQuestionId = `${categoryPrefix}_${timestamp}`;

      const { error } = await supabase
        .rpc('add_pulse_question', {
          question_id_param: autoQuestionId,
          question_text_param: newQuestion.question_text,
          category_param: newQuestion.category,
          type_param: newQuestion.type,
          options_param: newQuestion.options.length > 0 ? newQuestion.options : null
        });

      if (error) throw error;

      // Reset form and reload
      setNewQuestion({
        question_id: '',
        question_text: '',
        category: 'satisfaction',
        type: 'scale',
        options: []
      });
      setShowAddForm(false);
      loadQuestions();
    } catch (error: any) {
      console.error('Error adding question:', error);
      alert(error.message || 'Failed to add question');
    }
  };

  const handleEditQuestion = async () => {
    if (!editForm.question_text.trim() || !editingQuestion) {
      alert('Please fill in the question text');
      return;
    }

    try {
      const { error } = await supabase
        .from('pulse_questions')
        .update({
          question_text: editForm.question_text,
          category: editForm.category,
          type: editForm.type,
          options: editForm.options.length > 0 ? editForm.options : null
        })
        .eq('question_id', editingQuestion.question_id);

      if (error) throw error;

      // Reset and reload
      setEditingQuestion(null);
      loadQuestions();
      alert('Question updated successfully!');
    } catch (error: any) {
      console.error('Error updating question:', error);
      alert(error.message || 'Failed to update question');
    }
  };

  const handleDeleteQuestion = async (questionId: string) => {
    if (!confirm('Are you sure you want to deactivate this question? This will hide it from employees but preserve historical data.')) {
      return;
    }

    try {
      const { error } = await supabase
        .rpc('deactivate_pulse_question', {
          question_id_param: questionId
        });

      if (error) throw error;
      loadQuestions();
    } catch (error: any) {
      console.error('Error deactivating question:', error);
      alert(error.message || 'Failed to deactivate question');
    }
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'satisfaction': return <TrendingUp className="text-green-400" size={16} />;
      case 'workload': return <Activity className="text-yellow-400" size={16} />;
      case 'support': return <BarChart3 className="text-blue-400" size={16} />;
      case 'engagement': return <TrendingUp className="text-purple-400" size={16} />;
      default: return <Activity className="text-gray-400" size={16} />;
    }
  };

  const getResponseDistributionBar = (stats: QuestionStats) => {
    const total = stats.total_responses || 0;
    if (total === 0) return null;

    const responses = [
      { count: stats.response_1_count || 0, color: 'bg-red-500', label: '1' },
      { count: stats.response_2_count || 0, color: 'bg-orange-500', label: '2' },
      { count: stats.response_3_count || 0, color: 'bg-yellow-500', label: '3' },
      { count: stats.response_4_count || 0, color: 'bg-blue-500', label: '4' },
      { count: stats.response_5_count || 0, color: 'bg-green-500', label: '5' }
    ];

    return (
      <div className="w-full">
        <div className="flex h-4 bg-gray-700 rounded overflow-hidden mb-1">
          {responses.map((resp, idx) => (
            <div
              key={idx}
              className={resp.color}
              style={{ width: `${(resp.count / total) * 100}%` }}
              title={`${resp.label}: ${resp.count} responses (${((resp.count / total) * 100).toFixed(1)}%)`}
            />
          ))}
        </div>
        <div className="flex justify-between text-xs text-gray-400">
          <span>Poor (1)</span>
          <span>Excellent (5)</span>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="bg-gray-800 rounded-lg p-6">
        <div className="flex items-center justify-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-cyan-400"></div>
          <span className="ml-2 text-gray-300">Loading questions...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-gray-800 rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-xl font-bold text-white flex items-center">
            <BarChart3 className="mr-2" size={24} />
            Pulse Questions Management
          </h2>
        </div>
        <div className="bg-red-900/20 border border-red-500/30 rounded-lg p-4">
          <div className="flex items-center space-x-2 mb-2">
            <AlertTriangle className="text-red-400" size={20} />
            <h3 className="text-red-400 font-medium">Database Error</h3>
          </div>
          <p className="text-red-300 text-sm mb-4">{error}</p>
          <div className="bg-gray-700 rounded p-3 text-xs text-gray-300">
            <p className="font-medium mb-2">To fix this issue:</p>
            <ol className="list-decimal list-inside space-y-1">
              <li>Run the SQL file: <code className="bg-gray-600 px-1 rounded">pulse_questions_management.sql</code></li>
              <li>Check Supabase console for any errors</li>
              <li>Verify table permissions are set correctly</li>
            </ol>
          </div>
          <button
            onClick={() => {
              setError(null);
              setLoading(true);
              loadQuestions();
            }}
            className="mt-4 px-4 py-2 bg-cyan-600 hover:bg-cyan-700 text-white rounded transition-colors"
          >
            Retry Connection
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-gray-800 rounded-lg p-6">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-bold text-white flex items-center">
          <BarChart3 className="mr-2" size={24} />
          Pulse Questions Management
        </h2>
        <div className="flex items-center space-x-3">
          <div className="flex bg-gray-700 rounded-lg">
            <button
              onClick={() => setViewMode('manage')}
              className={`px-4 py-2 text-sm font-medium rounded-l-lg transition-colors ${
                viewMode === 'manage' 
                  ? 'bg-cyan-600 text-white' 
                  : 'text-gray-300 hover:text-white'
              }`}
            >
              Manage
            </button>
            <button
              onClick={() => setViewMode('statistics')}
              className={`px-4 py-2 text-sm font-medium rounded-r-lg transition-colors ${
                viewMode === 'statistics' 
                  ? 'bg-cyan-600 text-white' 
                  : 'text-gray-300 hover:text-white'
              }`}
            >
              Statistics
            </button>
          </div>
          {viewMode === 'manage' && (
            <button
              onClick={() => setShowAddForm(true)}
              className="flex items-center space-x-2 bg-cyan-600 hover:bg-cyan-700 text-white px-4 py-2 rounded-lg transition-colors"
            >
              <Plus size={16} />
              <span>Add Question</span>
            </button>
          )}
        </div>
      </div>

      {viewMode === 'manage' ? (
        <>
          {/* Questions List */}
          <div className="space-y-4">
            {questions.map(question => (
              <div key={question.id} className={`border rounded-lg p-4 ${
                question.is_active ? 'border-gray-600 bg-gray-750' : 'border-gray-700 bg-gray-800 opacity-60'
              }`}>
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      {getCategoryIcon(question.category)}
                      <span className="text-xs text-gray-400 uppercase font-medium">
                        {question.category}
                      </span>
                      <span className="text-xs text-gray-500">
                        ID: {question.question_id}
                      </span>
                      {!question.is_active && (
                        <span className="text-xs bg-red-900 text-red-300 px-2 py-1 rounded">
                          Inactive
                        </span>
                      )}
                    </div>
                    <p className="text-white font-medium mb-2">{question.question_text}</p>
                    <div className="flex items-center space-x-4 text-sm text-gray-400">
                      <span>Type: {question.type}</span>
                      <span>Order: {question.sort_order}</span>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => setSelectedQuestion(
                        selectedQuestion === question.question_id ? null : question.question_id
                      )}
                      className="p-2 text-gray-400 hover:text-cyan-400 transition-colors"
                      title="View Statistics"
                    >
                      <Eye size={16} />
                    </button>
                    <button
                      onClick={() => setEditingQuestion(question)}
                      className="p-2 text-gray-400 hover:text-blue-400 transition-colors"
                      title="Edit Question"
                    >
                      <Edit3 size={16} />
                    </button>
                    {question.is_active && (
                      <button
                        onClick={() => handleDeleteQuestion(question.question_id)}
                        className="p-2 text-gray-400 hover:text-red-400 transition-colors"
                        title="Deactivate Question"
                      >
                        <Trash2 size={16} />
                      </button>
                    )}
                  </div>
                </div>

                {/* Quick Stats Preview */}
                {selectedQuestion === question.question_id && (
                  <div className="mt-4 pt-4 border-t border-gray-600">
                    {questionStats.find(s => s.question_id === question.question_id) ? (
                      (() => {
                        const stats = questionStats.find(s => s.question_id === question.question_id)!;
                        return (
                          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                            <div>
                              <p className="text-sm text-gray-400 mb-1">Response Stats</p>
                              <p className="text-lg font-semibold text-white">
                                {(stats.avg_response || 0).toFixed(1)}/5.0
                              </p>
                              <p className="text-xs text-gray-400">
                                {stats.total_responses} responses
                              </p>
                            </div>
                            <div>
                              <p className="text-sm text-gray-400 mb-1">Satisfaction Rate</p>
                              <p className="text-lg font-semibold text-green-400">
                                {stats.satisfaction_percentage || 0}%
                              </p>
                              <p className="text-xs text-gray-400">4+ rating</p>
                            </div>
                            <div>
                              <p className="text-sm text-gray-400 mb-1">Distribution</p>
                              {getResponseDistributionBar(stats)}
                            </div>
                          </div>
                        );
                      })()
                    ) : (
                      <p className="text-gray-400 text-sm">No response data available</p>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>

          {/* Add Question Form */}
          {showAddForm && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
              <div className="bg-gray-800 rounded-lg p-6 max-w-lg w-full">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-white">Add New Question</h3>
                  <button
                    onClick={() => setShowAddForm(false)}
                    className="text-gray-400 hover:text-white"
                  >
                    <X size={20} />
                  </button>
                </div>

                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-1">
                      Question Text
                    </label>
                    <textarea
                      value={newQuestion.question_text}
                      onChange={(e) => setNewQuestion(prev => ({ ...prev, question_text: e.target.value }))}
                      className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                      rows={3}
                      placeholder="How would you rate..."
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-1">
                        Category
                      </label>
                      <select
                        value={newQuestion.category}
                        onChange={(e) => setNewQuestion(prev => ({ ...prev, category: e.target.value as any }))}
                        className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                      >
                        <option value="satisfaction">Satisfaction</option>
                        <option value="workload">Workload</option>
                        <option value="support">Support</option>
                        <option value="engagement">Engagement</option>
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-1">
                        Type
                      </label>
                      <select
                        value={newQuestion.type}
                        onChange={(e) => setNewQuestion(prev => ({ ...prev, type: e.target.value as any }))}
                        className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                      >
                        <option value="scale">Scale (1-5)</option>
                        <option value="boolean">Yes/No</option>
                        <option value="choice">Multiple Choice</option>
                      </select>
                    </div>
                  </div>

                  <div className="flex justify-end space-x-3">
                    <button
                      onClick={() => setShowAddForm(false)}
                      className="px-4 py-2 text-gray-300 hover:text-white transition-colors"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={handleAddQuestion}
                      className="flex items-center space-x-2 bg-cyan-600 hover:bg-cyan-700 text-white px-4 py-2 rounded transition-colors"
                    >
                      <Save size={16} />
                      <span>Add Question</span>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Edit Question Form */}
          {editingQuestion && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
              <div className="bg-gray-800 rounded-lg p-6 max-w-lg w-full">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-white">Edit Question</h3>
                  <button
                    onClick={() => setEditingQuestion(null)}
                    className="text-gray-400 hover:text-white"
                  >
                    <X size={20} />
                  </button>
                </div>

                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-1">
                      Question Text
                    </label>
                    <textarea
                      value={editForm.question_text}
                      onChange={(e) => setEditForm(prev => ({ ...prev, question_text: e.target.value }))}
                      className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                      rows={3}
                      placeholder="How would you rate..."
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-1">
                        Category
                      </label>
                      <select
                        value={editForm.category}
                        onChange={(e) => setEditForm(prev => ({ ...prev, category: e.target.value as any }))}
                        className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                      >
                        <option value="satisfaction">Satisfaction</option>
                        <option value="workload">Workload</option>
                        <option value="support">Support</option>
                        <option value="engagement">Engagement</option>
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-1">
                        Type
                      </label>
                      <select
                        value={editForm.type}
                        onChange={(e) => setEditForm(prev => ({ ...prev, type: e.target.value as any }))}
                        className="w-full bg-gray-700 border border-gray-600 rounded px-3 py-2 text-white"
                      >
                        <option value="scale">Scale (1-5)</option>
                        <option value="boolean">Yes/No</option>
                        <option value="choice">Multiple Choice</option>
                      </select>
                    </div>
                  </div>

                  <div className="flex justify-end space-x-3">
                    <button
                      onClick={() => setEditingQuestion(null)}
                      className="px-4 py-2 text-gray-300 hover:text-white transition-colors"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={handleEditQuestion}
                      className="flex items-center space-x-2 bg-cyan-600 hover:bg-cyan-700 text-white px-4 py-2 rounded transition-colors"
                    >
                      <Save size={16} />
                      <span>Update Question</span>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </>
      ) : (
        /* Statistics View */
        <div className="space-y-6">
          {questionStats.length === 0 ? (
            <div className="text-center py-8">
              <BarChart3 className="mx-auto mb-4 text-gray-500" size={48} />
              <p className="text-gray-400 mb-2">No statistics available</p>
              <p className="text-gray-500 text-sm">
                Statistics will show once the database statistics function is configured and there are employee responses.
              </p>
              <div className="mt-4 bg-blue-900/20 border border-blue-500/30 rounded-lg p-4 text-left max-w-lg mx-auto">
                <h4 className="text-blue-400 font-medium mb-2">Setup Required:</h4>
                <p className="text-blue-300 text-sm">
                  The <code className="bg-gray-700 px-1 rounded">get_question_statistics</code> database function needs to be created to display pulse question analytics.
                </p>
              </div>
            </div>
          ) : (
            questionStats.map(stats => (
              <div key={stats.question_id} className="border border-gray-600 rounded-lg p-6">
                <div className="flex justify-between items-start mb-4">
                  <div>
                    <div className="flex items-center space-x-2 mb-2">
                      {getCategoryIcon(stats.category)}
                      <span className="text-xs text-gray-400 uppercase font-medium">
                        {stats.category}
                      </span>
                    </div>
                    <h3 className="text-lg font-semibold text-white mb-2">
                      {stats.question_text}
                    </h3>
                  </div>
                  <div className="text-right">
                    <p className="text-2xl font-bold text-cyan-400">
                      {(stats.avg_response || 0).toFixed(1)}/5.0
                    </p>
                    <p className="text-sm text-gray-400">
                      {stats.total_responses} responses
                    </p>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <h4 className="text-sm font-medium text-gray-300 mb-3">Response Distribution</h4>
                    {getResponseDistributionBar(stats)}
                    <div className="mt-2 space-y-1">
                      {[
                        { label: 'Excellent (5)', count: stats.response_5_count, color: 'text-green-400' },
                        { label: 'Good (4)', count: stats.response_4_count, color: 'text-blue-400' },
                        { label: 'Neutral (3)', count: stats.response_3_count, color: 'text-yellow-400' },
                        { label: 'Poor (2)', count: stats.response_2_count, color: 'text-orange-400' },
                        { label: 'Very Poor (1)', count: stats.response_1_count, color: 'text-red-400' }
                      ].map(item => (
                        <div key={item.label} className="flex justify-between text-xs">
                          <span className="text-gray-400">{item.label}</span>
                          <span className={item.color}>{item.count}</span>
                        </div>
                      ))}
                    </div>
                  </div>

                  <div>
                    <h4 className="text-sm font-medium text-gray-300 mb-3">Key Metrics</h4>
                    <div className="space-y-3">
                      <div className="flex justify-between">
                        <span className="text-gray-400">Satisfaction Rate</span>
                        <span className="text-green-400 font-medium">
                          {stats.satisfaction_percentage || 0}%
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-400">Total Responses</span>
                        <span className="text-white font-medium">
                          {stats.total_responses || 0}
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-400">Last Response</span>
                        <span className="text-gray-300">
                          {stats.last_response ? 
                            new Date(stats.last_response).toLocaleDateString() : 
                            'Never'
                          }
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

// src/components/analytics/ChartComponents.tsx - Reusable chart components
import React from 'react';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  LineChart,
  Line,
  PieChart,
  Pie,
  Cell,
  AreaChart,
  Area,
  RadialBarChart,
  RadialBar
} from 'recharts';

// Color palette for consistent theming
export const CHART_COLORS = {
  primary: '#0891b2',
  secondary: '#67e8f9',
  success: '#10b981',
  warning: '#f59e0b',
  danger: '#ef4444',
  info: '#3b82f6',
  purple: '#8b5cf6',
  pink: '#ec4899',
  gradient: ['#0891b2', '#67e8f9', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6']
};

// Custom tooltip for dark theme
const CustomTooltip = ({ active, payload, label }: any) => {
  if (active && payload && payload.length) {
    return (
      <div className="bg-gray-800 border border-gray-600 rounded-lg p-3 shadow-lg">
        <p className="text-gray-300 text-sm font-medium">{label}</p>
        {payload.map((pld: any, index: number) => (
          <div key={index} className="flex items-center space-x-2 mt-1">
            <div 
              className="w-3 h-3 rounded-full" 
              style={{ backgroundColor: pld.color }}
            />
            <span className="text-white text-sm">
              {pld.name}: {typeof pld.value === 'number' ? pld.value.toLocaleString() : pld.value}
            </span>
          </div>
        ))}
      </div>
    );
  }
  return null;
};

// Performance Overview Bar Chart
interface PerformanceBarChartProps {
  data: Array<{
    name: string;
    completed: number;
    pending: number;
    overdue: number;
  }>;
  height?: number;
}

export const PerformanceBarChart: React.FC<PerformanceBarChartProps> = ({ 
  data, 
  height = 300 
}) => (
  <ResponsiveContainer width="100%" height={height}>
    <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
      <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
      <XAxis 
        dataKey="name" 
        tick={{ fill: '#9ca3af', fontSize: 12 }}
        axisLine={{ stroke: '#4b5563' }}
      />
      <YAxis 
        tick={{ fill: '#9ca3af', fontSize: 12 }}
        axisLine={{ stroke: '#4b5563' }}
      />
      <Tooltip content={<CustomTooltip />} />
      <Legend 
        wrapperStyle={{ color: '#9ca3af' }}
      />
      <Bar dataKey="completed" fill={CHART_COLORS.success} name="Completed" />
      <Bar dataKey="pending" fill={CHART_COLORS.warning} name="Pending" />
      <Bar dataKey="overdue" fill={CHART_COLORS.danger} name="Overdue" />
    </BarChart>
  </ResponsiveContainer>
);

// Trend Analysis Line Chart
interface TrendLineChartProps {
  data: Array<{
    date: string;
    assessments: number;
    reviews: number;
    satisfaction: number;
  }>;
  height?: number;
}

export const TrendLineChart: React.FC<TrendLineChartProps> = ({ 
  data, 
  height = 300 
}) => (
  <ResponsiveContainer width="100%" height={height}>
    <LineChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
      <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
      <XAxis 
        dataKey="date" 
        tick={{ fill: '#9ca3af', fontSize: 12 }}
        axisLine={{ stroke: '#4b5563' }}
      />
      <YAxis 
        tick={{ fill: '#9ca3af', fontSize: 12 }}
        axisLine={{ stroke: '#4b5563' }}
      />
      <Tooltip content={<CustomTooltip />} />
      <Legend wrapperStyle={{ color: '#9ca3af' }} />
      <Line 
        type="monotone" 
        dataKey="assessments" 
        stroke={CHART_COLORS.primary} 
        strokeWidth={3}
        dot={{ fill: CHART_COLORS.primary, strokeWidth: 2, r: 4 }}
        name="Assessments"
      />
      <Line 
        type="monotone" 
        dataKey="reviews" 
        stroke={CHART_COLORS.secondary} 
        strokeWidth={3}
        dot={{ fill: CHART_COLORS.secondary, strokeWidth: 2, r: 4 }}
        name="Reviews"
      />
      <Line 
        type="monotone" 
        dataKey="satisfaction" 
        stroke={CHART_COLORS.success} 
        strokeWidth={3}
        dot={{ fill: CHART_COLORS.success, strokeWidth: 2, r: 4 }}
        name="Satisfaction %"
      />
    </LineChart>
  </ResponsiveContainer>
);

// Department Distribution Pie Chart
interface DepartmentPieChartProps {
  data: Array<{
    name: string;
    value: number;
    color?: string;
  }>;
  height?: number;
}

export const DepartmentPieChart: React.FC<DepartmentPieChartProps> = ({ 
  data, 
  height = 300 
}) => (
  <ResponsiveContainer width="100%" height={height}>
    <PieChart>
      <Pie
        data={data}
        cx="50%"
        cy="50%"
        labelLine={false}
        label={({ name, percent }: any) => `${name} ${((percent || 0) * 100).toFixed(0)}%`}
        outerRadius={80}
        fill="#8884d8"
        dataKey="value"
      >
        {data.map((entry, index) => (
          <Cell 
            key={`cell-${index}`} 
            fill={entry.color || CHART_COLORS.gradient[index % CHART_COLORS.gradient.length]} 
          />
        ))}
      </Pie>
      <Tooltip content={<CustomTooltip />} />
      <Legend wrapperStyle={{ color: '#9ca3af' }} />
    </PieChart>
  </ResponsiveContainer>
);

// Engagement Area Chart
interface EngagementAreaChartProps {
  data: Array<{
    month: string;
    engagement: number;
    satisfaction: number;
    participation: number;
  }>;
  height?: number;
}

export const EngagementAreaChart: React.FC<EngagementAreaChartProps> = ({ 
  data, 
  height = 300 
}) => (
  <ResponsiveContainer width="100%" height={height}>
    <AreaChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
      <defs>
        <linearGradient id="engagementGradient" x1="0" y1="0" x2="0" y2="1">
          <stop offset="5%" stopColor={CHART_COLORS.primary} stopOpacity={0.8}/>
          <stop offset="95%" stopColor={CHART_COLORS.primary} stopOpacity={0.1}/>
        </linearGradient>
        <linearGradient id="satisfactionGradient" x1="0" y1="0" x2="0" y2="1">
          <stop offset="5%" stopColor={CHART_COLORS.success} stopOpacity={0.8}/>
          <stop offset="95%" stopColor={CHART_COLORS.success} stopOpacity={0.1}/>
        </linearGradient>
      </defs>
      <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
      <XAxis 
        dataKey="month" 
        tick={{ fill: '#9ca3af', fontSize: 12 }}
        axisLine={{ stroke: '#4b5563' }}
      />
      <YAxis 
        tick={{ fill: '#9ca3af', fontSize: 12 }}
        axisLine={{ stroke: '#4b5563' }}
      />
      <Tooltip content={<CustomTooltip />} />
      <Legend wrapperStyle={{ color: '#9ca3af' }} />
      <Area
        type="monotone"
        dataKey="engagement"
        stackId="1"
        stroke={CHART_COLORS.primary}
        fill="url(#engagementGradient)"
        name="Engagement Score"
      />
      <Area
        type="monotone"
        dataKey="satisfaction"
        stackId="2"
        stroke={CHART_COLORS.success}
        fill="url(#satisfactionGradient)"
        name="Satisfaction Score"
      />
    </AreaChart>
  </ResponsiveContainer>
);

// Goal Progress Radial Chart
interface GoalProgressRadialProps {
  data: Array<{
    name: string;
    value: number;
    fill: string;
  }>;
  height?: number;
}

export const GoalProgressRadial: React.FC<GoalProgressRadialProps> = ({ 
  data, 
  height = 300 
}) => (
  <ResponsiveContainer width="100%" height={height}>
    <RadialBarChart cx="50%" cy="50%" innerRadius="10%" outerRadius="80%" data={data}>
      <RadialBar
        label={{ position: 'insideStart', fill: '#fff', fontSize: 12 }}
        background
        dataKey="value"
      />
      <Legend 
        iconSize={10} 
        wrapperStyle={{ color: '#9ca3af', fontSize: '12px' }} 
        verticalAlign="bottom" 
        align="center"
      />
      <Tooltip content={<CustomTooltip />} />
    </RadialBarChart>
  </ResponsiveContainer>
);

// Metric Card Component
interface MetricCardProps {
  title: string;
  value: string | number;
  change?: number;
  icon: React.ComponentType<{ size?: number; className?: string }>;
  color?: 'blue' | 'green' | 'yellow' | 'red' | 'purple';
  children?: React.ReactNode;
}

export const MetricCard: React.FC<MetricCardProps> = ({
  title,
  value,
  change,
  icon: Icon,
  color = 'blue',
  children
}) => {
  const colorClasses = {
    blue: { bg: 'bg-blue-500', text: 'text-blue-400', ring: 'ring-blue-500/20' },
    green: { bg: 'bg-green-500', text: 'text-green-400', ring: 'ring-green-500/20' },
    yellow: { bg: 'bg-yellow-500', text: 'text-yellow-400', ring: 'ring-yellow-500/20' },
    red: { bg: 'bg-red-500', text: 'text-red-400', ring: 'ring-red-500/20' },
    purple: { bg: 'bg-purple-500', text: 'text-purple-400', ring: 'ring-purple-500/20' }
  };

  const colors = colorClasses[color];

  return (
    <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-gray-400 text-sm font-medium">{title}</p>
          <p className="text-2xl font-bold text-white mt-1">{value}</p>
          {change !== undefined && (
            <div className="flex items-center mt-2">
              <span className={`text-sm font-medium ${
                change >= 0 ? 'text-green-400' : 'text-red-400'
              }`}>
                {change >= 0 ? '+' : ''}{change}%
              </span>
              <span className="text-gray-500 text-sm ml-1">vs last period</span>
            </div>
          )}
        </div>
        <div className={`p-3 rounded-full ${colors.bg}/10 ${colors.ring} ring-1`}>
          <Icon size={24} className={colors.text} />
        </div>
      </div>
      {children && (
        <div className="mt-4 pt-4 border-t border-gray-700">
          {children}
        </div>
      )}
    </div>
  );
};

// src/components/analytics/QuestionPerformanceWidget.tsx - Top/Bottom questions widget
import React, { useState, useEffect } from 'react';
import { 
  TrendingUp, 
  TrendingDown, 
  BarChart3, 
  Award, 
  AlertTriangle,
  RefreshCw
} from 'lucide-react';
import { supabase } from '../../services/supabaseClient';

interface QuestionPerformance {
  question_id: string;
  question_text: string;
  avg_response: number;
  total_responses: number;
  satisfaction_percentage: number;
  performance_rank: 'top' | 'bottom';
}

interface QuestionPerformanceWidgetProps {
  departmentFilter?: string;
  managerIdFilter?: string;
  title?: string;
  className?: string;
}

export default function QuestionPerformanceWidget({ 
  departmentFilter, 
  managerIdFilter, 
  title = "Question Performance",
  className = ""
}: QuestionPerformanceWidgetProps) {
  const [questionData, setQuestionData] = useState<QuestionPerformance[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const loadQuestionPerformance = async () => {
    try {
      setLoading(true);
      setError(null);

      const { data, error } = await supabase
        .rpc('get_question_performance_ranking', {
          department_filter: departmentFilter || null,
          manager_id_filter: managerIdFilter || null,
          days_back: 30
        });

      if (error) throw error;
      setQuestionData(data || []);
    } catch (err: any) {
      console.error('Error loading question performance:', err);
      let errorMessage = 'Failed to load question performance data';
      
      if (err.message && err.message.includes('ambiguous')) {
        errorMessage = 'Database function needs to be updated. Run fix_question_performance_function.sql';
      } else if (err.message && err.message.includes('function') && err.message.includes('does not exist')) {
        errorMessage = 'Database functions not found. Run pulse_questions_management.sql first';
      } else if (err.message) {
        errorMessage = err.message;
      }
      
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadQuestionPerformance();
  }, [departmentFilter, managerIdFilter]);

  const topQuestions = questionData.filter(q => q.performance_rank === 'top');
  const bottomQuestions = questionData.filter(q => q.performance_rank === 'bottom');

  const truncateText = (text: string, maxLength: number = 80) => {
    return text.length > maxLength ? text.substring(0, maxLength) + '...' : text;
  };

  const getPerformanceColor = (rank: 'top' | 'bottom', avgResponse: number) => {
    if (rank === 'top') {
      return avgResponse >= 4.5 ? 'text-green-400' : 'text-blue-400';
    } else {
      return avgResponse <= 2.5 ? 'text-red-400' : 'text-orange-400';
    }
  };

  const getPerformanceIcon = (rank: 'top' | 'bottom') => {
    return rank === 'top' ? 
      <TrendingUp className="text-green-400" size={16} /> : 
      <TrendingDown className="text-red-400" size={16} />;
  };

  if (loading) {
    return (
      <div className={`bg-gray-800 rounded-lg p-6 ${className}`}>
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-white flex items-center">
            <BarChart3 className="mr-2" size={20} />
            {title}
          </h3>
        </div>
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-cyan-400"></div>
          <span className="ml-2 text-gray-400">Loading performance data...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`bg-gray-800 rounded-lg p-6 ${className}`}>
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-white flex items-center">
            <BarChart3 className="mr-2" size={20} />
            {title}
          </h3>
          <button
            onClick={loadQuestionPerformance}
            className="p-1 text-gray-400 hover:text-cyan-400 transition-colors"
            title="Retry"
          >
            <RefreshCw size={16} />
          </button>
        </div>
        <div className="flex flex-col items-center justify-center py-8">
          <AlertTriangle className="text-red-400 mb-3" size={24} />
          <span className="text-red-400 text-center mb-4">{error}</span>
          {error.includes('fix_question_performance_function.sql') && (
            <div className="bg-red-900/20 border border-red-500/30 rounded-lg p-3 text-xs text-red-300 max-w-md">
              <p className="font-medium mb-2">To fix this error:</p>
              <ol className="list-decimal list-inside space-y-1">
                <li>Go to Supabase SQL Editor</li>
                <li>Run the file: <code className="bg-red-800 px-1 rounded">fix_question_performance_function.sql</code></li>
                <li>Refresh this page</li>
              </ol>
            </div>
          )}
        </div>
      </div>
    );
  }

  if (questionData.length === 0) {
    return (
      <div className={`bg-gray-800 rounded-lg p-6 ${className}`}>
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-white flex items-center">
            <BarChart3 className="mr-2" size={20} />
            {title}
          </h3>
        </div>
        <div className="text-center py-8">
          <BarChart3 className="mx-auto mb-3 text-gray-500" size={32} />
          <p className="text-gray-400">Not enough response data available</p>
          <p className="text-gray-500 text-sm">Need at least 3 responses per question</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`bg-gray-800 rounded-lg p-6 ${className}`}>
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-white flex items-center">
          <BarChart3 className="mr-2" size={20} />
          {title}
        </h3>
        <button
          onClick={loadQuestionPerformance}
          className="p-1 text-gray-400 hover:text-cyan-400 transition-colors"
          title="Refresh Data"
        >
          <RefreshCw size={16} />
        </button>
      </div>

      <div className="space-y-6">
        {/* Top Performing Questions */}
        {topQuestions.length > 0 && (
          <div>
            <div className="flex items-center mb-3">
              <Award className="text-green-400 mr-2" size={18} />
              <h4 className="text-green-400 font-medium">Top Performing Questions</h4>
            </div>
            <div className="space-y-3">
              {topQuestions.map((question, index) => (
                <div 
                  key={question.question_id} 
                  className="bg-green-900/20 border border-green-500/30 rounded-lg p-4"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center mb-2">
                        {getPerformanceIcon('top')}
                        <span className="ml-1 text-xs text-green-300 font-medium">
                          #{index + 1} Best
                        </span>
                      </div>
                      <p className="text-white text-sm font-medium mb-2">
                        {truncateText(question.question_text)}
                      </p>
                      <div className="flex items-center space-x-4 text-xs text-gray-400">
                        <span>{question.total_responses} responses</span>
                        <span>{question.satisfaction_percentage}% satisfied</span>
                      </div>
                    </div>
                    <div className="text-right ml-4">
                      <div className={`text-xl font-bold ${getPerformanceColor('top', question.avg_response)}`}>
                        {question.avg_response.toFixed(1)}
                      </div>
                      <div className="text-xs text-gray-400">/5.0</div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Bottom Performing Questions */}
        {bottomQuestions.length > 0 && (
          <div>
            <div className="flex items-center mb-3">
              <AlertTriangle className="text-red-400 mr-2" size={18} />
              <h4 className="text-red-400 font-medium">Areas for Improvement</h4>
            </div>
            <div className="space-y-3">
              {bottomQuestions.map((question, index) => (
                <div 
                  key={question.question_id} 
                  className="bg-red-900/20 border border-red-500/30 rounded-lg p-4"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center mb-2">
                        {getPerformanceIcon('bottom')}
                        <span className="ml-1 text-xs text-red-300 font-medium">
                          Needs Attention
                        </span>
                      </div>
                      <p className="text-white text-sm font-medium mb-2">
                        {truncateText(question.question_text)}
                      </p>
                      <div className="flex items-center space-x-4 text-xs text-gray-400">
                        <span>{question.total_responses} responses</span>
                        <span>{question.satisfaction_percentage}% satisfied</span>
                      </div>
                    </div>
                    <div className="text-right ml-4">
                      <div className={`text-xl font-bold ${getPerformanceColor('bottom', question.avg_response)}`}>
                        {question.avg_response.toFixed(1)}
                      </div>
                      <div className="text-xs text-gray-400">/5.0</div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Summary Footer */}
      <div className="mt-6 pt-4 border-t border-gray-600">
        <div className="flex justify-between items-center text-sm">
          <span className="text-gray-400">
            Based on {questionData.reduce((sum, q) => sum + q.total_responses, 0)} total responses
          </span>
          <span className="text-gray-500">Last 30 days</span>
        </div>
      </div>
    </div>
  );
}

// Simplified version for smaller dashboard cards
export function QuestionPerformanceCard({ 
  departmentFilter, 
  managerIdFilter, 
  showOnlyTop = true,
  className = ""
}: QuestionPerformanceWidgetProps & { showOnlyTop?: boolean }) {
  const [questionData, setQuestionData] = useState<QuestionPerformance[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadData = async () => {
      try {
        const { data, error } = await supabase
          .rpc('get_question_performance_ranking', {
            department_filter: departmentFilter || null,
            manager_id_filter: managerIdFilter || null,
            days_back: 30
          });

        if (error) throw error;
        setQuestionData(data || []);
      } catch (err: any) {
        console.error('Error loading question performance:', err);
        // For card view, we'll just show no data instead of detailed errors
        setQuestionData([]);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [departmentFilter, managerIdFilter]);

  const displayQuestions = showOnlyTop 
    ? questionData.filter(q => q.performance_rank === 'top').slice(0, 1)
    : questionData.filter(q => q.performance_rank === 'bottom').slice(0, 1);

  if (loading || displayQuestions.length === 0) {
    return (
      <div className={`bg-gray-800 rounded-lg p-4 ${className}`}>
        <div className="flex items-center justify-center">
          <span className="text-gray-400 text-sm">
            {loading ? 'Loading...' : 'No data'}
          </span>
        </div>
      </div>
    );
  }

  const question = displayQuestions[0];

  return (
    <div className={`bg-gray-800 rounded-lg p-4 ${className}`}>
      <div className="flex items-center mb-2">
        {showOnlyTop ? (
          <Award className="text-green-400 mr-2" size={16} />
        ) : (
          <AlertTriangle className="text-red-400 mr-2" size={16} />
        )}
        <h4 className={`text-sm font-medium ${
          showOnlyTop ? 'text-green-400' : 'text-red-400'
        }`}>
          {showOnlyTop ? 'Top Question' : 'Needs Improvement'}
        </h4>
      </div>
      
      <p className="text-white text-sm mb-3 line-clamp-2">
        {question.question_text}
      </p>
      
      <div className="flex justify-between items-center">
        <div className={`text-lg font-bold ${
          showOnlyTop ? 'text-green-400' : 'text-red-400'
        }`}>
          {question.avg_response.toFixed(1)}/5.0
        </div>
        <div className="text-xs text-gray-400">
          {question.total_responses} responses
        </div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { X, User, Mail, Briefcase, Shield, Users, Copy, Check } from 'lucide-react';
import { Button, LoadingSpinner } from '../ui';
import { AdminService } from '../../services';
import DepartmentService from '../../services/DepartmentService';

const CreateEmployeeModal = ({ supabase, closeModal, modalProps }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    jobTitle: '',
    role: 'employee',
    managerId: '',
    primaryDepartmentId: '',
    secondaryDepartmentId: '',
    password: ''
  });
  const [managers, setManagers] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(null);
  const [copied, setCopied] = useState(false);

  useEffect(() => {
    fetchManagers();
    fetchDepartments();
  }, []);

  const fetchManagers = async () => {
    try {
      const managersData = await AdminService.getPotentialManagers();
      setManagers(managersData);
    } catch (err) {
      console.error('Error fetching managers:', err);
    }
  };

  const fetchDepartments = async () => {
    try {
      const departmentsData = await DepartmentService.getAllDepartments();
      setDepartments(departmentsData);
    } catch (err) {
      console.error('Error fetching departments:', err);
      // Fallback to default departments if service fails
      setDepartments([
        { id: 'general', name: 'General' },
        { id: 'accounting', name: 'Accounting' },
        { id: 'purchasing', name: 'Purchasing' },
        { id: 'engineering', name: 'Engineering' },
        { id: 'executive', name: 'Executive' },
        { id: 'quality', name: 'Quality' },
        { id: 'production', name: 'Production' },
        { id: 'machining', name: 'Machining' },
        { id: 'program-management', name: 'Program Management' },
        { id: 'sales', name: 'Sales' }
      ]);
    }
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (error) setError('');
  };

  const handlePrimaryDepartmentChange = (departmentId) => {
    setFormData(prev => ({ 
      ...prev, 
      primaryDepartmentId: departmentId,
      // Clear secondary if it's the same as primary
      secondaryDepartmentId: prev.secondaryDepartmentId === departmentId ? '' : prev.secondaryDepartmentId
    }));
    if (error) setError('');
  };

  const handleSecondaryDepartmentChange = (departmentId) => {
    setFormData(prev => ({ ...prev, secondaryDepartmentId: departmentId }));
    if (error) setError('');
  };

  const validateForm = () => {
    if (!formData.name.trim()) {
      setError('Employee name is required');
      return false;
    }
    if (!formData.email.trim()) {
      setError('Email is required');
      return false;
    }
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      setError('Please enter a valid email address');
      return false;
    }
    if (!formData.jobTitle.trim()) {
      setError('Job title is required');
      return false;
    }
    if (!formData.password.trim()) {
      setError('Password is required');
      return false;
    }
    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return false;
    }
    if (!formData.primaryDepartmentId) {
      setError('Primary department is required');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    try {
      setLoading(true);
      setError('');

      const result = await AdminService.createEmployee({
        name: formData.name,
        email: formData.email,
        jobTitle: formData.jobTitle,
        role: formData.role,
        managerId: formData.managerId || null,
        password: formData.password
      });

      if (result.success && result.data?.employee_id) {
        try {
          // Assign primary department (required)
          await DepartmentService.assignPrimaryDepartment(
            result.data.employee_id, 
            formData.primaryDepartmentId
          );
          
          // Assign secondary department if selected
          if (formData.secondaryDepartmentId) {
            await DepartmentService.addSecondaryDepartment(
              result.data.employee_id,
              formData.secondaryDepartmentId
            );
          }
        } catch (deptError) {
          console.warn('Error assigning departments:', deptError);
          // Don't fail the entire process for department assignment errors
        }
        
        const selectedDepartments = [];
        if (formData.primaryDepartmentId) {
          selectedDepartments.push(departments.find(d => d.id === parseInt(formData.primaryDepartmentId))?.name);
        }
        if (formData.secondaryDepartmentId) {
          selectedDepartments.push(departments.find(d => d.id === parseInt(formData.secondaryDepartmentId))?.name);
        }
        
        setSuccess({
          ...result,
          selectedDepartments: selectedDepartments.filter(Boolean)
        });
      } else {
        setError(result.error || 'Failed to create employee');
      }
    } catch (err) {
      console.error('Error creating employee:', err);
      setError(err.message || 'Failed to create employee');
    } finally {
      setLoading(false);
    }
  };

  const copyInstructions = () => {
    if (success) {
      const instructions = AdminService.generateInvitationInstructions(
        { ...formData, role: formData.role },
        success.next_steps
      );
      navigator.clipboard.writeText(instructions.copyText);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  const handleFinish = () => {
    if (modalProps?.onComplete) {
      modalProps.onComplete();
    }
    closeModal();
  };

  // Success view
  if (success) {
    const instructions = AdminService.generateInvitationInstructions(
      { ...formData, role: formData.role },
      success.next_steps
    );

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-gray-800 rounded-lg shadow-2xl w-full max-w-2xl max-h-[90vh] flex flex-col">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-gray-700 flex-shrink-0">
            <div className="flex items-center space-x-3">
              <Check className="text-green-400" size={24} />
              <div>
                <h2 className="text-xl font-bold text-white">Employee Created Successfully!</h2>
                <p className="text-gray-400 text-sm">Send invitation instructions to the new employee</p>
              </div>
            </div>
            <button
              onClick={handleFinish}
              className="text-gray-400 hover:text-white transition-colors"
            >
              <X size={24} />
            </button>
          </div>

          {/* Content - Scrollable */}
          <div className="flex-1 overflow-y-auto p-6 space-y-6">
            {/* Employee Summary */}
            <div className="bg-gray-700 rounded-lg p-4">
              <h3 className="text-white font-medium mb-2">New Employee Details</h3>
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="text-gray-400">Name:</span>
                  <span className="text-white ml-2">{formData.name}</span>
                </div>
                <div>
                  <span className="text-gray-400">Email:</span>
                  <span className="text-white ml-2">{formData.email}</span>
                </div>
                <div>
                  <span className="text-gray-400">Role:</span>
                  <span className="text-white ml-2 capitalize">{formData.role}</span>
                </div>
                <div>
                  <span className="text-gray-400">Job Title:</span>
                  <span className="text-white ml-2">{formData.jobTitle}</span>
                </div>
                {success.selectedDepartments && success.selectedDepartments.length > 0 && (
                  <div className="col-span-2">
                    <span className="text-gray-400">Departments:</span>
                    <span className="text-white ml-2">
                      {success.selectedDepartments.join(', ')}
                    </span>
                  </div>
                )}
              </div>
            </div>

            {/* Instructions */}
            <div className="space-y-4">
              <h3 className="text-white font-medium">Next Steps:</h3>
              
              <div className="bg-blue-900 bg-opacity-50 rounded-lg p-4 border border-blue-700">
                <h4 className="text-blue-200 font-medium mb-2"> Email Template</h4>
                <div className="text-blue-300 text-sm space-y-2">
                  <div><strong>Subject:</strong> {instructions.subject}</div>
                  <div className="bg-blue-800 bg-opacity-50 p-3 rounded text-xs whitespace-pre-line">
                    {instructions.body}
                  </div>
                </div>
              </div>

              <div className="bg-gray-700 rounded-lg p-4">
                <h4 className="text-gray-200 font-medium mb-2">Quick Copy</h4>
                <div className="flex items-center space-x-2">
                  <code className="flex-1 text-xs bg-gray-800 p-2 rounded text-gray-300">
                    {instructions.copyText}
                  </code>
                  <Button
                    onClick={copyInstructions}
                    variant="secondary"
                    size="sm"
                  >
                    {copied ? (
                      <>
                        <Check size={14} className="mr-1" />
                        Copied!
                      </>
                    ) : (
                      <>
                        <Copy size={14} className="mr-1" />
                        Copy
                      </>
                    )}
                  </Button>
                </div>
              </div>
            </div>
          </div>

          {/* Actions - Fixed at bottom */}
          <div className="flex justify-end p-6 border-t border-gray-700 flex-shrink-0">
            <Button onClick={handleFinish} variant="primary">
              Done
            </Button>
          </div>
        </div>
      </div>
    );
  }

  // Form view
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-gray-800 rounded-lg shadow-2xl w-full max-w-md max-h-[90vh] flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-700 flex-shrink-0">
          <div className="flex items-center space-x-3">
            <User className="text-cyan-400" size={24} />
            <div>
              <h2 className="text-xl font-bold text-white">Create New Employee</h2>
              <p className="text-gray-400 text-sm">Add a new team member to EDGE</p>
            </div>
          </div>
          <button
            onClick={closeModal}
            className="text-gray-400 hover:text-white transition-colors"
          >
            <X size={24} />
          </button>
        </div>

        {/* Content - Scrollable */}
        <div className="flex-1 overflow-y-auto">
          <form onSubmit={handleSubmit} className="p-6 space-y-4">
          {error && (
            <div className="bg-red-900 border border-red-700 rounded-lg p-3">
              <p className="text-red-200 text-sm">{error}</p>
            </div>
          )}

          {/* Name */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Full Name *
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => handleInputChange('name', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              placeholder="John Doe"
              disabled={loading}
            />
          </div>

          {/* Email */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Email Address *
            </label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => handleInputChange('email', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              placeholder="john.doe@company.com"
              disabled={loading}
            />
          </div>

          {/* Password */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Initial Password *
            </label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) => handleInputChange('password', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              placeholder="Minimum 6 characters"
              disabled={loading}
            />
            <p className="text-xs text-gray-400">
              Employee can change this password after first login
            </p>
          </div>

          {/* Job Title */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Job Title *
            </label>
            <input
              type="text"
              value={formData.jobTitle}
              onChange={(e) => handleInputChange('jobTitle', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              placeholder="Software Developer"
              disabled={loading}
            />
          </div>

          {/* Role */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Role *
            </label>
            <select
              value={formData.role}
              onChange={(e) => handleInputChange('role', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            >
              <option value="employee">Employee</option>
              <option value="manager">Manager</option>
              <option value="admin">Admin</option>
            </select>
          </div>

          {/* Manager */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Manager (Optional)
            </label>
            <select
              value={formData.managerId}
              onChange={(e) => handleInputChange('managerId', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            >
              <option value="">No Manager</option>
              {managers.map((manager) => (
                <option key={manager.id} value={manager.id}>
                  {manager.name} ({manager.job_title})
                </option>
              ))}
            </select>
          </div>

          {/* Primary Department */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Primary Department *
            </label>
            <div className="grid grid-cols-1 gap-2 p-3 bg-gray-700 border border-gray-600 rounded-md max-h-48 overflow-y-auto">
              {departments.map((department) => (
                <label
                  key={department.id}
                  className="flex items-center space-x-2 cursor-pointer hover:bg-gray-600 p-2 rounded transition-colors"
                >
                  <input
                    type="radio"
                    name="primaryDepartment"
                    value={department.id}
                    checked={formData.primaryDepartmentId === department.id.toString()}
                    onChange={() => handlePrimaryDepartmentChange(department.id.toString())}
                    className="w-4 h-4 text-cyan-600 bg-gray-800 border-gray-600 focus:ring-cyan-500 focus:ring-2"
                    disabled={loading}
                  />
                  <span className="text-white text-sm flex-1">{department.name}</span>
                </label>
              ))}
            </div>
            {formData.primaryDepartmentId && (
              <p className="text-xs text-green-400">
                Primary: {departments.find(d => d.id.toString() === formData.primaryDepartmentId)?.name}
              </p>
            )}
          </div>

          {/* Secondary Department */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Secondary Department (Optional)
            </label>
            <select
              value={formData.secondaryDepartmentId}
              onChange={(e) => handleSecondaryDepartmentChange(e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            >
              <option value="">No Secondary Department</option>
              {departments
                .filter(dept => dept.id.toString() !== formData.primaryDepartmentId)
                .map((department) => (
                <option key={department.id} value={department.id}>
                  {department.name}
                </option>
              ))}
            </select>
            {formData.secondaryDepartmentId && (
              <p className="text-xs text-blue-400">
                Secondary: {departments.find(d => d.id.toString() === formData.secondaryDepartmentId)?.name}
              </p>
            )}
          </div>

          {/* Info Box */}
          <div className="bg-blue-900 bg-opacity-50 rounded-lg p-4 border border-blue-700">
            <h4 className="text-blue-200 font-medium mb-2">What happens next?</h4>
            <ul className="text-blue-300 text-sm space-y-1">
              <li> Employee record will be created in the system</li>
              <li> You'll get invitation instructions to send them</li>
              <li> They'll need to sign up using the provided email</li>
              <li> Role-based permissions will be automatically applied</li>
            </ul>
          </div>

          </form>
        </div>

        {/* Actions - Fixed at bottom */}
        <div className="flex justify-end space-x-3 p-6 border-t border-gray-700 flex-shrink-0">
          <Button
            type="button"
            onClick={closeModal}
            variant="secondary"
            disabled={loading}
          >
            Cancel
          </Button>
          <Button
            onClick={handleSubmit}
            variant="primary"
            disabled={loading || !formData.name.trim() || !formData.email.trim() || !formData.jobTitle.trim() || !formData.password.trim() || !formData.primaryDepartmentId}
          >
            {loading ? (
              <>
                <LoadingSpinner size="sm" className="mr-2" />
                Creating...
              </>
            ) : (
              <>
                <User size={16} className="mr-2" />
                Create Employee
              </>
            )}
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CreateEmployeeModal;

import React, { useState } from 'react';
import { X, Calendar, Plus } from 'lucide-react';
import { Button, LoadingSpinner } from '../ui';

const CreateReviewCycleModal = ({ supabase, closeModal, modalProps }) => {
  const [formData, setFormData] = useState({
    name: '',
    startDate: '',
    endDate: '',
    dueDate: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (error) setError(''); // Clear error when user types
  };

  const validateForm = () => {
    if (!formData.name.trim()) {
      setError('Review cycle name is required');
      return false;
    }
    if (!formData.startDate) {
      setError('Start date is required');
      return false;
    }
    if (!formData.endDate) {
      setError('End date is required');
      return false;
    }
    if (!formData.dueDate) {
      setError('Due date is required');
      return false;
    }
    if (new Date(formData.startDate) >= new Date(formData.endDate)) {
      setError('End date must be after start date');
      return false;
    }
    if (new Date(formData.dueDate) < new Date(formData.startDate) || new Date(formData.dueDate) > new Date(formData.endDate)) {
      setError('Due date must be between start date and end date');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    try {
      setLoading(true);
      setError('');

      // Step 1: Create the review cycle
      const { data: createData, error: createError } = await supabase.rpc('create_simple_review_cycle', {
        p_name: formData.name.trim(),
        p_start_date: formData.startDate,
        p_end_date: formData.endDate
      });

      if (createError) throw createError;

      if (createData?.error) {
        setError(createData.error);
        return;
      }

      // Step 2: Activate the review cycle using the new reliable function
      const cycleId = createData?.cycle_id;
      if (cycleId) {
        try {
          console.log('Activating review cycle with reliable function:', cycleId);
          const { data: activateData, error: activateError } = await supabase.rpc('activate_review_cycle', {
            p_cycle_id: cycleId
          });
          
          if (activateError) throw activateError;
          
          if (activateData?.error) {
            console.error('Activation error:', activateData.error);
            alert(`Review cycle created but failed to activate: ${activateData.error}`);
          } else {
            // Set due dates for all assessments in this cycle
            try {
              const { error: dueDateError } = await supabase
                .from('assessments')
                .update({ due_date: formData.dueDate })
                .eq('review_cycle_id', cycleId);
                
              if (dueDateError) {
                console.error('Error setting due dates:', dueDateError);
                alert(`Review cycle activated successfully but failed to set due dates: ${dueDateError.message}`);
              } else {
                alert(`Review cycle activated successfully! Created ${activateData.assessments_created} assessments with due date ${formData.dueDate}.`);
              }
            } catch (dueDateErr) {
              console.error('Error setting due dates:', dueDateErr);
              alert(`Review cycle activated successfully but failed to set due dates: ${dueDateErr.message}`);
            }
          }
        } catch (err) {
          console.error('Error activating review cycle:', err);
          alert(`Review cycle created but failed to activate: ${err.message}`);
        }
      }

      // Success - call completion callback and close modal
      if (modalProps?.onComplete) {
        modalProps.onComplete();
      }
      
      closeModal();
      
    } catch (err) {
      console.error('Error creating review cycle:', err);
      setError(err.message || 'Failed to create review cycle');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    closeModal();
  };

  // Set default dates (current quarter)
  React.useEffect(() => {
    const now = new Date();
    const currentYear = now.getFullYear();
    const currentMonth = now.getMonth();
    
    // Default to current quarter
    let startMonth, endMonth;
    if (currentMonth < 3) { // Q1
      startMonth = 0; endMonth = 2;
    } else if (currentMonth < 6) { // Q2
      startMonth = 3; endMonth = 5;
    } else if (currentMonth < 9) { // Q3
      startMonth = 6; endMonth = 8;
    } else { // Q4
      startMonth = 9; endMonth = 11;
    }

    const startDate = new Date(currentYear, startMonth, 1).toISOString().split('T')[0];
    const endDate = new Date(currentYear, endMonth + 1, 0).toISOString().split('T')[0];
    
    // Default due date to 2 weeks before end date
    const dueDate = new Date(currentYear, endMonth + 1, 0);
    dueDate.setDate(dueDate.getDate() - 14);
    const dueDateString = dueDate.toISOString().split('T')[0];
    
    setFormData(prev => ({
      ...prev,
      startDate: prev.startDate || startDate,
      endDate: prev.endDate || endDate,
      dueDate: prev.dueDate || dueDateString
    }));
  }, []);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-gray-800 rounded-lg shadow-2xl w-full max-w-md">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-700">
          <div className="flex items-center space-x-3">
            <Calendar className="text-cyan-400" size={24} />
            <div>
              <h2 className="text-xl font-bold text-white">Create Review Cycle</h2>
              <p className="text-gray-400 text-sm">Set up a new quarterly review period</p>
            </div>
          </div>
          <button
            onClick={handleCancel}
            className="text-gray-400 hover:text-white transition-colors"
          >
            <X size={24} />
          </button>
        </div>

        {/* Content */}
        <form onSubmit={handleSubmit} className="p-6 space-y-4">
          {error && (
            <div className="bg-red-900 border border-red-700 rounded-lg p-3">
              <p className="text-red-200 text-sm">{error}</p>
            </div>
          )}

          {/* Review Cycle Name */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Review Cycle Name *
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => handleInputChange('name', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              placeholder="e.g., Q4 2024 Performance Review"
              disabled={loading}
            />
          </div>

          {/* Start Date */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Start Date *
            </label>
            <input
              type="date"
              value={formData.startDate}
              onChange={(e) => handleInputChange('startDate', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            />
          </div>

          {/* End Date */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              End Date *
            </label>
            <input
              type="date"
              value={formData.endDate}
              onChange={(e) => handleInputChange('endDate', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            />
          </div>

          {/* Due Date */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Assessment Due Date *
            </label>
            <input
              type="date"
              value={formData.dueDate}
              onChange={(e) => handleInputChange('dueDate', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            />
            <p className="text-xs text-gray-400">
              Date when employees must complete their self-assessments
            </p>
          </div>

          {/* Info Box */}
          <div className="bg-blue-900 bg-opacity-50 rounded-lg p-4 border border-blue-700">
            <h4 className="text-blue-200 font-medium mb-2">Review Cycle Info:</h4>
            <ul className="text-blue-300 text-sm space-y-1">
              <li> Creates a new performance review period</li>
              <li> Employees can complete self-assessments</li>
              <li> Managers can review and provide feedback</li>
              <li> Cycle starts in "upcoming" status</li>
            </ul>
          </div>

          {/* Actions */}
          <div className="flex justify-end space-x-3 pt-4">
            <Button
              type="button"
              onClick={handleCancel}
              variant="secondary"
              disabled={loading}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              variant="primary"
              disabled={loading || !formData.name.trim() || !formData.startDate || !formData.endDate || !formData.dueDate}
            >
              {loading ? (
                <>
                  <LoadingSpinner size="sm" className="mr-2" />
                  Creating...
                </>
              ) : (
                <>
                  <Plus size={16} className="mr-2" />
                  Create Cycle
                </>
              )}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateReviewCycleModal;

import React, { useState, useEffect } from 'react';
import { X, User, Mail, Briefcase, Shield, Users, Save, Trash2 } from 'lucide-react';
import { Button, LoadingSpinner } from '../ui';
import { AdminService, DepartmentService } from '../../services';
import { validateEmployeeForm } from '../../utils/validation';

const EditEmployeeModal = ({ supabase, closeModal, modalProps }) => {
  const { employee, onComplete } = modalProps;
  
  const [formData, setFormData] = useState({
    name: employee?.name || '',
    email: employee?.email || '',
    jobTitle: employee?.job_title || '',
    role: employee?.role || 'employee',
    managerId: employee?.manager_id || '',
    isActive: employee?.is_active !== false,
    primaryDepartmentId: '',
    secondaryDepartmentId: ''
  });
  
  const [managers, setManagers] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [hasChanges, setHasChanges] = useState(false);

  useEffect(() => {
    fetchManagers();
    fetchDepartments();
    fetchEmployeeDetails();
  }, []);

  useEffect(() => {
    // Check if form data has changed from original
    const original = {
      name: employee?.name || '',
      email: employee?.email || '',
      jobTitle: employee?.job_title || '',
      role: employee?.role || 'employee',
      managerId: employee?.manager_id || '',
      isActive: employee?.is_active !== false,
      primaryDepartmentId: '', // Will be set by fetchEmployeeDetails
      secondaryDepartmentId: '' // Will be set by fetchEmployeeDetails
    };
    
    const changed = Object.keys(formData).some(key => {
      if (key === 'primaryDepartmentId' || key === 'secondaryDepartmentId') {
        // For department fields, any change from initial loading counts
        return true; // We'll let the save logic handle department changes
      }
      return formData[key] !== original[key];
    });
    setHasChanges(changed);
  }, [formData, employee]);

  const fetchManagers = async () => {
    try {
      const managersData = await AdminService.getPotentialManagers();
      setManagers(managersData);
    } catch (err) {
      console.error('Error fetching managers:', err);
    }
  };

  const fetchDepartments = async () => {
    try {
      const departmentsData = await DepartmentService.getAllDepartments();
      console.log(' EditEmployeeModal: Departments loaded:', departmentsData);
      setDepartments(departmentsData || []);
    } catch (err) {
      console.error(' Error fetching departments:', err);
      // Fallback to direct Supabase query if service fails
      try {
        const { data, error } = await supabase
          .from('departments')
          .select('*')
          .order('name');
        
        if (error) throw error;
        console.log(' EditEmployeeModal: Fallback departments loaded:', data);
        setDepartments(data || []);
      } catch (fallbackErr) {
        console.error(' Fallback department fetch failed:', fallbackErr);
        setDepartments([]);
      }
    }
  };

  const fetchEmployeeDetails = async () => {
    try {
      const employeeDepartments = await DepartmentService.getEmployeeDepartments(employee.id);
      console.log(' EditEmployeeModal: Employee departments loaded:', employeeDepartments);
      
      const primaryDept = employeeDepartments.find(ed => ed.is_primary);
      const secondaryDept = employeeDepartments.find(ed => !ed.is_primary);
      
      console.log(' Primary dept:', primaryDept);
      console.log(' Secondary dept:', secondaryDept);
      
      setFormData(prev => ({
        ...prev,
        primaryDepartmentId: primaryDept ? primaryDept.department_id.toString() : '',
        secondaryDepartmentId: secondaryDept ? secondaryDept.department_id.toString() : ''
      }));
    } catch (err) {
      console.error(' Error fetching employee details:', err);
      // Try fallback approach
      try {
        const { data, error } = await supabase
          .from('employee_departments')
          .select(`
            *,
            department:departments(*)
          `)
          .eq('employee_id', employee.id);
        
        if (error) throw error;
        console.log(' Fallback employee departments:', data);
        
        const primaryDept = data.find(ed => ed.is_primary);
        const secondaryDept = data.find(ed => !ed.is_primary);
        
        setFormData(prev => ({
          ...prev,
          primaryDepartmentId: primaryDept ? primaryDept.department_id.toString() : '',
          secondaryDepartmentId: secondaryDept ? secondaryDept.department_id.toString() : ''
        }));
      } catch (fallbackErr) {
        console.error(' Fallback employee details fetch failed:', fallbackErr);
      }
    }
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (error) setError('');
  };

  const handlePrimaryDepartmentChange = (departmentId) => {
    setFormData(prev => ({ 
      ...prev, 
      primaryDepartmentId: departmentId,
      // Clear secondary if it's the same as primary
      secondaryDepartmentId: prev.secondaryDepartmentId === departmentId ? '' : prev.secondaryDepartmentId
    }));
    if (error) setError('');
  };

  const handleSecondaryDepartmentChange = (departmentId) => {
    setFormData(prev => ({ ...prev, secondaryDepartmentId: departmentId }));
    if (error) setError('');
  };

  const validateForm = () => {
    // Only validate changed fields
    const validation = validateEmployeeForm(formData);
    if (!validation.isValid) {
      setError(Object.values(validation.errors).join(', '));
      return false;
    }
    
    // Additional validation for primary department
    if (!formData.primaryDepartmentId) {
      setError('Primary department is required');
      return false;
    }
    
    return true;
  };

  const handleSave = async () => {
    if (!validateForm()) return;
    if (!hasChanges) {
      setError('No changes to save');
      return;
    }

    try {
      setLoading(true);
      setError('');

      // Prepare update data - only include changed fields
      const updateData = {};
      const original = {
        name: employee?.name || '',
        email: employee?.email || '',
        jobTitle: employee?.job_title || '',
        role: employee?.role || 'employee',
        managerId: employee?.manager_id || '',
        isActive: employee?.is_active !== false
      };

      Object.keys(formData).forEach(key => {
        if (formData[key] !== original[key]) {
          updateData[key] = formData[key];
        }
      });

      const result = await AdminService.updateEmployee(employee.id, updateData);

      if (result.success) {
        // Update departments using the new service
        try {
          // Assign primary department (required)
          if (formData.primaryDepartmentId) {
            await DepartmentService.assignPrimaryDepartment(
              employee.id, 
              formData.primaryDepartmentId
            );
          }
          
          // Handle secondary department
          const currentDepts = await DepartmentService.getEmployeeDepartments(employee.id);
          const currentSecondary = currentDepts.find(ed => !ed.is_primary);
          
          // Remove old secondary if exists and different from new
          if (currentSecondary && currentSecondary.department_id.toString() !== formData.secondaryDepartmentId) {
            await DepartmentService.removeDepartmentAssignment(employee.id, currentSecondary.department_id);
          }
          
          // Add new secondary if selected
          if (formData.secondaryDepartmentId && 
              (!currentSecondary || currentSecondary.department_id.toString() !== formData.secondaryDepartmentId)) {
            await DepartmentService.addSecondaryDepartment(
              employee.id,
              formData.secondaryDepartmentId
            );
          }
        } catch (deptErr) {
          console.error('Error updating employee departments:', deptErr);
          // Still proceed with success, but note the department update may have failed
        }

        if (onComplete) onComplete();
        closeModal();
        // Show success message
        setTimeout(() => {
          alert('Employee updated successfully!');
        }, 100);
      } else {
        setError(result.error || 'Failed to update employee');
      }
    } catch (err) {
      console.error('Error updating employee:', err);
      setError(err.message || 'Failed to update employee');
    } finally {
      setLoading(false);
    }
  };

  const handleDeactivate = async () => {
    if (!confirm(`Are you sure you want to ${formData.isActive ? 'deactivate' : 'reactivate'} ${employee.name}?`)) {
      return;
    }

    try {
      setLoading(true);
      setError('');

      const result = await AdminService.updateEmployee(employee.id, {
        isActive: !formData.isActive
      });

      if (result.success) {
        setFormData(prev => ({ ...prev, isActive: !prev.isActive }));
        if (onComplete) onComplete();
        alert(`Employee ${!formData.isActive ? 'reactivated' : 'deactivated'} successfully!`);
      } else {
        setError(result.error || 'Failed to update employee status');
      }
    } catch (err) {
      console.error('Error updating employee status:', err);
      setError(err.message || 'Failed to update employee status');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (hasChanges && !confirm('You have unsaved changes. Are you sure you want to close?')) {
      return;
    }
    closeModal();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-gray-800 rounded-lg shadow-2xl w-full max-w-md max-h-[90vh] flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-700 flex-shrink-0">
          <div className="flex items-center space-x-3">
            <User className="text-cyan-400" size={24} />
            <div>
              <h2 className="text-xl font-bold text-white">Edit Employee</h2>
              <p className="text-gray-400 text-sm">Update {employee?.name}'s information</p>
            </div>
          </div>
          <button
            onClick={handleCancel}
            className="text-gray-400 hover:text-white transition-colors"
          >
            <X size={24} />
          </button>
        </div>

        {/* Scrollable Content */}
        <div className="p-6 space-y-4 overflow-y-auto flex-1">
          {error && (
            <div className="bg-red-900 border border-red-700 rounded-lg p-3">
              <p className="text-red-200 text-sm">{error}</p>
            </div>
          )}

          {/* Current Status */}
          <div className="bg-gray-700 rounded-lg p-3">
            <div className="flex items-center justify-between">
              <span className="text-gray-300 text-sm">Status:</span>
              <span className={`px-2 py-1 rounded text-xs font-medium ${
                formData.isActive 
                  ? 'bg-green-900 text-green-200' 
                  : 'bg-red-900 text-red-200'
              }`}>
                {formData.isActive ? 'Active' : 'Inactive'}
              </span>
            </div>
          </div>

          {/* Name */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Full Name *
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => handleInputChange('name', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              placeholder="John Doe"
              disabled={loading}
            />
          </div>

          {/* Email */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Email Address *
            </label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => handleInputChange('email', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              placeholder="john.doe@company.com"
              disabled={loading}
            />
          </div>

          {/* Job Title */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Job Title *
            </label>
            <input
              type="text"
              value={formData.jobTitle}
              onChange={(e) => handleInputChange('jobTitle', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              placeholder="Software Developer"
              disabled={loading}
            />
          </div>

          {/* Role */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Role *
            </label>
            <select
              value={formData.role}
              onChange={(e) => handleInputChange('role', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            >
              <option value="employee">Employee</option>
              <option value="manager">Manager</option>
              <option value="admin">Admin</option>
            </select>
          </div>

          {/* Manager */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Manager (Optional)
            </label>
            <select
              value={formData.managerId}
              onChange={(e) => handleInputChange('managerId', e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            >
              <option value="">No Manager</option>
              {managers.map((manager) => (
                <option key={manager.id} value={manager.id}>
                  {manager.name} ({manager.job_title})
                </option>
              ))}
            </select>
          </div>

          {/* Primary Department */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Primary Department *
            </label>
            <select
              value={formData.primaryDepartmentId}
              onChange={(e) => handlePrimaryDepartmentChange(e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            >
              <option value="">Select Primary Department</option>
              {departments.map((department) => (
                <option key={department.id} value={department.id}>
                  {department.name}
                </option>
              ))}
            </select>
            {formData.primaryDepartmentId && (
              <p className="text-xs text-green-400">
                Primary: {departments.find(d => d.id.toString() === formData.primaryDepartmentId)?.name}
              </p>
            )}
            {departments.length === 0 && (
              <p className="text-xs text-red-400">
                 No departments available. Check console for errors.
              </p>
            )}
          </div>

          {/* Secondary Department */}
          <div className="space-y-2">
            <label className="block text-sm font-medium text-gray-300">
              Secondary Department (Optional)
            </label>
            <select
              value={formData.secondaryDepartmentId}
              onChange={(e) => handleSecondaryDepartmentChange(e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              disabled={loading}
            >
              <option value="">No Secondary Department</option>
              {departments
                .filter(dept => dept.id.toString() !== formData.primaryDepartmentId)
                .map((department) => (
                <option key={department.id} value={department.id}>
                  {department.name}
                </option>
              ))}
            </select>
            {formData.secondaryDepartmentId && (
              <p className="text-xs text-blue-400">
                Secondary: {departments.find(d => d.id.toString() === formData.secondaryDepartmentId)?.name}
              </p>
            )}
          </div>

          {/* Change Indicator */}
          {hasChanges && (
            <div className="bg-blue-900 bg-opacity-50 rounded-lg p-3 border border-blue-700">
              <p className="text-blue-200 text-sm">
                 You have unsaved changes
              </p>
            </div>
          )}

          {/* Actions */}
          <div className="flex flex-col space-y-3 pt-4">
            {/* Save Button */}
            <Button
              onClick={handleSave}
              variant="primary"
              disabled={loading || !hasChanges}
              className="w-full"
            >
              {loading ? (
                <>
                  <LoadingSpinner size="sm" className="mr-2" />
                  Saving...
                </>
              ) : (
                <>
                  <Save size={16} className="mr-2" />
                  Save Changes
                </>
              )}
            </Button>

            {/* Secondary Actions */}
            <div className="flex space-x-3">
              <Button
                onClick={handleDeactivate}
                variant={formData.isActive ? "danger" : "success"}
                disabled={loading}
                className="flex-1"
              >
                {formData.isActive ? (
                  <>
                    <Trash2 size={16} className="mr-2" />
                    Deactivate
                  </>
                ) : (
                  <>
                    <User size={16} className="mr-2" />
                    Reactivate
                  </>
                )}
              </Button>

              <Button
                onClick={handleCancel}
                variant="secondary"
                disabled={loading}
                className="flex-1"
              >
                Cancel
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditEmployeeModal;

import React, { useState, useEffect } from 'react';
import { X, Send, MessageSquare, User, Star, AlertCircle } from 'lucide-react';
import { Button, LoadingSpinner } from '../ui';
import { useApp } from '../../contexts';

const GiveFeedbackModal = ({ supabase, closeModal, modalProps }) => {
  const { userName } = useApp();
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [formData, setFormData] = useState({
    recipientId: modalProps?.recipientId || '',
    feedbackType: 'positive',
    message: '',
    category: 'general',
    anonymous: false
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const { data, error } = await supabase.rpc('get_employees_for_feedback');
      if (error) throw error;
      setEmployees(data || []);
    } catch (error) {
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.recipientId) {
      newErrors.recipientId = 'Please select a recipient';
    }
    if (!formData.message.trim()) {
      newErrors.message = 'Please provide feedback message';
    }
    if (formData.message.trim().length < 10) {
      newErrors.message = 'Feedback should be at least 10 characters';
    }
    if (formData.message.trim().length > 500) {
      newErrors.message = 'Feedback should be less than 500 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    try {
      setSubmitting(true);
      
      const { error } = await supabase.rpc('give_peer_feedback', {
        p_recipient_id: formData.recipientId,
        p_feedback_type: formData.feedbackType,
        p_message: formData.message.trim(),
        p_category: formData.category,
        p_is_anonymous: formData.anonymous
      });

      if (error) throw error;

      // Call completion callback
      if (modalProps?.onComplete) {
        modalProps.onComplete();
      }

      closeModal();
      
      // Show success message
      alert('Feedback sent successfully! This will help foster continuous dialogue and growth.');
      
    } catch (error) {
      console.error('Error submitting feedback:', error);
      alert('Error sending feedback: ' + error.message);
    } finally {
      setSubmitting(false);
    }
  };

  const feedbackTypes = [
    { value: 'positive', label: 'Positive Recognition', icon: Star, color: 'text-yellow-400' },
    { value: 'constructive', label: 'Constructive Feedback', icon: MessageSquare, color: 'text-blue-400' },
    { value: 'appreciation', label: 'Thank You', icon: User, color: 'text-green-400' }
  ];

  const categories = [
    { value: 'general', label: 'General Feedback' },
    { value: 'teamwork', label: 'Teamwork & Collaboration' },
    { value: 'communication', label: 'Communication' },
    { value: 'core_values', label: 'Core Values Demonstration' },
    { value: 'innovation', label: 'Innovation & Problem Solving' },
    { value: 'leadership', label: 'Leadership & Initiative' }
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-gray-800 rounded-lg shadow-2xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-700">
          <div className="flex items-center space-x-3">
            <MessageSquare className="text-cyan-400" size={24} />
            <div>
              <h2 className="text-xl font-bold text-white">Give Real-Time Feedback</h2>
              <p className="text-gray-400 text-sm">Foster continuous dialogue and growth</p>
            </div>
          </div>
          <button
            onClick={closeModal}
            className="text-gray-400 hover:text-white transition-colors"
          >
            <X size={24} />
          </button>
        </div>

        {/* Content */}
        <div className="p-6">
          {loading ? (
            <div className="text-center py-8">
              <LoadingSpinner size="md" message="Loading employees..." />
            </div>
          ) : (
            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Recipient Selection */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-300">
                  Give feedback to: *
                </label>
                <select
                  value={formData.recipientId}
                  onChange={(e) => handleInputChange('recipientId', e.target.value)}
                  className={`w-full p-3 bg-gray-700 border rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent ${
                    errors.recipientId ? 'border-red-500' : 'border-gray-600'
                  }`}
                >
                  <option value="">Select a colleague...</option>
                  {employees.map(employee => (
                    <option key={employee.employee_id} value={employee.employee_id}>
                      {employee.name} - {employee.job_title || 'Employee'}
                    </option>
                  ))}
                </select>
                {errors.recipientId && (
                  <p className="text-red-400 text-sm flex items-center">
                    <AlertCircle size={14} className="mr-1" />
                    {errors.recipientId}
                  </p>
                )}
              </div>

              {/* Feedback Type */}
              <div className="space-y-3">
                <label className="block text-sm font-medium text-gray-300">
                  Feedback Type: *
                </label>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                  {feedbackTypes.map(type => {
                    const Icon = type.icon;
                    return (
                      <button
                        key={type.value}
                        type="button"
                        onClick={() => handleInputChange('feedbackType', type.value)}
                        className={`p-3 border rounded-lg text-left transition-colors ${
                          formData.feedbackType === type.value
                            ? 'border-cyan-500 bg-cyan-900 bg-opacity-50'
                            : 'border-gray-600 bg-gray-700 hover:border-gray-500'
                        }`}
                      >
                        <div className="flex items-center space-x-2">
                          <Icon size={18} className={type.color} />
                          <span className="text-white text-sm font-medium">{type.label}</span>
                        </div>
                      </button>
                    );
                  })}
                </div>
              </div>

              {/* Category */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-300">
                  Category:
                </label>
                <select
                  value={formData.category}
                  onChange={(e) => handleInputChange('category', e.target.value)}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                >
                  {categories.map(category => (
                    <option key={category.value} value={category.value}>
                      {category.label}
                    </option>
                  ))}
                </select>
              </div>

              {/* Feedback Message */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-300">
                  Your Feedback: *
                </label>
                <textarea
                  value={formData.message}
                  onChange={(e) => handleInputChange('message', e.target.value)}
                  className={`w-full p-3 bg-gray-700 border rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent ${
                    errors.message ? 'border-red-500' : 'border-gray-600'
                  }`}
                  rows={4}
                  placeholder={
                    formData.feedbackType === 'positive' 
                      ? "Share what they did well and how it made a positive impact..."
                      : formData.feedbackType === 'constructive'
                      ? "Provide specific, actionable feedback to help them grow..."
                      : "Express your appreciation for their help, support, or collaboration..."
                  }
                />
                <div className="flex justify-between items-center">
                  {errors.message ? (
                    <p className="text-red-400 text-sm flex items-center">
                      <AlertCircle size={14} className="mr-1" />
                      {errors.message}
                    </p>
                  ) : (
                    <p className="text-gray-400 text-xs">
                      {formData.message.length}/500 characters
                    </p>
                  )}
                </div>
              </div>

              {/* Anonymous Option */}
              <div className="flex items-center space-x-3">
                <input
                  type="checkbox"
                  id="anonymous"
                  checked={formData.anonymous}
                  onChange={(e) => handleInputChange('anonymous', e.target.checked)}
                  className="w-4 h-4 text-cyan-600 bg-gray-700 border-gray-600 rounded focus:ring-cyan-500"
                />
                <label htmlFor="anonymous" className="text-sm text-gray-300">
                  Send this feedback anonymously
                </label>
              </div>

              {/* Guidelines */}
              <div className="bg-blue-900 bg-opacity-50 rounded-lg p-4 border border-blue-700">
                <h4 className="text-blue-200 font-medium mb-2">Feedback Guidelines:</h4>
                <ul className="text-blue-300 text-xs space-y-1">
                  <li> Be specific and focus on behaviors, not personality</li>
                  <li> Provide actionable insights when giving constructive feedback</li>
                  <li> Celebrate wins and recognize great work with positive feedback</li>
                  <li> Keep it professional and respectful</li>
                  <li> Focus on growth and improvement opportunities</li>
                </ul>
              </div>

              {/* Actions */}
              <div className="flex justify-end space-x-3 pt-4 border-t border-gray-700">
                <Button
                  type="button"
                  onClick={closeModal}
                  variant="secondary"
                  disabled={submitting}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  variant="primary"
                  disabled={submitting || !formData.recipientId || !formData.message.trim()}
                >
                  {submitting ? (
                    <>
                      <LoadingSpinner size="sm" className="mr-2" />
                      Sending...
                    </>
                  ) : (
                    <>
                      <Send size={16} className="mr-2" />
                      Send Feedback
                    </>
                  )}
                </Button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default GiveFeedbackModal;

// src/components/modals/GiveKudoModal.js
import React, { useState, useEffect } from 'react';
import { Award, X } from 'lucide-react';

const GiveKudoModal = ({ supabase, closeModal, modalProps }) => {
    const [employees, setEmployees] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState('');
    const [selectedCoreValue, setSelectedCoreValue] = useState('');
    const [comment, setComment] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [submitting, setSubmitting] = useState(false);

    const coreValues = [
        "Passionate about our purpose",
        "Driven to be the best",
        "Resilient, rising stronger together",
        "Respond swiftly and positively"
    ];

    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const { data, error } = await supabase.rpc('get_all_employees');
                if (error) throw error;
                setEmployees(data || []);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        fetchEmployees();
    }, [supabase]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!selectedEmployee || !selectedCoreValue || !comment.trim()) {
            setError('All fields are required.');
            return;
        }
        
        setError('');
        setSubmitting(true);

        try {
            const { data, error: rpcError } = await supabase.rpc('give_kudo', {
                p_receiver_id: selectedEmployee,
                p_core_value: selectedCoreValue,
                p_comment: comment.trim()
            });
            
            if (rpcError) throw rpcError;

            if (data.success) {
                // Call the onComplete callback if provided
                if (modalProps?.onComplete) {
                    modalProps.onComplete();
                }
                closeModal();
            } else {
                setError(data.error || 'Failed to send kudo');
            }
        } catch (err) {
            setError(`Failed to send kudo: ${err.message}`);
        } finally {
            setSubmitting(false);
        }
    };

    const resetForm = () => {
        setSelectedEmployee('');
        setSelectedCoreValue('');
        setComment('');
        setError('');
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center z-50">
            <div className="bg-gray-800 rounded-lg shadow-2xl p-8 w-full max-w-2xl">
                {/* Header */}
                <div className="flex justify-between items-center mb-6">
                    <div className="flex items-center">
                        <Award className="text-yellow-400 mr-3" size={24} />
                        <h3 className="text-2xl font-bold text-yellow-400">Give a Kudo</h3>
                    </div>
                    <button 
                        onClick={closeModal} 
                        className="text-gray-400 hover:text-white text-2xl"
                    >
                        <X size={24} />
                    </button>
                </div>

                {loading ? (
                    <div className="text-center py-8">
                        <div className="text-yellow-400">Loading employees...</div>
                    </div>
                ) : (
                    <form onSubmit={handleSubmit} className="space-y-6">
                        {/* Employee Selection */}
                        <div>
                            <label htmlFor="employee" className="block text-sm font-medium text-gray-300 mb-2">
                                To: *
                            </label>
                            <select
                                id="employee"
                                value={selectedEmployee}
                                onChange={(e) => setSelectedEmployee(e.target.value)}
                                className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-yellow-500"
                                required
                            >
                                <option value="">Select Employee</option>
                                {employees.map(emp => (
                                    <option key={emp.id} value={emp.id}>
                                        {emp.name} ({emp.email})
                                    </option>
                                ))}
                            </select>
                        </div>

                        {/* Core Value Selection */}
                        <div>
                            <label htmlFor="coreValue" className="block text-sm font-medium text-gray-300 mb-2">
                                For (Core Value): *
                            </label>
                            <select
                                id="coreValue"
                                value={selectedCoreValue}
                                onChange={(e) => setSelectedCoreValue(e.target.value)}
                                className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-yellow-500"
                                required
                            >
                                <option value="">Select Core Value</option>
                                {coreValues.map(cv => (
                                    <option key={cv} value={cv}>{cv}</option>
                                ))}
                            </select>
                        </div>

                        {/* Comment */}
                        <div>
                            <label htmlFor="comment" className="block text-sm font-medium text-gray-300 mb-2">
                                Comment: *
                            </label>
                            <textarea
                                id="comment"
                                value={comment}
                                onChange={(e) => setComment(e.target.value)}
                                placeholder="Describe what this person did that exemplifies this core value..."
                                rows="4"
                                className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-yellow-500 resize-none"
                                required
                            />
                            <div className="text-xs text-gray-500 mt-1 flex justify-between">
                                <span>Be specific about what they did that was great!</span>
                                <span>{comment.length}/500</span>
                            </div>
                        </div>

                        {/* Error Display */}
                        {error && (
                            <div className="p-3 bg-red-900 rounded border border-red-700 text-red-200">
                                {error}
                            </div>
                        )}

                        {/* Action Buttons */}
                        <div className="flex justify-end space-x-4 pt-6 border-t border-gray-700">
                            <button
                                type="button"
                                onClick={closeModal}
                                className="px-6 py-2 bg-gray-600 hover:bg-gray-700 rounded-md text-white transition"
                            >
                                Cancel
                            </button>
                            <button
                                type="button"
                                onClick={resetForm}
                                className="px-6 py-2 bg-gray-700 hover:bg-gray-600 rounded-md text-white transition"
                            >
                                Reset
                            </button>
                            <button
                                type="submit"
                                disabled={submitting || !selectedEmployee || !selectedCoreValue || !comment.trim()}
                                className="px-6 py-2 bg-yellow-500 hover:bg-yellow-600 text-black font-bold rounded-md disabled:bg-gray-500 disabled:text-gray-300 transition flex items-center"
                            >
                                {submitting ? (
                                    <>
                                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-black mr-2"></div>
                                        Sending...
                                    </>
                                ) : (
                                    <>
                                        <Award size={16} className="mr-2" />
                                        Send Kudo
                                    </>
                                )}
                            </button>
                        </div>
                    </form>
                )}

                {/* Tips */}
                <div className="mt-6 p-4 bg-yellow-900 rounded-lg border border-yellow-700">
                    <h4 className="text-yellow-200 font-semibold mb-2">Tips for Great Kudos:</h4>
                    <ul className="text-yellow-300 text-sm space-y-1">
                        <li> Be specific about what they did</li>
                        <li> Explain the positive impact it had</li>
                        <li> Connect it clearly to the core value</li>
                        <li> Make it personal and meaningful</li>
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default GiveKudoModal;

import React from 'react';
import { X } from 'lucide-react';

export default function Modal({ children, closeModal }) {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
      <div className="bg-gray-900 rounded-xl shadow-lg w-full max-w-lg relative p-6">
        <button onClick={closeModal} className="absolute top-4 right-4 text-gray-400 hover:text-white">
          <X size={20} />
        </button>
        {children}
      </div>
    </div>
  );
}

import React, { useEffect, useState } from 'react';

export default function StartReviewCycleModal({ supabase, closeModal, modalProps }) {
  const [cycles, setCycles] = useState([]);
  const [selected, setSelected] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchCycles = async () => {
      const { data, error } = await supabase
        .from('review_cycles')
        .select('id, name, status')
        .eq('status', 'active');
      if (error) setError(error.message);
      else setCycles(data);
      setLoading(false);
    };
    fetchCycles();
  }, [supabase]);

  const start = async () => {
    if (!selected) return;
    setSubmitting(true);
    const { error } = await supabase.rpc('start_review_cycle_for_my_team', { cycle_id_to_start: selected });
    if (error) setError(error.message);
    else {
      if (modalProps?.afterSave) await modalProps.afterSave();
      closeModal();
    }
    setSubmitting(false);
  };

  if (loading) return <div>Loading</div>;
  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Start Review Cycle</h2>
      {error && <p className="text-red-400 mb-2">{error}</p>}
      <label className="block mb-4">
        <span className="text-sm">Select Active Cycle</span>
        <select
          value={selected}
          onChange={(e) => setSelected(parseInt(e.target.value))}
          className="mt-1 w-full bg-gray-800 border border-gray-700 rounded-lg p-2">
          <option value=""> choose </option>
          {cycles.map((c) => (
            <option key={c.id} value={c.id}>{c.name}</option>
          ))}
        </select>
      </label>
      <div className="flex justify-end space-x-2">
        <button onClick={closeModal} className="px-4 py-2 rounded-lg bg-gray-700 hover:bg-gray-600">Cancel</button>
        <button
          onClick={start}
          disabled={!selected || submitting}
          className="px-4 py-2 rounded-lg bg-cyan-600 hover:bg-cyan-700 disabled:opacity-50">
          {submitting ? 'Starting' : 'Start'}
        </button>
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import { X, AlertTriangle, Bug, Lightbulb, HelpCircle, Upload, Copy, Send } from 'lucide-react';
import { Button } from '../ui';

const UATFeedbackModal = ({ closeModal }) => {
  const [feedbackData, setFeedbackData] = useState({
    category: 'bug',
    priority: 'medium',
    title: '',
    description: '',
    screenshot: null,
    reproductionSteps: '',
    browserInfo: `${navigator.userAgent} | Screen: ${screen.width}x${screen.height}`,
    url: window.location.href
  });
  
  const [submitting, setSubmitting] = useState(false);
  const [dragActive, setDragActive] = useState(false);

  const categories = [
    { 
      value: 'bug', 
      label: 'Bug Report', 
      icon: Bug, 
      color: 'text-red-400 bg-red-900/20',
      description: 'Something is broken or not working as expected'
    },
    { 
      value: 'ui_ux', 
      label: 'UI/UX Issue', 
      icon: AlertTriangle, 
      color: 'text-orange-400 bg-orange-900/20',
      description: 'Design, layout, or usability problems'
    },
    { 
      value: 'feature_request', 
      label: 'Feature Request', 
      icon: Lightbulb, 
      color: 'text-yellow-400 bg-yellow-900/20',
      description: 'Suggestion for new functionality or improvements'
    },
    { 
      value: 'question', 
      label: 'Question/Help', 
      icon: HelpCircle, 
      color: 'text-blue-400 bg-blue-900/20',
      description: 'Need help understanding how something works'
    },
    { 
      value: 'other', 
      label: 'Other', 
      icon: AlertTriangle, 
      color: 'text-purple-400 bg-purple-900/20',
      description: 'General feedback or other concerns'
    }
  ];

  const priorities = [
    { value: 'low', label: 'Low Priority', color: 'text-gray-400' },
    { value: 'medium', label: 'Medium Priority', color: 'text-yellow-400' },
    { value: 'high', label: 'High Priority', color: 'text-orange-400' },
    { value: 'critical', label: 'Critical - Blocking', color: 'text-red-400' }
  ];

  const handleFileUpload = (file) => {
    if (file && file.type.startsWith('image/')) {
      if (file.size > 5 * 1024 * 1024) { // 5MB limit
        alert('Image must be smaller than 5MB');
        return;
      }
      
      const reader = new FileReader();
      reader.onload = (e) => {
        setFeedbackData(prev => ({
          ...prev,
          screenshot: {
            file,
            dataUrl: e.target.result,
            name: file.name,
            size: file.size
          }
        }));
      };
      reader.readAsDataURL(file);
    } else {
      alert('Please upload an image file (PNG, JPG, GIF)');
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    const file = e.dataTransfer.files[0];
    if (file) handleFileUpload(file);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragActive(true);
  };

  const handleDragLeave = () => {
    setDragActive(false);
  };

  const copySystemInfo = () => {
    const systemInfo = `Browser: ${navigator.userAgent}
Screen Resolution: ${screen.width}x${screen.height}
URL: ${window.location.href}
Timestamp: ${new Date().toISOString()}`;
    
    navigator.clipboard.writeText(systemInfo).then(() => {
      alert('System information copied to clipboard!');
    });
  };

  const handleSubmit = async () => {
    if (!feedbackData.title.trim()) {
      alert('Please provide a title for your feedback');
      return;
    }
    
    if (!feedbackData.description.trim()) {
      alert('Please provide a description of the issue');
      return;
    }

    try {
      setSubmitting(true);
      
      // Import the service dynamically
      const { default: UATFeedbackService } = await import('../../services/UATFeedbackService');
      
      await UATFeedbackService.submitFeedback({
        ...feedbackData,
        submittedAt: new Date().toISOString()
      });
      
      alert(' Thank you! Your feedback has been submitted successfully. The admin team will review it shortly.');
      closeModal();
      
    } catch (error) {
      console.error('Error submitting feedback:', error);
      alert(' Error submitting feedback. Please try again or contact support directly.');
    } finally {
      setSubmitting(false);
    }
  };

  const selectedCategory = categories.find(cat => cat.value === feedbackData.category);
  const CategoryIcon = selectedCategory?.icon || Bug;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-gray-800 rounded-lg shadow-2xl w-full max-w-2xl max-h-[95vh] flex flex-col border-2 border-red-500">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-red-500/30 flex-shrink-0">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-red-500/20 rounded-lg">
              <AlertTriangle className="text-red-400" size={24} />
            </div>
            <div>
              <h2 className="text-xl font-bold text-white">UAT Feedback & Bug Report</h2>
              <p className="text-red-400 text-sm">Help us improve the system during testing</p>
            </div>
          </div>
          <button
            onClick={closeModal}
            className="text-gray-400 hover:text-white transition-colors"
          >
            <X size={24} />
          </button>
        </div>

        {/* Content - Scrollable */}
        <div className="flex-1 overflow-y-auto p-6 space-y-6">
          
          {/* Category Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-3">
              What type of feedback is this?
            </label>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {categories.map((category) => {
                const Icon = category.icon;
                return (
                  <button
                    key={category.value}
                    onClick={() => setFeedbackData(prev => ({ ...prev, category: category.value }))}
                    className={`p-3 rounded-lg border text-left transition-colors ${
                      feedbackData.category === category.value
                        ? `border-cyan-500 ${category.color}`
                        : 'border-gray-600 text-gray-300 hover:border-gray-500'
                    }`}
                  >
                    <div className="flex items-center space-x-3">
                      <Icon size={20} />
                      <div>
                        <div className="font-medium">{category.label}</div>
                        <div className="text-xs opacity-75">{category.description}</div>
                      </div>
                    </div>
                  </button>
                );
              })}
            </div>
          </div>

          {/* Priority */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Priority Level
            </label>
            <select
              value={feedbackData.priority}
              onChange={(e) => setFeedbackData(prev => ({ ...prev, priority: e.target.value }))}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-red-500 focus:border-transparent"
            >
              {priorities.map(priority => (
                <option key={priority.value} value={priority.value}>
                  {priority.label}
                </option>
              ))}
            </select>
          </div>

          {/* Title */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Issue Title *
            </label>
            <input
              type="text"
              value={feedbackData.title}
              onChange={(e) => setFeedbackData(prev => ({ ...prev, title: e.target.value }))}
              placeholder="Brief description of the issue..."
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-red-500 focus:border-transparent"
              maxLength={100}
            />
            <div className="text-xs text-gray-500 mt-1">{feedbackData.title.length}/100 characters</div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Detailed Description *
            </label>
            <textarea
              value={feedbackData.description}
              onChange={(e) => setFeedbackData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Please describe the issue in detail. What were you trying to do? What happened? What did you expect to happen?"
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-red-500 focus:border-transparent"
              rows={4}
              maxLength={1000}
            />
            <div className="text-xs text-gray-500 mt-1">{feedbackData.description.length}/1000 characters</div>
          </div>

          {/* Reproduction Steps */}
          {feedbackData.category === 'bug' && (
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Steps to Reproduce (Optional)
              </label>
              <textarea
                value={feedbackData.reproductionSteps}
                onChange={(e) => setFeedbackData(prev => ({ ...prev, reproductionSteps: e.target.value }))}
                placeholder="1. Go to... &#10;2. Click on... &#10;3. See error..."
                className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-red-500 focus:border-transparent"
                rows={3}
              />
            </div>
          )}

          {/* Screenshot Upload */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Screenshot (Optional)
            </label>
            <div
              onDrop={handleDrop}
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              className={`border-2 border-dashed rounded-lg p-6 text-center transition-colors ${
                dragActive 
                  ? 'border-red-500 bg-red-500/10' 
                  : 'border-gray-600 hover:border-gray-500'
              }`}
            >
              {feedbackData.screenshot ? (
                <div className="space-y-3">
                  <img
                    src={feedbackData.screenshot.dataUrl}
                    alt="Screenshot preview"
                    className="max-w-full max-h-48 mx-auto rounded border border-gray-600"
                  />
                  <div className="text-sm text-gray-300">
                    {feedbackData.screenshot.name} ({(feedbackData.screenshot.size / 1024).toFixed(1)} KB)
                  </div>
                  <button
                    onClick={() => setFeedbackData(prev => ({ ...prev, screenshot: null }))}
                    className="text-red-400 hover:text-red-300 text-sm"
                  >
                    Remove Screenshot
                  </button>
                </div>
              ) : (
                <div className="space-y-3">
                  <Upload className="mx-auto text-gray-400" size={32} />
                  <div className="text-gray-300">
                    <p>Drag & drop a screenshot here, or</p>
                    <label className="text-red-400 hover:text-red-300 cursor-pointer">
                      browse to upload
                      <input
                        type="file"
                        className="hidden"
                        accept="image/*"
                        onChange={(e) => e.target.files[0] && handleFileUpload(e.target.files[0])}
                      />
                    </label>
                  </div>
                  <p className="text-xs text-gray-500">PNG, JPG, GIF up to 5MB</p>
                </div>
              )}
            </div>
          </div>

          {/* System Information */}
          <div>
            <div className="flex items-center justify-between mb-2">
              <label className="block text-sm font-medium text-gray-300">
                System Information (Auto-collected)
              </label>
              <button
                onClick={copySystemInfo}
                className="text-xs text-cyan-400 hover:text-cyan-300 flex items-center space-x-1"
              >
                <Copy size={12} />
                <span>Copy</span>
              </button>
            </div>
            <div className="bg-gray-700/50 p-3 rounded text-xs text-gray-400 font-mono">
              <div>URL: {feedbackData.url}</div>
              <div>Browser: {navigator.userAgent.split(' ').slice(0, 3).join(' ')}...</div>
              <div>Screen: {screen.width}x{screen.height}</div>
              <div>Time: {new Date().toLocaleString()}</div>
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="p-6 border-t border-gray-700 flex justify-end space-x-3 flex-shrink-0">
          <Button
            onClick={closeModal}
            variant="secondary"
            disabled={submitting}
          >
            Cancel
          </Button>
          <Button
            onClick={handleSubmit}
            variant="primary"
            disabled={submitting}
            className="bg-red-600 hover:bg-red-700"
          >
            {submitting ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent mr-2" />
                Submitting...
              </>
            ) : (
              <>
                <Send size={16} className="mr-2" />
                Submit Feedback
              </>
            )}
          </Button>
        </div>
      </div>
    </div>
  );
};

export default UATFeedbackModal;

// src/components/pages/Admin.js - SIMPLIFIED VERSION FOR TESTING
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Users, Calendar, Plus, AlertTriangle, Edit, UserPlus, Square } from 'lucide-react';
import PulseQuestionsManager from '../admin/PulseQuestionsManager';
import { useAdmin } from '../../hooks';
import { useApp } from '../../contexts';
import { AdminService, supabase, DepartmentService } from '../../services';
import { getStatusBadgeColor, formatDate, validateRequired, validateDateRange } from '../../utils';

export default function Admin() {
  const { 
    employees, 
    cycles, 
    loading, 
    error, 
    createReviewCycle, 
    refresh 
  } = useAdmin();
  
  const [oversightRefreshTrigger, setOversightRefreshTrigger] = useState(0);
  
  const { openModal } = useApp();
  const [allEmployees, setAllEmployees] = useState([]);
  const [employeesLoading, setEmployeesLoading] = useState(true);
  const [currentRole, setCurrentRole] = useState(null);

  useEffect(() => {
    fetchEmployees();
    checkRole();
  }, []);

  const fetchEmployees = async () => {
    try {
      setEmployeesLoading(true);
      const employeesData = await AdminService.getAllEmployees();
      
      // Enrich employee data with primary department information
      const enrichedEmployees = await Promise.all(
        employeesData.map(async (employee) => {
          try {
            // Get primary department for each employee
            const { data: primaryDeptData, error: deptError } = await supabase
              .rpc('get_employee_primary_department', { emp_id: employee.id });
            
            if (!deptError && primaryDeptData && primaryDeptData.length > 0) {
              return {
                ...employee,
                primary_department: primaryDeptData[0].dept_name
              };
            }
            
            // Fallback: if no primary department found, use the backward compatibility column
            return {
              ...employee,
              primary_department: employee.department || 'General'
            };
          } catch (deptErr) {
            console.warn(`Error fetching department for employee ${employee.name}:`, deptErr);
            return {
              ...employee,
              primary_department: employee.department || 'General'
            };
          }
        })
      );
      
      setAllEmployees(enrichedEmployees);
    } catch (err) {
      console.error('Error fetching employees:', err);
    } finally {
      setEmployeesLoading(false);
    }
  };

  const checkRole = async () => {
    try {
      const role = await AdminService.checkCurrentRole();
      setCurrentRole(role);
    } catch (err) {
      console.error('Error checking role:', err);
    }
  };



  const handleCloseCycle = async (cycleId) => {
    // Confirm before closing
    if (!window.confirm('Are you sure you want to close this review cycle? This action cannot be undone.')) {
      return;
    }

    try {
      const data = await AdminService.closeReviewCycle(cycleId);
      
      if (data && data.success) {
        alert(' ' + data.message);
        refresh(); // Refresh the data to show updated status
        setOversightRefreshTrigger(prev => prev + 1); // Trigger oversight refresh
      } else {
        alert(' ' + (data?.error || 'Unknown error'));
      }
    } catch (err) {
      alert(' Error: ' + err.message);
    }
  };

  if (loading) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">Admin Panel</h1>
        <div className="text-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-cyan-400 mx-auto mb-4"></div>
          <div className="text-gray-400">Loading admin data...</div>
        </div>
      </div>
    );
  }


  return (
    <div className="p-8">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-bold text-cyan-400">Admin Panel</h1>
          <p className="text-gray-400 mt-2">Manage employees and review cycles</p>
        </div>
      </div>

      {/* Review Process Mini Dashboard - Sticky */}
      <div className="sticky top-4 z-10 mb-6">
        <ReviewOversightSection compact={true} refreshTrigger={oversightRefreshTrigger} />
      </div>

      {/* Error Display */}
      {error && (
        <div className="mb-6 p-4 bg-red-900 border border-red-700 rounded-lg">
          <div className="flex items-center text-red-200">
            <AlertTriangle size={16} className="mr-2" />
            <span>Error: {error}</span>
          </div>
          <button 
            onClick={refresh}
            className="mt-2 px-3 py-1 bg-red-800 hover:bg-red-700 rounded text-red-100 text-sm"
          >
            Retry
          </button>
        </div>
      )}
      
      {/* Review Cycles Section */}
      <div className="bg-gray-800 rounded-lg p-6 mb-8">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center">
            <Calendar className="mr-2 text-cyan-400" size={24} />
            <h2 className="text-xl font-semibold">Review Cycles ({cycles.length})</h2>
          </div>
          <button
            onClick={() => openModal('createReviewCycle', { onComplete: refresh })}
            className="bg-cyan-500 hover:bg-cyan-600 text-white px-4 py-2 rounded-lg flex items-center"
          >
            <Plus size={16} className="mr-2" />
            Create Review Cycle
          </button>
        </div>
        
        {cycles.length > 0 ? (
          <div className="space-y-3 max-h-96 overflow-y-auto">
            {cycles.map((cycle) => (
              <div key={cycle.id} className="p-3 bg-gray-700 rounded">
                <div className="flex justify-between items-start mb-2">
                  <div>
                    <p className="font-medium text-white">{cycle.name}</p>
                    <p className="text-sm text-gray-400">
                      {formatDate(cycle.start_date)} - {formatDate(cycle.end_date)}
                    </p>
                  </div>
                  <span className={`text-xs px-2 py-1 rounded ${getStatusBadgeColor(cycle.status)}`}>
                    {cycle.status}
                  </span>
                </div>
                
                <div className="flex gap-2 mt-2">
                  {cycle.status === 'active' && (
                    <button
                      onClick={() => handleCloseCycle(cycle.id)}
                      className="text-red-400 hover:text-red-300 flex items-center text-sm"
                    >
                      <Square size={14} className="mr-1" />
                      Close
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8">
            <Calendar size={48} className="mx-auto mb-4 text-gray-500" />
            <p className="text-gray-400 mb-4">No review cycles found</p>
            <p className="text-sm text-gray-500">Create your first review cycle to get started</p>
          </div>
        )}
      </div>

      {/* Summary Stats */}
      <div className="mt-8 grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-gray-800 p-4 rounded-lg text-center">
          <p className="text-2xl font-bold text-cyan-400">{allEmployees.length}</p>
          <p className="text-gray-400">Total Employees</p>
        </div>
        <div className="bg-gray-800 p-4 rounded-lg text-center">
          <p className="text-2xl font-bold text-green-400">{allEmployees.filter(e => e.is_active).length}</p>
          <p className="text-gray-400">Active Employees</p>
        </div>
        <div className="bg-gray-800 p-4 rounded-lg text-center">
          <p className="text-2xl font-bold text-yellow-400">
            {cycles.filter(c => c.status === 'active').length}
          </p>
          <p className="text-gray-400">Active Cycles</p>
        </div>
        <div className="bg-gray-800 p-4 rounded-lg text-center">
          <p className="text-2xl font-bold text-purple-400">{allEmployees.filter(e => e.manager_id).length}</p>
          <p className="text-gray-400">Manager Relations</p>
        </div>
      </div>

      {/* Employee Management Section */}
      <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
        <div className="flex justify-between items-center mb-6">
          <div>
            <h2 className="text-2xl font-bold text-cyan-400 flex items-center">
              <Users className="mr-3" size={24} />
              Employee Management
            </h2>
            <p className="text-gray-400 mt-1">Manage team members and their roles</p>
            {currentRole && (
              <p className="text-xs text-yellow-400 mt-1">Your role: {currentRole}</p>
            )}
          </div>
          <button
            onClick={() => openModal('createEmployee', { onComplete: () => { fetchEmployees(); refresh(); } })}
            className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg flex items-center"
          >
            <UserPlus size={16} className="mr-2" />
            Add Employee
          </button>
        </div>

        {/* Employee Table */}
        {employeesLoading ? (
          <div className="text-center py-8">
            <div className="w-8 h-8 border-2 border-cyan-400 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-gray-400">Loading employees...</p>
          </div>
        ) : allEmployees.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-gray-700">
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Name</th>
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Email</th>
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Role</th>
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Job Title</th>
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Primary Department</th>
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Manager</th>
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Reports</th>
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Status</th>
                  <th className="text-left py-3 px-2 text-gray-400 font-medium">Actions</th>
                </tr>
              </thead>
              <tbody>
                {allEmployees.map((employee) => (
                  <tr key={employee.id} className="border-b border-gray-700 hover:bg-gray-700">
                    <td className="py-3 px-2">
                      <div className="text-white font-medium">{employee.name}</div>
                    </td>
                    <td className="py-3 px-2">
                      <div className="text-gray-300 text-sm">{employee.email}</div>
                    </td>
                    <td className="py-3 px-2">
                      <span className={`px-2 py-1 rounded text-xs font-medium ${
                        employee.role === 'admin' 
                          ? 'bg-red-900 text-red-200' 
                          : employee.role === 'manager'
                          ? 'bg-blue-900 text-blue-200'
                          : 'bg-gray-600 text-gray-200'
                      }`}>
                        {employee.role.charAt(0).toUpperCase() + employee.role.slice(1)}
                      </span>
                    </td>
                    <td className="py-3 px-2">
                      <div className="text-gray-300 text-sm">{employee.job_title}</div>
                    </td>
                    <td className="py-3 px-2">
                      <div className="text-gray-300 text-sm">
                        {employee.primary_department || employee.department || ''}
                      </div>
                    </td>
                    <td className="py-3 px-2">
                      <div className="text-gray-300 text-sm">
                        {employee.manager_name || ''}
                      </div>
                    </td>
                    <td className="py-3 px-2">
                      <div className="text-gray-300 text-sm">
                        {employee.direct_reports_count}
                      </div>
                    </td>
                    <td className="py-3 px-2">
                      <span className={`px-2 py-1 rounded text-xs font-medium ${
                        employee.is_active 
                          ? 'bg-green-900 text-green-200' 
                          : 'bg-red-900 text-red-200'
                      }`}>
                        {employee.is_active ? 'Active' : 'Inactive'}
                      </span>
                    </td>
                    <td className="py-3 px-2">
                      <button
                        onClick={() => openModal('editEmployee', { 
                          employee: employee,
                          onComplete: () => { fetchEmployees(); refresh(); }
                        })}
                        className="text-cyan-400 hover:text-cyan-300 text-sm flex items-center"
                      >
                        <Edit size={14} className="mr-1" />
                        Edit
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="text-center py-8">
            <Users size={48} className="mx-auto mb-4 text-gray-500" />
            <p className="text-gray-400 mb-4">No employees found</p>
            <p className="text-sm text-gray-500">Add your first employee to get started</p>
          </div>
        )}
      </div>

      {/* Pulse Questions Management Section */}
      <PulseQuestionsManager />

    </div>
  );
}

// Review Oversight Component for Admin Dashboard
const ReviewOversightSection = ({ compact = false, refreshTrigger = 0 }) => {
  const navigate = useNavigate();
  const [reviewStats, setReviewStats] = useState({
    total: 0,
    pending_employee: 0,
    pending_manager: 0,
    completed: 0,
    overdue: 0
  });
  const [recentReviews, setRecentReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [assessmentsByCategory, setAssessmentsByCategory] = useState({
    total: [],
    pending_employee: [],
    pending_manager: [],
    completed: [],
    overdue: []
  });

  useEffect(() => {
    fetchReviewOversight();
  }, []);

  // Refresh when trigger changes
  useEffect(() => {
    if (refreshTrigger > 0) {
      console.log(' Oversight refresh triggered by external event');
      fetchReviewOversight();
    }
  }, [refreshTrigger]);

  const calculateReviewStats = (assessmentsData, cyclesData) => {
    console.log(' Raw assessments data:', assessmentsData);
    console.log(' Cycles data for filtering:', cyclesData);
    
    // Debug: Show sample assessment structure
    if (assessmentsData?.length > 0) {
      console.log(' Sample assessment structure:', {
        ...assessmentsData[0],
        available_fields: Object.keys(assessmentsData[0])
      });
    }
    
    // Debug: Show sample cycle structure  
    if (cyclesData?.length > 0) {
      console.log(' Sample cycle structure:', {
        ...cyclesData[0],
        available_fields: Object.keys(cyclesData[0])
      });
    }
    
    // Get list of active cycle IDs (exclude closed cycles)
    const activeCycleIds = cyclesData?.filter(cycle => cycle.status !== 'closed').map(cycle => cycle.id) || [];
    console.log(' Active cycle IDs:', activeCycleIds);
    
    // DEBUG: For now, show ALL assessments to see the system working
    // TODO: Fix this once we have proper assessment data for active cycles
    const allCycleIds = cyclesData?.map(cycle => cycle.id) || [];
    console.log(' Using ALL cycle IDs for demo:', allCycleIds);
    
    // Debug: Check all possible cycle ID field names in assessments
    const cycleIdFields = assessmentsData?.map(a => ({
      cycle_id: a.cycle_id,
      review_cycle_id: a.review_cycle_id, 
      cycle: a.cycle,
      review_cycle: a.review_cycle,
      all_fields: Object.keys(a).filter(k => k.toLowerCase().includes('cycle'))
    })).slice(0, 3) || [];
    console.log(' Assessment cycle field analysis:', cycleIdFields);
    
    // TEMPORARY: Filter assessments to include ALL cycles (not just active)
    const activeAssessments = assessmentsData?.filter(a => {
      const matchesCycleId = allCycleIds.includes(a.cycle_id);
      const matchesReviewCycleId = allCycleIds.includes(a.review_cycle_id);
      const matchesAny = matchesCycleId || matchesReviewCycleId;
      
      // Debug first few matches
      if (assessmentsData.indexOf(a) < 3) {
        console.log(` Assessment ${assessmentsData.indexOf(a)} match check:`, {
          cycle_id: a.cycle_id,
          review_cycle_id: a.review_cycle_id,
          allCycleIds,
          matchesCycleId,
          matchesReviewCycleId,
          matchesAny
        });
      }
      
      return matchesAny;
    }) || [];
    
    console.log(' Filtered to active assessments:', activeAssessments.length, 'of', assessmentsData?.length || 0);
    
    // Filter assessments by category
    const pendingEmployeeAssessments = activeAssessments.filter(a => 
      a.self_assessment_status === 'not_started' || a.self_assessment_status === 'in_progress'
    );
    const pendingManagerAssessments = activeAssessments.filter(a => 
      a.self_assessment_status === 'employee_complete' && a.manager_review_status === 'pending'
    );
    const completedAssessments = activeAssessments.filter(a => 
      a.manager_review_status === 'completed'
    );
    const overdueAssessments = activeAssessments.filter(a => {
      const dueDate = new Date(a.due_date);
      const now = new Date();
      return dueDate < now && (a.self_assessment_status !== 'employee_complete' || a.manager_review_status !== 'completed');
    });

    const total = activeAssessments.length;
    const pending_employee = pendingEmployeeAssessments.length;
    const pending_manager = pendingManagerAssessments.length;
    const completed = completedAssessments.length;
    const overdue = overdueAssessments.length;
    
    console.log(' Calculated stats (active cycles only):', {
      total,
      pending_employee,
      pending_manager,
      completed,
      overdue,
      active_cycles: activeCycleIds.length,
      total_assessments: assessmentsData?.length || 0,
      filtered_assessments: activeAssessments.length,
      sample_statuses: activeAssessments.slice(0, 3).map(a => ({
        cycle_id: a.cycle_id || a.review_cycle_id,
        self_assessment_status: a.self_assessment_status,
        manager_review_status: a.manager_review_status,
        due_date: a.due_date
      }))
    });
    
    return { 
      stats: { total, pending_employee, pending_manager, completed, overdue },
      assessments: {
        total: activeAssessments,
        pending_employee: pendingEmployeeAssessments,
        pending_manager: pendingManagerAssessments,
        completed: completedAssessments,
        overdue: overdueAssessments
      }
    };
  };

  const fetchReviewOversight = async () => {
    try {
      setLoading(true);
      
      console.log(' Fetching review oversight data...');
      
      let assessmentsData = null;
      let cyclesData = null;
      
      // Fetch cycles data first
      try {
        const cyclesResult = await AdminService.getReviewCycles();
        cyclesData = cyclesResult;
        console.log(' Fetched cycles for filtering:', cyclesData?.length || 0);
      } catch (cyclesError) {
        console.warn(' Could not fetch cycles data:', cyclesError);
        cyclesData = [];
      }
      
      // Try complex query first
      const { data: assessments, error: assessError } = await supabase
        .from('assessments')
        .select(`
          *,
          employee:employees(name, job_title),
          cycle:review_cycles(name)
        `)
        .order('created_at', { ascending: false });
      
      console.log(' Complex query result:', { assessments, error: assessError });
      
      if (assessError) {
        console.error(' Complex query failed:', assessError);
        
        // Fallback to simple query
        console.log(' Trying simpler assessments query...');
        const { data: simpleAssessments, error: simpleError } = await supabase
          .from('assessments')
          .select('*')
          .order('created_at', { ascending: false });
          
        console.log(' Simple query result:', { simpleAssessments, error: simpleError });
        
        if (simpleError || !simpleAssessments) {
          console.error(' All queries failed:', simpleError);
          setReviewStats({
            total: 0, pending_employee: 0, pending_manager: 0, completed: 0, overdue: 0
          });
          setRecentReviews([]);
          return;
        }
        
        console.log(' Using simple assessments data');
        assessmentsData = simpleAssessments;
      } else {
        console.log(' Using complex query with joins');
        assessmentsData = assessments;
      }
      
      // Calculate stats from whichever data we got, filtered by active cycles
      const result = calculateReviewStats(assessmentsData, cyclesData);
      
      setReviewStats(result.stats);
      setAssessmentsByCategory(result.assessments);
      
      // Get recent assessment activity (last 10 assessments with recent updates)
      const recentActivity = assessmentsData?.filter(a => 
        a.updated_at && new Date(a.updated_at) > new Date(Date.now() - 30 * 24 * 60 * 60 * 1000) // Last 30 days
      ).slice(0, 10).map(a => ({
        assessment_id: a.id,
        employee_name: a.employee?.name || `Employee ${a.employee_id}` || 'Unknown',
        cycle_name: a.cycle?.name || `Cycle ${a.cycle_id}` || 'Unknown Cycle',
        self_assessment_status: a.self_assessment_status,
        manager_review_status: a.manager_review_status,
        updated_at: a.updated_at
      })) || [];
      
      setRecentReviews(recentActivity);
      
    } catch (err) {
      console.error('Error fetching review oversight:', err);
      setReviewStats({
        total: 0,
        pending_employee: 0,
        pending_manager: 0,
        completed: 0,
        overdue: 0
      });
      setRecentReviews([]);
    } finally {
      setLoading(false);
    }
  };

  const handleViewAssessment = (assessmentId) => {
    navigate(`/assessment/${assessmentId}`);
  };

  const handleShowAssessmentList = (category, assessments) => {
    if (assessments.length === 0) {
      alert(`No ${category.replace('_', ' ')} assessments found.`);
      return;
    }

    const categoryTitles = {
      total: 'All Reviews',
      pending_employee: 'Employee Pending Reviews',
      pending_manager: 'Manager Pending Reviews',
      completed: 'Completed Reviews',
      overdue: 'Overdue Reviews'
    };

    const title = categoryTitles[category];
    const listItems = assessments.map(a => 
      ` ${a.employee?.name || 'Employee ' + a.employee_id} - ${a.cycle?.name || 'Cycle ' + a.cycle_id} ${a.due_date ? `(Due: ${new Date(a.due_date).toLocaleDateString()})` : ''}`
    ).join('\n');
    
    const message = `${title} (${assessments.length})\n\n${listItems}\n\nClick OK to view the first assessment, or Cancel to close.`;
    
    if (window.confirm(message)) {
      // Navigate to the first assessment
      handleViewAssessment(assessments[0].id);
    }
  };

  if (loading) {
    return (
      <div className={`${compact ? 'bg-gray-800/90 backdrop-blur-sm border border-gray-700' : 'mt-8 bg-gray-800'} rounded-lg p-${compact ? '4' : '6'}`}>
        <h2 className={`${compact ? 'text-lg' : 'text-xl'} font-semibold text-white mb-4`}>Review Process Oversight</h2>
        <div className="flex items-center justify-center py-4">
          <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-cyan-400"></div>
        </div>
      </div>
    );
  }

  if (compact) {
    return (
      <div className="bg-gray-800/90 backdrop-blur-sm border border-gray-700 rounded-lg p-4 shadow-lg">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center">
            <Square className="mr-2 text-cyan-400" size={20} />
            <h2 className="text-lg font-semibold text-white">Review Status</h2>
          </div>
          <button 
            onClick={fetchReviewOversight}
            className="text-cyan-400 hover:text-cyan-300 text-xs px-2 py-1 rounded bg-gray-700/50"
          >
            Refresh
          </button>
        </div>

        {/* Compact Stats Grid */}
        <div className="grid grid-cols-5 gap-2">
          <button 
            className="bg-gray-700/50 rounded p-2 text-center hover:bg-gray-600/50 transition-colors cursor-pointer"
            onClick={() => handleShowAssessmentList('total', assessmentsByCategory.total)}
            title="Click to view all reviews"
          >
            <div className="text-lg font-bold text-white">{reviewStats.total}</div>
            <div className="text-xs text-gray-400">Total</div>
          </button>
          <button 
            className="bg-yellow-900/30 rounded p-2 text-center border border-yellow-700/50 hover:bg-yellow-800/40 transition-colors cursor-pointer"
            onClick={() => handleShowAssessmentList('pending_employee', assessmentsByCategory.pending_employee)}
            title="Click to view employee pending reviews"
          >
            <div className="text-lg font-bold text-yellow-400">{reviewStats.pending_employee}</div>
            <div className="text-xs text-yellow-300">Employee</div>
          </button>
          <button 
            className="bg-orange-900/30 rounded p-2 text-center border border-orange-700/50 hover:bg-orange-800/40 transition-colors cursor-pointer"
            onClick={() => handleShowAssessmentList('pending_manager', assessmentsByCategory.pending_manager)}
            title="Click to view manager pending reviews"
          >
            <div className="text-lg font-bold text-orange-400">{reviewStats.pending_manager}</div>
            <div className="text-xs text-orange-300">Manager</div>
          </button>
          <button 
            className="bg-green-900/30 rounded p-2 text-center border border-green-700/50 hover:bg-green-800/40 transition-colors cursor-pointer"
            onClick={() => handleShowAssessmentList('completed', assessmentsByCategory.completed)}
            title="Click to view completed reviews"
          >
            <div className="text-lg font-bold text-green-400">{reviewStats.completed}</div>
            <div className="text-xs text-green-300">Done</div>
          </button>
          <button 
            className="bg-red-900/30 rounded p-2 text-center border border-red-700/50 hover:bg-red-800/40 transition-colors cursor-pointer"
            onClick={() => handleShowAssessmentList('overdue', assessmentsByCategory.overdue)}
            title="Click to view overdue reviews - immediate attention needed!"
          >
            <div className="text-lg font-bold text-red-400">{reviewStats.overdue}</div>
            <div className="text-xs text-red-300">Overdue</div>
          </button>
        </div>
        
        {/* Alert for overdue items */}
        {reviewStats.overdue > 0 && (
          <button 
            className="mt-3 w-full bg-red-900/20 border border-red-700/50 rounded p-2 flex items-center hover:bg-red-800/30 transition-colors"
            onClick={() => handleShowAssessmentList('overdue', assessmentsByCategory.overdue)}
            title="Click to view and take action on overdue reviews"
          >
            <AlertTriangle size={14} className="text-red-400 mr-2" />
            <span className="text-red-300 text-xs">
              {reviewStats.overdue} review{reviewStats.overdue > 1 ? 's' : ''} overdue - immediate attention needed
            </span>
          </button>
        )}
      </div>
    );
  }

  return (
    <div className="mt-8 bg-gray-800 rounded-lg p-6">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <Square className="mr-2 text-cyan-400" size={24} />
          <h2 className="text-xl font-semibold text-white">Review Process Oversight</h2>
        </div>
        <button 
          onClick={fetchReviewOversight}
          className="text-cyan-400 hover:text-cyan-300 text-sm"
        >
          Refresh
        </button>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-6">
        <button 
          className="bg-gray-700 rounded-lg p-4 text-center hover:bg-gray-600 transition-colors cursor-pointer"
          onClick={() => handleShowAssessmentList('total', assessmentsByCategory.total)}
          title="Click to view all reviews"
        >
          <div className="text-2xl font-bold text-white">{reviewStats.total}</div>
          <div className="text-sm text-gray-400">Total Reviews</div>
        </button>
        <button 
          className="bg-yellow-900 bg-opacity-50 rounded-lg p-4 text-center border border-yellow-700 hover:bg-yellow-800 hover:bg-opacity-60 transition-colors cursor-pointer"
          onClick={() => handleShowAssessmentList('pending_employee', assessmentsByCategory.pending_employee)}
          title="Click to view employee pending reviews"
        >
          <div className="text-2xl font-bold text-yellow-400">{reviewStats.pending_employee}</div>
          <div className="text-sm text-yellow-300">Employee Pending</div>
        </button>
        <button 
          className="bg-orange-900 bg-opacity-50 rounded-lg p-4 text-center border border-orange-700 hover:bg-orange-800 hover:bg-opacity-60 transition-colors cursor-pointer"
          onClick={() => handleShowAssessmentList('pending_manager', assessmentsByCategory.pending_manager)}
          title="Click to view manager pending reviews"
        >
          <div className="text-2xl font-bold text-orange-400">{reviewStats.pending_manager}</div>
          <div className="text-sm text-orange-300">Manager Pending</div>
        </button>
        <button 
          className="bg-green-900 bg-opacity-50 rounded-lg p-4 text-center border border-green-700 hover:bg-green-800 hover:bg-opacity-60 transition-colors cursor-pointer"
          onClick={() => handleShowAssessmentList('completed', assessmentsByCategory.completed)}
          title="Click to view completed reviews"
        >
          <div className="text-2xl font-bold text-green-400">{reviewStats.completed}</div>
          <div className="text-sm text-green-300">Completed</div>
        </button>
        <button 
          className="bg-red-900 bg-opacity-50 rounded-lg p-4 text-center border border-red-700 hover:bg-red-800 hover:bg-opacity-60 transition-colors cursor-pointer"
          onClick={() => handleShowAssessmentList('overdue', assessmentsByCategory.overdue)}
          title="Click to view overdue reviews - take immediate action!"
        >
          <div className="text-2xl font-bold text-red-400">{reviewStats.overdue}</div>
          <div className="text-sm text-red-300">Overdue</div>
        </button>
      </div>

      {/* Recent Activity */}
      <div>
        <h3 className="text-lg font-semibold text-white mb-4">Recent Review Activity</h3>
        {recentReviews.length > 0 ? (
          <div className="space-y-3 max-h-64 overflow-y-auto">
            {recentReviews.map((review, index) => (
              <div key={index} className="bg-gray-700 rounded-lg p-4 flex items-center justify-between">
                <div className="flex-1">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-cyan-600 rounded-full flex items-center justify-center">
                      <span className="text-white font-medium text-xs">
                        {review.employee_name?.split(' ').map(n => n[0]).join('') || 'N/A'}
                      </span>
                    </div>
                    <div>
                      <div className="text-white font-medium">{review.employee_name}</div>
                      <div className="text-sm text-gray-400">{review.cycle_name}</div>
                    </div>
                  </div>
                </div>
                <div className="flex items-center space-x-3">
                  <div className="text-center">
                    <div className={`px-2 py-1 rounded-full text-xs font-medium ${
                      review.self_assessment_status === 'employee_complete' ? 'bg-green-600 text-green-100' :
                      review.self_assessment_status === 'in_progress' ? 'bg-yellow-600 text-yellow-100' :
                      'bg-gray-600 text-gray-100'
                    }`}>
                      {review.self_assessment_status?.replace('_', ' ') || 'Not Started'}
                    </div>
                    <div className="text-xs text-gray-400 mt-1">Employee</div>
                  </div>
                  <div className="text-center">
                    <div className={`px-2 py-1 rounded-full text-xs font-medium ${
                      review.manager_review_status === 'completed' ? 'bg-green-600 text-green-100' :
                      review.manager_review_status === 'pending' ? 'bg-orange-600 text-orange-100' :
                      'bg-gray-600 text-gray-100'
                    }`}>
                      {review.manager_review_status || 'Not Started'}
                    </div>
                    <div className="text-xs text-gray-400 mt-1">Manager</div>
                  </div>
                  <button
                    onClick={() => handleViewAssessment(review.assessment_id)}
                    className="bg-cyan-600 hover:bg-cyan-700 text-white px-3 py-1 rounded text-sm"
                  >
                    View
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8">
            <Square size={48} className="mx-auto mb-4 text-gray-500" />
            <p className="text-gray-400">No recent review activity</p>
          </div>
        )}
      </div>

      {/* Pulse Questions Management Section */}
      <PulseQuestionsManager />
    </div>
  );
};

// src/components/pages/AdminDashboard.tsx - System-wide admin dashboard
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Shield,
  Users,
  BarChart3,
  TrendingUp,
  AlertTriangle,
  Activity,
  Database,
  Cpu,
  Clock,
  CheckCircle,
  Globe,
  Settings,
  Eye,
  RefreshCw,
  Download,
  Server,
  Zap,
  Target,
  Award,
  FileText,
  Inbox,
  MessageSquare,
  Star,
  ThumbsUp,
  X,
  ClipboardCheck,
  Check,
  AlertCircle,
  Edit,
  ExternalLink,
  MonitorSpeaker,
  UserCheck
} from 'lucide-react';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button } from '../ui';
import { OrgHealthWidget } from '../ui/TeamHealthPulse';
import TeamHealthAlerts from '../ui/TeamHealthAlerts';
import QuestionPerformanceWidget from '../analytics/QuestionPerformanceWidget';
import { 
  MetricCard, 
  PerformanceBarChart, 
  TrendLineChart, 
  DepartmentPieChart 
} from '../analytics/ChartComponents';
import { formatDate } from '../../utils';
import RoleBasedAnalyticsService, { AdminDashboardData } from '../../services/RoleBasedAnalyticsService';
import { FeedbackService, AdminApprovalService } from '../../services';
import SystemMonitoringService, { SystemHealth } from '../../services/SystemMonitoringService';

export default function AdminDashboard() {
  const navigate = useNavigate();
  const { userName } = useApp();
  const [dashboardData, setDashboardData] = useState<AdminDashboardData | null>(null);
  const [realtimeMetrics, setRealtimeMetrics] = useState<any>(null);
  const [systemHealth, setSystemHealth] = useState<SystemHealth | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [refreshing, setRefreshing] = useState(false);
  const [feedbackReceived, setFeedbackReceived] = useState<any[]>([]);
  const [feedbackLoading, setFeedbackLoading] = useState(true);
  const [pendingApprovals, setPendingApprovals] = useState<any[]>([]);
  const [approvalsLoading, setApprovalsLoading] = useState(true);
  const [uatFeedback, setUatFeedback] = useState<any[]>([]);
  const [uatLoading, setUatLoading] = useState(true);

  useEffect(() => {
    fetchAdminDashboard();
    fetchRecentFeedback();
    fetchPendingApprovals();
    fetchUATFeedback();
    // Set up metrics updates - less frequent since data is now consistent
    const interval = setInterval(fetchRealtimeMetrics, 120000); // Every 2 minutes instead of 30 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchAdminDashboard = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await RoleBasedAnalyticsService.getAdminDashboard();
      setDashboardData(data);
      await fetchRealtimeMetrics();
    } catch (err: any) {
      console.error('Error fetching admin dashboard:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchRealtimeMetrics = async () => {
    try {
      const monitoringService = SystemMonitoringService.getInstance();
      const health = await monitoringService.getSystemHealth();
      
      setSystemHealth(health);
      
      // Transform health metrics to display format
      const metrics = {
        currentLoad: Math.min(85, Math.max(15, (health.metrics.activeConnections * 2.5) + 15)),
        responseTime: health.metrics.databaseResponseTime,
        activeConnections: health.metrics.activeConnections,
        memoryUsage: Math.min(90, Math.max(30, (health.metrics.activeConnections * 1.2) + 40)),
        diskUsage: Math.min(85, Math.max(25, (health.metrics.activeConnections * 0.8) + 35)),
        errorRate: health.metrics.errorRate,
        uptime: health.metrics.uptime,
        status: health.status
      };
      
      setRealtimeMetrics(metrics);
    } catch (err) {
      console.warn('Error fetching real-time metrics:', err);
      // Fallback metrics if health check fails
      setRealtimeMetrics({
        currentLoad: 45,
        responseTime: 150,
        activeConnections: 0,
        memoryUsage: 65,
        diskUsage: 50,
        errorRate: 0,
        uptime: 0,
        status: 'warning'
      });
      setSystemHealth({
        status: 'warning',
        lastChecked: new Date().toISOString(),
        metrics: {
          databaseResponseTime: 150,
          activeConnections: 0,
          errorRate: 0,
          uptime: 0
        }
      });
    }
  };

  const handleRefresh = async () => {
    try {
      setRefreshing(true);
      await fetchAdminDashboard();
    } finally {
      setRefreshing(false);
    }
  };

  const fetchRecentFeedback = async () => {
    try {
      setFeedbackLoading(true);
      console.log(' AdminDashboard: Starting to fetch feedback...');
      const data = await FeedbackService.getMyFeedbackReceived(3);
      console.log(' AdminDashboard: Feedback received:', data);
      setFeedbackReceived(data || []);
    } catch (err: any) {
      console.error(' AdminDashboard: Error fetching feedback:', err);
    } finally {
      setFeedbackLoading(false);
    }
  };

  const handleDismissFeedback = (feedbackId: number) => {
    setFeedbackReceived(prev => prev.filter(f => f.feedback_id !== feedbackId));
  };

  const fetchPendingApprovals = async () => {
    try {
      setApprovalsLoading(true);
      console.log(' AdminDashboard: Fetching pending approvals...');
      const data = await AdminApprovalService.getPendingApprovals();
      console.log(' AdminDashboard: Pending approvals:', data);
      setPendingApprovals(data || []);
    } catch (err: any) {
      console.error(' AdminDashboard: Error fetching pending approvals:', err);
    } finally {
      setApprovalsLoading(false);
    }
  };

  const handleApproveReview = async (assessmentId: number, adminNotes?: string) => {
    try {
      await (AdminApprovalService as any).approveManagerReview(assessmentId, adminNotes);
      // Refresh the pending approvals list
      await fetchPendingApprovals();
      alert('Manager review approved successfully!');
    } catch (err: any) {
      console.error('Error approving review:', err);
      alert('Error approving review: ' + err.message);
    }
  };

  const handleRequestRevision = async (assessmentId: number, revisionNotes: string) => {
    try {
      await AdminApprovalService.requestRevision(assessmentId, revisionNotes);
      // Refresh the pending approvals list
      await fetchPendingApprovals();
      alert('Revision requested successfully! Manager will be notified.');
    } catch (err: any) {
      console.error('Error requesting revision:', err);
      alert('Error requesting revision: ' + err.message);
    }
  };

  const fetchUATFeedback = async () => {
    try {
      setUatLoading(true);
      console.log(' AdminDashboard: Fetching UAT feedback...');
      const { default: UATFeedbackService } = await import('../../services/UATFeedbackService');
      const data = await UATFeedbackService.getUATFeedback(5, 'open' as any); // Get 5 most recent open UAT feedback items
      console.log(' AdminDashboard: UAT feedback:', data);
      setUatFeedback(data || []);
    } catch (err: any) {
      console.error(' AdminDashboard: Error fetching UAT feedback:', err);
    } finally {
      setUatLoading(false);
    }
  };

  const handleUATFeedbackAction = async (feedbackId: number, action: string, notes?: string) => {
    try {
      const { default: UATFeedbackService } = await import('../../services/UATFeedbackService');
      await UATFeedbackService.updateFeedbackStatus(feedbackId, action, notes as any);
      
      // Refresh UAT feedback list
      await fetchUATFeedback();
      alert(` UAT feedback marked as ${action}!`);
    } catch (err: any) {
      console.error('Error updating UAT feedback:', err);
      alert('Error updating feedback: ' + err.message);
    }
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const getAlertSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return 'border-l-red-600 bg-red-900/20 text-red-300';
      case 'high': return 'border-l-red-500 bg-red-900/15 text-red-400';
      case 'medium': return 'border-l-yellow-500 bg-yellow-900/15 text-yellow-400';
      default: return 'border-l-blue-500 bg-blue-900/15 text-blue-400';
    }
  };

  const getAlertIcon = (type: string) => {
    switch (type) {
      case 'error': return AlertTriangle;
      case 'warning': return AlertTriangle;
      default: return Activity;
    }
  };

  if (loading) {
    return (
      <div className="p-8">
        <LoadingSpinner message="Loading system dashboard..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <ErrorMessage
          error={error}
          title="Dashboard Error"
          onRetry={fetchAdminDashboard}
        />
      </div>
    );
  }

  if (!dashboardData) {
    return (
      <div className="p-8">
        <div className="text-center">
          <Shield className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-300">No dashboard data</h3>
          <p className="mt-1 text-sm text-gray-400">Unable to load system dashboard.</p>
        </div>
      </div>
    );
  }

  // Prepare chart data - using real data only
  const departmentChartData = dashboardData.departmentBreakdown.map(dept => {
    const completed = Math.floor(dept.employeeCount * dept.completionRate / 100);
    const remaining = dept.employeeCount - completed;
    // Calculate overdue based on actual overdue ratio from organization metrics
    const overdueRatio = dashboardData.organizationMetrics.overdueItems / Math.max(dashboardData.organizationMetrics.totalAssessments, 1);
    const overdue = Math.floor(dept.employeeCount * overdueRatio);
    const pending = Math.max(0, remaining - overdue);
    
    return {
      name: dept.name,
      completed,
      pending,
      overdue
    };
  });

  const pieChartData = dashboardData.departmentBreakdown.map((dept, index) => ({
    name: dept.name,
    value: dept.employeeCount,
    color: ['#0891b2', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6'][index % 5]
  }));

  return (
    <div className="p-6 space-y-6 max-w-7xl mx-auto">
      {/* Admin Header */}
      <div className="bg-gradient-to-r from-red-900/30 to-orange-900/30 rounded-lg p-6 border border-red-500/20">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-white flex items-center">
              <Shield className="mr-3 text-red-400" size={28} />
              {getGreeting()}, {userName}! 
            </h1>
            <p className="text-red-400 mt-1">
              System Administrator - Full Access
            </p>
            <p className="text-gray-400 text-sm mt-2">
              Managing {dashboardData.systemStats.totalEmployees} employees across {dashboardData.departmentBreakdown.length} departments
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <Button
              onClick={handleRefresh}
              disabled={refreshing}
              variant="secondary"
              className="flex items-center space-x-2"
            >
              <RefreshCw size={16} className={refreshing ? 'animate-spin' : ''} />
              <span>Refresh</span>
            </Button>
            <Button
              variant="secondary"
              className="flex items-center space-x-2"
            >
              <Download size={16} />
              <span>Export</span>
            </Button>
          </div>
        </div>
      </div>

      {/* Real-time System Metrics */}
      {realtimeMetrics && (
        <div className="bg-gradient-to-r from-gray-800 to-gray-700 border border-gray-600 rounded-lg p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
              <span className="text-green-400 font-medium">System Health - Live</span>
            </div>
            <div className="flex items-center space-x-6 text-sm">
              <div className="flex items-center space-x-2">
                <Cpu size={14} className="text-gray-400" />
                <span className="text-white">CPU: {realtimeMetrics.currentLoad}%</span>
              </div>
              <div className="flex items-center space-x-2">
                <Database size={14} className="text-gray-400" />
                <span className="text-white">Memory: {realtimeMetrics.memoryUsage}%</span>
              </div>
              <div className="flex items-center space-x-2">
                <Globe size={14} className="text-gray-400" />
                <span className="text-white">{realtimeMetrics.activeConnections} connections</span>
              </div>
              <div className="flex items-center space-x-2">
                <Clock size={14} className="text-gray-400" />
                <span className="text-white">{realtimeMetrics.responseTime}ms avg</span>
              </div>
              <div className="flex items-center space-x-2">
                <Server size={14} className="text-gray-400" />
                <span className="text-white">Uptime: {dashboardData.systemStats.systemUptime}%</span>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* System KPIs */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-6 gap-4">
        <MetricCard
          title="Total Users"
          value={dashboardData.systemStats.totalEmployees}
          icon={Users}
          color="blue"
        />
        <MetricCard
          title="Active Users"
          value={dashboardData.systemStats.activeUsers}
          icon={Activity}
          color="green"
        />
        <MetricCard
          title="Completion Rate"
          value={`${dashboardData.organizationMetrics.overallCompletion}%`}
          icon={CheckCircle}
          color="green"
        />
        <MetricCard
          title="Satisfaction"
          value={typeof dashboardData.organizationMetrics.overallSatisfaction === 'number' 
            ? dashboardData.organizationMetrics.overallSatisfaction.toFixed(1) 
            : 'N/A'}
          icon={Award}
          color="yellow"
        />
        <MetricCard
          title="Pending Reviews"
          value={dashboardData.organizationMetrics.pendingReviews}
          icon={Clock}
          color={dashboardData.organizationMetrics.pendingReviews > 0 ? 'yellow' : 'green'}
        />
        <MetricCard
          title="System Alerts"
          value={dashboardData.systemAlerts.length}
          icon={AlertTriangle}
          color={dashboardData.systemAlerts.length > 0 ? 'red' : 'green'}
        />
      </div>

      {/* Pending Manager Review Approvals */}
      {pendingApprovals.length > 0 && (
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <ClipboardCheck className="mr-2 text-orange-400" size={20} />
              Pending Manager Review Approvals ({pendingApprovals.length})
            </h2>
            <Button
              onClick={fetchPendingApprovals}
              variant="ghost"
              size="sm"
              disabled={approvalsLoading}
            >
              <RefreshCw size={14} className={approvalsLoading ? 'animate-spin' : ''} />
            </Button>
          </div>
          
          <div className="space-y-4 max-h-96 overflow-y-auto">
            {pendingApprovals.map((approval) => (
              <PendingApprovalCard
                key={approval.assessment_id}
                approval={approval}
                onApprove={handleApproveReview}
                onRequestRevision={handleRequestRevision}
              />
            ))}
          </div>
        </div>
      )}

      {/* Main Analytics Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        
        {/* Department Performance Overview */}
        <div className="lg:col-span-2 bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <BarChart3 className="mr-2" size={20} />
              Department Performance
            </h2>
            <Button variant="ghost" size="sm">
              <Eye size={14} className="mr-1" />
              Drill Down
            </Button>
          </div>
          <PerformanceBarChart data={departmentChartData} height={300} />
        </div>

        {/* System Alerts */}
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <AlertTriangle className="mr-2" size={20} />
              System Alerts
            </h2>
            <div className="flex items-center space-x-2">
              {dashboardData.systemAlerts.filter(a => a.severity === 'high' || a.severity === 'critical').length > 0 && (
                <span className="bg-red-500 text-white text-xs px-2 py-1 rounded-full">
                  {dashboardData.systemAlerts.filter(a => a.severity === 'high' || a.severity === 'critical').length}
                </span>
              )}
              <button
                onClick={fetchRealtimeMetrics}
                className="text-gray-400 hover:text-white transition-colors text-xs"
                title="Refresh system health"
              >
                <RefreshCw size={14} />
              </button>
            </div>
          </div>
          
          <div className="space-y-3 max-h-64 overflow-y-auto">
            {dashboardData.systemAlerts.length === 0 ? (
              <div className="text-center py-8">
                <CheckCircle className="mx-auto h-8 w-8 text-green-400" />
                <p className="text-gray-400 text-sm mt-2">All systems normal</p>
              </div>
            ) : (
              dashboardData.systemAlerts.map((alert) => {
                const Icon = getAlertIcon(alert.type);
                return (
                  <SystemAlertCard
                    key={alert.id}
                    alert={alert}
                    Icon={Icon}
                    getAlertSeverityColor={getAlertSeverityColor}
                    formatDate={formatDate}
                    onNavigate={navigate}
                    systemHealth={systemHealth}
                  />
                );
              })
            )}
          </div>
          
          {/* Org Health Widget - Below System Alerts */}
          <div className="mt-6">
            <OrgHealthWidget />
          </div>
        </div>
      </div>

      {/* Organization Overview */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        
        {/* Performance Trends */}
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <TrendingUp className="mr-2" size={20} />
              Performance Trends
            </h2>
          </div>
          <TrendLineChart 
            data={dashboardData.performanceTrends.map(trend => ({
              date: trend.period,
              assessments: trend.completions,
              reviews: trend.completions, // Use actual completions, not estimated 80%
              satisfaction: Math.round(trend.satisfaction * 20) // Scale 5-point to percentage (already a real value)
            }))} 
            height={280} 
          />
        </div>

        {/* Employee Distribution */}
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <Users className="mr-2" size={20} />
              Employee Distribution
            </h2>
          </div>
          <DepartmentPieChart data={pieChartData} height={280} />
        </div>
      </div>

      {/* Department Breakdown Table */}
      <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-white flex items-center">
            <Database className="mr-2" size={20} />
            Department Breakdown
          </h2>
          <Button variant="ghost" size="sm">
            Export Data
          </Button>
        </div>
        
        <div className="overflow-x-auto">
          <table className="w-full text-sm text-gray-300">
            <thead>
              <tr className="border-b border-gray-700">
                <th className="text-left py-3 px-4">Department</th>
                <th className="text-center py-3 px-4">Employees</th>
                <th className="text-center py-3 px-4">Managers</th>
                <th className="text-center py-3 px-4">Completion Rate</th>
                <th className="text-center py-3 px-4">Satisfaction</th>
                <th className="text-center py-3 px-4">Status</th>
              </tr>
            </thead>
            <tbody>
              {dashboardData.departmentBreakdown.map((dept, index) => (
                <tr key={index} className="border-b border-gray-700/50 hover:bg-gray-700/30">
                  <td className="py-3 px-4 font-medium text-white">{dept.name}</td>
                  <td className="py-3 px-4 text-center">{dept.employeeCount}</td>
                  <td className="py-3 px-4 text-center">{dept.managerCount}</td>
                  <td className="py-3 px-4 text-center">
                    <span className={`px-2 py-1 rounded text-xs ${
                      dept.completionRate >= 90 ? 'bg-green-500/20 text-green-400' :
                      dept.completionRate >= 75 ? 'bg-yellow-500/20 text-yellow-400' :
                      'bg-red-500/20 text-red-400'
                    }`}>
                      {dept.completionRate}%
                    </span>
                  </td>
                  <td className="py-3 px-4 text-center">
                    {typeof dept.satisfactionScore === 'number' 
                      ? dept.satisfactionScore.toFixed(1) 
                      : 'N/A'}/5.0
                  </td>
                  <td className="py-3 px-4 text-center">
                    <span className={`w-2 h-2 rounded-full inline-block ${
                      dept.completionRate >= 85 ? 'bg-green-400' :
                      dept.completionRate >= 70 ? 'bg-yellow-400' :
                      'bg-red-400'
                    }`} />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Admin Quick Actions */}
      <div className="bg-gradient-to-r from-gray-800 to-gray-700 rounded-lg p-6 border border-gray-600">
        <h2 className="text-lg font-semibold text-white mb-4 flex items-center">
          <Zap className="mr-2" size={20} />
          System Administration
        </h2>
        <div className="grid grid-cols-2 md:grid-cols-6 gap-4">
          <Button 
            className="flex flex-col items-center space-y-2 h-16"
            onClick={() => navigate('/admin')}
          >
            <Settings size={20} />
            <span className="text-sm">Admin Panel</span>
          </Button>
          <Button 
            className="flex flex-col items-center space-y-2 h-16" 
            variant="secondary"
            onClick={() => navigate('/admin')}
          >
            <Users size={20} />
            <span className="text-sm">User Mgmt</span>
          </Button>
          <Button 
            className="flex flex-col items-center space-y-2 h-16" 
            variant="secondary"
            onClick={() => {
              alert('Reports feature is coming soon! This will provide comprehensive analytics and export capabilities.');
            }}
          >
            <FileText size={20} />
            <span className="text-sm">Reports</span>
          </Button>
          <Button 
            className="flex flex-col items-center space-y-2 h-16" 
            variant="secondary"
            onClick={() => {
              if (window.confirm('Open Supabase database dashboard?\n\nThis will open the external database management interface.')) {
                window.open('https://supabase.com/dashboard/project', '_blank');
              }
            }}
          >
            <Database size={20} />
            <span className="text-sm">Database</span>
          </Button>
          <Button 
            className="flex flex-col items-center space-y-2 h-16" 
            variant="secondary"
            onClick={() => {
              alert('System monitoring tools are integrated above in the real-time metrics panel. Enhanced monitoring dashboard coming soon!');
            }}
          >
            <Activity size={20} />
            <span className="text-sm">Monitoring</span>
          </Button>
          <Button 
            className="flex flex-col items-center space-y-2 h-16" 
            variant="secondary"
            onClick={() => navigate('/help')}
          >
            <FileText size={20} />
            <span className="text-sm">Admin Docs</span>
          </Button>
        </div>
      </div>

      {/* Team Health Alerts Section */}
      <TeamHealthAlerts role="admin" />

      {/* Question Performance Overview */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <QuestionPerformanceWidget 
          title="Company Question Performance"
          className="lg:col-span-2"
        />
      </div>

      {/* UAT Feedback Alerts Section */}
      {uatFeedback.length > 0 && (
        <div className="bg-gray-800 rounded-lg p-6 border-2 border-red-500/50 shadow-lg">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <AlertTriangle className="mr-2 text-red-400" size={20} />
               UAT User Reports ({uatFeedback.length})
            </h2>
            <div className="flex space-x-2">
              <button
                onClick={fetchUATFeedback}
                className="text-red-400 hover:text-red-300 transition-colors text-sm"
                title="Refresh UAT feedback"
              >
                
              </button>
              <span className="bg-red-500 text-white text-xs px-2 py-1 rounded-full animate-pulse">
                URGENT
              </span>
            </div>
          </div>
          
          <div className="space-y-4">
            {uatFeedback.map(feedback => (
              <UATFeedbackCard
                key={feedback.id}
                feedback={feedback}
                onAction={handleUATFeedbackAction}
              />
            ))}
          </div>
        </div>
      )}

      {/* Recent Feedback Section */}
      <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-white flex items-center">
            <Inbox className="mr-2" size={20} />
            Recent Feedback
          </h2>
          <div className="flex space-x-2">
            <button
              onClick={fetchRecentFeedback}
              className="text-gray-400 hover:text-white transition-colors text-sm"
              title="Refresh feedback"
            >
              
            </button>
            <button
              onClick={() => navigate('/feedback')}
              className="text-orange-400 hover:text-orange-300 text-sm transition-colors"
            >
              View All
            </button>
          </div>
        </div>
        
        <div className="space-y-3">
          {feedbackReceived.length > 0 ? (
            feedbackReceived.map(feedback => (
              <AdminFeedbackCard 
                key={feedback.feedback_id} 
                feedback={feedback} 
                onDismiss={handleDismissFeedback}
              />
            ))
          ) : (
            <div className="text-center py-4">
              <p className="text-gray-400 text-sm">No recent feedback</p>
            </div>
          )}
        </div>
      </div>

      {/* Admin Documentation Section */}
      <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-white flex items-center">
            <Shield className="mr-2" size={20} />
            Admin Documentation Hub
          </h2>
          <Button 
            variant="ghost" 
            size="sm"
            onClick={() => navigate('/help')}
          >
            View Full Documentation
          </Button>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {/* API Documentation */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <Server className="mr-2 text-blue-400" size={16} />
              API Documentation
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> REST API endpoints and authentication</li>
              <li> Request/response schemas</li>
              <li> Rate limiting and error handling</li>
              <li> Webhook configuration</li>
            </ul>
          </div>

          {/* Developer Guides */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <FileText className="mr-2 text-green-400" size={16} />
              Developer Guides
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> System architecture overview</li>
              <li> Database schema and relationships</li>
              <li> Component library documentation</li>
              <li> Deployment and CI/CD guides</li>
            </ul>
          </div>

          {/* Monitoring & Logs */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <Activity className="mr-2 text-purple-400" size={16} />
              Monitoring & Logs
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> System health monitoring dashboard</li>
              <li> Error tracking and alerting</li>
              <li> Performance metrics and analytics</li>
              <li> Log aggregation and search</li>
            </ul>
          </div>

          {/* System Management */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <Settings className="mr-2 text-yellow-400" size={16} />
              System Management
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> User management and permissions</li>
              <li> Review cycle configuration</li>
              <li> System settings and preferences</li>
              <li> Data backup and recovery</li>
            </ul>
          </div>

          {/* Security & Compliance */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <Shield className="mr-2 text-red-400" size={16} />
              Security & Compliance
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> Authentication and authorization</li>
              <li> Data privacy and GDPR compliance</li>
              <li> Security audit logs</li>
              <li> Penetration testing reports</li>
            </ul>
          </div>

          {/* Troubleshooting */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <AlertTriangle className="mr-2 text-orange-400" size={16} />
              Troubleshooting
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> Common issues and solutions</li>
              <li> Performance optimization tips</li>
              <li> Database maintenance procedures</li>
              <li> Emergency response procedures</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

// Feedback type configuration
const feedbackTypeConfig = {
  positive: { 
    icon: Star, 
    color: 'text-yellow-400', 
    bgColor: 'bg-yellow-900', 
    label: 'Recognition' 
  },
  constructive: { 
    icon: MessageSquare, 
    color: 'text-blue-400', 
    bgColor: 'bg-blue-900', 
    label: 'Growth' 
  },
  appreciation: { 
    icon: ThumbsUp, 
    color: 'text-green-400', 
    bgColor: 'bg-green-900', 
    label: 'Thanks' 
  }
};

// Feedback Card Component for AdminDashboard
const AdminFeedbackCard = ({ feedback, onDismiss }: { feedback: any, onDismiss: (id: number) => void }) => {
  const typeConfig = feedbackTypeConfig[feedback.feedback_type as keyof typeof feedbackTypeConfig] || feedbackTypeConfig.positive;
  const Icon = typeConfig.icon;

  return (
    <div className="bg-gray-700/50 p-4 rounded-lg group hover:bg-gray-700 transition-colors">
      <div className="flex items-start space-x-3">
        <div className={`p-2 rounded-lg ${typeConfig.bgColor} bg-opacity-50 flex-shrink-0`}>
          <Icon size={16} className={typeConfig.color} />
        </div>
        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center space-x-2">
              <span className="text-white font-medium text-sm">You</span>
              <span className="text-gray-400 text-xs">received</span>
              <span className={`px-2 py-1 text-xs rounded ${typeConfig.bgColor} ${typeConfig.color}`}>
                {typeConfig.label}
              </span>
            </div>
            <button
              onClick={() => onDismiss(feedback.feedback_id)}
              className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-white transition-all duration-200"
              title="Dismiss"
            >
              <X size={14} />
            </button>
          </div>
          <p className="text-gray-300 text-sm mb-2 line-clamp-2">{feedback.message}</p>
          <div className="flex justify-between items-center">
            <span className="text-gray-500 text-xs">
              from {feedback.is_anonymous ? 'Anonymous' : feedback.giver_name}
            </span>
            <span className="text-gray-500 text-xs">
              {formatDate(feedback.created_at)}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

// Pending Approval Card Component
const PendingApprovalCard = ({ 
  approval, 
  onApprove, 
  onRequestRevision 
}: { 
  approval: any, 
  onApprove: (id: number, notes?: string) => void,  
  onRequestRevision: (id: number, notes: string) => void 
}) => {
  const [showActions, setShowActions] = useState(false);
  const [revisionNotes, setRevisionNotes] = useState('');
  const [adminNotes, setAdminNotes] = useState('');
  const [showRevisionForm, setShowRevisionForm] = useState(false);
  const [showApprovalForm, setShowApprovalForm] = useState(false);

  const handleApprove = () => {
    onApprove(approval.assessment_id, adminNotes.trim() || undefined);
    setShowApprovalForm(false);
    setAdminNotes('');
  };

  const handleRequestRevision = () => {
    if (revisionNotes.trim()) {
      onRequestRevision(approval.assessment_id, revisionNotes.trim());
      setShowRevisionForm(false);
      setRevisionNotes('');
    }
  };

  return (
    <div className="bg-gray-700/50 rounded-lg p-4 border border-orange-500/20">
      <div className="flex items-start justify-between mb-3">
        <div>
          <h3 className="text-white font-medium">{approval.employee_name}</h3>
          <p className="text-gray-400 text-sm">{approval.review_cycle_name}</p>
          <p className="text-gray-500 text-xs">
            Manager: {approval.manager_name}  Submitted: {formatDate(approval.submitted_date)}
          </p>
        </div>
        <div className="flex items-center space-x-2">
          <span className="bg-orange-500/20 text-orange-400 px-2 py-1 rounded text-xs font-medium">
            Pending Approval
          </span>
          <button
            onClick={() => setShowActions(!showActions)}
            className="text-gray-400 hover:text-white transition-colors"
          >
            <Edit size={16} />
          </button>
        </div>
      </div>

      {approval.manager_performance_rating && (
        <div className="mb-3">
          <span className="text-sm text-gray-400">Rating: </span>
          <span className="text-white text-sm font-medium capitalize">
            {approval.manager_performance_rating.replace('_', ' ')}
          </span>
        </div>
      )}

      {approval.manager_summary_comments && (
        <div className="mb-3">
          <p className="text-gray-300 text-sm line-clamp-2">
            {approval.manager_summary_comments}
          </p>
        </div>
      )}

      {showActions && (
        <div className="mt-4 pt-4 border-t border-gray-600">
          <div className="flex space-x-3">
            <Button
              onClick={() => setShowApprovalForm(true)}
              variant="primary"
              size="sm"
              className="bg-green-600 hover:bg-green-700"
            >
              <Check size={14} className="mr-1" />
              Approve
            </Button>
            <Button
              onClick={() => setShowRevisionForm(true)}
              variant="secondary"
              size="sm"
              className="bg-yellow-600 hover:bg-yellow-700"
            >
              <AlertCircle size={14} className="mr-1" />
              Request Revision
            </Button>
          </div>
        </div>
      )}

      {showApprovalForm && (
        <div className="mt-4 pt-4 border-t border-gray-600">
          <div className="space-y-3">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Admin Notes (Optional)
              </label>
              <textarea
                value={adminNotes}
                onChange={(e) => setAdminNotes(e.target.value)}
                className="w-full p-2 bg-gray-700 border border-gray-600 rounded text-white text-sm"
                rows={2}
                placeholder="Add any notes about this approval..."
              />
            </div>
            <div className="flex space-x-2">
              <Button
                onClick={handleApprove}
                variant="primary"
                size="sm"
                className="bg-green-600 hover:bg-green-700"
              >
                Confirm Approval
              </Button>
              <Button
                onClick={() => {
                  setShowApprovalForm(false);
                  setAdminNotes('');
                }}
                variant="secondary"
                size="sm"
              >
                Cancel
              </Button>
            </div>
          </div>
        </div>
      )}

      {showRevisionForm && (
        <div className="mt-4 pt-4 border-t border-gray-600">
          <div className="space-y-3">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Revision Notes (Required)
              </label>
              <textarea
                value={revisionNotes}
                onChange={(e) => setRevisionNotes(e.target.value)}
                className="w-full p-2 bg-gray-700 border border-gray-600 rounded text-white text-sm"
                rows={3}
                placeholder="Explain what needs to be revised..."
                required
              />
            </div>
            <div className="flex space-x-2">
              <Button
                onClick={handleRequestRevision}
                variant="primary"
                size="sm"
                className="bg-yellow-600 hover:bg-yellow-700"
                disabled={!revisionNotes.trim()}
              >
                Send for Revision
              </Button>
              <Button
                onClick={() => {
                  setShowRevisionForm(false);
                  setRevisionNotes('');
                }}
                variant="secondary"
                size="sm"
              >
                Cancel
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Enhanced System Alert Card Component with actionable information
const SystemAlertCard = ({ 
  alert, 
  Icon, 
  getAlertSeverityColor, 
  formatDate, 
  onNavigate, 
  systemHealth 
}: { 
  alert: any, 
  Icon: any, 
  getAlertSeverityColor: (severity: string) => string, 
  formatDate: (date: string) => string, 
  onNavigate: (path: string) => void,
  systemHealth: SystemHealth | null 
}) => {
  const [showDetails, setShowDetails] = useState(false);

  // Generate actionable information based on alert type and source
  const getActionableInfo = () => {
    const actions = [];
    const details = [];

    switch (alert.source) {
      case 'database':
        if (alert.title.includes('Performance')) {
          details.push(`Current response time: ${systemHealth?.metrics.databaseResponseTime || 'Unknown'}ms`);
          details.push(`Active connections: ${systemHealth?.metrics.activeConnections || 0}`);
          actions.push({ 
            label: 'View Database Monitoring', 
            action: () => onNavigate('/admin'), 
            icon: Database 
          });
          actions.push({ 
            label: 'Check System Health', 
            action: () => window.open('https://console.supabase.com', '_blank'), 
            icon: ExternalLink 
          });
        }
        break;

      case 'authentication':
        if (alert.title.includes('Authentication')) {
          details.push(`Current error rate: ${((systemHealth?.metrics.errorRate || 0) * 100).toFixed(2)}%`);
          details.push(`Recent users active: ${systemHealth?.metrics.activeConnections || 0}`);
          details.push('Check browser console for authentication error details');
          actions.push({ 
            label: 'View Employee Management', 
            action: () => onNavigate('/admin'), 
            icon: UserCheck 
          });
          actions.push({ 
            label: 'Open Browser Console', 
            action: () => {
              window.alert('Press F12 or right-click -> Inspect -> Console tab to view authentication logs');
              // For developers: console logs are where auth events are recorded
              console.log(' Authentication Alert Details:', {
                errorRate: systemHealth?.metrics.errorRate,
                activeConnections: systemHealth?.metrics.activeConnections,
                timestamp: new Date().toISOString(),
                suggestion: 'Check console logs for detailed authentication events'
              });
            }, 
            icon: FileText 
          });
        }
        break;

      case 'performance':
        if (alert.title.includes('High System Usage')) {
          details.push(`Active users: ${systemHealth?.metrics.activeConnections || 0}`);
          details.push(`System uptime: ${Math.floor((systemHealth?.metrics.uptime || 0) / 3600000)}h`);
          actions.push({ 
            label: 'View System Monitoring', 
            action: () => onNavigate('/admin'), 
            icon: MonitorSpeaker 
          });
          actions.push({ 
            label: 'Check Performance Metrics', 
            action: () => setShowDetails(!showDetails), 
            icon: Activity 
          });
        }
        break;

      case 'application':
        if (alert.title.includes('Assessment')) {
          details.push('Check recent assessment submissions');
          details.push('Review incomplete assessments');
          actions.push({ 
            label: 'View Assessments', 
            action: () => onNavigate('/admin'), 
            icon: ClipboardCheck 
          });
          actions.push({ 
            label: 'Review Manager Dashboard', 
            action: () => onNavigate('/manager-dashboard'), 
            icon: Users 
          });
        } else if (alert.title.includes('Monitoring')) {
          details.push('System health monitoring is experiencing issues');
          details.push('Manual verification may be required');
          actions.push({ 
            label: 'Manual Health Check', 
            action: () => window.location.reload(), 
            icon: RefreshCw 
          });
        } else if (alert.title.includes('System Operating')) {
          details.push('All system components functioning normally');
          details.push('No immediate action required');
          actions.push({ 
            label: 'View Full Report', 
            action: () => setShowDetails(!showDetails), 
            icon: Eye 
          });
        }
        break;

      default:
        details.push('System alert requires attention');
        actions.push({ 
          label: 'View Admin Panel', 
          action: () => onNavigate('/admin'), 
          icon: Settings 
        });
    }

    return { actions, details };
  };

  const { actions, details } = getActionableInfo();

  return (
    <div className={`p-4 rounded-lg border-l-4 ${getAlertSeverityColor(alert.severity)} hover:bg-gray-700/30 transition-colors`}>
      <div className="flex items-start space-x-3">
        <Icon size={18} className="mt-0.5 flex-shrink-0" />
        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between mb-2">
            <h3 className="font-medium text-sm text-white">{alert.title}</h3>
            <button
              onClick={() => setShowDetails(!showDetails)}
              className="text-gray-400 hover:text-white transition-colors"
              title={showDetails ? 'Hide details' : 'Show details'}
            >
              <Eye size={14} />
            </button>
          </div>
          
          <p className="text-xs mt-1 text-gray-300 leading-relaxed">{alert.message}</p>
          
          {/* Quick Actions */}
          {actions.length > 0 && (
            <div className="flex items-center space-x-2 mt-3">
              {actions.slice(0, 2).map((actionItem, index) => {
                const ActionIcon = actionItem.icon;
                return (
                  <button
                    key={index}
                    onClick={actionItem.action}
                    className="flex items-center space-x-1 px-2 py-1 bg-gray-600 hover:bg-gray-500 rounded text-xs text-gray-200 hover:text-white transition-colors"
                    title={actionItem.label}
                  >
                    <ActionIcon size={12} />
                    <span>{actionItem.label}</span>
                  </button>
                );
              })}
            </div>
          )}

          {/* Detailed Information */}
          {showDetails && details.length > 0 && (
            <div className="mt-3 pt-3 border-t border-gray-600">
              <h4 className="text-xs font-medium text-gray-300 mb-2">Details:</h4>
              <ul className="space-y-1">
                {details.map((detail, index) => (
                  <li key={index} className="text-xs text-gray-400 flex items-center space-x-2">
                    <div className="w-1 h-1 bg-gray-500 rounded-full flex-shrink-0" />
                    <span>{detail}</span>
                  </li>
                ))}
              </ul>
              
              {/* Additional Actions */}
              {actions.length > 2 && (
                <div className="flex items-center space-x-2 mt-3">
                  {actions.slice(2).map((actionItem, index) => {
                    const ActionIcon = actionItem.icon;
                    return (
                      <button
                        key={index + 2}
                        onClick={actionItem.action}
                        className="flex items-center space-x-1 px-2 py-1 bg-blue-600/20 hover:bg-blue-600/40 rounded text-xs text-blue-300 hover:text-blue-200 transition-colors"
                        title={actionItem.label}
                      >
                        <ActionIcon size={12} />
                        <span>{actionItem.label}</span>
                      </button>
                    );
                  })}
                </div>
              )}
            </div>
          )}

          {/* Footer with timestamp and severity */}
          <div className="flex items-center justify-between mt-3">
            <span className="text-xs text-gray-500">
              {formatDate(alert.timestamp)}
            </span>
            <span className={`px-2 py-0.5 rounded text-xs font-medium ${
              alert.severity === 'critical' ? 'bg-red-600/20 text-red-300' :
              alert.severity === 'high' ? 'bg-red-500/20 text-red-400' :
              alert.severity === 'medium' ? 'bg-yellow-500/20 text-yellow-400' :
              'bg-blue-500/20 text-blue-400'
            }`}>
              {alert.severity.toUpperCase()}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};
// UAT Feedback Card Component
const UATFeedbackCard = ({ 
  feedback, 
  onAction 
}: { 
  feedback: any, 
  onAction: (feedbackId: number, action: string, notes?: string) => void 
}) => {
  const [showActions, setShowActions] = useState(false);
  const [showScreenshot, setShowScreenshot] = useState(false);

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case "critical": return "bg-red-600/20 text-red-300 border-red-500";
      case "high": return "bg-red-500/20 text-red-400 border-red-400";
      case "medium": return "bg-yellow-500/20 text-yellow-400 border-yellow-500";
      case "low": return "bg-gray-500/20 text-gray-400 border-gray-500";
      default: return "bg-blue-500/20 text-blue-400 border-blue-500";
    }
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case "bug": return "";
      case "ui_ux": return "";
      case "feature_request": return "";
      case "question": return "";
      case "other": return "";
      default: return "";
    }
  };

  const formatTimestamp = (timestamp: string) => {
    return new Date(timestamp).toLocaleString();
  };

  return (
    <div className="bg-red-900/10 border border-red-500/30 rounded-lg p-4 hover:bg-red-900/20 transition-colors">
      <div className="flex items-start justify-between mb-3">
        <div className="flex-1">
          <div className="flex items-center space-x-3 mb-2">
            <span className="text-lg">{getCategoryIcon(feedback.category)}</span>
            <h3 className="text-white font-medium text-sm">{feedback.title}</h3>
            <span className={`px-2 py-1 rounded text-xs font-medium border ${getPriorityColor(feedback.priority)}`}>
              {feedback.priority?.toUpperCase() || "MEDIUM"}
            </span>
          </div>
          
          <p className="text-gray-300 text-sm mb-2 line-clamp-2">{feedback.description}</p>
          
          {feedback.current_url && (
            <p className="text-blue-400 text-xs mb-2">
               {feedback.current_url}
            </p>
          )}
          
          <div className="flex items-center justify-between text-xs text-gray-500">
            <span> {feedback.user_email || "Anonymous"}</span>
            <span> {formatTimestamp(feedback.submitted_at || feedback.created_at)}</span>
          </div>
        </div>
        
        <div className="flex items-center space-x-2 ml-4">
          {feedback.screenshot_data && (
            <button
              onClick={() => setShowScreenshot(!showScreenshot)}
              className="text-cyan-400 hover:text-cyan-300 text-xs"
              title="View screenshot"
            >
              
            </button>
          )}
          <button
            onClick={() => setShowActions(!showActions)}
            className="text-red-400 hover:text-red-300 text-sm"
            title="Actions"
          >
            
          </button>
        </div>
      </div>

      {/* Screenshot Modal */}
      {showScreenshot && feedback.screenshot_data && (
        <div className="mt-3 p-3 bg-gray-700 rounded border">
          <div className="flex items-center justify-between mb-2">
            <span className="text-sm text-gray-300">Screenshot:</span>
            <button
              onClick={() => setShowScreenshot(false)}
              className="text-gray-400 hover:text-white text-xs"
            >
              
            </button>
          </div>
          <img
            src={feedback.screenshot_data}
            alt="User submitted screenshot"
            className="max-w-full max-h-64 rounded border border-gray-600"
          />
        </div>
      )}

      {/* Reproduction Steps */}
      {feedback.reproduction_steps && (
        <div className="mt-3 p-3 bg-gray-700/50 rounded">
          <h4 className="text-xs font-medium text-gray-300 mb-2">Steps to Reproduce:</h4>
          <pre className="text-xs text-gray-400 whitespace-pre-wrap">{feedback.reproduction_steps}</pre>
        </div>
      )}

      {/* System Info */}
      {feedback.browser_info && (
        <div className="mt-3 p-2 bg-gray-700/30 rounded">
          <h4 className="text-xs font-medium text-gray-300 mb-1">System Info:</h4>
          <p className="text-xs text-gray-500 font-mono">{feedback.browser_info}</p>
        </div>
      )}

      {/* Actions */}
      {showActions && (
        <div className="mt-4 pt-3 border-t border-red-500/30">
          <div className="flex space-x-2">
            <button
              onClick={() => {
                onAction(feedback.id, "in_progress");
                setShowActions(false);
              }}
              className="px-3 py-1 bg-yellow-600 hover:bg-yellow-700 text-white text-xs rounded"
            >
               Working On It
            </button>
            <button
              onClick={() => {
                const notes = window.prompt("Resolution notes (optional):");
                onAction(feedback.id, "resolved", notes || undefined);
                setShowActions(false);
              }}
              className="px-3 py-1 bg-green-600 hover:bg-green-700 text-white text-xs rounded"
            >
               Mark Resolved
            </button>
            <button
              onClick={() => {
                if (window.confirm("Are you sure you want to dismiss this feedback?")) {
                  onAction(feedback.id, "dismissed");
                  setShowActions(false);
                }
              }}
              className="px-3 py-1 bg-gray-600 hover:bg-gray-700 text-white text-xs rounded"
            >
               Dismiss
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AssessmentService } from '../../services';
import { useApp } from '../../contexts';
import { Save, Send, Edit3, CheckCircle, Clock, ArrowLeft, User, Target, BookOpen } from 'lucide-react';
import { Button, LoadingSpinner, ErrorMessage } from '../ui';

export default function Assessment({ pageProps }) {
  const navigate = useNavigate();
  const { userRole } = useApp();
  const { assessmentId } = pageProps;
  const [assessment, setAssessment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [saving, setSaving] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        if (!assessmentId) {
          throw new Error('No assessment ID provided');
        }
        const data = await AssessmentService.getAssessmentById(assessmentId);
        const assessmentData = data?.[0];
        setAssessment(assessmentData);
        
        // Initialize form data with current assessment values
        if (assessmentData) {
          setFormData({
            employee_strengths: assessmentData.employee_strengths || '',
            employee_improvements: assessmentData.employee_improvements || '',
            value_passionate_examples: assessmentData.value_passionate_examples || '',
            value_driven_examples: assessmentData.value_driven_examples || '',
            value_resilient_examples: assessmentData.value_resilient_examples || '',
            value_responsive_examples: assessmentData.value_responsive_examples || '',
            gwc_gets_it: assessmentData.gwc_gets_it || false,
            gwc_gets_it_feedback: assessmentData.gwc_gets_it_feedback || '',
            gwc_wants_it: assessmentData.gwc_wants_it || false,
            gwc_wants_it_feedback: assessmentData.gwc_wants_it_feedback || '',
            gwc_capacity: assessmentData.gwc_capacity || false,
            gwc_capacity_feedback: assessmentData.gwc_capacity_feedback || '',
            // Manager GWC fields
            manager_gwc_gets_it: assessmentData.manager_gwc_gets_it || false,
            manager_gwc_gets_it_feedback: assessmentData.manager_gwc_gets_it_feedback || '',
            manager_gwc_wants_it: assessmentData.manager_gwc_wants_it || false,
            manager_gwc_wants_it_feedback: assessmentData.manager_gwc_wants_it_feedback || '',
            manager_gwc_capacity: assessmentData.manager_gwc_capacity || false,
            manager_gwc_capacity_feedback: assessmentData.manager_gwc_capacity_feedback || '',
            // Manager fields
            manager_performance_rating: assessmentData.manager_performance_rating || '',
            manager_summary_comments: assessmentData.manager_summary_comments || '',
            manager_core_values_feedback: assessmentData.manager_core_values_feedback || '',
            manager_development_plan: assessmentData.manager_development_plan || '',
            manager_action_items: assessmentData.manager_action_items || ''
          });
          
          // Auto-enable editing based on user role and assessment state
          if (assessmentData.can_edit_self_assessment && 
              (assessmentData.self_assessment_status === 'not_started' || 
               assessmentData.self_assessment_status === 'in_progress')) {
            setIsEditing(true);
          }
          
          // Auto-enable editing for managers when employee has submitted
          if (userRole === 'manager' && 
              assessmentData.self_assessment_status === 'employee_complete' &&
              assessmentData.manager_review_status === 'pending') {
            setIsEditing(true);
          }
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchDetails();
  }, [assessmentId]);

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      
      await AssessmentService.updateAssessment(assessmentId, formData);
      
      // Refresh the assessment data
      const updatedData = await AssessmentService.getAssessmentById(assessmentId);
      setAssessment(updatedData?.[0]);
      
      alert('Assessment saved successfully!');
    } catch (err) {
      console.error('Error saving assessment:', err);
      alert('Error saving assessment: ' + err.message);
    } finally {
      setSaving(false);
    }
  };

  const handleSubmit = async () => {
    try {
      setSubmitting(true);
      await AssessmentService.submitAssessment(assessmentId);
      
      // Refresh the assessment data
      const updatedData = await AssessmentService.getAssessmentById(assessmentId);
      setAssessment(updatedData?.[0]);
      setIsEditing(false);
      
      alert('Self-assessment submitted successfully! Your manager will now review it.');
    } catch (err) {
      alert('Error submitting assessment: ' + err.message);
    } finally {
      setSubmitting(false);
    }
  };

  const handleManagerSubmit = async () => {
    try {
      setSubmitting(true);
      
      // FIXED: Single atomic call to submit all manager feedback and update status
      await AssessmentService.submitManagerReview(assessmentId, formData);
      
      // Refresh the assessment data to reflect the changes
      const updatedData = await AssessmentService.getAssessmentById(assessmentId);
      
      setAssessment(updatedData?.[0]);
      setIsEditing(false);
      
      alert('Manager review submitted successfully! The employee will be notified.');
    } catch (err) {
      console.error('Error submitting manager review:', err);
      alert('Error submitting manager review: ' + err.message);
    } finally {
      setSubmitting(false);
    }
  };

  const handleEmployeeAcknowledgment = async () => {
    try {
      setSubmitting(true);
      
      // Mark the review as acknowledged by employee
      await AssessmentService.acknowledgeReview(assessmentId);
      
      // Refresh the assessment data
      const updatedData = await AssessmentService.getAssessmentById(assessmentId);
      setAssessment(updatedData?.[0]);
      
      alert('Review acknowledged successfully! The review process is now complete.');
    } catch (err) {
      alert('Error acknowledging review: ' + err.message);
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return (
    <div className="p-8">
      <LoadingSpinner size="lg" message="Loading assessment..." />
    </div>
  );
  
  if (error) return (
    <div className="p-8">
      <ErrorMessage error={error} title="Assessment Error" />
    </div>
  );
  
  if (!assessment) return (
    <div className="p-8">
      <div className="text-center py-12">
        <p className="text-gray-400">Assessment not found.</p>
      </div>
    </div>
  );

  // Permission logic for different user roles
  const canEditSelfAssessment = userRole === 'admin' || (assessment.can_edit_self_assessment && !assessment.is_manager_view);
  const canEditManagerReview = userRole === 'admin' || (userRole === 'manager' && assessment.self_assessment_status === 'employee_complete' && assessment.manager_review_status === 'pending');
  const canAcknowledgeReview = (userRole === 'employee' || userRole === 'admin') && assessment.manager_review_status === 'completed' && !assessment.employee_acknowledgment;
  const canEdit = canEditSelfAssessment || canEditManagerReview;
  
  const isInProgress = assessment.self_assessment_status === 'in_progress';
  const isNotStarted = assessment.self_assessment_status === 'not_started';
  const isSubmitted = assessment.self_assessment_status === 'employee_complete';
  const isManagerReview = userRole === 'manager' && assessment.self_assessment_status === 'employee_complete';
  const isReviewComplete = assessment.manager_review_status === 'completed';
  const isFullyComplete = assessment.employee_acknowledgment;
  

  return (
    <div className="p-8 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <button 
            onClick={() => navigate(userRole === 'manager' ? '/team' : '/reviews')} 
            className="text-cyan-400 hover:underline flex items-center"
          >
            <ArrowLeft size={16} className="mr-1" />
            Back to {userRole === 'manager' ? 'My Team' : 'My Reviews'}
          </button>
        </div>
        
        {/* Action Buttons */}
        <div className="flex items-center space-x-3">
          {canEdit && isEditing && (
            <>
              <Button
                onClick={handleSave}
                disabled={saving}
                variant="secondary"
                size="sm"
              >
                {saving ? (
                  <><Clock size={14} className="mr-1" /> Saving...</>
                ) : (
                  <><Save size={14} className="mr-1" /> Save Draft</>
                )}
              </Button>
              
              {/* Employee Submit Button */}
              {canEditSelfAssessment && !isManagerReview && (
                <Button
                  onClick={handleSubmit}
                  disabled={submitting}
                  variant="primary"
                  size="sm"
                >
                  {submitting ? (
                    <><Clock size={14} className="mr-1" /> Submitting...</>
                  ) : (
                    <><Send size={14} className="mr-1" /> Submit for Review</>
                  )}
                </Button>
              )}

              {/* Manager Submit Button */}
              {canEditManagerReview && (
                <Button
                  onClick={handleManagerSubmit}
                  disabled={submitting}
                  variant="primary"
                  size="sm"
                >
                  {submitting ? (
                    <><Clock size={14} className="mr-1" /> Submitting...</>
                  ) : (
                    <><Send size={14} className="mr-1" /> Complete Manager Review</>
                  )}
                </Button>
              )}
            </>
          )}
          
          {/* Start/Continue Buttons */}
          {canEditSelfAssessment && !isEditing && (isNotStarted || isInProgress) && (
            <Button
              onClick={() => setIsEditing(true)}
              variant="primary"
              size="sm"
            >
              <Edit3 size={14} className="mr-1" />
              {isNotStarted ? 'Start Self-Assessment' : 'Continue Assessment'}
            </Button>
          )}

          {/* Manager Review Button */}
          {canEditManagerReview && !isEditing && (
            <Button
              onClick={() => setIsEditing(true)}
              variant="primary"
              size="sm"
            >
              <Edit3 size={14} className="mr-1" />
              Start Manager Review
            </Button>
          )}

          {/* Employee Acknowledgment Button */}
          {canAcknowledgeReview && (
            <Button
              onClick={handleEmployeeAcknowledgment}
              disabled={submitting}
              variant="primary"
              size="sm"
              className="bg-green-600 hover:bg-green-700 border-green-600"
            >
              {submitting ? (
                <><Clock size={14} className="mr-1" /> Processing...</>
              ) : (
                <><CheckCircle size={14} className="mr-1" /> Acknowledge Review</>
              )}
            </Button>
          )}
        </div>
      </div>

      {/* Assessment Header */}
      <div className="bg-gray-800 rounded-lg p-6">
        <h2 className="text-3xl font-bold text-white mb-2">{assessment.employee_name}</h2>
        <p className="text-gray-400 mb-4">Review Cycle: {assessment.review_cycle_name}</p>
        
        {/* Status Indicator */}
        <div className="flex items-center space-x-4">
          {/* Employee Status */}
          {isSubmitted && (
            <div className="flex items-center text-green-400">
              <CheckCircle size={16} className="mr-1" />
              <span className="text-sm font-medium">Employee Assessment Complete</span>
            </div>
          )}
          {isInProgress && (
            <div className="flex items-center text-yellow-400">
              <Clock size={16} className="mr-1" />
              <span className="text-sm font-medium">Self-Assessment In Progress</span>
            </div>
          )}
          {isNotStarted && canEdit && (
            <div className="flex items-center text-blue-400">
              <Edit3 size={16} className="mr-1" />
              <span className="text-sm font-medium">Ready to Start</span>
            </div>
          )}

          {/* Manager Status */}
          {assessment.manager_review_status === 'completed' && (
            <div className="flex items-center text-green-400">
              <CheckCircle size={16} className="mr-1" />
              <span className="text-sm font-medium">Manager Review Complete</span>
            </div>
          )}
          {assessment.manager_review_status === 'pending' && isSubmitted && (
            <div className="flex items-center text-yellow-400">
              <Clock size={16} className="mr-1" />
              <span className="text-sm font-medium">Awaiting Manager Review</span>
            </div>
          )}

          {/* Final Status */}
          {isFullyComplete && (
            <div className="flex items-center text-green-400">
              <CheckCircle size={16} className="mr-1" />
              <span className="text-sm font-medium">Review Process Complete</span>
            </div>
          )}
          {canAcknowledgeReview && (
            <div className="flex items-center text-blue-400">
              <Clock size={16} className="mr-1" />
              <span className="text-sm font-medium">Awaiting Employee Acknowledgment</span>
            </div>
          )}
        </div>
      </div>

      {/* Core Values Section */}
      <SelfAssessmentSection
        title="Core Values Examples"
        subtitle="Provide specific examples of how you've demonstrated our core values"
        icon={User}
      >
        <CoreValuesSection 
          assessment={assessment}
          formData={formData}
          isEditing={isEditing}
          onChange={handleInputChange}
          isManagerReview={isManagerReview}
          canEditManagerReview={canEditManagerReview}
        />
      </SelfAssessmentSection>

      {/* GWC Section */}
      <SelfAssessmentSection
        title="GWC Assessment"
        subtitle="Evaluate your alignment with your role's requirements"
        icon={Target}
      >
        <GWCSection 
          assessment={assessment}
          formData={formData}
          isEditing={isEditing}
          onChange={handleInputChange}
          isManagerReview={isManagerReview}
          canEditManagerReview={canEditManagerReview}
        />
      </SelfAssessmentSection>

      {/* Strengths & Improvements */}
      <SelfAssessmentSection
        title="Self-Reflection"
        subtitle="Reflect on your strengths and areas for improvement"
        icon={BookOpen}
      >
        <StrengthsSection 
          assessment={assessment}
          formData={formData}
          isEditing={isEditing}
          onChange={handleInputChange}
          isManagerReview={isManagerReview}
          canEditManagerReview={canEditManagerReview}
        />
      </SelfAssessmentSection>

      {/* Rocks Section */}
      <SelfAssessmentSection
        title="Quarterly Rocks"
        subtitle="Your key objectives for this quarter"
        icon={Target}
      >
        <RocksSection assessment={assessment} />
      </SelfAssessmentSection>

      {/* Manager Section (show when employee has submitted or when manager is reviewing) */}
      {(assessment.self_assessment_status === 'employee_complete' || assessment.manager_review_status === 'completed') && (
        <SelfAssessmentSection
          title="Manager Review & Feedback"
          subtitle="Manager assessment, development planning, and feedback"
          icon={User}
        >
          <ManagerOnlySection 
            assessment={assessment}
            formData={formData}
            isEditing={isEditing && canEditManagerReview}
            onChange={handleInputChange}
          />
        </SelfAssessmentSection>
      )}

      {/* Fixed Bottom Action Bar - Always visible when editing */}
      {canEdit && isEditing && (
        <div className="fixed bottom-0 left-64 right-0 bg-gray-800 border-t border-gray-700 p-4 shadow-2xl z-30">
          <div className="flex justify-end space-x-3 max-w-4xl mx-auto">
            <Button
              onClick={handleSave}
              disabled={saving}
              variant="secondary"
            >
              {saving ? (
                <><Clock size={16} className="mr-2" /> Saving...</>
              ) : (
                <><Save size={16} className="mr-2" /> Save Draft</>
              )}
            </Button>
            
            {/* Employee Submit Button */}
            {canEditSelfAssessment && !isManagerReview && (
              <Button
                onClick={handleSubmit}
                disabled={submitting}
                variant="primary"
              >
                {submitting ? (
                  <><Clock size={16} className="mr-2" /> Submitting...</>
                ) : (
                  <><Send size={16} className="mr-2" /> Submit for Review</>
                )}
              </Button>
            )}

            {/* Manager Submit Button */}
            {canEditManagerReview && (
              <Button
                onClick={handleManagerSubmit}
                disabled={submitting}
                variant="primary"
              >
                {submitting ? (
                  <><Clock size={16} className="mr-2" /> Submitting...</>
                ) : (
                  <><Send size={16} className="mr-2" /> Complete Manager Review</>
                )}
              </Button>
            )}
          </div>
        </div>
      )}

      {/* Add padding to prevent content from being hidden behind fixed bottom bar */}
      {canEdit && isEditing && <div className="h-20" />}
    </div>
  );
}

// Section Container Component
const SelfAssessmentSection = ({ title, subtitle, icon: Icon, children }) => (
  <div className="bg-gray-800 rounded-lg shadow-2xl">
    <div className="p-6 border-b border-gray-700">
      <div className="flex items-center mb-2">
        {Icon && <Icon size={24} className="text-cyan-400 mr-3" />}
        <h3 className="text-xl font-semibold text-white">{title}</h3>
      </div>
      <p className="text-gray-400 text-sm">{subtitle}</p>
    </div>
    <div className="p-6">
      {children}
    </div>
  </div>
);

// Core Values Section Component
const CoreValuesSection = ({ assessment, formData, isEditing, onChange, isManagerReview, canEditManagerReview }) => {
  const coreValues = [
    { key: 'passionate', label: 'Passionate', color: 'red' },
    { key: 'driven', label: 'Driven', color: 'blue' },
    { key: 'resilient', label: 'Resilient', color: 'green' },
    { key: 'responsive', label: 'Responsive', color: 'purple' }
  ];

  // If this is manager review mode OR manager has completed review, show two-column layout
  if (isManagerReview || (assessment.self_assessment_status === 'employee_complete' && (canEditManagerReview || assessment.manager_review_status === 'completed'))) {
    return (
      <div className="space-y-8">
        {coreValues.map(value => (
          <div key={value.key} className="border border-gray-600 rounded-lg p-4">
            <h4 className="text-lg font-medium text-white mb-4 border-b border-gray-600 pb-2">
              {value.label}
            </h4>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Employee Column */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-blue-300">
                  Employee Examples
                </label>
                <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[80px] border-l-4 border-blue-500">
                  {assessment[`value_${value.key}_examples`] || 'No examples provided by employee.'}
                </div>
              </div>
              
              {/* Manager Column */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-green-300">
                  Manager Feedback
                </label>
                {isEditing && canEditManagerReview ? (
                  <textarea
                    value={formData[`manager_${value.key}_feedback`] || ''}
                    onChange={(e) => onChange(`manager_${value.key}_feedback`, e.target.value)}
                    className="w-full p-3 bg-gray-700 border border-green-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-green-500 focus:border-transparent"
                    rows={3}
                    placeholder={`Provide feedback on employee's ${value.label.toLowerCase()} examples...`}
                  />
                ) : (
                  <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[80px] border-l-4 border-green-500">
                    {assessment[`manager_${value.key}_feedback`] || 'Manager feedback pending.'}
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  // Regular employee view (single column)
  return (
    <div className="space-y-6">
      {coreValues.map(value => (
        <div key={value.key} className="space-y-2">
          <label className="block text-sm font-medium text-gray-300">
            {value.label} - Provide specific examples
          </label>
          {isEditing ? (
            <textarea
              value={formData[`value_${value.key}_examples`] || ''}
              onChange={(e) => onChange(`value_${value.key}_examples`, e.target.value)}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              rows={3}
              placeholder={`Describe specific situations where you demonstrated being ${value.label.toLowerCase()}...`}
            />
          ) : (
            <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[80px]">
              {assessment[`value_${value.key}_examples`] || 'No examples provided yet.'}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

// GWC Section Component
const GWCSection = ({ assessment, formData, isEditing, onChange, isManagerReview, canEditManagerReview }) => {
  const gwcItems = [
    { key: 'gets_it', label: 'Gets It', description: 'Do you understand the role and its requirements?' },
    { key: 'wants_it', label: 'Wants It', description: 'Do you have the passion and desire for this role?' },
    { key: 'capacity', label: 'Has Capacity', description: 'Do you have the time and capability to fulfill this role?' }
  ];

  // If this is manager review mode OR manager has completed review, show two-column layout
  if (isManagerReview || (assessment.self_assessment_status === 'employee_complete' && (canEditManagerReview || assessment.manager_review_status === 'completed'))) {
    return (
      <div className="space-y-8">
        {gwcItems.map(item => (
          <div key={item.key} className="border border-gray-600 rounded-lg p-4">
            <div className="flex items-center space-x-3 mb-4 border-b border-gray-600 pb-2">
              <span className="text-2xl">
                {assessment[`gwc_${item.key}`] ? '' : ''}
              </span>
              <div>
                <h4 className="text-lg font-medium text-white">{item.label}</h4>
                <p className="text-sm text-gray-400">{item.description}</p>
              </div>
            </div>
            
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Employee Column */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-blue-300">
                  Employee Assessment
                </label>
                <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[60px] border-l-4 border-blue-500">
                  {/* Show employee feedback only if they answered No or provided explanation */}
                  {!assessment[`gwc_${item.key}`] || assessment[`gwc_${item.key}_feedback`] ? (
                    assessment[`gwc_${item.key}_feedback`] || 'No explanation provided by employee.'
                  ) : (
                    'Employee answered Yes - no explanation needed.'
                  )}
                </div>
              </div>
              
              {/* Manager Column */}
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-green-300 mb-2">
                    Manager Assessment
                  </label>
                  {isEditing && canEditManagerReview ? (
                    <div className="flex items-center space-x-3">
                      <input
                        type="checkbox"
                        checked={formData[`manager_gwc_${item.key}`] || false}
                        onChange={(e) => onChange(`manager_gwc_${item.key}`, e.target.checked)}
                        className="w-5 h-5 text-green-600 bg-gray-700 border-gray-600 rounded focus:ring-green-500"
                      />
                      <span className="text-white">Manager agrees: {item.label}</span>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-3">
                      <span className="text-2xl">
                        {assessment[`manager_gwc_${item.key}`] ? '' : ''}
                      </span>
                      <span className="text-white">Manager agrees: {item.label}</span>
                    </div>
                  )}
                </div>
                
                {/* Manager Feedback - only show if No or existing feedback */}
                {(isEditing && canEditManagerReview && !(formData[`manager_gwc_${item.key}`] || false)) || 
                 (!isEditing && (assessment[`manager_gwc_${item.key}_feedback`] || !assessment[`manager_gwc_${item.key}`])) ? (
                  <div>
                    <label className="block text-sm font-medium text-green-300 mb-2">
                      {!(formData[`manager_gwc_${item.key}`] || assessment[`manager_gwc_${item.key}`]) ? 'Manager explanation why not:' : 'Manager feedback:'}
                    </label>
                    {isEditing && canEditManagerReview ? (
                      <textarea
                        value={formData[`manager_gwc_${item.key}_feedback`] || ''}
                        onChange={(e) => onChange(`manager_gwc_${item.key}_feedback`, e.target.value)}
                        className="w-full p-3 bg-gray-700 border border-green-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-green-500 focus:border-transparent"
                        rows={2}
                        placeholder={`Explain why you don't agree the employee ${item.label.toLowerCase()}...`}
                      />
                    ) : (
                      <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[60px] border-l-4 border-green-500">
                        {assessment[`manager_gwc_${item.key}_feedback`] || 'Manager feedback pending.'}
                      </div>
                    )}
                  </div>
                ) : null}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  // Regular employee view (single column)
  return (
    <div className="space-y-6">
      {gwcItems.map(item => (
        <div key={item.key} className="space-y-3">
          <div className="flex items-center space-x-3">
            {isEditing ? (
              <input
                type="checkbox"
                checked={formData[`gwc_${item.key}`] || false}
                onChange={(e) => onChange(`gwc_${item.key}`, e.target.checked)}
                className="w-5 h-5 text-cyan-600 bg-gray-700 border-gray-600 rounded focus:ring-cyan-500"
              />
            ) : (
              <span className="text-2xl">
                {assessment[`gwc_${item.key}`] ? '' : ''}
              </span>
            )}
            <div>
              <h4 className="text-lg font-medium text-white">{item.label}</h4>
              <p className="text-sm text-gray-400">{item.description}</p>
            </div>
          </div>
          
          {/* Only show comment box if answer is No (unchecked) or if there's existing feedback */}
          {(isEditing && !(formData[`gwc_${item.key}`] || false)) || 
           (!isEditing && (assessment[`gwc_${item.key}_feedback`] || !assessment[`gwc_${item.key}`])) ? (
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                {!(formData[`gwc_${item.key}`] || assessment[`gwc_${item.key}`]) ? 'Please explain why not:' : 'Explanation:'}
              </label>
              {isEditing ? (
                <textarea
                  value={formData[`gwc_${item.key}_feedback`] || ''}
                  onChange={(e) => onChange(`gwc_${item.key}_feedback`, e.target.value)}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  rows={2}
                  placeholder={`Explain why you don't ${item.label.toLowerCase()}...`}
                />
              ) : (
                <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[60px]">
                  {assessment[`gwc_${item.key}_feedback`] || 'No explanation provided yet.'}
                </div>
              )}
            </div>
          ) : null}
        </div>
      ))}
    </div>
  );
};

// Strengths Section Component
const StrengthsSection = ({ assessment, formData, isEditing, onChange, isManagerReview, canEditManagerReview }) => {
  const sections = [
    { key: 'strengths', label: 'Key Strengths', question: 'What are your key strengths?' },
    { key: 'improvements', label: 'Areas for Improvement', question: 'What areas would you like to improve?' }
  ];

  // If this is manager review mode OR manager has completed review, show two-column layout
  if (isManagerReview || (assessment.self_assessment_status === 'employee_complete' && (canEditManagerReview || assessment.manager_review_status === 'completed'))) {
    return (
      <div className="space-y-8">
        {sections.map(section => (
          <div key={section.key} className="border border-gray-600 rounded-lg p-4">
            <h4 className="text-lg font-medium text-white mb-4 border-b border-gray-600 pb-2">
              {section.label}
            </h4>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Employee Column */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-blue-300">
                  Employee Response
                </label>
                <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[100px] border-l-4 border-blue-500">
                  {assessment[`employee_${section.key}`] || `No ${section.label.toLowerCase()} provided by employee.`}
                </div>
              </div>
              
              {/* Manager Column */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-green-300">
                  Manager Feedback
                </label>
                {isEditing && canEditManagerReview ? (
                  <textarea
                    value={formData[`manager_${section.key}_feedback`] || ''}
                    onChange={(e) => onChange(`manager_${section.key}_feedback`, e.target.value)}
                    className="w-full p-3 bg-gray-700 border border-green-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-green-500 focus:border-transparent"
                    rows={4}
                    placeholder={`Provide feedback on employee's ${section.label.toLowerCase()}...`}
                  />
                ) : (
                  <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[100px] border-l-4 border-green-500">
                    {assessment[`manager_${section.key}_feedback`] || 'Manager feedback pending.'}
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  // Regular employee view (single column)
  return (
    <div className="space-y-6">
      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-300">
          What are your key strengths?
        </label>
        {isEditing ? (
          <textarea
            value={formData.employee_strengths || ''}
            onChange={(e) => onChange('employee_strengths', e.target.value)}
            className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
            rows={4}
            placeholder="Describe your key strengths and what you excel at..."
          />
        ) : (
          <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[100px]">
            {assessment.employee_strengths || 'No strengths identified yet.'}
          </div>
        )}
      </div>

      <div className="space-y-2">
        <label className="block text-sm font-medium text-gray-300">
          What areas would you like to improve?
        </label>
        {isEditing ? (
          <textarea
            value={formData.employee_improvements || ''}
            onChange={(e) => onChange('employee_improvements', e.target.value)}
            className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
            rows={4}
            placeholder="Describe areas where you'd like to grow and improve..."
          />
        ) : (
          <div className="p-3 bg-gray-700 rounded-md text-gray-300 min-h-[100px]">
            {assessment.employee_improvements || 'No improvement areas identified yet.'}
          </div>
        )}
      </div>
    </div>
  );
};

// Rocks Section Component
const RocksSection = ({ assessment }) => (
  <div className="space-y-4">
    {assessment.rocks && assessment.rocks.length > 0 ? (
      assessment.rocks.map((rock, index) => (
        <div key={rock.id || index} className="bg-gray-700 p-4 rounded-lg">
          <div className="flex justify-between items-start">
            <div className="flex-1">
              <h4 className="text-lg font-medium text-white mb-2">{rock.description}</h4>
              <div className="flex items-center space-x-2">
                <span className={`px-2 py-1 text-xs rounded ${
                  rock.status === 'completed' ? 'bg-green-600 text-green-100' :
                  rock.status === 'in_progress' ? 'bg-yellow-600 text-yellow-100' :
                  'bg-gray-600 text-gray-100'
                }`}>
                  {rock.status?.replace('_', ' ') || 'Not Started'}
                </span>
              </div>
            </div>
          </div>
        </div>
      ))
    ) : (
      <div className="text-center py-8 text-gray-500">
        <Target size={48} className="mx-auto mb-4 text-gray-600" />
        <p>No rocks assigned for this review cycle.</p>
      </div>
    )}
  </div>
);

// Manager Only Section Component (without duplicate Core Values)
