              className="flex items-center space-x-2"
            >
              <ArrowLeft size={16} />
              <span>Back to Reviews</span>
            </Button>
          </div>
        </div>
      )}

      {error && (
        <ErrorMessage error={error} title="Save Error" />
      )}
    </div>
  );
}

// src/components/pages/Dashboard.js - Enhanced V2.5
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Calendar, CheckCircle, Clock, Award, TrendingUp, MessageSquare, Inbox, Star, ThumbsUp, X } from 'lucide-react';
import { useAssessments, useKudos } from '../../hooks';
import { useApp } from '../../contexts';
import { getStatusDisplay, filterActiveReviews, filterCompletedReviews, formatDate } from '../../utils';
import { LoadingSpinner, ErrorMessage, StatusBadge, Button, Card } from '../ui';
import { FeedbackService } from '../../services';

const Dashboard = () => {
    const navigate = useNavigate();
    const { openModal } = useApp();
    const { assessments, loading: assessmentsLoading, error: assessmentsError } = useAssessments();
    const { kudos, loading: kudosLoading, error: kudosError } = useKudos();
    const [feedbackReceived, setFeedbackReceived] = useState([]);
    const [feedbackLoading, setFeedbackLoading] = useState(true);
    
    const loading = assessmentsLoading || kudosLoading || feedbackLoading;
    const error = assessmentsError || kudosError;

    useEffect(() => {
        fetchRecentFeedback();
    }, []);

    const fetchRecentFeedback = async () => {
        try {
            setFeedbackLoading(true);
            const data = await FeedbackService.getMyFeedbackReceived(3); // Get 3 most recent
            setFeedbackReceived(data || []);
        } catch (err) {
            console.error('Error fetching feedback:', err);
        } finally {
            setFeedbackLoading(false);
        }
    };

    const handleDismissFeedback = (feedbackId) => {
        setFeedbackReceived(prev => prev.filter(f => f.feedback_id !== feedbackId));
    };

    const handleViewAssessment = (assessment) => {
        navigate(`/assessment/${assessment.assessment_id}`);
    };

    if (loading) {
        return (
            <div className="p-8">
                <LoadingSpinner size="lg" message="Loading your dashboard..." />
            </div>
        );
    }

    if (error) {
        return (
            <div className="p-8">
                <ErrorMessage error={error} title="Dashboard Error" />
            </div>
        );
    }

    const activeReviews = filterActiveReviews(assessments);
    const completedReviews = filterCompletedReviews(assessments);
    const recentKudos = kudos.slice(0, 5);

    return (
        <div className="p-8">
            {/* Header */}
            <header className="mb-8">
                <h1 className="text-4xl font-bold text-white">Dashboard</h1>
                <p className="text-gray-400 mt-2">
                    Welcome back! Here's your summary.
                </p>
            </header>

            {/* Quick Stats */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
                <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-gray-400 text-sm">Active Reviews</p>
                            <p className="text-2xl font-bold text-cyan-400">{activeReviews.length}</p>
                        </div>
                        <Calendar className="text-cyan-400" size={24} />
                    </div>
                </div>

                <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-gray-400 text-sm">Total Reviews</p>
                            <p className="text-2xl font-bold text-green-400">{assessments.length}</p>
                        </div>
                        <CheckCircle className="text-green-400" size={24} />
                    </div>
                </div>

                <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-gray-400 text-sm">Recent Kudos</p>
                            <p className="text-2xl font-bold text-purple-400">{recentKudos.length}</p>
                        </div>
                        <Award className="text-purple-400" size={24} />
                    </div>
                </div>

                <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
                    <div className="flex items-center justify-between">
                        <div>
                            <p className="text-gray-400 text-sm">Feedback Received</p>
                            <p className="text-2xl font-bold text-orange-400">{feedbackReceived.length}</p>
                        </div>
                        <Inbox className="text-orange-400" size={24} />
                    </div>
                </div>
            </div>

            {/* Main Content Grid */}
            <div className={`grid grid-cols-1 ${feedbackReceived.length > 0 ? 'lg:grid-cols-2' : ''} gap-8 mb-8`}>
                {/* Active Reviews */}
                <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
                    <h2 className="text-2xl font-semibold mb-6 text-cyan-400 flex items-center">
                        <Calendar className="mr-3" size={24} />
                        Active Reviews
                    </h2>
                    <div className="space-y-4">
                        {activeReviews.length > 0 ? (
                            activeReviews.map(assessment => {
                                const statusInfo = getStatusDisplay(assessment);
                                
                                return (
                                    <div key={assessment.assessment_id} className="bg-gray-700 p-4 rounded-lg">
                                        <div className="flex justify-between items-start mb-3">
                                            <div>
                                                <h3 className="text-lg font-semibold text-white">{assessment.cycle?.name || assessment.cycle_name || 'Unnamed Cycle'}</h3>
                                                <div className="flex items-center mt-1">
                                                    <StatusBadge status={assessment.self_assessment_status || assessment.status} />
                                                </div>
                                            </div>
                                            <Button 
                                                onClick={() => handleViewAssessment(assessment)}
                                                variant="primary"
                                                size="sm"
                                            >
                                                {statusInfo.actionLabel}
                                            </Button>
                                        </div>
                                        
                                        {/* Enhanced Progress indicator */}
{assessment.self_assessment_status === 'in_progress' && (
    <div className="mt-3 p-2 bg-yellow-900 rounded text-yellow-200 text-sm flex items-center">
        <Clock size={14} className="mr-2" />
        <div>
            <div className="font-medium">Continue your self-assessment</div>
            <div className="text-xs text-yellow-300">Complete all sections to submit for review</div>
        </div>
    </div>
)}

                                      {assessment.self_assessment_status === 'employee_complete' && (
                                          <div className="mt-3 p-2 bg-blue-900 rounded text-blue-200 text-sm flex items-center">
                                              <CheckCircle size={14} className="mr-2" />
                                              <div>
                                                  <div className="font-medium">Submitted for manager review</div>
                                                  <div className="text-xs text-blue-300">Your manager will review and provide feedback</div>
                                              </div>
                                          </div>
                                      )}

                                      {assessment.self_assessment_status === 'manager_complete' && (
                                          <div className="mt-3 p-2 bg-purple-900 rounded text-purple-200 text-sm flex items-center">
                                              <Award size={14} className="mr-2" />
                                              <div>
                                                  <div className="font-medium">Manager review complete</div>
                                                  <div className="text-xs text-purple-300">Review the feedback and development plan</div>
                                              </div>
                                          </div>
                                      )}
                                    </div>
                                );
                            })
                        ) : (
                            <div className="text-center py-8 text-gray-500">
                                <Calendar size={48} className="mx-auto mb-4 text-gray-600" />
                                <p>No active reviews at this time.</p>
                            </div>
                        )}
                    </div>
                </div>


                {/* Recent Feedback - Only show if feedback exists */}
                {feedbackReceived.length > 0 && (
                    <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
                        <div className="flex justify-between items-center mb-6">
                            <h2 className="text-2xl font-semibold text-orange-400 flex items-center">
                                <Inbox className="mr-3" size={24} />
                                Recent Feedback
                            </h2>
                            <div className="flex space-x-2">
                                <button
                                    onClick={fetchRecentFeedback}
                                    className="bg-gray-600 hover:bg-gray-500 text-white font-bold py-2 px-3 rounded-lg transition text-sm"
                                    title="Refresh feedback"
                                >
                                    
                                </button>
                                <button
                                    onClick={() => navigate('/feedback')}
                                    className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 px-4 rounded-lg transition text-sm"
                                >
                                    View All
                                </button>
                            </div>
                        </div>
                        
                        <div className="space-y-4">
                            {feedbackReceived.map(feedback => (
                                <FeedbackCard 
                                    key={feedback.feedback_id} 
                                    feedback={feedback} 
                                    onDismiss={handleDismissFeedback}
                                />
                            ))}
                        </div>
                    </div>
                )}
            </div>

            {/* Quick Actions */}
            <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
                <h2 className="text-2xl font-semibold mb-6 text-white flex items-center">
                    <TrendingUp className="mr-3" size={24} />
                    Quick Actions
                </h2>
                
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <button
                        onClick={() => navigate('/reviews')}
                        className="bg-blue-700 hover:bg-blue-600 text-left p-4 rounded-lg transition group"
                    >
                        <div className="flex items-center">
                            <Calendar size={20} className="text-blue-400 mr-3" />
                            <div>
                                <p className="text-white font-medium">View All Reviews</p>
                                <p className="text-blue-300 text-sm">See your review history</p>
                            </div>
                        </div>
                    </button>

                    {openModal && (
                        <button
                            onClick={() => navigate('/feedback')}
                            className="bg-orange-700 hover:bg-orange-600 text-left p-4 rounded-lg transition group"
                        >
                            <div className="flex items-center">
                                <MessageSquare size={20} className="text-orange-400 mr-3" />
                                <div>
                                    <p className="text-white font-medium">Give Feedback</p>
                                    <p className="text-orange-300 text-sm">Share feedback with colleagues</p>
                                </div>
                            </div>
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

// Feedback type configuration
const feedbackTypeConfig = {
    positive: { 
        icon: Star, 
        color: 'text-yellow-400', 
        bgColor: 'bg-yellow-900', 
        label: 'Recognition' 
    },
    constructive: { 
        icon: MessageSquare, 
        color: 'text-blue-400', 
        bgColor: 'bg-blue-900', 
        label: 'Growth' 
    },
    appreciation: { 
        icon: ThumbsUp, 
        color: 'text-green-400', 
        bgColor: 'bg-green-900', 
        label: 'Thanks' 
    }
};

// Feedback Card Component for Dashboard
const FeedbackCard = ({ feedback, onDismiss }) => {
    const typeConfig = feedbackTypeConfig[feedback.feedback_type] || feedbackTypeConfig.positive;
    const Icon = typeConfig.icon;

    return (
        <div className="bg-gray-700 p-4 rounded-lg group hover:bg-gray-600 transition-colors">
            <div className="flex items-start space-x-3">
                <div className={`p-2 rounded-lg ${typeConfig.bgColor} bg-opacity-50 flex-shrink-0`}>
                    <Icon size={16} className={typeConfig.color} />
                </div>
                <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between mb-1">
                        <div className="flex items-center space-x-2">
                            <span className="text-white font-medium">You</span>
                            <span className="text-gray-400 text-sm">received</span>
                            <span className={`px-2 py-1 text-xs rounded ${typeConfig.bgColor} ${typeConfig.color}`}>
                                {typeConfig.label}
                            </span>
                        </div>
                        <button
                            onClick={() => onDismiss(feedback.feedback_id)}
                            className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-white transition-all duration-200"
                            title="Dismiss"
                        >
                            <X size={14} />
                        </button>
                    </div>
                    <p className="text-gray-300 text-sm mb-2 line-clamp-2">{feedback.message}</p>
                    <div className="flex justify-between items-center">
                        <span className="text-gray-500 text-xs">
                            from {feedback.is_anonymous ? 'Anonymous' : feedback.giver_name}
                        </span>
                        <span className="text-gray-500 text-xs">
                            {formatDate(feedback.created_at)}
                        </span>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;

// src/components/pages/EmployeeDashboard.tsx - Personal employee dashboard
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  User,
  CheckCircle, 
  Clock, 
  Target, 
  Users,
  Calendar,
  TrendingUp,
  AlertCircle,
  BookOpen,
  Award,
  Activity,
  ArrowRight,
  FileText,
  Inbox,
  MessageSquare,
  Star,
  ThumbsUp,
  X,
  BarChart3
} from 'lucide-react';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button } from '../ui';
import { OrgHealthWidget } from '../ui/TeamHealthPulse';
import { MetricCard } from '../analytics/ChartComponents';
import { formatDate, calculateAssessmentScore, getAssessmentTrends } from '../../utils';
import RoleBasedAnalyticsService, { EmployeeDashboardData } from '../../services/RoleBasedAnalyticsService';
import { FeedbackService } from '../../services';
import { useAssessments } from '../../hooks';

export default function EmployeeDashboard() {
  const navigate = useNavigate();
  const { userName, user } = useApp();
  const { assessments } = useAssessments();
  const [dashboardData, setDashboardData] = useState<EmployeeDashboardData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [feedbackReceived, setFeedbackReceived] = useState<any[]>([]);
  const [dismissedFeedback, setDismissedFeedback] = useState<number[]>([]);
  const [assessmentTrends, setAssessmentTrends] = useState<any[]>([]);
  const [feedbackLoading, setFeedbackLoading] = useState(true);

  useEffect(() => {
    if (user?.id) {
      fetchEmployeeDashboard();
      fetchRecentFeedback();
    }
  }, [user?.id]);

  // Calculate assessment trends when assessments change
  useEffect(() => {
    if (assessments && assessments.length > 0) {
      const trends = getAssessmentTrends(assessments);
      setAssessmentTrends(trends);
    }
  }, [assessments]);

  const fetchEmployeeDashboard = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await RoleBasedAnalyticsService.getEmployeeDashboard(user!.id);
      setDashboardData(data);
      
      // Assessment trends will be calculated from useAssessments hook
    } catch (err: any) {
      console.error('Error fetching employee dashboard:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchRecentFeedback = async () => {
    try {
      setFeedbackLoading(true);
      console.log(' EmployeeDashboard: Starting to fetch feedback...');
      const data = await FeedbackService.getMyFeedbackReceived(3);
      console.log(' EmployeeDashboard: Feedback received:', data);
      console.log(' EmployeeDashboard: Feedback count:', data?.length || 0);
      if (data && data.length > 0) {
        console.log(' EmployeeDashboard: First feedback item:', data[0]);
      }
      setFeedbackReceived(data || []);
    } catch (err: any) {
      console.error(' EmployeeDashboard: Error fetching feedback:', err);
    } finally {
      setFeedbackLoading(false);
    }
  };

  const handleDismissFeedback = (feedbackId: number) => {
    setDismissedFeedback(prev => [...prev, feedbackId]);
    // Also remove from current display
    setFeedbackReceived(prev => prev.filter(f => f.feedback_id !== feedbackId));
  };

  const getVisibleFeedback = () => {
    return feedbackReceived.filter(f => !dismissedFeedback.includes(f.feedback_id));
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const getTaskPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'border-l-red-500 bg-red-900/10';
      case 'medium': return 'border-l-yellow-500 bg-yellow-900/10';
      default: return 'border-l-blue-500 bg-blue-900/10';
    }
  };

  if (loading) {
    return (
      <div className="p-8">
        <LoadingSpinner message="Loading your dashboard..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <ErrorMessage
          error={error}
          title="Dashboard Error"
          onRetry={fetchEmployeeDashboard}
        />
      </div>
    );
  }

  if (!dashboardData) {
    return (
      <div className="p-8">
        <div className="text-center">
          <User className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-300">No dashboard data</h3>
          <p className="mt-1 text-sm text-gray-400">Unable to load your personal dashboard.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6 max-w-6xl mx-auto">
      {/* Personal Header */}
      <div className="bg-gradient-to-r from-cyan-900/30 to-blue-900/30 rounded-lg p-6 border border-cyan-500/20">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-white">
              {getGreeting()}, {userName}! 
            </h1>
            <p className="text-cyan-400 mt-1">
              Welcome to your personal dashboard
            </p>
            <p className="text-gray-400 text-sm mt-2">
              Department: {dashboardData.departmentInfo.departmentName}  
              Team Size: {dashboardData.departmentInfo.teamSize} members
            </p>
          </div>
          <div className="text-right">
            <div className="text-cyan-400 text-2xl font-bold">
              {dashboardData.departmentInfo.teamSize}
            </div>
            <div className="text-gray-400 text-sm">Team Members</div>
          </div>
        </div>
      </div>

      {/* Quick Stats - Compact */}
      <div className={`grid grid-cols-1 gap-4 ${getVisibleFeedback().length > 0 ? 'md:grid-cols-3' : 'md:grid-cols-2'}`}>
        <MetricCard
          title="Active Reviews"
          value={dashboardData.personalStats.pendingAssessments}
          icon={Clock}
          color={dashboardData.personalStats.pendingAssessments > 0 ? 'yellow' : 'green'}
        />
        {/* Only show Recent Feedback if there's unseen feedback */}
        {getVisibleFeedback().length > 0 && (
          <MetricCard
            title="New Feedback"
            value={getVisibleFeedback().length}
            icon={Inbox}
            color="yellow"
          />
        )}
        <MetricCard
          title="Completed Reviews"
          value={dashboardData.personalStats.completedAssessments}
          icon={CheckCircle}
          color="green"
        />
      </div>


      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        
        {/* My Tasks & Priorities - Enhanced */}
        <div className="lg:col-span-2 bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <Target className="mr-2" size={20} />
              My Tasks & Priorities
            </h2>
            <Button 
              variant="ghost" 
              size="sm"
              onClick={() => navigate('/reviews')}
            >
              View All
            </Button>
          </div>
          
          <div className="space-y-4">
            {/*  TRUMPETED Feedback Wall Notifications */}
            {getVisibleFeedback().map(feedback => (
              <div 
                key={`feedback-${feedback.feedback_id}`}
                className="p-4 rounded-lg border-l-4 border-l-orange-500 bg-gradient-to-r from-orange-900/20 to-orange-800/10 cursor-pointer hover:bg-orange-800/20 transition-all duration-200 shadow-md"
                onClick={() => navigate('/feedback')}
              >
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <div className="p-1.5 rounded-full bg-orange-500/20 animate-pulse">
                        <Star size={14} className="text-orange-400" />
                      </div>
                      <span className="text-orange-400 font-medium text-sm"> New Feedback Received!</span>
                      <span className="px-2 py-1 rounded-full text-xs bg-orange-500/20 text-orange-400 font-medium">
                        HIGH PRIORITY
                      </span>
                    </div>
                    <h3 className="text-white font-medium text-sm mb-1">
                      Feedback from {feedback.is_anonymous ? 'Anonymous' : feedback.giver_name}
                    </h3>
                    <p className="text-gray-300 text-sm line-clamp-2 mb-2">{feedback.message}</p>
                    <p className="text-orange-400 text-xs">
                      Click to view on Feedback Wall  {formatDate(feedback.created_at)}
                    </p>
                  </div>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDismissFeedback(feedback.feedback_id);
                    }}
                    className="ml-4 text-gray-400 hover:text-white transition-colors p-1"
                    title="Dismiss notification"
                  >
                    <X size={16} />
                  </button>
                </div>
              </div>
            ))}

            {/* Regular Tasks */}
            {dashboardData.myTasks.length === 0 && getVisibleFeedback().length === 0 ? (
              <div className="text-center py-8">
                <CheckCircle className="mx-auto h-12 w-12 text-green-400" />
                <h3 className="mt-2 text-sm font-medium text-gray-300">All caught up!</h3>
                <p className="mt-1 text-sm text-gray-400">You have no pending tasks or feedback right now.</p>
              </div>
            ) : (
              <>
                {dashboardData.myTasks.map((task) => (
                  <div 
                    key={task.id} 
                    className={`p-4 rounded-lg border-l-4 ${getTaskPriorityColor(task.priority)} cursor-pointer hover:bg-gray-700/30 transition-colors`}
                    onClick={() => {
                      if (task.type === 'assessment') {
                        navigate(`/assessment/${task.id}`);
                      }
                    }}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="text-white font-medium text-sm">{task.title}</h3>
                        <p className="text-gray-400 text-xs mt-1">
                          Due: {formatDate(task.dueDate)}
                        </p>
                        <div className="flex items-center mt-2 space-x-2">
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                            task.priority === 'high' ? 'bg-red-500/20 text-red-400' :
                            task.priority === 'medium' ? 'bg-yellow-500/20 text-yellow-400' :
                            'bg-blue-500/20 text-blue-400'
                          }`}>
                            {task.priority.toUpperCase()}
                          </span>
                          <span className="text-gray-500 text-xs capitalize">
                            {task.type.replace('_', ' ')}
                          </span>
                        </div>
                      </div>
                      <ArrowRight size={16} className="text-gray-400" />
                    </div>
                  </div>
                ))}
              </>
            )}
          </div>
        </div>

        {/* Right Column - Assessment Trends Only */}
        <div className="space-y-6">
          {/* Assessment Score Trends */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold text-white flex items-center">
                <TrendingUp className="mr-2" size={18} />
                My Assessment Trends
              </h2>
              <button
                onClick={() => navigate('/reviews')}
                className="text-cyan-400 hover:text-cyan-300 text-sm transition-colors"
              >
                View History
              </button>
            </div>
            
            {assessmentTrends.length > 0 ? (
              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-cyan-400">
                      {assessmentTrends[assessmentTrends.length - 1]?.selfScore || 'N/A'}
                    </div>
                    <div className="text-xs text-gray-400">Latest Self Score</div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-purple-400">
                      {assessmentTrends[assessmentTrends.length - 1]?.managerScore || 'N/A'}
                    </div>
                    <div className="text-xs text-gray-400">Latest Manager Score</div>
                  </div>
                </div>
                <div className="text-center py-4">
                  <div className="text-sm text-gray-400">
                     Based on {assessmentTrends.length} completed assessment{assessmentTrends.length !== 1 ? 's' : ''}
                  </div>
                </div>
              </div>
            ) : (
              <div className="text-center py-6">
                <BarChart3 className="mx-auto h-8 w-8 text-gray-500" />
                <p className="text-gray-400 text-sm mt-2">Complete assessments to see your trends</p>
              </div>
            )}
          </div>
          
          {/* Company Satisfaction Widget */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h2 className="text-lg font-semibold text-white mb-4 flex items-center">
              <Award className="mr-2" size={18} />
              Satisfaction Scores
            </h2>
            <div className="grid grid-cols-2 gap-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-green-400">
                  {typeof dashboardData.departmentInfo.avgSatisfaction === 'number' 
                    ? dashboardData.departmentInfo.avgSatisfaction.toFixed(1) 
                    : 'N/A'}
                </div>
                <div className="text-xs text-gray-400">Department</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-400">
                  {typeof dashboardData.departmentInfo.companySatisfaction === 'number' 
                    ? dashboardData.departmentInfo.companySatisfaction.toFixed(1) 
                    : 'N/A'}
                </div>
                <div className="text-xs text-gray-400">Company</div>
              </div>
            </div>
            <div className="text-center mt-2">
