import { FeedbackService } from '../../services';

export default function ManagerDashboard() {
  const navigate = useNavigate();
  const { userName, user } = useApp();
  const [dashboardData, setDashboardData] = useState<ManagerDashboardData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [managerId, setManagerId] = useState<string | null>(null);
  const [feedbackReceived, setFeedbackReceived] = useState<any[]>([]);
  const [feedbackLoading, setFeedbackLoading] = useState(true);

  useEffect(() => {
    if (user?.id) {
      fetchManagerDashboard();
      fetchRecentFeedback();
    }
  }, [user?.id]);

  const fetchManagerDashboard = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Get manager employee ID for alerts
      const { supabase } = await import('../../services/supabaseClient');
      const { data: manager } = await supabase
        .from('employees')
        .select('id')
        .eq('user_id', user!.id)
        .single();
      
      if (manager) {
        setManagerId(manager.id);
      }
      
      const data = await RoleBasedAnalyticsService.getManagerDashboard(user!.id);
      setDashboardData(data);
    } catch (err: any) {
      console.error('Error fetching manager dashboard:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchRecentFeedback = async () => {
    try {
      setFeedbackLoading(true);
      console.log(' ManagerDashboard: Starting to fetch feedback...');
      const data = await FeedbackService.getMyFeedbackReceived(3);
      console.log(' ManagerDashboard: Feedback received:', data);
      setFeedbackReceived(data || []);
    } catch (err: any) {
      console.error(' ManagerDashboard: Error fetching feedback:', err);
    } finally {
      setFeedbackLoading(false);
    }
  };

  const handleDismissFeedback = (feedbackId: number) => {
    setFeedbackReceived(prev => prev.filter(f => f.feedback_id !== feedbackId));
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'text-green-400 bg-green-900/20';
      case 'on_track': return 'text-blue-400 bg-blue-900/20';
      case 'behind': return 'text-red-400 bg-red-900/20';
      default: return 'text-gray-400 bg-gray-700/20';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'border-l-red-500 bg-red-900/10';
      case 'medium': return 'border-l-yellow-500 bg-yellow-900/10';
      default: return 'border-l-blue-500 bg-blue-900/10';
    }
  };

  if (loading) {
    return (
      <div className="p-8">
        <LoadingSpinner message="Loading team dashboard..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <ErrorMessage
          error={error}
          title="Dashboard Error"
          onRetry={fetchManagerDashboard}
        />
      </div>
    );
  }

  if (!dashboardData) {
    return (
      <div className="p-8">
        <div className="text-center">
          <Users className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-300">No dashboard data</h3>
          <p className="mt-1 text-sm text-gray-400">Unable to load your team dashboard.</p>
        </div>
      </div>
    );
  }

  // Generate chart data for team performance
  const teamChartData = [
    {
      name: 'My Team',
      completed: dashboardData.teamStats.completedReviews,
      pending: dashboardData.teamStats.pendingReviews,
      overdue: dashboardData.teamStats.overdueItems
    }
  ];

  return (
    <div className="p-6 space-y-6 max-w-7xl mx-auto">
      {/* Manager Header */}
      <div className="bg-gradient-to-r from-purple-900/30 to-blue-900/30 rounded-lg p-6 border border-purple-500/20">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-white">
              {getGreeting()}, {userName}! 
            </h1>
            <p className="text-purple-400 mt-1">
              Managing {dashboardData.teamStats.teamSize} team members
            </p>
            <p className="text-gray-400 text-sm mt-2">
              Department: {dashboardData.departmentMetrics.departmentName}  
              Rank: #{dashboardData.peerComparison.myTeamRank} among {dashboardData.departmentMetrics.totalManagers} managers
            </p>
          </div>
          <div className="text-right">
            <div className="text-purple-400 text-2xl font-bold">
              {dashboardData.peerComparison.myTeamCompletion}%
            </div>
            <div className="text-gray-400 text-sm">Team Completion</div>
            <div className="text-xs text-gray-500 mt-1">
              vs {dashboardData.peerComparison.avgManagerCompletion}% avg
            </div>
          </div>
        </div>
      </div>

      {/* Team KPIs */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-6 gap-4">
        <MetricCard
          title="Team Size"
          value={dashboardData.teamStats.teamSize}
          icon={Users}
          color="blue"
        />
        <MetricCard
          title="Pending Reviews"
          value={dashboardData.teamStats.pendingReviews}
          icon={Clock}
          color={dashboardData.teamStats.pendingReviews > 0 ? 'yellow' : 'green'}
        />
        <MetricCard
          title="Completed"
          value={dashboardData.teamStats.completedReviews}
          icon={CheckCircle}
          color="green"
        />
        <MetricCard
          title="Team Satisfaction"
          value={dashboardData.teamStats.teamSatisfactionAvg.toFixed(1)}
          icon={Award}
          color="purple"
        />
        <MetricCard
          title="Company Satisfaction"
          value={typeof dashboardData.departmentMetrics.companySatisfaction === 'number' 
            ? dashboardData.departmentMetrics.companySatisfaction.toFixed(1) 
            : 'N/A'}
          icon={TrendingUp}
          color="blue"
        />
        <MetricCard
          title="Overdue Items"
          value={dashboardData.teamStats.overdueItems}
          icon={AlertTriangle}
          color={dashboardData.teamStats.overdueItems > 0 ? 'red' : 'green'}
        />
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        
        {/* Pending Actions - Priority Section */}
        <div className="lg:col-span-2 bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <FileText className="mr-2" size={20} />
              Pending Actions ({dashboardData.pendingActions.length})
            </h2>
            <Button 
              variant="ghost" 
              size="sm"
              onClick={() => navigate('/team')}
            >
              View Team
            </Button>
          </div>
          
          {dashboardData.pendingActions.length === 0 ? (
            <div className="text-center py-8">
              <CheckCircle className="mx-auto h-12 w-12 text-green-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-300">All caught up!</h3>
              <p className="mt-1 text-sm text-gray-400">No pending team actions right now.</p>
            </div>
          ) : (
            <div className="space-y-3">
              {dashboardData.pendingActions.slice(0, 6).map((action) => (
                <div 
                  key={action.id} 
                  className={`p-4 rounded-lg border-l-4 ${getPriorityColor(action.priority)} cursor-pointer hover:bg-gray-700/30 transition-colors`}
                  onClick={() => {
                    if (action.type === 'review') {
                      navigate(`/review?assessmentId=${action.id}&employeeName=${action.employeeName}`);
                    }
                  }}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-white font-medium text-sm">{action.action}</h3>
                      <p className="text-gray-400 text-sm">
                        <span className="font-medium">{action.employeeName}</span>
                      </p>
                      <p className="text-gray-500 text-xs mt-1">
                        Due: {formatDate(action.dueDate)}
                      </p>
                      <div className="flex items-center mt-2 space-x-2">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          action.priority === 'high' ? 'bg-red-500/20 text-red-400' :
                          action.priority === 'medium' ? 'bg-yellow-500/20 text-yellow-400' :
                          'bg-blue-500/20 text-blue-400'
                        }`}>
                          {action.priority.toUpperCase()}
                        </span>
                        <span className="text-gray-500 text-xs capitalize">
                          {action.type}
                        </span>
                      </div>
                    </div>
                    <ArrowRight size={16} className="text-gray-400" />
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Team Performance & Peer Comparison */}
        <div className="space-y-6">
          
          {/* Peer Comparison Card */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h2 className="text-lg font-semibold text-white mb-4 flex items-center">
              <Crown className="mr-2 text-yellow-500" size={18} />
              Manager Rankings
            </h2>
            <div className="space-y-4">
              <div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-400 text-sm">My Team Rank</span>
                  <span className="text-white font-bold text-lg">
                    #{dashboardData.peerComparison.myTeamRank}
                  </span>
                </div>
              </div>
              
              <div>
                <div className="flex items-center justify-between mb-2">
                  <span className="text-gray-400 text-sm">My Completion</span>
                  <span className="text-green-400 font-medium">
                    {dashboardData.peerComparison.myTeamCompletion}%
                  </span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-400 text-sm">Avg Manager</span>
                  <span className="text-gray-300 font-medium">
                    {dashboardData.peerComparison.avgManagerCompletion}%
                  </span>
                </div>
                <div className="mt-2 text-xs text-gray-500">
                  Top performer: {dashboardData.peerComparison.topPerformingManager}
                </div>
              </div>
              
              <div className="pt-2 border-t border-gray-700">
                <div className="text-center">
                  {dashboardData.peerComparison.myTeamCompletion > dashboardData.peerComparison.avgManagerCompletion ? (
                    <div className="text-green-400 text-sm font-medium">
                       Above Average Performance!
                    </div>
                  ) : (
                    <div className="text-yellow-400 text-sm font-medium">
                       Room for Improvement
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
          
          {/* Department Overview */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h2 className="text-lg font-semibold text-white mb-4 flex items-center">
              <BarChart3 className="mr-2" size={18} />
              Department Metrics
            </h2>
            <div className="space-y-4">
              <div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-400 text-sm">Total Managers</span>
                  <span className="text-white font-medium">
                    {dashboardData.departmentMetrics.totalManagers}
                  </span>
                </div>
              </div>
              
              <div>
                <div className="flex items-center justify-between mb-2">
                  <span className="text-gray-400 text-sm">Dept Completion</span>
                  <span className="text-white font-medium">
                    {dashboardData.departmentMetrics.departmentCompletion}%
                  </span>
                </div>
                <div className="w-full bg-gray-700 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-purple-500 to-blue-500 h-2 rounded-full transition-all duration-500"
                    style={{ width: `${dashboardData.departmentMetrics.departmentCompletion}%` }}
                  />
                </div>
              </div>
              
              <div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-400 text-sm">Dept Satisfaction</span>
                  <div className="flex items-center space-x-1">
                    <span className="text-yellow-400 font-medium">
                      {typeof dashboardData.departmentMetrics.departmentSatisfaction === 'number' 
                        ? dashboardData.departmentMetrics.departmentSatisfaction.toFixed(1) 
                        : 'N/A'}
                    </span>
                    <span className="text-gray-500 text-xs">/5.0</span>
                  </div>
                </div>
              </div>
            </div>
            
            {/* Org Health Widget - Below Department Metrics */}
            <div className="mt-6">
              <OrgHealthWidget />
            </div>
          </div>
        </div>
      </div>

      {/* Team Performance Overview */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        
        {/* Team Members Performance */}
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <Users className="mr-2" size={20} />
              Team Performance
            </h2>
            <Button 
              variant="ghost" 
              size="sm"
              onClick={() => navigate('/team')}
            >
              <Eye size={14} className="mr-1" />
              Details
            </Button>
          </div>
          
          <div className="space-y-3">
            {dashboardData.teamPerformance.map((member, index) => (
              <div key={index} className="flex items-center justify-between p-3 rounded-lg bg-gray-700/30">
                <div>
                  <h3 className="text-white font-medium text-sm">{member.employeeName}</h3>
                  <p className="text-gray-400 text-xs">
                    Last active: {formatDate(member.lastActivity)}
                  </p>
                </div>
                <div className="flex items-center space-x-3">
                  <div className="text-right">
                    <div className="text-white font-medium text-sm">
                      {member.completionRate}%
                    </div>
                    <div className="text-xs text-gray-500">completion</div>
                  </div>
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(member.status)}`}>
                    {member.status.replace('_', ' ')}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Team Statistics Chart */}
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <h2 className="text-xl font-semibold text-white mb-6 flex items-center">
            <BarChart3 className="mr-2" size={20} />
            Team Overview
          </h2>
          <PerformanceBarChart data={teamChartData} height={250} />
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-gradient-to-r from-gray-800 to-gray-700 rounded-lg p-6 border border-gray-600">
        <h2 className="text-lg font-semibold text-white mb-4">Manager Quick Actions</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <Button 
            className="flex flex-col items-center space-y-2 h-16"
            onClick={() => navigate('/team')}
          >
            <Users size={20} />
            <span className="text-sm">My Team</span>
          </Button>
          <Button 
            className="flex flex-col items-center space-y-2 h-16" 
            variant="secondary"
            onClick={() => navigate('/playbook')}
          >
            <FileText size={20} />
            <span className="text-sm">Playbook</span>
          </Button>
          <Button 
            className="flex flex-col items-center space-y-2 h-16" 
            variant="secondary"
            onClick={() => navigate('/reviews')}
          >
            <Calendar size={20} />
            <span className="text-sm">Reviews</span>
          </Button>
          <Button 
            className="flex flex-col items-center space-y-2 h-16" 
            variant="secondary"
            onClick={() => navigate('/help')}
          >
            <FileText size={20} />
            <span className="text-sm">Manager Guide</span>
          </Button>
        </div>
      </div>

      {/* Team Health Alerts Section */}
      {managerId && (
        <TeamHealthAlerts role="manager" managerId={managerId} />
      )}

      {/* Team Question Performance */}
      {managerId && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <QuestionPerformanceCard 
            managerIdFilter={managerId}
            showOnlyTop={true}
            title="Top Team Question"
            className=""
          />
          <QuestionPerformanceCard 
            managerIdFilter={managerId}
            showOnlyTop={false}
            title="Team Area for Improvement"
            className=""
          />
        </div>
      )}

      {/* Recent Feedback Section */}
      {feedbackReceived.length > 0 && (
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold text-white flex items-center">
              <Inbox className="mr-2" size={20} />
              Recent Feedback
            </h2>
            <div className="flex space-x-2">
              <button
                onClick={fetchRecentFeedback}
                className="text-gray-400 hover:text-white transition-colors text-sm"
                title="Refresh feedback"
              >
                
              </button>
              <button
                onClick={() => navigate('/feedback')}
                className="text-orange-400 hover:text-orange-300 text-sm transition-colors"
              >
                View All
              </button>
            </div>
          </div>
          
          <div className="space-y-3">
            {feedbackReceived.map(feedback => (
              <ManagerFeedbackCard 
                key={feedback.feedback_id} 
                feedback={feedback} 
                onDismiss={handleDismissFeedback}
              />
            ))}
          </div>
        </div>
      )}

      {/* Manager Guide Section */}
      <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-white flex items-center">
            <Crown className="mr-2" size={20} />
            Manager Quick Guide
          </h2>
          <Button 
            variant="ghost" 
            size="sm"
            onClick={() => navigate('/help')}
          >
            View Full Guide
          </Button>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* Team Management */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <Users className="mr-2 text-blue-400" size={16} />
              Team Management
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> Monitor team performance metrics</li>
              <li> Track review cycle progress</li>
              <li> Address overdue assessments promptly</li>
              <li> Schedule regular one-on-ones</li>
            </ul>
          </div>

          {/* Review Process */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <FileText className="mr-2 text-green-400" size={16} />
              Review Process
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> Provide specific, actionable feedback</li>
              <li> Use examples from recent work</li>
              <li> Focus on both strengths and growth areas</li>
              <li> Complete reviews within deadline</li>
            </ul>
          </div>

          {/* Development Planning */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <Target className="mr-2 text-purple-400" size={16} />
              Development Planning
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> Identify growth opportunities for each team member</li>
              <li> Create development plans with SMART goals</li>
              <li> Provide resources and learning opportunities</li>
              <li> Track progress and adjust as needed</li>
            </ul>
          </div>

          {/* Best Practices */}
          <div className="bg-gray-700/30 rounded-lg p-4 border border-gray-600">
            <h3 className="text-white font-medium mb-3 flex items-center">
              <Award className="mr-2 text-yellow-400" size={16} />
              Best Practices
            </h3>
            <ul className="space-y-2 text-sm text-gray-300">
              <li> Hold effective review meetings</li>
              <li> Use the manager playbook for guidance</li>
              <li> Encourage peer feedback and collaboration</li>
              <li> Document important conversations</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

// Feedback type configuration
const feedbackTypeConfig = {
  positive: { 
    icon: Star, 
    color: 'text-yellow-400', 
    bgColor: 'bg-yellow-900', 
    label: 'Recognition' 
  },
  constructive: { 
    icon: MessageSquare, 
    color: 'text-blue-400', 
    bgColor: 'bg-blue-900', 
    label: 'Growth' 
  },
  appreciation: { 
    icon: ThumbsUp, 
    color: 'text-green-400', 
    bgColor: 'bg-green-900', 
    label: 'Thanks' 
  }
};

// Feedback Card Component for ManagerDashboard
const ManagerFeedbackCard = ({ feedback, onDismiss }: { feedback: any, onDismiss: (id: number) => void }) => {
  const typeConfig = feedbackTypeConfig[feedback.feedback_type as keyof typeof feedbackTypeConfig] || feedbackTypeConfig.positive;
  const Icon = typeConfig.icon;

  return (
    <div className="bg-gray-700/50 p-4 rounded-lg group hover:bg-gray-700 transition-colors">
      <div className="flex items-start space-x-3">
        <div className={`p-2 rounded-lg ${typeConfig.bgColor} bg-opacity-50 flex-shrink-0`}>
          <Icon size={16} className={typeConfig.color} />
        </div>
        <div className="flex-1 min-w-0">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center space-x-2">
              <span className="text-white font-medium text-sm">You</span>
              <span className="text-gray-400 text-xs">received</span>
              <span className={`px-2 py-1 text-xs rounded ${typeConfig.bgColor} ${typeConfig.color}`}>
                {typeConfig.label}
              </span>
            </div>
            <button
              onClick={() => onDismiss(feedback.feedback_id)}
              className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-white transition-all duration-200"
              title="Dismiss"
            >
              <X size={14} />
            </button>
          </div>
          <p className="text-gray-300 text-sm mb-2 line-clamp-2">{feedback.message}</p>
          <div className="flex justify-between items-center">
            <span className="text-gray-500 text-xs">
              from {feedback.is_anonymous ? 'Anonymous' : feedback.giver_name}
            </span>
            <span className="text-gray-500 text-xs">
              {formatDate(feedback.created_at)}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { BookOpen, Plus, Search, Edit3, Save, X, User, Clock, MessageSquare, Target, Star, Trash2 } from 'lucide-react';
import { useApp } from '../../contexts';
import { Button, LoadingSpinner, ErrorMessage } from '../ui';
import { formatDate } from '../../utils';
import ManagerPlaybookService from '../../services/ManagerPlaybookService';

const ManagerPlaybook = () => {
  const { userRole } = useApp();
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddNote, setShowAddNote] = useState(false);
  const [editingNote, setEditingNote] = useState(null);
  const [newNote, setNewNote] = useState({
    title: '',
    content: '',
    category: 'general',
    priority: 'medium'
  });

  useEffect(() => {
    if (userRole !== 'manager' && userRole !== 'admin') {
      setError('Access denied. Manager Playbook is only available to managers and admins.');
      setLoading(false);
      return;
    }
    fetchEmployees();
  }, [userRole]);

  useEffect(() => {
    if (selectedEmployee) {
      fetchEmployeeNotes(selectedEmployee.employee_id);
    }
  }, [selectedEmployee]);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const data = await ManagerPlaybookService.getManagerEmployees();
      setEmployees(data);
      
      if (data.length > 0) {
        setSelectedEmployee(data[0]);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchEmployeeNotes = async (employeeId) => {
    try {
      const data = await ManagerPlaybookService.getEmployeeNotes(employeeId);
      setNotes(data);
    } catch (err) {
      console.error('Error fetching notes:', err);
      setNotes([]);
    }
  };

  const handleSaveNote = async () => {
    try {
      // Validate note
      if (!newNote.title.trim() || !newNote.content.trim()) {
        alert('Please provide both title and content for the note.');
        return;
      }

      const noteData = {
        employee_id: selectedEmployee.employee_id,
        title: newNote.title,
        content: newNote.content,
        category: newNote.category,
        priority: newNote.priority
      };

      await ManagerPlaybookService.saveManagerNote(noteData);
      
      // Refresh notes list
      await fetchEmployeeNotes(selectedEmployee.employee_id);
      
      setNewNote({ title: '', content: '', category: 'general', priority: 'medium' });
      setShowAddNote(false);
      
    } catch (err) {
      alert('Error saving note: ' + err.message);
    }
  };

  const handleEditNote = (note) => {
    setEditingNote(note);
    setNewNote({
      title: note.title,
      content: note.content,
      category: note.category,
      priority: note.priority
    });
    setShowAddNote(true);
  };

  const handleUpdateNote = async () => {
    try {
      const noteData = {
        title: newNote.title,
        content: newNote.content,
        category: newNote.category,
        priority: newNote.priority
      };

      await ManagerPlaybookService.updateManagerNote(editingNote.id, noteData);
      
      // Refresh notes list
      await fetchEmployeeNotes(selectedEmployee.employee_id);
      
      setEditingNote(null);
      setNewNote({ title: '', content: '', category: 'general', priority: 'medium' });
      setShowAddNote(false);
      
    } catch (err) {
      alert('Error updating note: ' + err.message);
    }
  };

  const handleCancelNote = () => {
    setShowAddNote(false);
    setEditingNote(null);
    setNewNote({ title: '', content: '', category: 'general', priority: 'medium' });
  };

  const handleDeleteNote = async (noteId, noteTitle) => {
    if (!window.confirm(`Are you sure you want to delete the note "${noteTitle}"? This action cannot be undone.`)) {
      return;
    }

    try {
      await ManagerPlaybookService.deleteManagerNote(noteId);
      
      // Refresh notes after successful deletion
      if (selectedEmployee) {
        await fetchEmployeeNotes(selectedEmployee.employee_id);
      }
      
      alert('Note deleted successfully!');
    } catch (err) {
      console.error('Delete note error:', err);
      alert('Error deleting note: ' + err.message);
    }
  };

  const filteredEmployees = employees.filter(emp => 
    emp.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    emp.job_title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const categories = [
    { value: 'general', label: 'General Notes', color: 'bg-gray-600' },
    { value: 'performance', label: 'Performance', color: 'bg-blue-600' },
    { value: 'development', label: 'Development', color: 'bg-green-600' },
    { value: 'personal', label: 'Personal', color: 'bg-purple-600' },
    { value: 'goals', label: 'Goals', color: 'bg-yellow-600' }
  ];

  const priorities = [
    { value: 'low', label: 'Low', color: 'text-gray-400' },
    { value: 'medium', label: 'Medium', color: 'text-yellow-400' },
    { value: 'high', label: 'High', color: 'text-red-400' },
    { value: 'urgent', label: 'Urgent', color: 'text-red-600' }
  ];

  if (loading) {
    return (
      <div className="p-8">
        <LoadingSpinner size="lg" message="Loading Manager Playbook..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <ErrorMessage error={error} title="Manager Playbook Error" />
      </div>
    );
  }

  return (
    <div className="p-8">
      {/* Header */}
      <header className="mb-8">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-4xl font-bold text-white flex items-center">
              <BookOpen className="mr-3 text-cyan-400" size={36} />
              Manager's Playbook
            </h1>
            <p className="text-gray-400 mt-2">
              Private notes and coaching insights for your direct reports
            </p>
          </div>
          <div className="bg-blue-900 bg-opacity-50 rounded-lg p-4 border border-blue-700">
            <p className="text-blue-200 text-sm font-medium"> Confidential</p>
            <p className="text-blue-300 text-xs">Notes are private and visible only to you</p>
          </div>
        </div>
      </header>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Employee List */}
        <div className="bg-gray-800 rounded-lg shadow-2xl">
          <div className="p-6 border-b border-gray-700">
            <h2 className="text-xl font-semibold text-white mb-4">Team Members</h2>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
              <input
                type="text"
                placeholder="Search team members..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              />
            </div>
          </div>
          
          <div className="p-4 space-y-2 max-h-96 overflow-y-auto">
            {filteredEmployees.map(employee => (
              <button
                key={employee.employee_id}
                onClick={() => setSelectedEmployee(employee)}
                className={`w-full text-left p-3 rounded-lg transition-colors ${
                  selectedEmployee?.employee_id === employee.employee_id
                    ? 'bg-cyan-600 text-white'
                    : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                }`}
              >
                <div className="flex items-center space-x-3">
                  <User size={16} className="text-cyan-400" />
                  <div>
                    <p className="font-medium">{employee.name}</p>
                    <p className="text-xs opacity-75">{employee.job_title}</p>
                  </div>
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Notes Section */}
        <div className="lg:col-span-2 space-y-6">
          {selectedEmployee ? (
            <>
              {/* Employee Header */}
              <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-cyan-600 rounded-full flex items-center justify-center">
                      <User className="text-white" size={24} />
                    </div>
                    <div>
                      <h2 className="text-2xl font-bold text-white">{selectedEmployee.name}</h2>
                      <p className="text-gray-400">{selectedEmployee.job_title}</p>
                    </div>
                  </div>
                  <Button
                    onClick={() => setShowAddNote(true)}
                    variant="primary"
                  >
                    <Plus size={16} className="mr-2" />
                    Add Note
                  </Button>
                </div>
              </div>

              {/* Add/Edit Note Form */}
              {showAddNote && (
                <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-semibold text-white">
                      {editingNote ? 'Edit Note' : 'Add New Note'}
                    </h3>
                    <button
                      onClick={handleCancelNote}
                      className="text-gray-400 hover:text-white"
                    >
                      <X size={20} />
                    </button>
                  </div>

                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">
                        Title *
                      </label>
                      <input
                        type="text"
                        value={newNote.title}
                        onChange={(e) => setNewNote(prev => ({ ...prev, title: e.target.value }))}
                        className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                        placeholder="Brief note title..."
                      />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Category
                        </label>
                        <select
                          value={newNote.category}
                          onChange={(e) => setNewNote(prev => ({ ...prev, category: e.target.value }))}
                          className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                        >
                          {categories.map(cat => (
                            <option key={cat.value} value={cat.value}>{cat.label}</option>
                          ))}
                        </select>
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                          Priority
                        </label>
                        <select
                          value={newNote.priority}
                          onChange={(e) => setNewNote(prev => ({ ...prev, priority: e.target.value }))}
                          className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                        >
                          {priorities.map(priority => (
                            <option key={priority.value} value={priority.value}>{priority.label}</option>
                          ))}
                        </select>
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">
                        Content *
                      </label>
                      <textarea
                        value={newNote.content}
                        onChange={(e) => setNewNote(prev => ({ ...prev, content: e.target.value }))}
                        className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                        rows={4}
                        placeholder="Your private notes about this team member..."
                      />
                    </div>

                    <div className="flex justify-end space-x-3">
                      <Button
                        onClick={handleCancelNote}
                        variant="secondary"
                      >
                        Cancel
                      </Button>
                      <Button
                        onClick={editingNote ? handleUpdateNote : handleSaveNote}
                        variant="primary"
                      >
                        <Save size={16} className="mr-2" />
                        {editingNote ? 'Update Note' : 'Save Note'}
                      </Button>
                    </div>
                  </div>
                </div>
              )}

              {/* Notes List */}
              <div className="bg-gray-800 rounded-lg shadow-2xl">
                <div className="p-6 border-b border-gray-700">
                  <h3 className="text-lg font-semibold text-white">
                    Notes for {selectedEmployee.name} ({notes.length})
                  </h3>
                </div>

                <div className="p-6">
                  {notes.length === 0 ? (
                    <div className="text-center py-12">
                      <MessageSquare size={48} className="mx-auto text-gray-500 mb-4" />
                      <h4 className="text-white text-lg mb-2">No notes yet</h4>
                      <p className="text-gray-400 mb-6">
                        Start building your coaching insights by adding the first note for {selectedEmployee.name}.
                      </p>
                      <Button
                        onClick={() => setShowAddNote(true)}
                        variant="primary"
                      >
                        <Plus size={16} className="mr-2" />
                        Add First Note
                      </Button>
                    </div>
                  ) : (
                    <div className="space-y-4">
                      {notes.map(note => (
                        <NoteCard
                          key={note.id}
                          note={note}
                          categories={categories}
                          priorities={priorities}
                          onEdit={handleEditNote}
                          onDelete={handleDeleteNote}
                        />
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </>
          ) : (
            <div className="bg-gray-800 rounded-lg shadow-2xl p-12 text-center">
              <User size={64} className="mx-auto text-gray-500 mb-4" />
              <h3 className="text-xl font-semibold text-white mb-2">
                Select a Team Member
              </h3>
              <p className="text-gray-400">
                Choose a team member from the list to view and manage their coaching notes.
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// Note Card Component
const NoteCard = ({ note, categories, priorities, onEdit, onDelete }) => {
  const category = categories.find(c => c.value === note.category);
  const priority = priorities.find(p => p.value === note.priority);

  return (
    <div className="bg-gray-700 rounded-lg p-4 border border-gray-600 hover:border-gray-500 transition-colors">
      <div className="flex justify-between items-start mb-3">
        <div className="flex-1">
          <div className="flex items-center space-x-2 mb-2">
            <h4 className="text-white font-medium">{note.title}</h4>
            <span className={`px-2 py-1 text-xs rounded ${category?.color} text-white`}>
              {category?.label}
            </span>
            <span className={`text-xs font-medium ${priority?.color}`}>
              {priority?.label} Priority
            </span>
          </div>
          <p className="text-gray-300 text-sm leading-relaxed">{note.content}</p>
        </div>
        <div className="flex items-center space-x-2 ml-4">
          <button
            onClick={() => onEdit(note)}
            className="text-gray-400 hover:text-cyan-400 transition-colors"
            title="Edit note"
          >
            <Edit3 size={16} />
          </button>
          <button
            onClick={() => onDelete(note.id, note.title)}
            className="text-gray-400 hover:text-red-400 transition-colors"
            title="Delete note"
          >
            <Trash2 size={16} />
          </button>
        </div>
      </div>
      
      <div className="flex items-center justify-between text-xs text-gray-500">
        <span className="flex items-center">
          <Clock size={12} className="mr-1" />
          {formatDate(note.created_at)}
        </span>
        {note.updated_at && note.updated_at !== note.created_at && (
          <span>Updated {formatDate(note.updated_at)}</span>
        )}
      </div>
    </div>
  );
};

export default ManagerPlaybook;

// src/components/pages/ManagerReview.js - Manager interface for reviewing employee assessments
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  User, 
  Calendar, 
  Clock, 
  Save, 
  ArrowLeft, 
  FileText, 
  MessageSquare,
  Star,
  Target,
  TrendingUp,
  Award
} from 'lucide-react';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button, StatusBadge } from '../ui';
import { formatDate } from '../../utils';
import { supabase } from '../../services';

export default function ManagerReview({ pageProps = {} }) {
  const navigate = useNavigate();
  const { 
    assessmentId, 
    employeeId, 
    employeeName, 
    cycleId, 
    cycleName 
  } = pageProps;

  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [savingDraft, setSavingDraft] = useState(false);
  const [error, setError] = useState(null);
  const [assessment, setAssessment] = useState(null);
  const [managerFeedback, setManagerFeedback] = useState({
    overall_performance: '',
    strengths: '',
    areas_for_improvement: '',
    goals_for_next_period: '',
    development_recommendations: '',
    rating: 3
  });

  // Fetch assessment data
  useEffect(() => {
    if (assessmentId) {
      fetchAssessmentData();
    }
  }, [assessmentId]);

  const fetchAssessmentData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch assessment details including employee self-assessment
      const { data, error } = await supabase
        .from('assessments')
        .select(`
          *,
          employee:employees(name, email, job_title),
          review_cycle:review_cycles(name, description, start_date, end_date)
        `)
        .eq('id', assessmentId)
        .single();

      if (error) throw error;

      setAssessment(data);

      // Load existing manager feedback if available
      if (data.manager_feedback && Object.keys(data.manager_feedback).length > 0) {
        setManagerFeedback(prev => ({
          ...prev,
          ...data.manager_feedback
        }));
      }
    } catch (err) {
      console.error('Error fetching assessment data:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSaveReview = async () => {
    try {
      setSaving(true);
      setError(null);

      // Update assessment with manager feedback
      const { error: updateError } = await supabase
        .from('assessments')
        .update({
          manager_feedback: managerFeedback,
          manager_review_status: 'completed',
          manager_reviewed_at: new Date().toISOString(),
          updated_at: new Date().toISOString()
        })
        .eq('id', assessmentId);

      if (updateError) throw updateError;

      // Navigate back to My Team
      navigate('/team');
    } catch (err) {
      console.error('Error saving manager review:', err);
      setError(err.message);
    } finally {
      setSaving(false);
    }
  };

  const handleSaveDraft = async () => {
    try {
      setSavingDraft(true);
      setError(null);

      // Update assessment with manager feedback as draft
      const { error: updateError } = await supabase
        .from('assessments')
        .update({
          manager_feedback: managerFeedback,
          manager_review_status: 'in_progress',
          updated_at: new Date().toISOString()
        })
        .eq('id', assessmentId);

      if (updateError) throw updateError;

      console.log(' Draft saved successfully');
    } catch (err) {
      console.error('Error saving draft:', err);
      setError(err.message);
    } finally {
      setSavingDraft(false);
    }
  };

  // Calculate completion percentage
  const calculateCompletionPercentage = () => {
    const fields = [
      managerFeedback.overall_performance,
      managerFeedback.strengths,
      managerFeedback.areas_for_improvement,
      managerFeedback.goals_for_next_period,
      managerFeedback.development_recommendations
    ];
    
    const completedFields = fields.filter(field => field && field.trim().length > 0).length;
    const totalFields = fields.length;
    
    return Math.round((completedFields / totalFields) * 100);
  };

  const handleInputChange = (field, value) => {
    setManagerFeedback(prev => ({
      ...prev,
      [field]: value
    }));
  };

  if (loading) {
    return (
      <div className="p-8">
        <LoadingSpinner size="lg" message="Loading assessment..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <ErrorMessage 
          error={error} 
          title="Error Loading Assessment" 
          onRetry={fetchAssessmentData}
        />
      </div>
    );
  }

  if (!assessment) {
    return (
      <div className="p-8">
        <div className="text-center py-12">
          <FileText size={64} className="mx-auto text-gray-500 mb-4" />
          <h3 className="text-xl font-semibold text-white mb-2">Assessment Not Found</h3>
          <p className="text-gray-400">The requested assessment could not be found.</p>
          <Button 
            onClick={() => navigate('/team')}
            className="mt-4"
          >
            Return to My Team
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="p-8">
      {/* Header */}
      <div className="flex items-center justify-between mb-8">
        <div className="flex items-center space-x-4">
          <Button 
            variant="secondary" 
            onClick={() => navigate('/team')}
          >
            <ArrowLeft size={16} className="mr-2" />
            Back to My Team
          </Button>
          <div>
            <h1 className="text-3xl font-bold text-cyan-400">Manager Review</h1>
            <p className="text-gray-400 mt-1">
              Review assessment for {assessment.employee?.name}
            </p>
          </div>
        </div>
        <div className="flex space-x-3">
          <Button 
            variant="secondary"
            onClick={handleSaveDraft}
            disabled={savingDraft}
          >
            <Save size={16} className="mr-2" />
            {savingDraft ? 'Saving Draft...' : 'Save Draft'}
          </Button>
          <Button 
            variant="primary"
            onClick={handleSaveReview}
            disabled={saving}
          >
            <Save size={16} className="mr-2" />
            {saving ? 'Saving...' : 'Complete Review'}
          </Button>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Employee Self-Assessment (Left Side) */}
        <div className="lg:col-span-2 space-y-6">
          {/* Assessment Info */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <div className="flex items-center space-x-4 mb-4">
              <div className="w-12 h-12 bg-cyan-600 rounded-full flex items-center justify-center">
                <User className="text-white" size={24} />
              </div>
              <div>
                <h2 className="text-xl font-semibold text-white">{assessment.employee?.name}</h2>
                <p className="text-gray-400">{assessment.employee?.job_title}</p>
                <p className="text-gray-500 text-sm">{assessment.employee?.email}</p>
              </div>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <p className="text-sm text-gray-400">Review Cycle</p>
                <p className="text-white font-medium">{assessment.review_cycle?.name}</p>
              </div>
              <div>
                <p className="text-sm text-gray-400">Period</p>
                <p className="text-white">
                  {formatDate(assessment.review_cycle?.start_date)} - {formatDate(assessment.review_cycle?.end_date)}
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-400">Status</p>
                <StatusBadge 
                  status={assessment.self_assessment_status} 
                  color={assessment.self_assessment_status === 'submitted' ? 'green' : 'yellow'}
                />
              </div>
            </div>
          </div>

          {/* Employee Self-Assessment */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h3 className="text-lg font-semibold text-white mb-4 flex items-center">
              <FileText className="mr-2 text-cyan-400" size={20} />
              Employee Self-Assessment
            </h3>
            
            {assessment.responses && Object.keys(assessment.responses).length > 0 ? (
              <div className="space-y-4">
                {Object.entries(assessment.responses).map(([question, answer]) => (
                  <div key={question} className="border-b border-gray-700 pb-4 last:border-b-0">
                    <p className="text-gray-300 font-medium mb-2">{question}</p>
                    <p className="text-gray-400">{answer || 'No response provided'}</p>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-400">No self-assessment responses available.</p>
            )}
          </div>
        </div>

        {/* Manager Review Form (Right Side) */}
        <div className="space-y-6">
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-white flex items-center">
                <MessageSquare className="mr-2 text-cyan-400" size={20} />
                Your Review
              </h3>
              <div className="text-sm text-gray-400">
                <span className="text-cyan-400 font-medium">{calculateCompletionPercentage()}%</span> complete
              </div>
            </div>
            
            <div className="space-y-4">
              {/* Overall Rating */}
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Overall Performance Rating
                </label>
                <select
                  value={managerFeedback.rating}
                  onChange={(e) => handleInputChange('rating', parseInt(e.target.value))}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                >
                  <option value={1}>1 - Needs Improvement</option>
                  <option value={2}>2 - Below Expectations</option>
                  <option value={3}>3 - Meets Expectations</option>
                  <option value={4}>4 - Exceeds Expectations</option>
                  <option value={5}>5 - Outstanding</option>
                </select>
              </div>

              {/* Overall Performance */}
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Overall Performance Summary
                </label>
                <textarea
                  value={managerFeedback.overall_performance}
                  onChange={(e) => handleInputChange('overall_performance', e.target.value)}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  rows={3}
                  placeholder="Provide an overall summary of the employee's performance..."
                />
              </div>

              {/* Strengths */}
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Key Strengths
                </label>
                <textarea
                  value={managerFeedback.strengths}
                  onChange={(e) => handleInputChange('strengths', e.target.value)}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  rows={3}
                  placeholder="What are this employee's key strengths?"
                />
              </div>

              {/* Areas for Improvement */}
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Areas for Improvement
                </label>
                <textarea
                  value={managerFeedback.areas_for_improvement}
                  onChange={(e) => handleInputChange('areas_for_improvement', e.target.value)}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  rows={3}
                  placeholder="What areas could this employee focus on improving?"
                />
              </div>

              {/* Goals for Next Period */}
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Goals for Next Period
                </label>
                <textarea
                  value={managerFeedback.goals_for_next_period}
                  onChange={(e) => handleInputChange('goals_for_next_period', e.target.value)}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  rows={3}
                  placeholder="What goals should this employee focus on next?"
                />
              </div>

              {/* Development Recommendations */}
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Development Recommendations
                </label>
                <textarea
                  value={managerFeedback.development_recommendations}
                  onChange={(e) => handleInputChange('development_recommendations', e.target.value)}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  rows={3}
                  placeholder="What development opportunities would you recommend?"
                />
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <div className="space-y-3">
              <div className="mb-3">
                <div className="text-sm text-gray-400 mb-2">Progress</div>
                <div className="w-full bg-gray-700 rounded-full h-2">
                  <div 
                    className="bg-cyan-500 h-2 rounded-full transition-all duration-300" 
                    style={{ width: `${calculateCompletionPercentage()}%` }}
                  ></div>
                </div>
                <div className="text-xs text-gray-500 mt-1">
                  {calculateCompletionPercentage()}% of fields completed
                </div>
              </div>
              
              <Button 
                variant="secondary"
                onClick={handleSaveDraft}
                disabled={savingDraft}
                className="w-full"
              >
                <Save size={16} className="mr-2" />
                {savingDraft ? 'Saving Draft...' : 'Save Draft'}
              </Button>
              
              <Button 
                variant="primary"
                onClick={handleSaveReview}
                disabled={saving}
                className="w-full"
              >
                <Save size={16} className="mr-2" />
                {saving ? 'Saving Review...' : 'Complete Review'}
              </Button>
              
              <Button 
                variant="secondary"
                onClick={() => navigate('/team')}
                className="w-full"
              >
                <ArrowLeft size={16} className="mr-2" />
                Back to My Team
              </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// src/components/pages/ManagerReviewRouter.tsx - Router-enabled manager review
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import { 
  User, 
  Calendar, 
  Clock, 
  Save, 
  ArrowLeft, 
  FileText, 
  MessageSquare,
  Star,
  Target,
  TrendingUp,
  Award
} from 'lucide-react';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button, StatusBadge } from '../ui';
import { formatDate } from '../../utils';
import { supabase } from '../../services';

export default function ManagerReviewRouter() {
  const navigate = useNavigate();
  const { employeeId } = useParams<{ employeeId?: string }>();
  const [searchParams] = useSearchParams();
  
  // Get additional parameters from URL search params
  const assessmentId = searchParams.get('assessmentId');
  const employeeName = searchParams.get('employeeName');
  const cycleId = searchParams.get('cycleId');
  const cycleName = searchParams.get('cycleName');

  const [assessment, setAssessment] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [reviewData, setReviewData] = useState<{
    overall_rating: string;
    strengths: string;
    areas_of_improvement: string;
    goals_for_next_period: string;
    manager_comments: string;
    development_priorities: string;
    
    // Employee response feedback fields
    key_accomplishments_feedback: string;
    challenges_feedback: string;
    skills_feedback: string;
    goals_feedback: string;
    support_response: string;
    satisfaction_feedback: string;
    
    // Core values feedback
    passion_feedback: string;
    excellence_feedback: string;
    resilience_feedback: string;
    responsiveness_feedback: string;
    
    // Manager GWC assessments - allow null or boolean
    manager_gwc_gets_it: boolean | null;
    manager_gwc_gets_it_feedback: string;
    manager_gwc_wants_it: boolean | null;
    manager_gwc_wants_it_feedback: string;
    manager_gwc_capacity: boolean | null;
    manager_gwc_capacity_feedback: string;
    
    // Additional comments
    additional_manager_comments: string;
  }>({
    overall_rating: '',
    strengths: '',
    areas_of_improvement: '',
    goals_for_next_period: '',
    manager_comments: '',
    development_priorities: '',
    
    // Employee response feedback fields
    key_accomplishments_feedback: '',
    challenges_feedback: '',
    skills_feedback: '',
    goals_feedback: '',
    support_response: '',
    satisfaction_feedback: '',
    
    // Core values feedback
    passion_feedback: '',
    excellence_feedback: '',
    resilience_feedback: '',
    responsiveness_feedback: '',
    
    // Manager GWC assessments
    manager_gwc_gets_it: null,
    manager_gwc_gets_it_feedback: '',
    manager_gwc_wants_it: null,
    manager_gwc_wants_it_feedback: '',
    manager_gwc_capacity: null,
    manager_gwc_capacity_feedback: '',
    
    // Additional comments
    additional_manager_comments: ''
  });

  // Load assessment data
  useEffect(() => {
    const loadAssessment = async () => {
      console.log(' ManagerReviewRouter loading assessment with:', {
        assessmentId,
        employeeId,
        searchParams: Object.fromEntries(searchParams.entries())
      });

      if (!assessmentId && !employeeId) {
        setError('No assessment or employee specified');
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError('');

        let query = supabase
          .from('assessments')
          .select(`
            *,
            employee:employees!employee_id(*),
            cycle:review_cycles(*)
          `);

        if (assessmentId) {
          console.log(' Querying by assessment ID:', assessmentId);
          query = query.eq('id', assessmentId);
        } else if (employeeId) {
          console.log(' Querying by employee ID:', employeeId);
          query = query.eq('employee_id', employeeId);
        }

        console.log(' About to execute query...');
        
        // For assessment ID queries, try the database function first
        if (assessmentId) {
          console.log(' Trying get_assessment_for_manager_review function first...');
          try {
            const { data: functionData, error: functionError } = await supabase.rpc('get_assessment_for_manager_review', {
              p_assessment_id: parseInt(assessmentId)
            });
            
            console.log(' Function result:', { functionData, functionError });
            
            if (functionError) {
              console.log(' Function failed:', functionError);
              throw new Error(`Database function error: ${functionError.message}`);
            }
            
            if (functionData && functionData.length > 0) {
              const assessment = functionData[0];
              const transformedAssessment = {
                ...assessment,
                employee: {
                  id: assessment.employee_id,
                  name: assessment.employee_name,
                  email: assessment.employee_email,
                  job_title: assessment.employee_job_title
                },
                cycle: {
                  id: assessment.review_cycle_id,
                  name: assessment.cycle_name,
                  status: assessment.cycle_status
                }
              };
              
              console.log(' Successfully loaded assessment via function:', transformedAssessment);
              setAssessment(transformedAssessment);
              
              // Pre-populate form if manager review data exists
              if (assessment.manager_review_data) {
                setReviewData(prev => ({
                  ...prev,
                  ...assessment.manager_review_data
                }));
              }
              return; // Success, exit early
            } else {
              throw new Error('Assessment function returned no data');
            }
          } catch (functionError) {
            console.error(' Function approach failed:', functionError);
            // Continue to fallback approaches below
          }
        }
        
        // Fallback: Try direct query approaches
        console.log(' Trying direct query as fallback...');
        try {
          const { data, error: fetchError } = await query.single();
          console.log(' Direct query result:', { data, fetchError });
          
          if (fetchError) {
            // If direct query fails, try alternative approaches
            if (fetchError.code === 'PGRST116') {
              console.log(' No rows returned - trying basic query...');
              
              // Try querying without joins first
              const { data: basicData, error: basicError } = await supabase
                .from('assessments')
                .select('*')
                .eq('id', assessmentId)
                .single();
                
              console.log(' Basic query result:', { basicData, basicError });
              
              if (basicError) {
                throw new Error(`Assessment ID ${assessmentId} not found or access denied. This may be due to Row Level Security policies preventing direct table access.`);
              }
              
              // If basic query works, get related data separately
              const [employeeResult, cycleResult] = await Promise.all([
                supabase
                  .from('employees')
                  .select('*')
                  .eq('id', basicData.employee_id)
                  .single(),
                supabase
                  .from('review_cycles')
                  .select('*')
                  .eq('id', basicData.review_cycle_id)
                  .single()
              ]);
              
              console.log(' Related data:', { employeeResult, cycleResult });
              
              const assessment = {
                ...basicData,
                employee: employeeResult.data,
                cycle: cycleResult.data
              };
              
              setAssessment(assessment);
              
              // Pre-populate form if manager review data exists
              if (assessment.manager_review_data) {
                setReviewData(prev => ({
                  ...prev,
                  ...assessment.manager_review_data
                }));
              }
            } else {
              throw fetchError;
            }
          } else {
            setAssessment(data);
            
            // Pre-populate form if manager review data exists
            if (data.manager_review_data) {
              setReviewData(prev => ({
                ...prev,
                ...data.manager_review_data
              }));
            }
          }
        } catch (queryError) {
          console.error(' All query attempts failed:', queryError);
          throw queryError;
        }

      } catch (err: any) {
        console.error('Error loading assessment:', err);
        setError(`Failed to load assessment: ${err?.message || 'Unknown error'}`);
      } finally {
        setLoading(false);
      }
    };

    loadAssessment();
  }, [assessmentId, employeeId]);

  // Save manager review
  const handleSave = async () => {
    if (!assessment) return;

    try {
      setSaving(true);
      setError('');

      // Use the submit_manager_review function if available, otherwise direct update
      try {
        const { data, error: functionError } = await supabase.rpc('submit_manager_review', {
          p_assessment_id: assessment.id,
          p_manager_review_data: reviewData,
          p_overall_rating: reviewData.overall_rating,
          p_manager_comments: reviewData.manager_comments || ''
        });

        if (functionError) {
          console.log('Function submit failed, using direct update:', functionError);
          throw functionError;
        }

        console.log('Manager review submitted via function:', data);
        alert('Manager review submitted successfully!');
        navigate('/team');
        return;
      } catch (functionError) {
        console.log('Falling back to direct update...');
      }

      // Fallback to direct update
      const { error: updateError } = await supabase
        .from('assessments')
        .update({
          manager_review_data: reviewData,
          manager_review_status: 'completed',
          manager_reviewed_at: new Date().toISOString(),
          
          // Save individual GWC manager assessments
          manager_gwc_gets_it: reviewData.manager_gwc_gets_it,
          manager_gwc_gets_it_feedback: reviewData.manager_gwc_gets_it_feedback,
          manager_gwc_wants_it: reviewData.manager_gwc_wants_it,
          manager_gwc_wants_it_feedback: reviewData.manager_gwc_wants_it_feedback,
          manager_gwc_capacity: reviewData.manager_gwc_capacity,
          manager_gwc_capacity_feedback: reviewData.manager_gwc_capacity_feedback,
          
          // Save manager performance rating and summary
          manager_performance_rating: reviewData.overall_rating,
          manager_summary_comments: reviewData.manager_comments,
          
          updated_at: new Date().toISOString()
        })
        .eq('id', assessment.id);

      if (updateError) throw updateError;

      alert('Manager review saved successfully!');
      
      // Navigate back to team page
      navigate('/team');

    } catch (err: any) {
      console.error('Error saving review:', err);
      setError(`Failed to save review: ${err?.message || 'Unknown error'}`);
    } finally {
      setSaving(false);
    }
  };

  const handleBack = () => {
    navigate('/team');
  };

  if (loading) {
    return <LoadingSpinner message="Loading assessment..." />;
  }

  if (error) {
    return (
      <ErrorMessage
        error={error}
        title="Assessment Error"
        onRetry={() => window.location.reload()}
      />
    );
  }

  if (!assessment) {
    return (
      <ErrorMessage
        error="Assessment not found"
        title="Not Found"
        onRetry={handleBack}
      />
    );
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <Button
            variant="ghost"
            onClick={handleBack}
            className="flex items-center space-x-2"
          >
            <ArrowLeft size={20} />
            <span>Back to Team</span>
          </Button>
          <div>
            <h1 className="text-2xl font-bold text-white">Manager Review</h1>
            <p className="text-gray-400">
              {assessment.employee?.name || employeeName} - {assessment.cycle?.name || cycleName}
            </p>
          </div>
        </div>
        <StatusBadge 
          status={assessment.manager_review_status} 
        />
      </div>

      {/* Employee Self-Assessment with Manager Feedback - Two Column Layout */}
      <div className="bg-gray-800 rounded-lg p-6">
        <h2 className="text-xl font-semibold text-white mb-6 flex items-center">
          <User className="mr-2" size={20} />
          Employee Assessment & Manager Feedback
        </h2>

        {assessment.self_assessment_data ? (
          <div className="space-y-8">
            {/* Key Accomplishments */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <h3 className="font-medium text-cyan-300 mb-3">Employee Response: Key Accomplishments</h3>
                <div className="bg-gray-700 p-4 rounded-lg min-h-[120px]">
                  <p className="text-gray-300 text-sm whitespace-pre-wrap">
                    {assessment.self_assessment_data.key_accomplishments || 'No response provided'}
                  </p>
                </div>
              </div>
              <div>
                <h3 className="font-medium text-yellow-300 mb-3">Manager Feedback: Key Accomplishments</h3>
                <textarea
                  value={reviewData.key_accomplishments_feedback || ''}
                  onChange={(e) => setReviewData(prev => ({ ...prev, key_accomplishments_feedback: e.target.value }))}
                  rows={6}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                  placeholder="Provide feedback on the employee's key accomplishments..."
                />
              </div>
            </div>

            {/* Challenges Faced */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <h3 className="font-medium text-cyan-300 mb-3">Employee Response: Challenges Faced</h3>
                <div className="bg-gray-700 p-4 rounded-lg min-h-[120px]">
                  <p className="text-gray-300 text-sm whitespace-pre-wrap">
                    {assessment.self_assessment_data.challenges_faced || 'No response provided'}
                  </p>
                </div>
              </div>
              <div>
                <h3 className="font-medium text-yellow-300 mb-3">Manager Feedback: Challenges</h3>
                <textarea
                  value={reviewData.challenges_feedback || ''}
                  onChange={(e) => setReviewData(prev => ({ ...prev, challenges_feedback: e.target.value }))}
                  rows={6}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                  placeholder="Provide feedback on how the employee handled challenges..."
                />
              </div>
            </div>

            {/* Skills Developed */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <h3 className="font-medium text-cyan-300 mb-3">Employee Response: Skills Developed</h3>
                <div className="bg-gray-700 p-4 rounded-lg min-h-[100px]">
                  <p className="text-gray-300 text-sm whitespace-pre-wrap">
                    {assessment.self_assessment_data.skills_developed || 'No response provided'}
                  </p>
                </div>
              </div>
              <div>
                <h3 className="font-medium text-yellow-300 mb-3">Manager Feedback: Skills Development</h3>
                <textarea
                  value={reviewData.skills_feedback || ''}
                  onChange={(e) => setReviewData(prev => ({ ...prev, skills_feedback: e.target.value }))}
                  rows={5}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                  placeholder="Acknowledge skills development and suggest areas for growth..."
                />
              </div>
            </div>

            {/* Goals for Next Period */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <h3 className="font-medium text-cyan-300 mb-3">Employee Response: Goals for Next Period</h3>
                <div className="bg-gray-700 p-4 rounded-lg min-h-[120px]">
                  <p className="text-gray-300 text-sm whitespace-pre-wrap">
                    {assessment.self_assessment_data.goals_next_period || 'No response provided'}
                  </p>
                </div>
              </div>
              <div>
                <h3 className="font-medium text-yellow-300 mb-3">Manager Feedback: Goals Alignment</h3>
                <textarea
                  value={reviewData.goals_feedback || ''}
                  onChange={(e) => setReviewData(prev => ({ ...prev, goals_feedback: e.target.value }))}
                  rows={6}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                  placeholder="Provide feedback on proposed goals and suggest modifications or additions..."
                />
              </div>
            </div>

            {/* Support Needed */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <h3 className="font-medium text-cyan-300 mb-3">Employee Response: Support Needed</h3>
                <div className="bg-gray-700 p-4 rounded-lg min-h-[100px]">
                  <p className="text-gray-300 text-sm whitespace-pre-wrap">
                    {assessment.self_assessment_data.support_needed || 'No response provided'}
                  </p>
                </div>
              </div>
              <div>
                <h3 className="font-medium text-yellow-300 mb-3">Manager Response: Support & Resources</h3>
                <textarea
                  value={reviewData.support_response || ''}
                  onChange={(e) => setReviewData(prev => ({ ...prev, support_response: e.target.value }))}
                  rows={5}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                  placeholder="Address support requests and identify additional resources you can provide..."
                />
              </div>
            </div>

            {/* Overall Satisfaction */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <h3 className="font-medium text-cyan-300 mb-3">Employee Response: Overall Satisfaction</h3>
                <div className="bg-gray-700 p-4 rounded-lg">
                  <p className="text-gray-300 text-sm">
                    <strong>Level:</strong> {assessment.self_assessment_data.overall_satisfaction?.replace(/_/g, ' ').toUpperCase() || 'Not provided'}
                  </p>
                </div>
              </div>
              <div>
                <h3 className="font-medium text-yellow-300 mb-3">Manager Comments: Satisfaction</h3>
                <textarea
                  value={reviewData.satisfaction_feedback || ''}
                  onChange={(e) => setReviewData(prev => ({ ...prev, satisfaction_feedback: e.target.value }))}
                  rows={4}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                  placeholder="Comment on the employee's satisfaction level and discuss how to improve engagement..."
                />
              </div>
            </div>

            {/* Core Values Assessment */}
            <div className="bg-gray-700/30 rounded-lg p-6 border border-gray-600">
              <h3 className="text-lg font-semibold text-white mb-6 flex items-center">
                <Star className="mr-2" size={18} />
                Core Values Assessment
              </h3>
              
              <div className="space-y-8">
                {/* Passionate about our purpose */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div>
                    <h4 className="font-medium text-cyan-300 mb-3">Employee: Passionate about our purpose</h4>
                    <div className="bg-gray-700 p-4 rounded-lg min-h-[120px]">
                      <p className="text-gray-300 text-sm whitespace-pre-wrap">
                        {assessment.self_assessment_data.core_values_passionate_purpose || assessment.value_passionate_examples || 'No response provided'}
                      </p>
                    </div>
                  </div>
                  <div>
                    <h4 className="font-medium text-yellow-300 mb-3">Manager Feedback: Passion</h4>
                    <textarea
                      value={reviewData.passion_feedback || ''}
                      onChange={(e) => setReviewData(prev => ({ ...prev, passion_feedback: e.target.value }))}
                      rows={6}
                      className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                      placeholder="Provide feedback on how the employee demonstrates passion for our purpose..."
                    />
                  </div>
                </div>

                {/* Driven to be the best */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div>
                    <h4 className="font-medium text-cyan-300 mb-3">Employee: Driven to be the best</h4>
                    <div className="bg-gray-700 p-4 rounded-lg min-h-[120px]">
                      <p className="text-gray-300 text-sm whitespace-pre-wrap">
                        {assessment.self_assessment_data.core_values_driven_best || assessment.value_driven_examples || 'No response provided'}
                      </p>
                    </div>
                  </div>
                  <div>
                    <h4 className="font-medium text-yellow-300 mb-3">Manager Feedback: Excellence</h4>
                    <textarea
                      value={reviewData.excellence_feedback || ''}
                      onChange={(e) => setReviewData(prev => ({ ...prev, excellence_feedback: e.target.value }))}
                      rows={6}
                      className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                      placeholder="Comment on the employee's drive for excellence and continuous improvement..."
                    />
                  </div>
                </div>

                {/* Resilient, rising stronger together */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div>
                    <h4 className="font-medium text-cyan-300 mb-3">Employee: Resilient, rising stronger together</h4>
                    <div className="bg-gray-700 p-4 rounded-lg min-h-[120px]">
                      <p className="text-gray-300 text-sm whitespace-pre-wrap">
                        {assessment.self_assessment_data.core_values_resilient_together || assessment.value_resilient_examples || 'No response provided'}
                      </p>
                    </div>
                  </div>
                  <div>
                    <h4 className="font-medium text-yellow-300 mb-3">Manager Feedback: Resilience</h4>
                    <textarea
                      value={reviewData.resilience_feedback || ''}
                      onChange={(e) => setReviewData(prev => ({ ...prev, resilience_feedback: e.target.value }))}
                      rows={6}
                      className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                      placeholder="Provide feedback on the employee's resilience and teamwork..."
                    />
                  </div>
                </div>

                {/* Respond swiftly and positively */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
