                    <h4 className="font-medium text-cyan-300 mb-3">Employee: Respond swiftly and positively</h4>
                    <div className="bg-gray-700 p-4 rounded-lg min-h-[120px]">
                      <p className="text-gray-300 text-sm whitespace-pre-wrap">
                        {assessment.self_assessment_data.core_values_respond_swiftly || assessment.value_responsive_examples || 'No response provided'}
                      </p>
                    </div>
                  </div>
                  <div>
                    <h4 className="font-medium text-yellow-300 mb-3">Manager Feedback: Responsiveness</h4>
                    <textarea
                      value={reviewData.responsiveness_feedback || ''}
                      onChange={(e) => setReviewData(prev => ({ ...prev, responsiveness_feedback: e.target.value }))}
                      rows={6}
                      className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                      placeholder="Comment on the employee's responsiveness and customer focus..."
                    />
                  </div>
                </div>
              </div>
            </div>

            {/* GWC Assessment */}
            <div className="bg-gray-700/30 rounded-lg p-6 border border-gray-600">
              <h3 className="text-lg font-semibold text-white mb-6 flex items-center">
                <Target className="mr-2" size={18} />
                GWC Assessment (Get it, Want it, Capacity to do it)
              </h3>
              
              <div className="space-y-8">
                {/* Get It */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div>
                    <h4 className="font-medium text-cyan-300 mb-3">Employee: Get It</h4>
                    <div className="bg-gray-700 p-4 rounded-lg">
                      <div className="flex items-center space-x-3 mb-3">
                        <div className={`w-4 h-4 rounded border-2 flex items-center justify-center ${
                          assessment.gwc_gets_it ? 'bg-green-600 border-green-600' : 'bg-red-600 border-red-600'
                        }`}>
                          {assessment.gwc_gets_it ? '' : ''}
                        </div>
                        <span className="text-gray-300">
                          {assessment.gwc_gets_it ? 'Yes - Gets the role' : 'No - Does not get the role'}
                        </span>
                      </div>
                      {assessment.gwc_gets_it_feedback && (
                        <p className="text-gray-300 text-sm whitespace-pre-wrap">
                          <strong>Explanation:</strong> {assessment.gwc_gets_it_feedback}
                        </p>
                      )}
                    </div>
                  </div>
                  <div>
                    <h4 className="font-medium text-yellow-300 mb-3">Manager Assessment: Get It</h4>
                    <div className="space-y-3">
                      <div className="flex items-center space-x-4">
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="manager_gwc_gets_it"
                            value="true"
                            checked={reviewData.manager_gwc_gets_it === true}
                            onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_gets_it: true }))}
                            className="text-green-600"
                          />
                          <span className="text-green-400">Yes - Gets it</span>
                        </label>
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="manager_gwc_gets_it"
                            value="false"
                            checked={reviewData.manager_gwc_gets_it === false}
                            onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_gets_it: false }))}
                            className="text-red-600"
                          />
                          <span className="text-red-400">No - Doesn't get it</span>
                        </label>
                      </div>
                      <textarea
                        value={reviewData.manager_gwc_gets_it_feedback || ''}
                        onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_gets_it_feedback: e.target.value }))}
                        rows={4}
                        className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                        placeholder="Explain your assessment of whether the employee truly 'gets' their role..."
                      />
                    </div>
                  </div>
                </div>

                {/* Want It */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div>
                    <h4 className="font-medium text-cyan-300 mb-3">Employee: Want It</h4>
                    <div className="bg-gray-700 p-4 rounded-lg">
                      <div className="flex items-center space-x-3 mb-3">
                        <div className={`w-4 h-4 rounded border-2 flex items-center justify-center ${
                          assessment.gwc_wants_it ? 'bg-green-600 border-green-600' : 'bg-red-600 border-red-600'
                        }`}>
                          {assessment.gwc_wants_it ? '' : ''}
                        </div>
                        <span className="text-gray-300">
                          {assessment.gwc_wants_it ? 'Yes - Wants the role' : 'No - Does not want the role'}
                        </span>
                      </div>
                      {assessment.gwc_wants_it_feedback && (
                        <p className="text-gray-300 text-sm whitespace-pre-wrap">
                          <strong>Explanation:</strong> {assessment.gwc_wants_it_feedback}
                        </p>
                      )}
                    </div>
                  </div>
                  <div>
                    <h4 className="font-medium text-yellow-300 mb-3">Manager Assessment: Want It</h4>
                    <div className="space-y-3">
                      <div className="flex items-center space-x-4">
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="manager_gwc_wants_it"
                            value="true"
                            checked={reviewData.manager_gwc_wants_it === true}
                            onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_wants_it: true }))}
                            className="text-green-600"
                          />
                          <span className="text-green-400">Yes - Wants it</span>
                        </label>
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="manager_gwc_wants_it"
                            value="false"
                            checked={reviewData.manager_gwc_wants_it === false}
                            onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_wants_it: false }))}
                            className="text-red-600"
                          />
                          <span className="text-red-400">No - Doesn't want it</span>
                        </label>
                      </div>
                      <textarea
                        value={reviewData.manager_gwc_wants_it_feedback || ''}
                        onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_wants_it_feedback: e.target.value }))}
                        rows={4}
                        className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                        placeholder="Explain your assessment of the employee's genuine desire for this role..."
                      />
                    </div>
                  </div>
                </div>

                {/* Capacity */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div>
                    <h4 className="font-medium text-cyan-300 mb-3">Employee: Has Capacity</h4>
                    <div className="bg-gray-700 p-4 rounded-lg">
                      <div className="flex items-center space-x-3 mb-3">
                        <div className={`w-4 h-4 rounded border-2 flex items-center justify-center ${
                          assessment.gwc_capacity ? 'bg-green-600 border-green-600' : 'bg-red-600 border-red-600'
                        }`}>
                          {assessment.gwc_capacity ? '' : ''}
                        </div>
                        <span className="text-gray-300">
                          {assessment.gwc_capacity ? 'Yes - Has capacity' : 'No - Lacks capacity'}
                        </span>
                      </div>
                      {assessment.gwc_capacity_feedback && (
                        <p className="text-gray-300 text-sm whitespace-pre-wrap">
                          <strong>Explanation:</strong> {assessment.gwc_capacity_feedback}
                        </p>
                      )}
                    </div>
                  </div>
                  <div>
                    <h4 className="font-medium text-yellow-300 mb-3">Manager Assessment: Has Capacity</h4>
                    <div className="space-y-3">
                      <div className="flex items-center space-x-4">
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="manager_gwc_capacity"
                            value="true"
                            checked={reviewData.manager_gwc_capacity === true}
                            onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_capacity: true }))}
                            className="text-green-600"
                          />
                          <span className="text-green-400">Yes - Has capacity</span>
                        </label>
                        <label className="flex items-center space-x-2">
                          <input
                            type="radio"
                            name="manager_gwc_capacity"
                            value="false"
                            checked={reviewData.manager_gwc_capacity === false}
                            onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_capacity: false }))}
                            className="text-red-600"
                          />
                          <span className="text-red-400">No - Lacks capacity</span>
                        </label>
                      </div>
                      <textarea
                        value={reviewData.manager_gwc_capacity_feedback || ''}
                        onChange={(e) => setReviewData(prev => ({ ...prev, manager_gwc_capacity_feedback: e.target.value }))}
                        rows={4}
                        className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                        placeholder="Explain your assessment of the employee's capacity and ability to excel in this role..."
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Additional Comments */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div>
                <h3 className="font-medium text-cyan-300 mb-3">Employee: Additional Comments</h3>
                <div className="bg-gray-700 p-4 rounded-lg min-h-[120px]">
                  <p className="text-gray-300 text-sm whitespace-pre-wrap">
                    {assessment.self_assessment_data.additional_comments || 'No additional comments provided'}
                  </p>
                </div>
              </div>
              <div>
                <h3 className="font-medium text-yellow-300 mb-3">Manager: Additional Comments</h3>
                <textarea
                  value={reviewData.additional_manager_comments || ''}
                  onChange={(e) => setReviewData(prev => ({ ...prev, additional_manager_comments: e.target.value }))}
                  rows={6}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
                  placeholder="Any additional thoughts, observations, or feedback..."
                />
              </div>
            </div>
          </div>
        ) : (
          <p className="text-gray-400">Employee has not completed their self-assessment yet.</p>
        )}
      </div>

      {/* Manager Review Form */}
      <div className="bg-gray-800 rounded-lg p-6">
        <h2 className="text-xl font-semibold text-white mb-4 flex items-center">
          <MessageSquare className="mr-2" size={20} />
          Manager Review
        </h2>

        <div className="space-y-6">
          {/* Overall Rating */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Overall Performance Rating
            </label>
            <select
              value={reviewData.overall_rating}
              onChange={(e) => setReviewData(prev => ({ ...prev, overall_rating: e.target.value }))}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white"
            >
              <option value="">Select Rating</option>
              <option value="exceeds_expectations">Exceeds Expectations</option>
              <option value="meets_expectations">Meets Expectations</option>
              <option value="partially_meets">Partially Meets Expectations</option>
              <option value="does_not_meet">Does Not Meet Expectations</option>
            </select>
          </div>

          {/* Strengths */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Key Strengths & Accomplishments
            </label>
            <textarea
              value={reviewData.strengths}
              onChange={(e) => setReviewData(prev => ({ ...prev, strengths: e.target.value }))}
              rows={4}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
              placeholder="Highlight the employee's key strengths and notable accomplishments..."
            />
          </div>

          {/* Areas of Improvement */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Areas for Development
            </label>
            <textarea
              value={reviewData.areas_of_improvement}
              onChange={(e) => setReviewData(prev => ({ ...prev, areas_of_improvement: e.target.value }))}
              rows={4}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
              placeholder="Identify specific areas where the employee can grow and improve..."
            />
          </div>

          {/* Goals for Next Period */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Goals for Next Review Period
            </label>
            <textarea
              value={reviewData.goals_for_next_period}
              onChange={(e) => setReviewData(prev => ({ ...prev, goals_for_next_period: e.target.value }))}
              rows={4}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
              placeholder="Set clear, measurable goals for the upcoming period..."
            />
          </div>

          {/* Development Priorities */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Development Priorities
            </label>
            <textarea
              value={reviewData.development_priorities}
              onChange={(e) => setReviewData(prev => ({ ...prev, development_priorities: e.target.value }))}
              rows={3}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
              placeholder="Identify key skills or competencies to focus on..."
            />
          </div>

          {/* Manager Comments */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">
              Additional Manager Comments
            </label>
            <textarea
              value={reviewData.manager_comments}
              onChange={(e) => setReviewData(prev => ({ ...prev, manager_comments: e.target.value }))}
              rows={4}
              className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white resize-none"
              placeholder="Any additional feedback, observations, or comments..."
            />
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="flex justify-end space-x-4">
        <Button
          variant="secondary"
          onClick={handleBack}
        >
          Cancel
        </Button>
        <Button
          onClick={handleSave}
          loading={saving}
          className="flex items-center space-x-2"
        >
          <Save size={16} />
          <span>Save Manager Review</span>
        </Button>
      </div>

      {error && (
        <ErrorMessage error={error} title="Save Error" />
      )}
    </div>
  );
}

// V3.7 STEP 3: My Development Center - Replace src/components/pages/Settings.js
// Transform Settings into a comprehensive employee development platform

import React, { useState, useEffect } from 'react';
import { Target, BookOpen, MessageSquare, User, Plus, Calendar, AlertTriangle, Info } from 'lucide-react';

const MyDevelopmentCenter = () => {
    const [activeTab, setActiveTab] = useState('goals');
    const [profile, setProfile] = useState(null);
    const [developmentGoals, setDevelopmentGoals] = useState([]);
    const [trainingRequests, setTrainingRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showGuidance, setShowGuidance] = useState(true);

    useEffect(() => {
        fetchAllData();
    }, []);

    const fetchAllData = async () => {
        try {
            setLoading(true);
            await Promise.all([
                fetchProfile(),
                fetchDevelopmentGoals(),
                fetchTrainingRequests()
            ]);
        } catch (error) {
            console.error('Error fetching development data:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchProfile = async () => {
        try {
            // Mock profile data for now
            setProfile({ name: 'Employee', job_title: 'Software Developer', department: 'Engineering' });
        } catch (error) {
            console.error('Error fetching profile:', error);
        }
    };

    const fetchDevelopmentGoals = async () => {
        try {
            // Mock development goals for now
            setDevelopmentGoals([]);
        } catch (error) {
            console.error('Error fetching development goals:', error);
        }
    };

    const fetchTrainingRequests = async () => {
        try {
            // Mock training requests for now
            setTrainingRequests([]);
        } catch (error) {
            console.error('Error fetching training requests:', error);
        }
    };

    const tabs = [
        { 
            id: 'profile', 
            name: 'My Profile', 
            icon: User,
            description: 'View your current role and information'
        },
        { 
            id: 'goals', 
            name: 'Development Goals', 
            icon: Target,
            description: 'Set and track your career growth objectives'
        },
        { 
            id: 'training', 
            name: 'Training Requests', 
            icon: BookOpen,
            description: 'Request courses, certifications, and learning opportunities'
        },
        { 
            id: 'communication', 
            name: 'Manager Communication', 
            icon: MessageSquare,
            description: 'Direct messaging with your manager about development'
        }
    ];

    // Check if user is new (no goals, no training requests)
    const isNewUser = developmentGoals.length === 0 && trainingRequests.length === 0;

    if (loading) {
        return (
            <div className="p-8">
                <div className="text-center py-12">
                    <div className="text-yellow-400 text-lg">Loading your development center...</div>
                </div>
            </div>
        );
    }

    return (
        <div className="p-8">
            <header className="mb-8">
                <h1 className="text-4xl font-bold text-white">My Development Center</h1>
                <p className="text-gray-400 mt-2">Take ownership of your career growth and professional development.</p>
                
                {/* Welcome guidance for new users */}
                {showGuidance && isNewUser && (
                    <div className="mt-6 bg-gradient-to-r from-cyan-900 to-blue-900 rounded-lg p-6 border border-cyan-700">
                        <div className="flex items-start justify-between">
                            <div className="flex items-start space-x-3">
                                <Info className="text-cyan-400 mt-1" size={20} />
                                <div>
                                    <h3 className="text-cyan-200 font-semibold text-lg mb-2">Welcome to Your Development Center</h3>
                                    <div className="text-cyan-300 text-sm space-y-2">
                                        <p><strong>Purpose:</strong> This is your personal space to actively manage your career growth, set learning goals, and communicate development needs with your manager.</p>
                                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
                                            <div className="space-y-2">
                                                <p className="font-medium">What you can do:</p>
                                                <ul className="text-xs space-y-1 ml-4">
                                                    <li> Set career aspirations and skill development goals</li>
                                                    <li> Request training, courses, and certifications</li>
                                                    <li> Communicate directly with your manager about growth</li>
                                                    <li> Track your professional development progress</li>
                                                </ul>
                                            </div>
                                            <div className="space-y-2">
                                                <p className="font-medium">Expected outcomes:</p>
                                                <ul className="text-xs space-y-1 ml-4">
                                                    <li> Clear career development roadmap</li>
                                                    <li> Regular learning opportunities</li>
                                                    <li> Better manager-employee development conversations</li>
                                                    <li> Enhanced skills and career advancement</li>
                                                </ul>
                                            </div>
                                        </div>
                                        <div className="mt-4 p-3 bg-cyan-800 rounded border border-cyan-600">
                                            <p className="font-medium flex items-center">
                                                <Target size={16} className="mr-2" />
                                                Get started: Add your first development goal or request training to begin your journey.
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <button 
                                onClick={() => setShowGuidance(false)}
                                className="text-cyan-400 hover:text-cyan-300 ml-4"
                            >
                                
                            </button>
                        </div>
                    </div>
                )}
            </header>

            {/* Tab Navigation */}
            <div className="mb-8">
                <div className="border-b border-gray-700">
                    <nav className="-mb-px flex space-x-8">
                        {tabs.map(tab => {
                            const Icon = tab.icon;
                            return (
                                <button
                                    key={tab.id}
                                    onClick={() => setActiveTab(tab.id)}
                                    className={`py-2 px-1 border-b-2 font-medium text-sm flex items-center group relative ${
                                        activeTab === tab.id
                                            ? 'border-cyan-500 text-cyan-400'
                                            : 'border-transparent text-gray-400 hover:text-gray-300 hover:border-gray-300'
                                    }`}
                                    title={tab.description}
                                >
                                    <Icon size={16} className="mr-2" />
                                    {tab.name}
                                </button>
                            );
                        })}
                    </nav>
                </div>
                
                {/* Active Tab Description */}
                <div className="mt-2 text-gray-400 text-sm">
                    {tabs.find(tab => tab.id === activeTab)?.description}
                </div>
            </div>

            {/* Tab Content */}
            {activeTab === 'profile' && <ProfileSection profile={profile} />}
            {activeTab === 'goals' && (
                <GoalsSection 
                    goals={developmentGoals}
                    onRefresh={fetchDevelopmentGoals}
                />
            )}
            {activeTab === 'training' && (
                <TrainingSection 
                    requests={trainingRequests}
                    onRefresh={fetchTrainingRequests}
                />
            )}
            {activeTab === 'communication' && <CommunicationSection />}
        </div>
    );
};

// Profile Section Component
const ProfileSection = ({ profile }) => (
    <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
        <h2 className="text-2xl font-semibold text-white mb-6">My Profile</h2>
        
        {profile ? (
            <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-300 mb-1">Name</label>
                        <div className="p-3 bg-gray-700 rounded-md text-white">{profile.name}</div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-300 mb-1">Email</label>
                        <div className="p-3 bg-gray-700 rounded-md text-white">{profile.email}</div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-300 mb-1">Job Title</label>
                        <div className="p-3 bg-gray-700 rounded-md text-white">{profile.job_title || 'Not specified'}</div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-300 mb-1">Manager</label>
                        <div className="p-3 bg-gray-700 rounded-md text-white">{profile.manager_name || 'No manager assigned'}</div>
                    </div>
                </div>
                
                <div className="mt-6 p-4 bg-blue-900 rounded-lg border border-blue-700">
                    <h4 className="text-blue-200 font-semibold mb-2">Note:</h4>
                    <p className="text-blue-300 text-sm">
                        Profile information is managed by your administrator. If you need to update your role or manager assignment, 
                        please contact HR or your system administrator.
                    </p>
                </div>
            </div>
        ) : (
            <div className="text-center py-8">
                <User size={48} className="mx-auto text-gray-500 mb-4" />
                <p className="text-gray-400">Profile information not available.</p>
            </div>
        )}
    </div>
);

// Goals Section Component - Simplified for V3.7
const GoalsSection = ({ goals, onRefresh }) => (
    <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
        <div className="flex justify-between items-center mb-6">
            <div>
                <h2 className="text-2xl font-semibold text-white">My Development Goals</h2>
                <p className="text-gray-400 text-sm mt-1">
                    Set specific, measurable goals for your professional growth
                </p>
            </div>
            <button
                onClick={() => alert('Goal creation coming in next update!')}
                className="bg-cyan-500 hover:bg-cyan-600 text-white font-bold py-2 px-4 rounded-lg flex items-center"
            >
                <Plus size={18} className="mr-2" />
                Add Goal
            </button>
        </div>

        {goals.length === 0 ? (
            <div className="text-center py-12">
                <Target size={48} className="mx-auto text-gray-500 mb-4" />
                <h3 className="text-white text-lg mb-2">No development goals yet</h3>
                <p className="text-gray-400 mb-6 max-w-md mx-auto">
                    Development goals help you focus your learning and career growth. 
                    Start by setting 1-3 specific goals for the next quarter.
                </p>
                <button
                    onClick={() => alert('Goal creation coming in next update!')}
                    className="bg-cyan-500 hover:bg-cyan-600 text-white font-bold py-3 px-6 rounded-lg"
                >
                    Set My First Goal
                </button>
            </div>
        ) : (
            <div className="space-y-4">
                {goals.map(goal => (
                    <div key={goal.goal_id} className="bg-gray-700 p-4 rounded-lg">
                        <h3 className="text-lg font-semibold text-white">{goal.title}</h3>
                        <p className="text-gray-400 capitalize">{goal.goal_type.replace('_', ' ')}</p>
                        {goal.description && <p className="text-gray-300 mt-2">{goal.description}</p>}
                    </div>
                ))}
            </div>
        )}
    </div>
);

// Training Section Component - Simplified for V3.7
const TrainingSection = ({ requests, onRefresh }) => (
    <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
        <div className="flex justify-between items-center mb-6">
