);

export default MyDevelopmentCenter;

// src/components/pages/MyDevelopmentCenterEnhanced.js - Enhanced with development plan submission workflow
import React, { useState, useEffect } from 'react';
import { 
  Target, 
  Plus, 
  Calendar, 
  User, 
  BookOpen, 
  TrendingUp, 
  Save, 
  X,
  CheckCircle,
  Clock,
  MessageSquare,
  Award,
  Lightbulb,
  Edit3,
  AlertCircle,
  Check,
  MessageCircle
} from 'lucide-react';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button, StatusBadge } from '../ui';
import { formatDate } from '../../utils';
import NotificationService from '../../services/NotificationService';

export default function MyDevelopmentCenterEnhanced() {
  const { userName, userRole, user } = useApp();
  const [activeTab, setActiveTab] = useState('overview');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [developmentPlans, setDevelopmentPlans] = useState([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  
  // New state for enhanced functionality
  const [editingPlan, setEditingPlan] = useState(null);
  const [showCommentModal, setShowCommentModal] = useState(false);
  const [currentCompletionItem, setCurrentCompletionItem] = useState(null);
  const [completionComment, setCompletionComment] = useState('');
  const [managerReplyMode, setManagerReplyMode] = useState(null); // planId when in reply mode
  const [managerReply, setManagerReply] = useState('');
  
  const [newPlan, setNewPlan] = useState({
    title: '',
    description: '',
    goals: [{ goal: '', timeline: '', priority: 'medium' }],
    skills_to_develop: [{ skill: '', reason: '', method: '' }],
    timeline: '3-6 months'
  });

  // Fetch development plans on component mount
  useEffect(() => {
    fetchDevelopmentPlans();
  }, []);

  const fetchDevelopmentPlans = async () => {
    try {
      setLoading(true);
      setError(null);
      const plans = await NotificationService.getDevelopmentPlans();
      setDevelopmentPlans(plans);
    } catch (err) {
      console.error('Error fetching development plans:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitPlan = async () => {
    try {
      setSubmitting(true);
      setError(null);
      
      // Validate required fields
      if (!newPlan.title.trim()) {
        throw new Error('Plan title is required');
      }
      if (newPlan.goals.length === 0 || !newPlan.goals[0].goal.trim()) {
        throw new Error('At least one goal is required');
      }

      await NotificationService.submitDevelopmentPlan(newPlan);
      
      // Reset form and refresh plans
      setNewPlan({
        title: '',
        description: '',
        goals: [{ goal: '', timeline: '', priority: 'medium' }],
        skills_to_develop: [{ skill: '', reason: '', method: '' }],
        timeline: '3-6 months'
      });
      setShowCreateForm(false);
      await fetchDevelopmentPlans();
      
    } catch (err) {
      console.error('Error submitting development plan:', err);
      setError(err.message);
    } finally {
      setSubmitting(false);
    }
  };

  const addGoal = () => {
    setNewPlan(prev => ({
      ...prev,
      goals: [...prev.goals, { goal: '', timeline: '', priority: 'medium' }]
    }));
  };

  const removeGoal = (index) => {
    setNewPlan(prev => ({
      ...prev,
      goals: prev.goals.filter((_, i) => i !== index)
    }));
  };

  const updateGoal = (index, field, value) => {
    setNewPlan(prev => ({
      ...prev,
      goals: prev.goals.map((goal, i) => 
        i === index ? { ...goal, [field]: value } : goal
      )
    }));
  };

  const addSkill = () => {
    setNewPlan(prev => ({
      ...prev,
      skills_to_develop: [...prev.skills_to_develop, { skill: '', reason: '', method: '' }]
    }));
  };

  const removeSkill = (index) => {
    setNewPlan(prev => ({
      ...prev,
      skills_to_develop: prev.skills_to_develop.filter((_, i) => i !== index)
    }));
  };

  const updateSkill = (index, field, value) => {
    setNewPlan(prev => ({
      ...prev,
      skills_to_develop: prev.skills_to_develop.map((skill, i) => 
        i === index ? { ...skill, [field]: value } : skill
      )
    }));
  };

  const getStatusColor = (status) => {
    const colors = {
      'draft': 'gray',
      'submitted': 'blue',
      'under_review': 'yellow',
      'approved': 'green',
      'needs_revision': 'red'
    };
    return colors[status] || 'gray';
  };

  // New functions for enhanced functionality
  const canEditPlan = (plan) => {
    // Only managers can edit plans, and only if they're the manager of the employee who created it
    return userRole === 'manager' || userRole === 'admin';
  };

  const canRequestModification = (plan) => {
    // Employees can request modifications to their own plans
    return userRole === 'employee' && plan.created_by === user?.id;
  };

  const handleRequestModification = async (planId) => {
    try {
      // Create a notification for the manager
      await NotificationService.createNotification({
        recipient_id: developmentPlans.find(p => p.id === planId)?.manager_id,
        title: 'Development Plan Modification Request',
        message: `${userName} has requested modifications to their development plan`,
        type: 'plan_modification_request',
        metadata: { plan_id: planId, employee_name: userName }
      });
      
      // Show success message
      alert('Modification request sent to your manager');
    } catch (error) {
      console.error('Error requesting modification:', error);
      alert('Failed to send modification request');
    }
  };

  const handleGoalSkillCompletion = (planId, itemType, itemIndex, isCompleted) => {
    if (isCompleted) {
      // Open comment modal
      setCurrentCompletionItem({ planId, itemType, itemIndex });
      setShowCommentModal(true);
    } else {
      // Mark as incomplete directly
      updatePlanProgress(planId, itemType, itemIndex, false, '');
    }
  };

  const updatePlanProgress = async (planId, itemType, itemIndex, isCompleted, comment = '') => {
    try {
      const planIndex = developmentPlans.findIndex(p => p.id === planId);
      if (planIndex === -1) return;

      const updatedPlans = [...developmentPlans];
      const plan = updatedPlans[planIndex];
      
      // Update the specific goal or skill
      if (itemType === 'goal') {
        if (!plan.goals[itemIndex].progress) {
          plan.goals[itemIndex].progress = {};
        }
        plan.goals[itemIndex].progress.completed = isCompleted;
        plan.goals[itemIndex].progress.completion_comment = comment;
        plan.goals[itemIndex].progress.completed_at = isCompleted ? new Date().toISOString() : null;
      } else if (itemType === 'skill') {
        if (!plan.skills_to_develop[itemIndex].progress) {
          plan.skills_to_develop[itemIndex].progress = {};
        }
        plan.skills_to_develop[itemIndex].progress.completed = isCompleted;
        plan.skills_to_develop[itemIndex].progress.completion_comment = comment;
        plan.skills_to_develop[itemIndex].progress.completed_at = isCompleted ? new Date().toISOString() : null;
      }

      setDevelopmentPlans(updatedPlans);

      // If marking as complete, notify manager (if they have one)
      if (isCompleted && plan.manager_id) {
        try {
          await NotificationService.createNotification({
            recipient_id: plan.manager_id,
            title: 'Development Plan Progress Update',
            message: `${userName} has completed a ${itemType} in their development plan: "${comment}"`,
            type: 'plan_progress_update',
            metadata: { 
              plan_id: planId, 
              item_type: itemType, 
              item_index: itemIndex,
              employee_name: userName,
              comment: comment
            }
          });
          console.log(' Manager notification sent successfully');
        } catch (notificationError) {
          console.error(' Failed to notify manager:', notificationError);
          // Don't fail the entire operation if notification fails
        }
      } else if (isCompleted && !plan.manager_id) {
        console.log(' No manager assigned - skipping manager notification');
      }

      // TODO: Save to backend
      console.log('Plan progress updated:', { planId, itemType, itemIndex, isCompleted, comment });

    } catch (error) {
      console.error('Error updating plan progress:', error);
      alert('Failed to update progress');
    }
  };

  const submitCompletionComment = async () => {
    if (!currentCompletionItem || !completionComment.trim()) {
      alert('Please enter a comment about your completion');
      return;
    }

    const { planId, itemType, itemIndex } = currentCompletionItem;
    await updatePlanProgress(planId, itemType, itemIndex, true, completionComment);
    
    // Reset modal state
    setShowCommentModal(false);
    setCurrentCompletionItem(null);
    setCompletionComment('');
  };

  const submitManagerReply = async (planId) => {
    if (!managerReply.trim()) {
      alert('Please enter a reply comment');
      return;
    }

    try {
      const planIndex = developmentPlans.findIndex(p => p.id === planId);
      if (planIndex === -1) return;

      const updatedPlans = [...developmentPlans];
      const plan = updatedPlans[planIndex];
      
      // Add manager reply to the plan
      if (!plan.manager_replies) {
        plan.manager_replies = [];
      }
      
      plan.manager_replies.push({
        reply: managerReply,
        replied_at: new Date().toISOString(),
        replied_by: userName
      });

      setDevelopmentPlans(updatedPlans);

      // Notify the employee
      await NotificationService.createNotification({
        recipient_id: plan.created_by,
        title: 'Manager Response to Development Plan',
        message: `Your manager has responded to your development plan progress`,
        type: 'plan_manager_response',
        metadata: { 
          plan_id: planId, 
          manager_name: userName,
          reply: managerReply
        }
      });

      // Reset reply state
      setManagerReplyMode(null);
      setManagerReply('');

      // TODO: Save to backend
      console.log('Manager reply submitted:', { planId, reply: managerReply });

    } catch (error) {
      console.error('Error submitting manager reply:', error);
      alert('Failed to submit reply');
    }
  };

  const tabs = [
    { id: 'overview', name: 'Overview', icon: Target },
    { id: 'plans', name: 'My Development Plans', icon: BookOpen },
    { id: 'create', name: 'Create New Plan', icon: Plus }
  ];

  if (loading) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Development Center</h1>
        <LoadingSpinner size="lg" message="Loading your development center..." />
      </div>
    );
  }

  return (
    <div className="p-8">
      {/* Header */}
      <div className="mb-8">
        <div>
          <h1 className="text-3xl font-bold text-cyan-400">My Development Center</h1>
          <p className="text-gray-400 mt-2">Plan and track your professional development journey</p>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <div className="mb-6">
          <ErrorMessage 
            error={error} 
            title="Development Center Error" 
            onRetry={() => setError(null)}
          />
        </div>
      )}

      {/* Tab Navigation */}
      <div className="border-b border-gray-700 mb-6">
        <nav className="-mb-px flex space-x-8">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center py-4 px-1 border-b-2 font-medium text-sm ${
                activeTab === tab.id
                  ? 'border-cyan-500 text-cyan-400'
                  : 'border-transparent text-gray-400 hover:text-gray-300 hover:border-gray-300'
              }`}
            >
              <tab.icon className="mr-2" size={16} />
              {tab.name}
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      {activeTab === 'overview' && (
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
              <div className="flex items-center">
                <BookOpen className="text-blue-400 mr-3" size={24} />
                <div>
                  <p className="text-2xl font-bold text-white">{developmentPlans.length}</p>
                  <p className="text-gray-400 text-sm">Development Plans</p>
                </div>
              </div>
            </div>
            
            <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
              <div className="flex items-center">
                <CheckCircle className="text-green-400 mr-3" size={24} />
                <div>
                  <p className="text-2xl font-bold text-white">
                    {developmentPlans.filter(p => p.status === 'approved').length}
                  </p>
                  <p className="text-gray-400 text-sm">Approved Plans</p>
                </div>
              </div>
            </div>
            
            <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
              <div className="flex items-center">
                <Clock className="text-yellow-400 mr-3" size={24} />
                <div>
                  <p className="text-2xl font-bold text-white">
                    {developmentPlans.filter(p => p.status === 'under_review').length}
                  </p>
                  <p className="text-gray-400 text-sm">Under Review</p>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h2 className="text-xl font-semibold text-white mb-4 flex items-center">
              <Lightbulb className="mr-2 text-yellow-400" size={20} />
              Development Tips
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="bg-gray-700 rounded-lg p-4">
                <h3 className="font-medium text-cyan-400 mb-2">Set SMART Goals</h3>
                <p className="text-gray-300 text-sm">Make your goals Specific, Measurable, Achievable, Relevant, and Time-bound.</p>
              </div>
              <div className="bg-gray-700 rounded-lg p-4">
                <h3 className="font-medium text-cyan-400 mb-2">Regular Review</h3>
                <p className="text-gray-300 text-sm">Schedule regular check-ins with your manager to discuss progress and adjustments.</p>
              </div>
              <div className="bg-gray-700 rounded-lg p-4">
                <h3 className="font-medium text-cyan-400 mb-2">Skill Diversity</h3>
                <p className="text-gray-300 text-sm">Balance technical skills with soft skills for comprehensive growth.</p>
              </div>
              <div className="bg-gray-700 rounded-lg p-4">
                <h3 className="font-medium text-cyan-400 mb-2">Document Progress</h3>
                <p className="text-gray-300 text-sm">Keep track of your achievements and lessons learned along the way.</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'plans' && (
        <div className="space-y-6">
          <h2 className="text-xl font-semibold text-white">My Development Plans</h2>
          
          {developmentPlans.length === 0 ? (
            <div className="text-center py-12">
              <BookOpen size={64} className="mx-auto text-gray-500 mb-4" />
              <h3 className="text-xl font-semibold text-white mb-2">No Development Plans Yet</h3>
              <p className="text-gray-400 mb-6">Start your development journey by creating your first plan.</p>
              <Button 
                variant="primary"
                onClick={() => setActiveTab('create')}
              >
                <Plus size={16} className="mr-2" />
                Create Your First Plan
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {developmentPlans.map((plan) => (
                <div key={plan.id} className="bg-gray-800 rounded-lg p-6 border border-gray-700">
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex-1">
                      <h3 className="text-lg font-semibold text-white mb-2">{plan.title}</h3>
                      <p className="text-gray-400 mb-3">{plan.description}</p>
                      
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                        <div>
                          <p className="text-sm text-gray-400">Status</p>
                          <StatusBadge 
                            status={plan.status} 
                            color={getStatusColor(plan.status)}
                          />
                        </div>
                        <div>
                          <p className="text-sm text-gray-400">Timeline</p>
                          <p className="text-white">{plan.timeline}</p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-400">Created</p>
                          <p className="text-white">{formatDate(plan.created_at)}</p>
                        </div>
                      </div>

                      {/* Enhanced Goals with Progress Tracking - Hidden when editing */}
                      {!editingPlan && plan.goals && plan.goals.length > 0 && (
                        <div className="mb-4">
                          <p className="text-sm text-gray-400 mb-3">Development Goals ({plan.goals.length})</p>
                          <div className="space-y-3">
                            {plan.goals.map((goal, index) => (
                              <div key={index} className="bg-gray-700 rounded-lg p-3">
                                <div className="flex items-start space-x-3">
                                  <input
                                    type="checkbox"
                                    checked={goal.progress?.completed || false}
                                    onChange={(e) => handleGoalSkillCompletion(plan.id, 'goal', index, e.target.checked)}
                                    className="mt-1 h-4 w-4 text-cyan-500 bg-gray-600 border-gray-500 rounded focus:ring-cyan-500"
                                  />
                                  <div className="flex-1">
                                    <p className={`text-sm font-medium ${goal.progress?.completed ? 'text-green-400 line-through' : 'text-white'}`}>
                                      {goal.goal}
                                    </p>
                                    <p className="text-xs text-gray-400 mt-1">
                                      Priority: {goal.priority} | Timeline: {goal.timeline}
                                    </p>
                                    
                                    {goal.progress?.completed && goal.progress?.completion_comment && (
                                      <div className="mt-2 p-2 bg-gray-600 rounded text-xs">
                                        <p className="text-green-400 font-medium">Completion Note:</p>
                                        <p className="text-gray-300">{goal.progress.completion_comment}</p>
                                        <p className="text-gray-500 mt-1">
                                          Completed: {formatDate(goal.progress.completed_at)}
                                        </p>
                                      </div>
                                    )}
                                  </div>
                                </div>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {/* Enhanced Skills with Progress Tracking - Hidden when editing */}
                      {!editingPlan && plan.skills_to_develop && plan.skills_to_develop.length > 0 && (
                        <div className="mb-4">
                          <p className="text-sm text-gray-400 mb-3">Skills to Develop ({plan.skills_to_develop.length})</p>
                          <div className="space-y-3">
                            {plan.skills_to_develop.map((skill, index) => (
                              <div key={index} className="bg-gray-700 rounded-lg p-3">
                                <div className="flex items-start space-x-3">
                                  <input
                                    type="checkbox"
                                    checked={skill.progress?.completed || false}
                                    onChange={(e) => handleGoalSkillCompletion(plan.id, 'skill', index, e.target.checked)}
                                    className="mt-1 h-4 w-4 text-cyan-500 bg-gray-600 border-gray-500 rounded focus:ring-cyan-500"
                                  />
                                  <div className="flex-1">
                                    <p className={`text-sm font-medium ${skill.progress?.completed ? 'text-green-400 line-through' : 'text-white'}`}>
                                      {skill.skill}
                                    </p>
                                    <p className="text-xs text-gray-400 mt-1">
                                      Method: {skill.method}
                                    </p>
                                    
                                    {skill.progress?.completed && skill.progress?.completion_comment && (
                                      <div className="mt-2 p-2 bg-gray-600 rounded text-xs">
                                        <p className="text-green-400 font-medium">Completion Note:</p>
                                        <p className="text-gray-300">{skill.progress.completion_comment}</p>
                                        <p className="text-gray-500 mt-1">
                                          Completed: {formatDate(skill.progress.completed_at)}
                                        </p>
                                      </div>
                                    )}
                                  </div>
                                </div>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {/* Manager Feedback and Replies Section - Hidden when editing */}
                      {!editingPlan && plan.manager_feedback && (
                        <div className="bg-gray-700 rounded-lg p-3 mt-4">
                          <p className="text-sm text-gray-400 mb-1">Manager Feedback</p>
                          <p className="text-gray-300 text-sm">{plan.manager_feedback}</p>
                          <p className="text-xs text-gray-500 mt-1">
                            Reviewed on {formatDate(plan.manager_reviewed_at)}
                          </p>
                        </div>
                      )}

                      {/* Edit Plan Interface (when editing) */}
                      {editingPlan === plan.id && (
                        <div className="mt-4 bg-gray-700 rounded-lg p-4 border-2 border-cyan-500">
                          <h4 className="text-lg font-semibold text-white mb-4 flex items-center">
                            <Edit3 className="mr-2 text-cyan-400" size={18} />
                            Edit Development Plan
                          </h4>
                          
                          <div className="space-y-4">
                            {/* Edit Title */}
                            <div>
                              <label className="block text-sm font-medium text-gray-300 mb-2">Plan Title</label>
                              <input
                                type="text"
                                value={plan.title}
                                onChange={(e) => {
                                  const updatedPlans = developmentPlans.map(p => 
                                    p.id === plan.id ? { ...p, title: e.target.value } : p
                                  );
                                  setDevelopmentPlans(updatedPlans);
                                }}
                                className="w-full p-3 bg-gray-600 border border-gray-500 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                              />
                            </div>
                            
                            {/* Edit Description */}
                            <div>
                              <label className="block text-sm font-medium text-gray-300 mb-2">Description</label>
                              <textarea
                                value={plan.description || ''}
                                onChange={(e) => {
                                  const updatedPlans = developmentPlans.map(p => 
                                    p.id === plan.id ? { ...p, description: e.target.value } : p
                                  );
                                  setDevelopmentPlans(updatedPlans);
                                }}
                                rows={3}
                                className="w-full p-3 bg-gray-600 border border-gray-500 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                              />
                            </div>
                            
                            {/* Edit Goals */}
                            {plan.goals && plan.goals.length > 0 && (
                              <div>
                                <label className="block text-sm font-medium text-gray-300 mb-3">Goals</label>
                                <div className="space-y-2">
                                  {plan.goals.map((goal, goalIndex) => (
                                    <div key={goalIndex} className="bg-gray-600 rounded p-3">
                                      <input
                                        type="text"
                                        value={goal.goal || ''}
                                        onChange={(e) => {
                                          const updatedPlans = developmentPlans.map(p => {
                                            if (p.id === plan.id) {
                                              const updatedGoals = p.goals.map((g, i) => 
                                                i === goalIndex ? { ...g, goal: e.target.value } : g
                                              );
                                              return { ...p, goals: updatedGoals };
                                            }
                                            return p;
                                          });
                                          setDevelopmentPlans(updatedPlans);
                                        }}
                                        className="w-full p-2 bg-gray-500 border border-gray-400 rounded text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                                        placeholder="Goal description"
                                      />
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}
                            
                            {/* Save/Cancel Buttons */}
                            <div className="flex justify-end space-x-2 pt-4 border-t border-gray-600">
                              <Button
                                variant="secondary"
                                size="sm"
                                onClick={() => {
                                  setEditingPlan(null);
                                  fetchDevelopmentPlans(); // Refresh to undo changes
                                }}
                              >
                                Cancel
                              </Button>
                              <Button
                                variant="primary"
                                size="sm"
                                onClick={async () => {
                                  try {
                                    // Here you would typically save to backend
                                    // For now, just close the editor
                                    setEditingPlan(null);
                                    alert('Plan updated successfully (Note: Backend save not yet implemented)');
                                  } catch (error) {
                                    console.error('Error saving plan:', error);
                                    alert('Failed to save plan changes');
                                  }
                                }}
                              >
                                <Save size={14} className="mr-1" />
                                Save Changes
                              </Button>
                            </div>
                          </div>
                        </div>
                      )}

                      {/* Manager Replies */}
                      {plan.manager_replies && plan.manager_replies.length > 0 && (
                        <div className="mt-4 space-y-2">
                          {plan.manager_replies.map((reply, index) => (
                            <div key={index} className="bg-blue-900 bg-opacity-50 rounded-lg p-3 border-l-4 border-blue-400">
                              <div className="flex items-start justify-between mb-1">
                                <p className="text-sm text-blue-300 font-medium">Manager Response</p>
                                <p className="text-xs text-blue-400">{formatDate(reply.replied_at)}</p>
                              </div>
                              <p className="text-gray-300 text-sm">{reply.reply}</p>
                            </div>
                          ))}
                        </div>
                      )}

                      {/* Manager Reply Interface (for managers only) - Hidden when editing */}
                      {!editingPlan && userRole === 'manager' && (
                        <div className="mt-4">
                          {managerReplyMode === plan.id ? (
                            <div className="bg-gray-700 rounded-lg p-3">
                              <p className="text-sm text-gray-400 mb-2">Add your response:</p>
                              <textarea
                                value={managerReply}
                                onChange={(e) => setManagerReply(e.target.value)}
                                placeholder="Provide feedback, guidance, or acknowledgment of their progress..."
                                className="w-full h-24 p-3 bg-gray-600 border border-gray-500 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent resize-none"
                              />
                              <div className="flex justify-end space-x-2 mt-2">
                                <Button
                                  variant="secondary"
                                  size="sm"
                                  onClick={() => {
                                    setManagerReplyMode(null);
                                    setManagerReply('');
                                  }}
                                >
                                  Cancel
                                </Button>
                                <Button
                                  variant="primary"
                                  size="sm"
                                  onClick={() => submitManagerReply(plan.id)}
                                  disabled={!managerReply.trim()}
                                >
                                  <MessageCircle size={14} className="mr-1" />
                                  Send Response
                                </Button>
                              </div>
                            </div>
                          ) : (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => setManagerReplyMode(plan.id)}
                            >
                              <MessageCircle size={14} className="mr-1" />
                              Respond to Progress
                            </Button>
                          )}
                        </div>
                      )}
                    </div>
                    
                    {/* Action Buttons - Only show when not editing */}
                    {!editingPlan && (
                      <div className="flex space-x-2">
                        {canEditPlan(plan) && (
                          <Button
                            variant="secondary"
                            size="sm"
                            onClick={() => setEditingPlan(plan.id)}
                          >
                            <Edit3 size={14} className="mr-1" />
                            Edit Plan
                          </Button>
                        )}
                        {canRequestModification(plan) && (
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleRequestModification(plan.id)}
                          >
                            <AlertCircle size={14} className="mr-1" />
                            Request Modification
                          </Button>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {activeTab === 'create' && (
        <div className="space-y-6">
          <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
            <h2 className="text-xl font-semibold text-white mb-4">Create Development Plan</h2>
            
            <div className="space-y-6">
              {/* Basic Information */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    Plan Title *
                  </label>
                  <input
                    type="text"
                    value={newPlan.title}
                    onChange={(e) => setNewPlan(prev => ({ ...prev, title: e.target.value }))}
                    className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                    placeholder="e.g., Leadership Skills Development"
                    data-testid="development-plan-title-input"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    Timeline
                  </label>
                  <select
                    value={newPlan.timeline}
                    onChange={(e) => setNewPlan(prev => ({ ...prev, timeline: e.target.value }))}
                    className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                    data-testid="development-plan-timeline-select"
                  >
                    <option value="1-3 months">1-3 months</option>
                    <option value="3-6 months">3-6 months</option>
                    <option value="6-12 months">6-12 months</option>
                    <option value="1+ years">1+ years</option>
                  </select>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Description
                </label>
                <textarea
                  value={newPlan.description}
                  onChange={(e) => setNewPlan(prev => ({ ...prev, description: e.target.value }))}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                  rows={3}
                  placeholder="Describe your development objectives and what you hope to achieve..."
                  data-testid="development-plan-description-textarea"
                />
              </div>

              {/* Goals Section */}
              <div>
                <div className="flex items-center justify-between mb-4">
                  <label className="block text-sm font-medium text-gray-300">
                    Development Goals *
                  </label>
                  <Button variant="secondary" size="sm" onClick={addGoal}>
                    <Plus size={14} className="mr-1" />
                    Add Goal
                  </Button>
                </div>
                
                <div className="space-y-4">
                  {newPlan.goals.map((goal, index) => (
                    <div key={index} className="bg-gray-700 rounded-lg p-4">
                      <div className="flex items-start justify-between mb-3">
                        <span className="text-sm font-medium text-gray-300">Goal {index + 1}</span>
                        {newPlan.goals.length > 1 && (
                          <button
                            onClick={() => removeGoal(index)}
                            className="text-gray-400 hover:text-red-400 transition-colors"
                          >
                            <X size={16} />
                          </button>
                        )}
                      </div>
                      
                      <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                        <div className="lg:col-span-2">
                          <input
                            type="text"
                            value={goal.goal}
                            onChange={(e) => updateGoal(index, 'goal', e.target.value)}
                            className="w-full p-3 bg-gray-600 border border-gray-500 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                            placeholder="Describe your specific goal..."
                          />
                        </div>
                        <div>
                          <input
                            type="text"
                            value={goal.timeline}
                            onChange={(e) => updateGoal(index, 'timeline', e.target.value)}
                            className="w-full p-3 bg-gray-600 border border-gray-500 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                            placeholder="Timeline (e.g., 3 months)"
                          />
                        </div>
                        <div>
                          <select
                            value={goal.priority}
                            onChange={(e) => updateGoal(index, 'priority', e.target.value)}
                            className="w-full p-3 bg-gray-600 border border-gray-500 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                          >
                            <option value="low">Low Priority</option>
                            <option value="medium">Medium Priority</option>
                            <option value="high">High Priority</option>
                          </select>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Skills Section */}
              <div>
                <div className="flex items-center justify-between mb-4">
                  <label className="block text-sm font-medium text-gray-300">
                    Skills to Develop
                  </label>
                  <Button variant="secondary" size="sm" onClick={addSkill}>
                    <Plus size={14} className="mr-1" />
                    Add Skill
                  </Button>
                </div>
                
                <div className="space-y-4">
                  {newPlan.skills_to_develop.map((skill, index) => (
                    <div key={index} className="bg-gray-700 rounded-lg p-4">
                      <div className="flex items-start justify-between mb-3">
                        <span className="text-sm font-medium text-gray-300">Skill {index + 1}</span>
                        <button
                          onClick={() => removeSkill(index)}
                          className="text-gray-400 hover:text-red-400 transition-colors"
                        >
                          <X size={16} />
                        </button>
                      </div>
                      
                      <div className="space-y-3">
                        <input
                          type="text"
                          value={skill.skill}
                          onChange={(e) => updateSkill(index, 'skill', e.target.value)}
                          className="w-full p-3 bg-gray-600 border border-gray-500 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                          placeholder="Skill name (e.g., Public Speaking)"
                        />
                        <input
                          type="text"
                          value={skill.reason}
                          onChange={(e) => updateSkill(index, 'reason', e.target.value)}
                          className="w-full p-3 bg-gray-600 border border-gray-500 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                          placeholder="Why is this skill important for your role?"
                        />
                        <input
                          type="text"
                          value={skill.method}
                          onChange={(e) => updateSkill(index, 'method', e.target.value)}
                          className="w-full p-3 bg-gray-600 border border-gray-500 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                          placeholder="How will you develop this skill?"
                        />
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex justify-end space-x-3">
                <Button 
                  variant="secondary"
                  onClick={() => {
                    setNewPlan({
                      title: '',
                      description: '',
                      goals: [{ goal: '', timeline: '', priority: 'medium' }],
                      skills_to_develop: [{ skill: '', reason: '', method: '' }],
                      timeline: '3-6 months'
                    });
                    setActiveTab('plans');
                  }}
                >
                  Cancel
                </Button>
                <Button 
                  variant="primary"
                  onClick={handleSubmitPlan}
                  disabled={submitting}
                  data-testid="submit-development-plan-button"
                >
                  <Save size={16} className="mr-2" />
                  {submitting ? 'Submitting...' : 'Submit Plan'}
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Completion Comment Modal */}
      {showCommentModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-gray-800 rounded-lg p-6 w-full max-w-md border border-gray-700">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-white flex items-center">
                <Check className="mr-2 text-green-400" size={20} />
                Mark as Complete
              </h3>
              <button
                onClick={() => {
                  setShowCommentModal(false);
                  setCurrentCompletionItem(null);
                  setCompletionComment('');
                }}
                className="text-gray-400 hover:text-white"
              >
                <X size={20} />
              </button>
            </div>

            <p className="text-gray-300 mb-4">
              Please add a comment about what you accomplished and how you completed this {currentCompletionItem?.itemType}:
            </p>

            <textarea
              value={completionComment}
              onChange={(e) => setCompletionComment(e.target.value)}
              placeholder="Describe what you learned, what you accomplished, or how you completed this item..."
              className="w-full h-32 p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent resize-none"
              autoFocus
            />

            <div className="flex justify-end space-x-3 mt-4">
              <Button
                variant="secondary"
                onClick={() => {
                  setShowCommentModal(false);
                  setCurrentCompletionItem(null);
                  setCompletionComment('');
                }}
              >
                Cancel
              </Button>
              <Button
                variant="primary"
                onClick={submitCompletionComment}
                disabled={!completionComment.trim()}
              >
                <Check size={16} className="mr-2" />
                Mark Complete
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// STEP 5: Enhanced MyReviews Page - Replace src/components/pages/MyReviews.js
// This shows better status information like V4 had

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Calendar, Clock, CheckCircle, Award, ArrowRight, Filter } from 'lucide-react';
import { useAssessments } from '../../hooks';
import { useApp } from '../../contexts';
import { getStatusDisplay, filterActiveReviews, filterCompletedReviews, formatDate } from '../../utils';

export default function MyReviews() {
  const navigate = useNavigate();
  const { assessments: reviews, loading, error, refresh } = useAssessments();
  
  const [filter, setFilter] = useState('active'); // Default to active

  const getStatusInfo = (review) => {
    return getStatusDisplay(review);
  };

  const activeCount = filterActiveReviews(reviews).length;
  const completedCount = filterCompletedReviews(reviews).length;

  // Auto-switch to completed if no active reviews
  useEffect(() => {
    if (!loading && activeCount === 0 && completedCount > 0 && filter === 'active') {
      setFilter('completed');
    }
  }, [loading, activeCount, completedCount, filter]);

  const filteredReviews = reviews.filter(review => {
    const statusInfo = getStatusDisplay(review);
    if (filter === 'active') return statusInfo.isActive;
    if (filter === 'completed') return !statusInfo.isActive;
    return true;
  });

  if (loading) {
    return (
      <div className="p-8">
        <div className="text-center py-12">
          <div className="text-yellow-400 text-lg">Loading your reviews...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <div className="text-center py-12">
          <div className="text-red-400 text-lg">Error: {error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-8">
      <header className="mb-8">
        <h1 className="text-4xl font-bold text-white">My Review History</h1>
        <p className="text-gray-400 mt-2">Access your current and past performance reviews.</p>
      </header>

      {/* Summary Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Total Reviews</p>
              <p className="text-2xl font-bold text-cyan-400">{reviews.length}</p>
            </div>
            <Calendar className="text-cyan-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Active Reviews</p>
              <p className="text-2xl font-bold text-yellow-400">{activeCount}</p>
            </div>
            <Clock className="text-yellow-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Completed</p>
              <p className="text-2xl font-bold text-green-400">{completedCount}</p>
            </div>
            <CheckCircle className="text-green-400" size={24} />
          </div>
        </div>
      </div>

      {/* Filter Tabs */}
      <div className="mb-6">
        <div className="border-b border-gray-700">
          <nav className="-mb-px flex space-x-8">
            {[
              { id: 'active', name: 'Active', count: activeCount },
              { id: 'completed', name: 'Completed', count: completedCount }
            ].map(tab => (
              <button
                key={tab.id}
                onClick={() => setFilter(tab.id)}
                className={`py-2 px-1 border-b-2 font-medium text-sm flex items-center ${
                  filter === tab.id
                    ? 'border-cyan-500 text-cyan-400'
                    : 'border-transparent text-gray-400 hover:text-gray-300 hover:border-gray-300'
                }`}
              >
                <Filter size={16} className="mr-2" />
                {tab.name} ({tab.count})
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Reviews List */}
      <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
        {filteredReviews.length > 0 ? (
          <div className="space-y-4">
            {filteredReviews.map(review => {
              const statusInfo = getStatusInfo(review);
              const StatusIcon = statusInfo.icon;
              
              
              return (
                <div key={review.id || review.assessment_id} className="bg-gray-700 p-4 rounded-lg hover:bg-gray-600 transition">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-2">
                        <h3 className="text-lg font-semibold text-white">
                          {review.cycle_name || review.review_cycle_name || review.name || 'Unnamed Review'}
                        </h3>
                        <span className={`px-2 py-1 text-xs rounded-full ${statusInfo.bgColor} text-white flex items-center`}>
                          <StatusIcon size={12} className="mr-1" />
                          {statusInfo.label}
                        </span>
                      </div>
                      
                      <div className="text-sm text-gray-400 space-y-1">
                        {review.review_period && (
                          <p>Review Period: {review.review_period}</p>
                        )}
                        <p>Created: {new Date(review.created_at).toLocaleDateString()}</p>
                        {review.due_date && (
                          <p>Due: {new Date(review.due_date).toLocaleDateString()}</p>
                        )}
                        
                        {/* Show action needed for active reviews */}
                        {statusInfo.isActive && (
                          <p className={`font-medium ${statusInfo.color}`}>
                            {review.self_assessment_status === 'not_started' && 'Action needed: Start your self-assessment'}
                            {review.self_assessment_status === 'in_progress' && 'Action needed: Complete your self-assessment'}
                            {review.self_assessment_status === 'employee_complete' && review.manager_review_status === 'pending' && 'Waiting for manager review'}
                            {review.self_assessment_status === 'employee_complete' && review.manager_review_status === 'completed' && !review.employee_acknowledged_at && 'Manager review complete - please acknowledge'}
                            {review.self_assessment_status === 'employee_complete' && review.manager_review_status === 'completed' && review.employee_acknowledged_at && 'Review process complete'}
                            {review.self_assessment_status === 'manager_complete' && 'Review complete - view feedback'}
                          </p>
                        )}
                      </div>
                    </div>
                    
                    <button
                      onClick={() => {
                        navigate(`/assessment/${review.id || review.assessment_id}`);
                      }}
                      className="flex items-center text-cyan-400 hover:text-cyan-300 transition"
                    >
                      View Details 
                      <ArrowRight size={16} className="ml-2" />
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="text-center py-12">
            <Calendar size={48} className="mx-auto text-gray-500 mb-4" />
            <h3 className="text-white text-lg mb-2">
              {filter === 'all' ? 'No reviews found' : 
               filter === 'active' ? 'No active reviews' : 'No completed reviews'}
            </h3>
            <p className="text-gray-400">
              {filter === 'all' ? 'Your review history will appear here once cycles are created.' :
               filter === 'active' ? 'No reviews are currently in progress.' : 
               'Completed reviews will appear here.'}
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

// src/components/pages/MyTeam.js - Enhanced with Error Handling
import React from 'react';
import { Play, Users, Calendar, RefreshCw, AlertTriangle } from 'lucide-react';
import { useTeam } from '../../hooks';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button } from '../ui';

export default function MyTeam() {
  const { openModal, setActivePage } = useApp();
  const { 
    team, 
    teamAssessments, 
    loading, 
    error, 
    startReviewCycle, 
    refresh 
  } = useTeam();
  
  // For compatibility, use team assessments as team status
  const teamStatus = teamAssessments;

  if (loading) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Team</h1>
        <LoadingSpinner size="lg" message="Loading your team..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Team</h1>
        <ErrorMessage 
          error={error} 
          title="Error Loading Team" 
          onRetry={refresh}
        />
      </div>
    );
  }

  return (
    <div className="p-8">
      <div className="flex items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-cyan-400">My Team</h1>
          <p className="text-gray-400 mt-2">Manage your direct reports and their assessments</p>
        </div>
        <div className="flex items-center space-x-4">
          <button
            onClick={refresh}
            className="bg-gray-700 hover:bg-gray-600 text-white px-4 py-2 rounded-lg flex items-center"
          >
            <RefreshCw size={16} className="mr-2" />
            Refresh
          </button>
          {openModal && (
            <button
              onClick={() => openModal('startReviewCycle', { afterSave: refresh })}
              className="bg-cyan-500 hover:bg-cyan-600 text-white px-4 py-2 rounded-lg flex items-center"
            >
              <Play size={16} className="mr-2" />
              Start Review Cycle
            </button>
          )}
        </div>
      </div>

      {/* Team Summary Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Team Members</p>
              <p className="text-2xl font-bold text-cyan-400">{teamStatus.length}</p>
            </div>
            <Users className="text-cyan-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Active Assessments</p>
              <p className="text-2xl font-bold text-green-400">
                {teamStatus.filter(member => member.assessment_id).length}
              </p>
            </div>
            <Calendar className="text-green-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Completion Rate</p>
              <p className="text-2xl font-bold text-purple-400">
                {teamStatus.length > 0 
                  ? Math.round((teamStatus.filter(m => m.assessment_status === 'completed').length / teamStatus.length) * 100)
                  : 0}%
              </p>
            </div>
            <AlertTriangle className="text-purple-400" size={24} />
          </div>
        </div>
      </div>

      {/* Team Table */}
      <div className="bg-gray-800 rounded-lg shadow-2xl p-6">
        <h2 className="text-xl font-semibold text-white mb-4">Team Members</h2>
        
        {teamStatus.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="min-w-full text-sm">
              <thead className="bg-gray-700 text-gray-300">
                <tr>
                  <th className="px-4 py-3 text-left">Employee</th>
                  <th className="px-4 py-3 text-left">Job Title</th>
                  <th className="px-4 py-3 text-left">Assessment Status</th>
                  <th className="px-4 py-3 text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {teamStatus.map((member) => (
                  <tr key={member.employee_id} className="border-b border-gray-700 hover:bg-gray-700 transition">
                    <td className="px-4 py-3">
                      <div>
                        <p className="font-medium text-white">{member.employee_name}</p>
                        <p className="text-xs text-gray-400">ID: {member.employee_id.slice(0, 8)}...</p>
                      </div>
                    </td>
                    <td className="px-4 py-3 text-gray-300">
                      {member.job_title || 'No Title'}
                    </td>
                    <td className="px-4 py-3">
                      {member.assessment_status ? (
                        <span className={`px-2 py-1 text-xs rounded-full ${
                          member.assessment_status === 'completed' ? 'bg-green-600 text-green-100' :
                          member.assessment_status === 'in_progress' ? 'bg-yellow-600 text-yellow-100' :
                          'bg-gray-600 text-gray-100'
                        }`}>
                          {member.assessment_status.replace('_', ' ')}
                        </span>
                      ) : (
                        <span className="text-gray-500">No Assessment</span>
                      )}
                    </td>
                    <td className="px-4 py-3 text-center">
                      {member.assessment_id ? (
                        <button
                          onClick={() => setActivePage({ 
                            name: 'Assessment', 
                            props: { assessmentId: member.assessment_id } 
                          })}
                          className="text-cyan-400 hover:text-cyan-300 font-medium"
                        >
                          View Assessment
                        </button>
                      ) : (
                        <span className="text-gray-500"></span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="text-center py-12">
            <Users size={48} className="mx-auto text-gray-500 mb-4" />
            <h3 className="text-white text-lg mb-2">No Team Members Found</h3>
            <div className="text-gray-400 space-y-1">
              <p>This could mean:</p>
              <ul className="text-sm text-gray-500 mt-2">
                <li> You don't have any direct reports assigned</li>
                <li> You're not set up as a manager in the system</li>
                <li> No active review cycles have been started</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// src/components/pages/MyTeamConsolidated.js - Comprehensive single-page team view
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Users, 
  Calendar, 
  Clock, 
  CheckCircle, 
  AlertTriangle, 
  Eye, 
  Edit, 
  MessageSquare,
  FileText,
  Target,
  Award,
  Save,
  RefreshCw,
  Play,
  Star,
  TrendingUp,
  AlertCircle,
  User,
  Mail,
  Phone,
  MapPin,
  Briefcase,
  Hash
} from 'lucide-react';
import { useTeam } from '../../hooks';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button, StatusBadge } from '../ui';
import { formatDate } from '../../utils';
import NotificationService from '../../services/NotificationService';

export default function MyTeamConsolidated() {
  const navigate = useNavigate();
  const { openModal, setActivePage } = useApp();
  const { 
    team, 
    teamAssessments, 
    loading, 
    error, 
    refresh 
  } = useTeam();
  
  const [developmentPlans, setDevelopmentPlans] = useState([]);
  const [employeePerformance, setEmployeePerformance] = useState({});
  const [expandedEmployees, setExpandedEmployees] = useState(new Set());

  // Process all team data for comprehensive view
  useEffect(() => {
    if (teamAssessments) {
      // Group assessments by employee
      const performanceData = {};
      teamAssessments.forEach(assessment => {
        if (!performanceData[assessment.employee_id]) {
          performanceData[assessment.employee_id] = {
            assessments: [],
            pending_reviews: 0,
            completed_reviews: 0,
            latest_assessment: null,
            performance_trend: 'stable'
          };
        }
        
        performanceData[assessment.employee_id].assessments.push(assessment);
        
        if (assessment.self_assessment_status === 'employee_complete' && 
            assessment.manager_review_status === 'pending') {
          performanceData[assessment.employee_id].pending_reviews++;
        }
        
        if (assessment.manager_review_status === 'completed') {
          performanceData[assessment.employee_id].completed_reviews++;
        }
        
        // Track latest assessment
        if (!performanceData[assessment.employee_id].latest_assessment ||
            new Date(assessment.created_at) > new Date(performanceData[assessment.employee_id].latest_assessment.created_at)) {
          performanceData[assessment.employee_id].latest_assessment = assessment;
        }
      });
      
      setEmployeePerformance(performanceData);
    }
  }, [teamAssessments]);

  const toggleEmployeeExpansion = (employeeId) => {
    const newExpanded = new Set(expandedEmployees);
    if (newExpanded.has(employeeId)) {
      newExpanded.delete(employeeId);
    } else {
      newExpanded.add(employeeId);
    }
    setExpandedEmployees(newExpanded);
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'bg-green-600 text-green-100 border-green-500';
      case 'employee_complete': return 'bg-yellow-600 text-yellow-100 border-yellow-500';
      case 'in_progress': return 'bg-blue-600 text-blue-100 border-blue-500';
      case 'pending': return 'bg-orange-600 text-orange-100 border-orange-500';
      case 'not_started': return 'bg-gray-600 text-gray-100 border-gray-500';
      default: return 'bg-gray-600 text-gray-100 border-gray-500';
    }
  };

  const getPriorityIcon = (pendingCount) => {
    if (pendingCount > 2) return <AlertCircle className="text-red-400" size={16} />;
    if (pendingCount > 0) return <Clock className="text-yellow-400" size={16} />;
    return <CheckCircle className="text-green-400" size={16} />;
  };

  const calculateCompletionRate = (employeeId) => {
    const performance = employeePerformance[employeeId];
    if (!performance || performance.assessments.length === 0) return 0;
    
    const completedCount = performance.assessments.filter(
      a => a.manager_review_status === 'completed'
    ).length;
    
    return Math.round((completedCount / performance.assessments.length) * 100);
  };

  if (loading) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Team</h1>
        <LoadingSpinner size="lg" message="Loading your team..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Team</h1>
        <ErrorMessage 
          error={error} 
          title="Error Loading Team" 
          onRetry={refresh}
        />
      </div>
    );
  }

  // Get unique team members from assessments
  const teamMembers = teamAssessments.reduce((acc, assessment) => {
    if (!acc.find(member => member.employee_id === assessment.employee_id)) {
      acc.push({
        employee_id: assessment.employee_id,
        employee_name: assessment.employee_name,
        job_title: assessment.job_title,
        department: assessment.department,
        email: assessment.email || `${assessment.employee_name.toLowerCase().replace(' ', '.')}@company.com`,
        hire_date: assessment.hire_date,
        manager_id: assessment.manager_id
      });
    }
    return acc;
  }, []);

  const totalPending = Object.values(employeePerformance).reduce(
    (sum, perf) => sum + perf.pending_reviews, 0
  );
  
  const totalCompleted = Object.values(employeePerformance).reduce(
    (sum, perf) => sum + perf.completed_reviews, 0
  );

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-cyan-400">My Team</h1>
          <p className="text-gray-400 mt-2">Comprehensive team management and review oversight</p>
        </div>
        <div className="flex items-center space-x-3">
          <button
            onClick={refresh}
            className="bg-gray-700 hover:bg-gray-600 text-white px-4 py-2 rounded-lg flex items-center"
          >
            <RefreshCw size={16} className="mr-2" />
            Refresh
          </button>
          <button
            onClick={() => openModal('startReviewCycle', { afterSave: refresh })}
            className="bg-cyan-500 hover:bg-cyan-600 text-white px-4 py-2 rounded-lg flex items-center"
          >
            <Play size={16} className="mr-2" />
            Start Review Cycle
          </button>
        </div>
      </div>

      {/* Team Summary Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Team Members</p>
              <p className="text-2xl font-bold text-cyan-400">{teamMembers.length}</p>
            </div>
            <Users className="text-cyan-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Pending Reviews</p>
              <p className="text-2xl font-bold text-yellow-400">{totalPending}</p>
            </div>
            <Clock className="text-yellow-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Completed Reviews</p>
              <p className="text-2xl font-bold text-green-400">{totalCompleted}</p>
            </div>
            <CheckCircle className="text-green-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Avg Completion</p>
              <p className="text-2xl font-bold text-purple-400">
                {teamMembers.length > 0 
                  ? Math.round(teamMembers.reduce((sum, member) => 
                      sum + calculateCompletionRate(member.employee_id), 0) / teamMembers.length)
                  : 0}%
              </p>
            </div>
            <TrendingUp className="text-purple-400" size={24} />
          </div>
        </div>
      </div>

      {/* Employee Widgets */}
      <div className="space-y-4">
        <h2 className="text-xl font-semibold text-white flex items-center">
          <Users className="mr-2" size={20} />
          Team Members ({teamMembers.length})
        </h2>
        
        {teamMembers.length > 0 ? (
          <div className="grid grid-cols-1 gap-4">
            {teamMembers.map((member) => {
              const performance = employeePerformance[member.employee_id] || {};
              const isExpanded = expandedEmployees.has(member.employee_id);
              const completionRate = calculateCompletionRate(member.employee_id);
              const latestAssessment = performance.latest_assessment;
              
              return (
                <div key={member.employee_id} className="bg-gray-800 rounded-lg border border-gray-700 overflow-hidden">
                  {/* Employee Header */}
                  <div className="p-6">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gradient-to-br from-cyan-500 to-blue-600 rounded-full flex items-center justify-center">
                          <User className="text-white" size={20} />
                        </div>
                        <div>
                          <h3 className="text-lg font-semibold text-white">{member.employee_name}</h3>
                          <p className="text-gray-400">{member.job_title || 'No Title'}</p>
                          <p className="text-gray-500 text-sm">{member.department || 'No Department'}</p>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-4">
                        {/* Priority Indicator */}
                        <div className="flex items-center space-x-2">
                          {getPriorityIcon(performance.pending_reviews || 0)}
                          <span className="text-sm text-gray-400">
                            {performance.pending_reviews > 0 
                              ? `${performance.pending_reviews} pending` 
                              : 'Up to date'}
                          </span>
                        </div>

                        {/* Completion Rate */}
                        <div className="text-right">
                          <p className="text-sm text-gray-400">Completion Rate</p>
                          <p className={`text-lg font-bold ${
                            completionRate >= 80 ? 'text-green-400' :
                            completionRate >= 60 ? 'text-yellow-400' :
                            'text-red-400'
                          }`}>
                            {completionRate}%
                          </p>
                        </div>

                        {/* Expand Button */}
                        <button
                          onClick={() => toggleEmployeeExpansion(member.employee_id)}
                          className="p-2 text-gray-400 hover:text-white transition-colors"
                        >
                          <Eye size={20} />
                        </button>
                      </div>
                    </div>

                    {/* Status Row */}
                    <div className="mt-4 flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        {/* Current Status */}
                        {latestAssessment ? (
                          <div className="flex items-center space-x-2">
                            <span className="text-sm text-gray-400">Status:</span>
                            <span className={`px-2 py-1 text-xs rounded-full border ${
                              getStatusColor(latestAssessment.manager_review_status)
                            }`}>
                              {latestAssessment.manager_review_status === 'employee_complete' 
                                ? 'Ready for Review' 
                                : latestAssessment.manager_review_status?.replace('_', ' ') || 'Not Started'}
                            </span>
                          </div>
                        ) : (
                          <span className="text-sm text-gray-500">No active assessments</span>
                        )}

                        {/* Last Activity */}
                        {latestAssessment && (
                          <div className="flex items-center space-x-2 text-sm text-gray-400">
                            <Calendar size={14} />
                            <span>Last activity: {formatDate(latestAssessment.updated_at)}</span>
                          </div>
                        )}
                      </div>

                      {/* Quick Actions */}
                      <div className="flex items-center space-x-2">
                        {latestAssessment?.assessment_id && (
                          <button
                            onClick={() => setActivePage({ 
                              name: 'Assessment', 
                              props: { assessmentId: latestAssessment.assessment_id } 
                            })}
                            className="bg-cyan-600 hover:bg-cyan-700 text-white px-3 py-1 rounded text-sm flex items-center"
                          >
                            <Eye size={14} className="mr-1" />
                            Review
                          </button>
                        )}
                        
                        <button
                          onClick={() => openModal('giveFeedback', { 
                            employeeId: member.employee_id,
                            employeeName: member.employee_name 
                          })}
                          className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm flex items-center"
                        >
                          <MessageSquare size={14} className="mr-1" />
                          Feedback
                        </button>
                        
                        <button
                          onClick={() => openModal('giveKudo', { 
                            employeeId: member.employee_id,
                            employeeName: member.employee_name 
                          })}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm flex items-center"
                        >
                          <Star size={14} className="mr-1" />
                          Kudos
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Expanded Details */}
                  {isExpanded && (
                    <div className="border-t border-gray-700 bg-gray-750">
                      <div className="p-6 space-y-6">
                        {/* Contact Info */}
                        <div>
                          <h4 className="text-sm font-medium text-gray-300 mb-3">Contact Information</h4>
                          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                            <div className="flex items-center space-x-2">
                              <Mail className="text-gray-400" size={14} />
                              <span className="text-gray-300">{member.email}</span>
                            </div>
                            <div className="flex items-center space-x-2">
                              <Hash className="text-gray-400" size={14} />
                              <span className="text-gray-300">ID: {member.employee_id.slice(0, 8)}...</span>
                            </div>
                          </div>
                        </div>

                        {/* Assessment History */}
                        <div>
                          <h4 className="text-sm font-medium text-gray-300 mb-3">Assessment History</h4>
                          {performance.assessments && performance.assessments.length > 0 ? (
                            <div className="space-y-3">
                              {performance.assessments.slice(0, 3).map((assessment) => (
                                <div key={assessment.assessment_id} className="bg-gray-700 rounded-lg p-3">
                                  <div className="flex items-center justify-between">
                                    <div>
                                      <p className="text-white font-medium">{assessment.cycle_name}</p>
                                      <p className="text-gray-400 text-sm">
                                        Created: {formatDate(assessment.created_at)}
                                      </p>
                                    </div>
                                    <div className="flex items-center space-x-2">
                                      <span className={`px-2 py-1 text-xs rounded-full border ${
                                        getStatusColor(assessment.manager_review_status)
                                      }`}>
                                        {assessment.manager_review_status?.replace('_', ' ') || 'Not Started'}
                                      </span>
                                      <button
                                        onClick={() => setActivePage({ 
                                          name: 'Assessment', 
                                          props: { assessmentId: assessment.assessment_id } 
                                        })}
                                        className="text-cyan-400 hover:text-cyan-300 text-sm"
                                      >
                                        View
                                      </button>
                                    </div>
                                  </div>
                                </div>
                              ))}
                              {performance.assessments.length > 3 && (
                                <p className="text-gray-400 text-sm text-center">
                                  +{performance.assessments.length - 3} more assessments
                                </p>
                              )}
                            </div>
                          ) : (
                            <p className="text-gray-500 text-sm">No assessments found</p>
                          )}
                        </div>

                        {/* Performance Metrics */}
                        <div>
                          <h4 className="text-sm font-medium text-gray-300 mb-3">Performance Metrics</h4>
                          <div className="grid grid-cols-3 gap-4">
                            <div className="bg-gray-700 rounded-lg p-3 text-center">
                              <p className="text-2xl font-bold text-green-400">
                                {performance.completed_reviews || 0}
                              </p>
                              <p className="text-gray-400 text-xs">Completed</p>
                            </div>
                            <div className="bg-gray-700 rounded-lg p-3 text-center">
                              <p className="text-2xl font-bold text-yellow-400">
                                {performance.pending_reviews || 0}
                              </p>
                              <p className="text-gray-400 text-xs">Pending</p>
                            </div>
                            <div className="bg-gray-700 rounded-lg p-3 text-center">
                              <p className="text-2xl font-bold text-cyan-400">
                                {performance.assessments?.length || 0}
                              </p>
                              <p className="text-gray-400 text-xs">Total</p>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        ) : (
          <div className="text-center py-12 bg-gray-800 rounded-lg">
            <Users size={48} className="mx-auto text-gray-500 mb-4" />
            <h3 className="text-white text-lg mb-2">No Team Members Found</h3>
            <div className="text-gray-400 space-y-1">
              <p>This could mean:</p>
              <ul className="text-sm text-gray-500 mt-2">
                <li> You don't have any direct reports assigned</li>
                <li> You're not set up as a manager in the system</li>
                <li> No active review cycles have been started</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// src/components/pages/MyTeamEnhanced.js - Enhanced with pending reviews workflow
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Users, 
  Calendar, 
  Clock, 
  CheckCircle, 
  AlertTriangle, 
  Eye, 
  Edit, 
  MessageSquare,
  FileText,
  Target,
  Award,
  Save
} from 'lucide-react';
import { useTeam } from '../../hooks';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button, StatusBadge } from '../ui';
import { formatDate } from '../../utils';
import NotificationService from '../../services/NotificationService';

export default function MyTeamEnhanced() {
  const navigate = useNavigate();
  const { openModal } = useApp();
  const { 
    team, 
    teamAssessments, 
    loading, 
    error, 
    refresh 
  } = useTeam();
  
  const [selectedTab, setSelectedTab] = useState('overview');
  const [pendingReviews, setPendingReviews] = useState([]);
  const [completedReviews, setCompletedReviews] = useState([]);
  const [developmentPlans, setDevelopmentPlans] = useState([]);
  const [selectedEmployeeId, setSelectedEmployeeId] = useState(null);

  // Process assessments for workflow display
  useEffect(() => {
    if (teamAssessments) {
      console.log('Team assessments received:', teamAssessments);
      
      const pending = teamAssessments.filter(assessment => 
        assessment.self_assessment_status === 'employee_complete' && 
        assessment.manager_review_status === 'pending' &&
        assessment.cycle_status === 'active'  // Only show active cycles
      );
      const completed = teamAssessments.filter(assessment => 
        assessment.manager_review_status === 'completed'
      );
      
      console.log('Pending reviews filtered:', pending);
      console.log('Completed reviews filtered:', completed);
      
      setPendingReviews(pending);
      setCompletedReviews(completed);
    }
  }, [teamAssessments]);

  // Fetch development plans for review
  useEffect(() => {
    fetchDevelopmentPlans();
  }, []);

  const fetchDevelopmentPlans = async () => {
    try {
      const plans = await NotificationService.getDevelopmentPlansForReview();
      setDevelopmentPlans(plans);
    } catch (error) {
      console.error('Error fetching development plans:', error);
    }
  };

  const handleReviewAssessment = (assessment) => {
    // Navigate to manager review page with assessment details as search params
    navigate(`/review/${assessment.employee_id}?assessmentId=${assessment.assessment_id}&employeeName=${encodeURIComponent(assessment.employee_name)}&cycleId=${assessment.cycle_id}&cycleName=${encodeURIComponent(assessment.cycle_name)}`);
  };

  const handleReviewDevelopmentPlan = async (planId, status, feedback) => {
    try {
      await NotificationService.reviewDevelopmentPlan(planId, status, feedback);
      // Refresh development plans after review
      await fetchDevelopmentPlans();
    } catch (error) {
      console.error('Error reviewing development plan:', error);
      // You could show a toast notification here
    }
  };

  const getAssessmentStatusColor = (status) => {
    const colorMap = {
      'not_started': 'gray',
      'in_progress': 'yellow',
      'submitted': 'blue',
      'completed': 'green',
      'overdue': 'red'
    };
    return colorMap[status] || 'gray';
  };

  const getManagerReviewStatusColor = (status) => {
    const colorMap = {
      'pending': 'yellow',
      'in_progress': 'blue',
      'completed': 'green'
    };
    return colorMap[status] || 'gray';
  };

  if (loading) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Team</h1>
        <LoadingSpinner size="lg" message="Loading your team..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Team</h1>
        <ErrorMessage 
          error={error} 
          title="Error Loading Team" 
          onRetry={refresh}
        />
      </div>
    );
  }

  const tabs = [
    { id: 'overview', name: 'Team Overview', icon: Users },
    { id: 'pending', name: `Pending Reviews (${pendingReviews.length})`, icon: Clock },
    { id: 'completed', name: 'Completed Reviews', icon: CheckCircle },
    { id: 'development', name: `Development Plans (${developmentPlans.filter(p => p.status === 'submitted').length})`, icon: Target }
  ];

  return (
    <div className="p-8">
      {/* Header */}
      <div className="flex items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-cyan-400">My Team</h1>
          <p className="text-gray-400 mt-2">Manage your direct reports and their assessments</p>
        </div>
        <div className="flex space-x-3">
          <Button onClick={refresh} variant="secondary">
            <Calendar className="mr-2" size={16} />
            Refresh
          </Button>
        </div>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center">
            <Users className="text-cyan-400 mr-3" size={24} />
            <div>
              <p className="text-2xl font-bold text-white">{team.length}</p>
              <p className="text-gray-400 text-sm">Team Members</p>
            </div>
          </div>
        </div>
        
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center">
            <Clock className="text-yellow-400 mr-3" size={24} />
            <div>
              <p className="text-2xl font-bold text-white">{pendingReviews.length}</p>
              <p className="text-gray-400 text-sm">Pending Reviews</p>
            </div>
          </div>
        </div>
        
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center">
            <CheckCircle className="text-green-400 mr-3" size={24} />
            <div>
              <p className="text-2xl font-bold text-white">{completedReviews.length}</p>
              <p className="text-gray-400 text-sm">Completed Reviews</p>
            </div>
          </div>
        </div>
        
        <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
          <div className="flex items-center">
            <Target className="text-purple-400 mr-3" size={24} />
            <div>
              <p className="text-2xl font-bold text-white">{developmentPlans.filter(p => p.status === 'submitted').length}</p>
              <p className="text-gray-400 text-sm">Plans for Review</p>
            </div>
          </div>
        </div>
      </div>

      {/* Tab Navigation */}
      <div className="border-b border-gray-700 mb-6">
        <nav className="-mb-px flex space-x-8">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => {
                setSelectedTab(tab.id);
                // Reset employee filter when switching tabs
                if (tab.id === 'overview' || tab.id === 'development') {
                  setSelectedEmployeeId(null);
                }
              }}
              className={`flex items-center py-4 px-1 border-b-2 font-medium text-sm ${
                selectedTab === tab.id
                  ? 'border-cyan-500 text-cyan-400'
                  : 'border-transparent text-gray-400 hover:text-gray-300 hover:border-gray-300'
              }`}
            >
              <tab.icon className="mr-2" size={16} />
              {tab.name}
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      {selectedTab === 'overview' && (
        <div className="space-y-6">
          <h2 className="text-xl font-semibold text-white mb-4">Team Overview</h2>
          {team.length === 0 ? (
            <div className="text-center py-12">
              <Users size={64} className="mx-auto text-gray-500 mb-4" />
              <h3 className="text-xl font-semibold text-white mb-2">No Team Members</h3>
              <p className="text-gray-400">No direct reports are currently assigned to you.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {team.map((member) => (
                <div key={member.id} className="bg-gray-800 rounded-lg p-6 border border-gray-700">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-cyan-600 rounded-full flex items-center justify-center">
                        <span className="text-white font-medium">
                          {member.name.split(' ').map(n => n[0]).join('')}
                        </span>
                      </div>
                      <div>
                        <h3 className="text-lg font-semibold text-white">{member.name}</h3>
                        <p className="text-gray-400">{member.job_title}</p>
                        <p className="text-gray-500 text-sm">{member.email}</p>
                      </div>
                    </div>
                    <StatusBadge 
                      status={member.is_active ? 'active' : 'inactive'} 
                      color={member.is_active ? 'green' : 'gray'} 
                    />
                  </div>
                  
                  {/* Member Actions */}
                  <div className="mt-4 flex space-x-2">
                    <Button 
                      variant="secondary" 
                      size="sm"
                      onClick={() => {
                        // Navigate to manager playbook for this employee
                        navigate(`/playbook?employeeId=${member.id}`);
                      }}
                    >
                      <MessageSquare size={14} className="mr-1" />
                      Notes
                    </Button>
                    <Button 
                      variant="secondary" 
                      size="sm"
                      onClick={() => {
                        // Filter to show this employee's reviews and switch to appropriate tab
                        setSelectedEmployeeId(member.id);
                        const employeePendingReviews = pendingReviews.filter(assessment => 
                          assessment.employee_id === member.id
                        );
                        const employeeCompletedReviews = completedReviews.filter(assessment => 
                          assessment.employee_id === member.id
                        );
                        
                        // Switch to the tab that has reviews for this employee, or pending by default
                        if (employeePendingReviews.length > 0) {
                          setSelectedTab('pending');
                        } else if (employeeCompletedReviews.length > 0) {
                          setSelectedTab('completed');
                        } else {
                          setSelectedTab('pending');
                        }
                      }}
                    >
                      <FileText size={14} className="mr-1" />
                      Reviews
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {selectedTab === 'pending' && (
        <div className="space-y-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-white">
              Pending Reviews
              {selectedEmployeeId && (
                <span className="text-gray-400 text-sm ml-2">
                  (Filtered for {team.find(m => m.id === selectedEmployeeId)?.name})
                </span>
              )}
            </h2>
            {selectedEmployeeId && (
              <Button
                variant="secondary"
                size="sm"
                onClick={() => setSelectedEmployeeId(null)}
              >
                Show All
              </Button>
            )}
          </div>
          {(selectedEmployeeId ? pendingReviews.filter(r => r.employee_id === selectedEmployeeId) : pendingReviews).length === 0 ? (
            <div className="text-center py-12">
              <Clock size={64} className="mx-auto text-gray-500 mb-4" />
              <h3 className="text-xl font-semibold text-white mb-2">No Pending Reviews</h3>
              <p className="text-gray-400">All team member assessments are up to date!</p>
            </div>
          ) : (
            <div className="space-y-4">
              {(selectedEmployeeId ? pendingReviews.filter(r => r.employee_id === selectedEmployeeId) : pendingReviews).map((assessment) => (
                <div key={assessment.assessment_id} className="bg-gray-800 rounded-lg p-6 border border-gray-700 border-l-4 border-l-yellow-500">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-4 mb-3">
                        <div className="w-10 h-10 bg-cyan-600 rounded-full flex items-center justify-center">
                          <span className="text-white font-medium text-sm">
                            {assessment.employee_name.split(' ').map(n => n[0]).join('')}
                          </span>
                        </div>
                        <div>
                          <h3 className="text-lg font-semibold text-white">{assessment.employee_name}</h3>
                          <p className="text-gray-400">{assessment.employee_job_title}</p>
                        </div>
                      </div>
                      
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                        <div>
                          <p className="text-sm text-gray-400">Review Cycle</p>
                          <p className="text-white font-medium">{assessment.cycle_name}</p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-400">Assessment Progress</p>
                          <div className="flex flex-col space-y-1">
                            <div className="flex items-center space-x-2">
                              <CheckCircle size={14} className="text-green-400" />
                              <span className="text-green-400 text-sm">Employee: Complete</span>
                            </div>
                            <div className="flex items-center space-x-2">
                              <Clock size={14} className="text-yellow-400" />
                              <span className="text-yellow-400 text-sm">Manager: Pending Review</span>
                            </div>
                          </div>
                        </div>
                        <div>
                          <p className="text-sm text-gray-400">Due Date</p>
                          <p className="text-white">{formatDate(assessment.due_date)}</p>
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex flex-col space-y-2">
                      <Button 
                        variant="primary"
                        onClick={() => handleReviewAssessment(assessment)}
                        className="bg-yellow-600 hover:bg-yellow-700 border-yellow-600"
                      >
                        <Edit className="mr-2" size={16} />
                        Start Manager Review
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {selectedTab === 'completed' && (
        <div className="space-y-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-white">
              Completed Reviews
              {selectedEmployeeId && (
                <span className="text-gray-400 text-sm ml-2">
                  (Filtered for {team.find(m => m.id === selectedEmployeeId)?.name})
                </span>
              )}
            </h2>
            {selectedEmployeeId && (
              <Button
                variant="secondary"
                size="sm"
                onClick={() => setSelectedEmployeeId(null)}
              >
                Show All
              </Button>
            )}
          </div>
          {(selectedEmployeeId ? completedReviews.filter(r => r.employee_id === selectedEmployeeId) : completedReviews).length === 0 ? (
            <div className="text-center py-12">
              <CheckCircle size={64} className="mx-auto text-gray-500 mb-4" />
              <h3 className="text-xl font-semibold text-white mb-2">No Completed Reviews</h3>
              <p className="text-gray-400">Completed reviews will appear here.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {(selectedEmployeeId ? completedReviews.filter(r => r.employee_id === selectedEmployeeId) : completedReviews).map((assessment) => (
                <div key={assessment.assessment_id} className="bg-gray-800 rounded-lg p-6 border border-gray-700 border-l-4 border-l-green-500">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-4 mb-3">
                        <div className="w-10 h-10 bg-cyan-600 rounded-full flex items-center justify-center">
                          <span className="text-white font-medium text-sm">
                            {assessment.employee_name.split(' ').map(n => n[0]).join('')}
                          </span>
                        </div>
                        <div>
                          <h3 className="text-lg font-semibold text-white">{assessment.employee_name}</h3>
                          <p className="text-gray-400">{assessment.employee_job_title}</p>
                        </div>
                      </div>
                      
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                        <div>
                          <p className="text-sm text-gray-400">Review Cycle</p>
                          <p className="text-white font-medium">{assessment.cycle_name}</p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-400">Assessment Progress</p>
                          <div className="flex flex-col space-y-1">
                            <div className="flex items-center space-x-2">
                              <CheckCircle size={14} className="text-green-400" />
                              <span className="text-green-400 text-sm">Employee: Complete</span>
                            </div>
                            <div className="flex items-center space-x-2">
                              <CheckCircle size={14} className="text-green-400" />
                              <span className="text-green-400 text-sm">Manager: Complete</span>
                            </div>
                          </div>
                        </div>
                        <div>
                          <p className="text-sm text-gray-400">Completed</p>
                          <p className="text-white">{formatDate(assessment.updated_at)}</p>
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex flex-col space-y-2">
                      <Button 
                        variant="secondary"
                        onClick={() => handleReviewAssessment(assessment)}
                      >
                        <Eye className="mr-2" size={16} />
                        View Review
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {selectedTab === 'development' && (
        <div className="space-y-6">
          <h2 className="text-xl font-semibold text-white mb-4">Development Plans for Review</h2>
          {developmentPlans.length === 0 ? (
            <div className="text-center py-12">
              <Target size={64} className="mx-auto text-gray-500 mb-4" />
              <h3 className="text-xl font-semibold text-white mb-2">No Development Plans</h3>
              <p className="text-gray-400">Your team members haven't submitted any development plans yet.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {developmentPlans.map((plan) => (
                <DevelopmentPlanCard
                  key={plan.id}
                  plan={plan}
                  onReview={(planId, status, feedback) => handleReviewDevelopmentPlan(planId, status, feedback)}
                />
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

// Development Plan Card Component for Manager Review
const DevelopmentPlanCard = ({ plan, onReview }) => {
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewStatus, setReviewStatus] = useState('under_review');
  const [feedback, setFeedback] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleSubmitReview = async () => {
    try {
      setSubmitting(true);
      await onReview(plan.id, reviewStatus, feedback);
      setShowReviewForm(false);
      setFeedback('');
    } catch (error) {
      console.error('Error submitting review:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const getStatusColor = (status) => {
    const colors = {
      'submitted': 'yellow',
      'under_review': 'blue',
      'approved': 'green',
      'needs_revision': 'red'
    };
    return colors[status] || 'gray';
  };

  const formatGoals = (goals) => {
    try {
      const goalArray = typeof goals === 'string' ? JSON.parse(goals) : goals;
      return Array.isArray(goalArray) ? goalArray : [];
    } catch {
      return [];
    }
  };

  const formatSkills = (skills) => {
    try {
      const skillArray = typeof skills === 'string' ? JSON.parse(skills) : skills;
      return Array.isArray(skillArray) ? skillArray : [];
    } catch {
      return [];
    }
  };

  return (
    <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <div className="flex items-center space-x-4 mb-3">
            <div className="w-10 h-10 bg-cyan-600 rounded-full flex items-center justify-center">
              <span className="text-white font-medium text-sm">
                {plan.employee_name.split(' ').map(n => n[0]).join('')}
              </span>
            </div>
            <div>
              <h3 className="text-lg font-semibold text-white">{plan.employee_name}</h3>
              <p className="text-gray-400">{plan.employee_job_title}</p>
            </div>
          </div>
          
          <h4 className="text-xl font-semibold text-white mb-2">{plan.title}</h4>
          {plan.description && (
            <p className="text-gray-300 mb-4">{plan.description}</p>
          )}

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div>
              <p className="text-sm text-gray-400">Status</p>
              <StatusBadge 
                status={plan.status} 
                color={getStatusColor(plan.status)}
              />
            </div>
            <div>
              <p className="text-sm text-gray-400">Timeline</p>
              <p className="text-white">{plan.timeline}</p>
            </div>
            <div>
              <p className="text-sm text-gray-400">Submitted</p>
              <p className="text-white">{formatDate(plan.created_at)}</p>
            </div>
          </div>

          {/* Goals */}
          {formatGoals(plan.goals).length > 0 && (
            <div className="mb-4">
              <p className="text-sm text-gray-400 mb-2">Goals ({formatGoals(plan.goals).length})</p>
              <div className="space-y-2">
                {formatGoals(plan.goals).slice(0, 3).map((goal, index) => (
                  <div key={index} className="bg-gray-700 rounded-lg p-3">
                    <p className="text-gray-300 font-medium">{goal.goal}</p>
                    {goal.timeline && (
                      <p className="text-gray-500 text-sm mt-1">Timeline: {goal.timeline}</p>
                    )}
                    {goal.priority && (
                      <span className={`inline-block px-2 py-1 text-xs rounded mt-1 ${
                        goal.priority === 'high' ? 'bg-red-600 text-white' :
                        goal.priority === 'medium' ? 'bg-yellow-600 text-white' :
                        'bg-gray-600 text-gray-300'
                      }`}>
                        {goal.priority} priority
                      </span>
                    )}
                  </div>
                ))}
                {formatGoals(plan.goals).length > 3 && (
                  <p className="text-gray-500 text-sm">... and {formatGoals(plan.goals).length - 3} more goals</p>
                )}
              </div>
            </div>
          )}

          {/* Skills */}
          {formatSkills(plan.skills_to_develop).length > 0 && (
            <div className="mb-4">
              <p className="text-sm text-gray-400 mb-2">Skills to Develop ({formatSkills(plan.skills_to_develop).length})</p>
              <div className="space-y-2">
                {formatSkills(plan.skills_to_develop).slice(0, 2).map((skill, index) => (
                  <div key={index} className="bg-gray-700 rounded-lg p-3">
                    <p className="text-gray-300 font-medium">{skill.skill}</p>
                    {skill.reason && (
                      <p className="text-gray-400 text-sm mt-1">{skill.reason}</p>
                    )}
                  </div>
                ))}
                {formatSkills(plan.skills_to_develop).length > 2 && (
                  <p className="text-gray-500 text-sm">... and {formatSkills(plan.skills_to_develop).length - 2} more skills</p>
                )}
              </div>
            </div>
          )}

          {/* Existing Manager Feedback */}
          {plan.manager_feedback && (
            <div className="bg-gray-700 rounded-lg p-4 mb-4">
              <p className="text-sm text-gray-400 mb-2">Previous Manager Feedback</p>
              <p className="text-gray-300">{plan.manager_feedback}</p>
              <p className="text-xs text-gray-500 mt-2">
                Reviewed on {formatDate(plan.manager_reviewed_at)}
              </p>
            </div>
          )}
        </div>
        
        <div className="flex flex-col space-y-2 ml-6">
          {plan.status === 'submitted' && (
            <Button 
              variant="primary"
              onClick={() => setShowReviewForm(!showReviewForm)}
            >
              <MessageSquare className="mr-2" size={16} />
              Review Plan
            </Button>
          )}
        </div>
      </div>

      {/* Review Form */}
      {showReviewForm && (
        <div className="border-t border-gray-700 pt-4 mt-4">
          <h5 className="text-lg font-semibold text-white mb-4">Review Development Plan</h5>
          
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Review Decision
              </label>
              <select
                value={reviewStatus}
                onChange={(e) => setReviewStatus(e.target.value)}
                className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
              >
                <option value="under_review">Keep Under Review</option>
                <option value="approved">Approve Plan</option>
                <option value="needs_revision">Needs Revision</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Manager Feedback
              </label>
              <textarea
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
                className="w-full p-3 bg-gray-700 border border-gray-600 rounded-md text-white placeholder-gray-400 focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                rows={4}
                placeholder="Provide feedback on this development plan..."
              />
            </div>

            <div className="flex justify-end space-x-3">
              <Button 
                variant="secondary"
                onClick={() => setShowReviewForm(false)}
              >
                Cancel
              </Button>
              <Button 
                variant="primary"
                onClick={handleSubmitReview}
                disabled={submitting}
              >
                <Save size={16} className="mr-2" />
                {submitting ? 'Submitting...' : 'Submit Review'}
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// src/components/pages/MyTeamEnhancedRouter.tsx - Comprehensive single-page team view
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Users, 
  Calendar, 
  Clock, 
  CheckCircle, 
  AlertTriangle, 
  Eye, 
  Edit, 
  MessageSquare,
  FileText,
  Target,
  Award,
  Save,
  RefreshCw,
  Play,
  Star,
  TrendingUp,
  AlertCircle,
  User,
  Mail,
  Phone,
  MapPin,
  Briefcase,
  Hash
} from 'lucide-react';
import { useTeam } from '../../hooks';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button, StatusBadge } from '../ui';
import { formatDate } from '../../utils';
import NotificationService from '../../services/NotificationService';

export default function MyTeamEnhancedRouter() {
  const navigate = useNavigate();
  const { openModal, setActivePage } = useApp();
  const { 
    team, 
    teamAssessments, 
    loading, 
    error, 
    refresh 
  } = useTeam();
  
  const [developmentPlans, setDevelopmentPlans] = useState([]);
  const [employeePerformance, setEmployeePerformance] = useState<Record<string, {
    assessments: any[];
    pending_reviews: number;
    completed_reviews: number;
    latest_assessment: any;
    performance_trend: string;
  }>>({});
  const [expandedEmployees, setExpandedEmployees] = useState(new Set());

  // Process all team data for comprehensive view
  useEffect(() => {
    if (teamAssessments && Array.isArray(teamAssessments)) {
      // Group assessments by employee
      const performanceData: Record<string, {
        assessments: any[];
        pending_reviews: number;
        completed_reviews: number;
        latest_assessment: any;
        performance_trend: string;
      }> = {};
      teamAssessments.forEach((assessment: any) => {
        if (!performanceData[assessment.employee_id]) {
          performanceData[assessment.employee_id] = {
            assessments: [],
            pending_reviews: 0,
            completed_reviews: 0,
            latest_assessment: null,
            performance_trend: 'stable'
          };
        }
        
        performanceData[assessment.employee_id].assessments.push(assessment);
        
        if (assessment.self_assessment_status === 'employee_complete' && 
            assessment.manager_review_status === 'pending') {
          performanceData[assessment.employee_id].pending_reviews++;
        }
        
        if (assessment.manager_review_status === 'completed') {
          performanceData[assessment.employee_id].completed_reviews++;
        }
        
        // Track latest assessment
        if (!performanceData[assessment.employee_id].latest_assessment ||
            new Date(assessment.created_at) > new Date(performanceData[assessment.employee_id].latest_assessment.created_at)) {
          performanceData[assessment.employee_id].latest_assessment = assessment;
        }
      });
      
      setEmployeePerformance(performanceData);
    }
  }, [teamAssessments]);

  const toggleEmployeeExpansion = (employeeId: string) => {
    const newExpanded = new Set(expandedEmployees);
    if (newExpanded.has(employeeId)) {
      newExpanded.delete(employeeId);
    } else {
      newExpanded.add(employeeId);
    }
    setExpandedEmployees(newExpanded);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-600 text-green-100 border-green-500';
      case 'employee_complete': return 'bg-yellow-600 text-yellow-100 border-yellow-500';
      case 'in_progress': return 'bg-blue-600 text-blue-100 border-blue-500';
      case 'pending': return 'bg-orange-600 text-orange-100 border-orange-500';
      case 'not_started': return 'bg-gray-600 text-gray-100 border-gray-500';
      default: return 'bg-gray-600 text-gray-100 border-gray-500';
    }
  };

  const getPriorityIcon = (pendingCount: number) => {
    if (pendingCount > 2) return <AlertCircle className="text-red-400" size={16} />;
    if (pendingCount > 0) return <Clock className="text-yellow-400" size={16} />;
    return <CheckCircle className="text-green-400" size={16} />;
  };

  const calculateCompletionRate = (employeeId: string) => {
    const performance = employeePerformance[employeeId];
    if (!performance || performance.assessments.length === 0) return 0;
    
    const completedCount = performance.assessments.filter(
      a => a.manager_review_status === 'completed'
    ).length;
    
    return Math.round((completedCount / performance.assessments.length) * 100);
  };

  const handleReviewAssessment = (assessment: any) => {
    // Navigate to manager review page with assessment details
    const searchParams = new URLSearchParams({
      assessmentId: assessment.assessment_id,
      employeeName: assessment.employee_name,
      cycleId: assessment.cycle_id,
      cycleName: assessment.cycle_name
    });
    
    navigate(`/review/${assessment.employee_id}?${searchParams.toString()}`);
  };

  if (loading) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Team</h1>
        <LoadingSpinner size="lg" message="Loading your team..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <h1 className="text-3xl font-bold text-cyan-400 mb-8">My Team</h1>
        <ErrorMessage 
          error={error} 
          title="Error Loading Team" 
          onRetry={refresh}
        />
      </div>
    );
  }

  // Get unique team members from assessments
  const teamMembers = (teamAssessments || []).reduce((acc: any[], assessment: any) => {
    if (!acc.find(member => member.employee_id === assessment.employee_id)) {
      acc.push({
        employee_id: assessment.employee_id,
        employee_name: assessment.employee_name,
        job_title: assessment.job_title,
        department: assessment.department,
        email: assessment.email || `${assessment.employee_name.toLowerCase().replace(' ', '.')}@company.com`,
        hire_date: assessment.hire_date,
        manager_id: assessment.manager_id
      });
    }
    return acc;
  }, []);

  const totalPending = Object.values(employeePerformance).reduce(
    (sum: number, perf: any) => sum + perf.pending_reviews, 0
  );
  
  const totalCompleted = Object.values(employeePerformance).reduce(
    (sum: number, perf: any) => sum + perf.completed_reviews, 0
  );

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-cyan-400">My Team</h1>
          <p className="text-gray-400 mt-2">Comprehensive team management and review oversight</p>
        </div>
        <div className="flex items-center space-x-3">
          <button
            onClick={refresh}
            className="bg-gray-700 hover:bg-gray-600 text-white px-4 py-2 rounded-lg flex items-center"
          >
            <RefreshCw size={16} className="mr-2" />
            Refresh
          </button>
          <button
            onClick={() => openModal('startReviewCycle', { afterSave: refresh })}
            className="bg-cyan-500 hover:bg-cyan-600 text-white px-4 py-2 rounded-lg flex items-center"
          >
            <Play size={16} className="mr-2" />
            Start Review Cycle
          </button>
        </div>
      </div>

      {/* Team Summary Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Team Members</p>
              <p className="text-2xl font-bold text-cyan-400">{teamMembers.length}</p>
            </div>
            <Users className="text-cyan-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Pending Reviews</p>
              <p className="text-2xl font-bold text-yellow-400">{totalPending}</p>
            </div>
            <Clock className="text-yellow-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Completed Reviews</p>
              <p className="text-2xl font-bold text-green-400">{totalCompleted}</p>
            </div>
            <CheckCircle className="text-green-400" size={24} />
          </div>
        </div>

        <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-gray-400 text-sm">Avg Completion</p>
              <p className="text-2xl font-bold text-purple-400">
                {teamMembers.length > 0 
                  ? Math.round(teamMembers.reduce((sum, member) => 
                      sum + calculateCompletionRate(member.employee_id), 0) / teamMembers.length)
                  : 0}%
              </p>
            </div>
            <TrendingUp className="text-purple-400" size={24} />
          </div>
        </div>
      </div>

      {/* Employee Widgets */}
      <div className="space-y-4">
        <h2 className="text-xl font-semibold text-white flex items-center">
          <Users className="mr-2" size={20} />
          Team Members ({teamMembers.length})
        </h2>
        
        {teamMembers.length > 0 ? (
          <div className="grid grid-cols-1 gap-4">
            {teamMembers.map((member) => {
              const performance = employeePerformance[member.employee_id] || {};
              const isExpanded = expandedEmployees.has(member.employee_id);
              const completionRate = calculateCompletionRate(member.employee_id);
              const latestAssessment = performance.latest_assessment;
              
              return (
                <div key={member.employee_id} className="bg-gray-800 rounded-lg border border-gray-700 overflow-hidden">
                  {/* Employee Header */}
                  <div className="p-6">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gradient-to-br from-cyan-500 to-blue-600 rounded-full flex items-center justify-center">
                          <User className="text-white" size={20} />
                        </div>
                        <div>
                          <h3 className="text-lg font-semibold text-white">{member.employee_name}</h3>
                          <p className="text-gray-400">{member.job_title || 'No Title'}</p>
                          <p className="text-gray-500 text-sm">{member.department || 'No Department'}</p>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-4">
                        {/* Priority Indicator */}
                        <div className="flex items-center space-x-2">
                          {getPriorityIcon(performance.pending_reviews || 0)}
                          <span className="text-sm text-gray-400">
                            {performance.pending_reviews > 0 
                              ? `${performance.pending_reviews} pending` 
                              : 'Up to date'}
                          </span>
                        </div>

                        {/* Completion Rate */}
                        <div className="text-right">
                          <p className="text-sm text-gray-400">Completion Rate</p>
                          <p className={`text-lg font-bold ${
                            completionRate >= 80 ? 'text-green-400' :
                            completionRate >= 60 ? 'text-yellow-400' :
                            'text-red-400'
                          }`}>
                            {completionRate}%
                          </p>
                        </div>

                        {/* Expand Button */}
                        <button
                          onClick={() => toggleEmployeeExpansion(member.employee_id)}
                          className="p-2 text-gray-400 hover:text-white transition-colors"
                        >
                          <Eye size={20} />
                        </button>
                      </div>
                    </div>

                    {/* Status Row */}
                    <div className="mt-4 flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        {/* Current Status */}
                        {latestAssessment ? (
                          <div className="flex items-center space-x-2">
                            <span className="text-sm text-gray-400">Status:</span>
                            <span className={`px-2 py-1 text-xs rounded-full border ${
                              getStatusColor(latestAssessment.manager_review_status)
                            }`}>
                              {latestAssessment.manager_review_status === 'employee_complete' 
                                ? 'Ready for Review' 
                                : latestAssessment.manager_review_status?.replace('_', ' ') || 'Not Started'}
                            </span>
                          </div>
                        ) : (
                          <span className="text-sm text-gray-500">No active assessments</span>
                        )}

                        {/* Last Activity */}
                        {latestAssessment && (
                          <div className="flex items-center space-x-2 text-sm text-gray-400">
                            <Calendar size={14} />
                            <span>Last activity: {formatDate(latestAssessment.updated_at)}</span>
                          </div>
                        )}
                      </div>

                      {/* Quick Actions */}
                      <div className="flex items-center space-x-2">
                        {latestAssessment?.assessment_id && (
                          <button
                            onClick={() => handleReviewAssessment(latestAssessment)}
                            className="bg-cyan-600 hover:bg-cyan-700 text-white px-3 py-1 rounded text-sm flex items-center"
                          >
                            <Eye size={14} className="mr-1" />
                            Review
                          </button>
                        )}
                        
                        <button
                          onClick={() => openModal('giveFeedback', { 
                            employeeId: member.employee_id,
                            employeeName: member.employee_name 
                          })}
                          className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm flex items-center"
                        >
                          <MessageSquare size={14} className="mr-1" />
                          Feedback
                        </button>
                        
                        <button
                          onClick={() => openModal('giveKudo', { 
                            employeeId: member.employee_id,
                            employeeName: member.employee_name 
                          })}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm flex items-center"
                        >
                          <Star size={14} className="mr-1" />
                          Kudos
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Expanded Details */}
                  {isExpanded && (
                    <div className="border-t border-gray-700 bg-gray-750">
                      <div className="p-6 space-y-6">
                        {/* Contact Info */}
                        <div>
                          <h4 className="text-sm font-medium text-gray-300 mb-3">Contact Information</h4>
                          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                            <div className="flex items-center space-x-2">
                              <Mail className="text-gray-400" size={14} />
                              <span className="text-gray-300">{member.email}</span>
                            </div>
                            <div className="flex items-center space-x-2">
                              <Hash className="text-gray-400" size={14} />
                              <span className="text-gray-300">ID: {member.employee_id.slice(0, 8)}...</span>
                            </div>
                          </div>
                        </div>

                        {/* Assessment History */}
                        <div>
                          <h4 className="text-sm font-medium text-gray-300 mb-3">Assessment History</h4>
                          {performance.assessments && performance.assessments.length > 0 ? (
                            <div className="space-y-3">
                              {performance.assessments.slice(0, 3).map((assessment) => (
                                <div key={assessment.assessment_id} className="bg-gray-700 rounded-lg p-3">
                                  <div className="flex items-center justify-between">
                                    <div>
                                      <p className="text-white font-medium">{assessment.cycle_name}</p>
                                      <p className="text-gray-400 text-sm">
                                        Created: {formatDate(assessment.created_at)}
                                      </p>
                                    </div>
                                    <div className="flex items-center space-x-2">
                                      <span className={`px-2 py-1 text-xs rounded-full border ${
                                        getStatusColor(assessment.manager_review_status)
                                      }`}>
                                        {assessment.manager_review_status?.replace('_', ' ') || 'Not Started'}
                                      </span>
                                      <button
                                        onClick={() => handleReviewAssessment(assessment)}
                                        className="text-cyan-400 hover:text-cyan-300 text-sm"
                                      >
                                        View
                                      </button>
                                    </div>
                                  </div>
                                </div>
                              ))}
                              {performance.assessments.length > 3 && (
                                <p className="text-gray-400 text-sm text-center">
                                  +{performance.assessments.length - 3} more assessments
                                </p>
                              )}
                            </div>
                          ) : (
                            <p className="text-gray-500 text-sm">No assessments found</p>
                          )}
                        </div>

                        {/* Performance Metrics */}
                        <div>
                          <h4 className="text-sm font-medium text-gray-300 mb-3">Performance Metrics</h4>
                          <div className="grid grid-cols-3 gap-4">
                            <div className="bg-gray-700 rounded-lg p-3 text-center">
                              <p className="text-2xl font-bold text-green-400">
                                {performance.completed_reviews || 0}
                              </p>
                              <p className="text-gray-400 text-xs">Completed</p>
                            </div>
                            <div className="bg-gray-700 rounded-lg p-3 text-center">
                              <p className="text-2xl font-bold text-yellow-400">
                                {performance.pending_reviews || 0}
                              </p>
                              <p className="text-gray-400 text-xs">Pending</p>
                            </div>
                            <div className="bg-gray-700 rounded-lg p-3 text-center">
                              <p className="text-2xl font-bold text-cyan-400">
                                {performance.assessments?.length || 0}
                              </p>
                              <p className="text-gray-400 text-xs">Total</p>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        ) : (
          <div className="text-center py-12 bg-gray-800 rounded-lg">
            <Users size={48} className="mx-auto text-gray-500 mb-4" />
            <h3 className="text-white text-lg mb-2">No Team Members Found</h3>
            <div className="text-gray-400 space-y-1">
              <p>This could mean:</p>
              <ul className="text-sm text-gray-500 mt-2">
                <li> You don't have any direct reports assigned</li>
                <li> You're not set up as a manager in the system</li>
                <li> No active review cycles have been started</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import React from 'react';

export default function Settings() {
  return (
    <div className="p-8 max-w-xl">
      <h2 className="text-2xl font-semibold mb-6">Settings</h2>
      <p className="mb-4 text-gray-400">This minimal MVP has no userconfigurable settings yet.</p>
      <p className="text-sm text-gray-500">Future iterations will include notification preferences, theme selection, and profile editing.</p>
    </div>
  );
}

// src/components/pages/SuperEnhancedDashboard.tsx - Advanced analytics dashboard
import React, { useState, useEffect } from 'react';
import { 
  BarChart3, 
  TrendingUp, 
  Users, 
  FileText, 
  Target, 
  Calendar, 
  Clock, 
  CheckCircle, 
  AlertTriangle,
  Award,
  Bell,
  Activity,
  Zap,
  Eye,
  RefreshCw,
  Download,
  Filter,
  Globe,
  Cpu,
  Timer
} from 'lucide-react';
import { useApp } from '../../contexts';
import { LoadingSpinner, ErrorMessage, Button } from '../ui';
import { formatDate } from '../../utils';
import AnalyticsService, { AnalyticsData } from '../../services/AnalyticsService';
import {
  MetricCard,
  PerformanceBarChart,
  TrendLineChart,
  DepartmentPieChart,
  EngagementAreaChart,
  GoalProgressRadial
} from '../analytics/ChartComponents';

export default function SuperEnhancedDashboard() {
  const { userRole, userName } = useApp();
  const [analytics, setAnalytics] = useState<AnalyticsData | null>(null);
  const [realtimeMetrics, setRealtimeMetrics] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [refreshing, setRefreshing] = useState(false);
  const [selectedTimeframe, setSelectedTimeframe] = useState('30d');

  useEffect(() => {
    fetchDashboardAnalytics();
    // Set up real-time metrics updates
    const interval = setInterval(fetchRealtimeMetrics, 30000); // Every 30 seconds
    return () => clearInterval(interval);
  }, [userRole]);

  const fetchDashboardAnalytics = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await AnalyticsService.getDashboardAnalytics(userRole || 'employee');
      setAnalytics(data);
      await fetchRealtimeMetrics();
    } catch (err: any) {
      console.error('Error fetching dashboard analytics:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchRealtimeMetrics = async () => {
    try {
      const metrics = await AnalyticsService.getRealtimeMetrics();
      setRealtimeMetrics(metrics);
    } catch (err) {
      console.warn('Error fetching real-time metrics:', err);
    }
  };

  const handleRefresh = async () => {
    try {
      setRefreshing(true);
      await fetchDashboardAnalytics();
    } finally {
      setRefreshing(false);
    }
  };

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return 'Good morning';
    if (hour < 17) return 'Good afternoon';
    return 'Good evening';
  };

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'assessment_completed': return CheckCircle;
      case 'review_submitted': return FileText;
      case 'goal_achieved': return Target;
      case 'feedback_given': return Award;
      default: return Activity;
    }
  };

  const getAlertColor = (type: string) => {
    switch (type) {
      case 'error': return 'border-red-500 bg-red-900/20';
      case 'warning': return 'border-yellow-500 bg-yellow-900/20';
      case 'success': return 'border-green-500 bg-green-900/20';
      default: return 'border-blue-500 bg-blue-900/20';
    }
  };

  if (loading) {
    return (
      <div className="p-8">
        <LoadingSpinner message="Loading advanced analytics..." />
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8">
        <ErrorMessage
          error={error}
          title="Dashboard Error"
          onRetry={fetchDashboardAnalytics}
        />
      </div>
    );
  }

  if (!analytics) {
    return (
      <div className="p-8">
        <div className="text-center">
          <BarChart3 className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-300">No analytics data</h3>
          <p className="mt-1 text-sm text-gray-400">Unable to load dashboard analytics.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6 max-w-7xl mx-auto">
      {/* Header with greeting and controls */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-white">
            {getGreeting()}, {userName}!
          </h1>
          <p className="text-gray-400 mt-1">
            Here's what's happening with your team today
          </p>
        </div>
        <div className="flex items-center space-x-3">
          <select
            value={selectedTimeframe}
            onChange={(e) => setSelectedTimeframe(e.target.value)}
            className="bg-gray-700 border border-gray-600 rounded-lg px-3 py-2 text-white text-sm"
          >
            <option value="7d">Last 7 days</option>
            <option value="30d">Last 30 days</option>
            <option value="90d">Last 90 days</option>
            <option value="1y">Last year</option>
          </select>
          <Button
            onClick={handleRefresh}
            disabled={refreshing}
            variant="secondary"
            className="flex items-center space-x-2"
          >
            <RefreshCw size={16} className={refreshing ? 'animate-spin' : ''} />
            <span>Refresh</span>
          </Button>
          <Button
            variant="secondary"
            className="flex items-center space-x-2"
          >
            <Download size={16} />
            <span>Export</span>
          </Button>
        </div>
      </div>

      {/* Real-time metrics banner */}
      {realtimeMetrics && (
        <div className="bg-gradient-to-r from-cyan-900/20 to-blue-900/20 border border-cyan-500/30 rounded-lg p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
              <span className="text-cyan-400 font-medium">Live Metrics</span>
            </div>
            <div className="flex items-center space-x-6 text-sm">
              <div className="flex items-center space-x-2">
                <Globe size={14} className="text-gray-400" />
                <span className="text-white">{realtimeMetrics.activeUsers} active users</span>
              </div>
