              appearance={{ 
                theme: ThemeSupa,
                style: {
                  button: { 
                    background: '#0891b2', 
                    color: 'white',
                    borderRadius: '0.5rem'
                  },
                  anchor: { color: '#67e8f9' },
                  input: { 
                    background: '#374151',
                    border: '1px solid #4b5563',
                    borderRadius: '0.5rem',
                    color: 'white'
                  }
                }
              }}
              providers={[]}
              view="sign_in"
            />
            
          </div>
        </div>
      </div>
    );
  }

  // Secure logging - no sensitive data exposed
  if (process.env.NODE_ENV === 'development') {
    console.log(' Authenticated app loaded - User authenticated:', !!user);
  }

  // Show the main app with routing
  return <AppRouter />;
};

export default AuthenticatedApp;

// src/components/routing/DashboardRouter.tsx - Role-based dashboard routing
import React from 'react';
import { useApp } from '../../contexts';

// Import role-specific dashboards
import EmployeeDashboard from '../pages/EmployeeDashboard';
import ManagerDashboard from '../pages/ManagerDashboard';
import AdminDashboard from '../pages/AdminDashboard';

// Fallback dashboard for unknown roles
import SuperEnhancedDashboard from '../pages/SuperEnhancedDashboard';

const DashboardRouter: React.FC = () => {
  const { userRole, loading } = useApp();

  // Show loading state while user role is being determined
  if (loading) {
    return (
      <div className="p-8 flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
        <span className="ml-3 text-gray-400">Loading dashboard...</span>
      </div>
    );
  }

  // Route to appropriate dashboard based on user role
  switch (userRole) {
    case 'employee':
      return <EmployeeDashboard />;
    
    case 'manager':
      return <ManagerDashboard />;
    
    case 'admin':
      return <AdminDashboard />;
    
    default:
      console.warn(`Unknown user role: ${userRole}, falling back to enhanced dashboard`);
      return <SuperEnhancedDashboard />;
  }
};

export default DashboardRouter;

// src/components/routing/ModalsContainer.tsx - Centralized modal management
import React from 'react';
import { useApp } from '../../contexts';
import { supabase } from '../../services';

// Import modals
import StartReviewCycleModal from '../modals/StartReviewCycleModal';
import CreateReviewCycleModal from '../modals/CreateReviewCycleModal';
import CreateEmployeeModal from '../modals/CreateEmployeeModal';
import EditEmployeeModal from '../modals/EditEmployeeModal';
import GiveKudoModal from '../modals/GiveKudoModal';
import GiveFeedbackModal from '../modals/GiveFeedbackModal';
import UATFeedbackModal from '../modals/UATFeedbackModal';

const ModalsContainer: React.FC = () => {
  const { modal, closeModal } = useApp();

  if (!modal.isOpen) {
    return null;
  }

  // Helper function to handle modal completion
  const handleModalComplete = () => {
    console.log(` ${modal.name} modal completed`);
    if (modal.props?.onComplete) {
      modal.props.onComplete();
    }
    closeModal();
  };

  // Render the appropriate modal based on modal.name
  switch (modal.name) {
    case 'startReviewCycle':
      return (
        <StartReviewCycleModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: handleModalComplete
          }} 
        />
      );

    case 'createReviewCycle':
      return (
        <CreateReviewCycleModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: handleModalComplete
          }} 
        />
      );

    case 'createEmployee':
      return (
        <CreateEmployeeModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: handleModalComplete
          }} 
        />
      );

    case 'editEmployee':
      return (
        <EditEmployeeModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: handleModalComplete
          }} 
        />
      );

    case 'giveKudo':
      return (
        <GiveKudoModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: handleModalComplete
          }} 
        />
      );

    case 'giveFeedback':
      return (
        <GiveFeedbackModal 
          supabase={supabase} 
          closeModal={closeModal} 
          modalProps={{
            ...modal.props,
            onComplete: handleModalComplete
          }} 
        />
      );

    case 'uatFeedback':
      return (
        <UATFeedbackModal 
          closeModal={closeModal}
        />
      );

    default:
      console.warn(`Unknown modal: ${modal.name}`);
      return null;
  }
};

export default ModalsContainer;

