// src/components/ui/TeamHealthAlerts.tsx - Team health alerts component
import React, { useState, useEffect } from 'react';
import { AlertTriangle, User, Clock, CheckCircle, X, Heart } from 'lucide-react';
import { TeamHealthService, TeamHealthAlert } from '../../services/TeamHealthService';
import { useApp } from '../../contexts';

interface TeamHealthAlertsProps {
  role: 'manager' | 'admin';
  managerId?: string;
  showInline?: boolean;
}

export default function TeamHealthAlerts({ role, managerId, showInline = false }: TeamHealthAlertsProps) {
  const [alerts, setAlerts] = useState<TeamHealthAlert[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadAlerts();
    
    // Refresh alerts every 30 seconds
    const interval = setInterval(loadAlerts, 30000);
    return () => clearInterval(interval);
  }, [role, managerId]);

  const loadAlerts = () => {
    try {
      setLoading(true);
      let alertsData: TeamHealthAlert[] = [];
      
      if (role === 'manager' && managerId) {
        alertsData = TeamHealthService.getManagerAlerts(managerId);
      } else if (role === 'admin') {
        alertsData = TeamHealthService.getAdminAlerts();
      }
      
      // Sort by severity and timestamp
      alertsData.sort((a, b) => {
        const severityOrder = { high: 3, medium: 2, low: 1 };
        const severityDiff = severityOrder[b.severity] - severityOrder[a.severity];
        if (severityDiff !== 0) return severityDiff;
        return new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime();
      });
      
      setAlerts(alertsData);
    } catch (error) {
      console.error('Error loading team health alerts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAcknowledge = (alertId: string) => {
    TeamHealthService.acknowledgeAlert(alertId);
    setAlerts(prev => prev.filter(alert => alert.id !== alertId));
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'high': return 'border-l-red-500 bg-red-900/10 text-red-200';
      case 'medium': return 'border-l-yellow-500 bg-yellow-900/10 text-yellow-200';
      case 'low': return 'border-l-blue-500 bg-blue-900/10 text-blue-200';
      default: return 'border-l-gray-500 bg-gray-900/10 text-gray-200';
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'high': return <AlertTriangle className="text-red-400" size={16} />;
      case 'medium': return <AlertTriangle className="text-yellow-400" size={16} />;
      case 'low': return <AlertTriangle className="text-blue-400" size={16} />;
      default: return <AlertTriangle className="text-gray-400" size={16} />;
    }
  };

  const formatResponse = (response: any) => {
    if (typeof response === 'boolean') {
      return response ? 'Yes' : 'No';
    }
    if (typeof response === 'number') {
      return `${response}/5`;
    }
    return response;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-4">
        <div className="text-gray-400">Loading alerts...</div>
      </div>
    );
  }

  if (alerts.length === 0) {
    return showInline ? null : (
      <div className="bg-green-900/20 border border-green-500/30 rounded-lg p-4">
        <div className="flex items-center space-x-3">
          <CheckCircle className="text-green-400" size={20} />
          <div>
            <h4 className="text-green-200 font-medium">No Health Concerns</h4>
            <p className="text-green-300 text-sm">Your team members are reporting positive feedback</p>
          </div>
        </div>
      </div>
    );
  }

  const alertsList = (
    <div className="space-y-3">
      {alerts.slice(0, showInline ? 3 : alerts.length).map(alert => (
        <div key={alert.id} className={`p-4 rounded-lg border-l-4 ${getSeverityColor(alert.severity)}`}>
          <div className="flex items-start justify-between">
            <div className="flex-1">
              <div className="flex items-center space-x-2 mb-2">
                {getSeverityIcon(alert.severity)}
                <span className="font-medium capitalize">{alert.severity} Priority</span>
                <span className="text-gray-400"></span>
                <span className="text-gray-400 text-sm">{new Date(alert.timestamp).toLocaleDateString()}</span>
              </div>
              
              <div className="mb-2">
                <div className="flex items-center space-x-2 mb-1">
                  <User size={14} className="text-gray-400" />
                  <span className="font-medium">{alert.userName}</span>
                  {alert.department && (
                    <>
                      <span className="text-gray-400"></span>
                      <span className="text-gray-400 text-sm">{alert.department}</span>
                    </>
                  )}
                </div>
                <p className="text-sm">{alert.question}</p>
                <p className="text-sm font-medium mt-1">Response: {formatResponse(alert.response)}</p>
              </div>
            </div>
            
            <button
              onClick={() => handleAcknowledge(alert.id)}
              className="ml-3 text-gray-400 hover:text-white transition-colors"
              title="Acknowledge and dismiss"
            >
              <X size={16} />
            </button>
          </div>
        </div>
      ))}
    </div>
  );

  if (showInline) {
    return alerts.length > 0 ? (
      <div className="space-y-3">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold text-white flex items-center">
            <Heart className="mr-2 text-red-400" size={18} />
            Team Health Alerts ({alerts.length})
          </h3>
        </div>
        {alertsList}
        {alerts.length > 3 && (
          <div className="text-center">
            <p className="text-gray-400 text-sm">
              Showing 3 of {alerts.length} alerts
            </p>
          </div>
        )}
      </div>
    ) : null;
  }

  return (
    <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-xl font-semibold text-white flex items-center">
          <Heart className="mr-2 text-red-400" size={20} />
          Team Health Alerts
        </h2>
        <div className="text-sm text-gray-400">
          {alerts.length} unacknowledged
        </div>
      </div>
      
      {alertsList}
    </div>
  );
}

// Compact version for notification center
export function TeamHealthNotificationBadge({ role, managerId }: { role: 'manager' | 'admin', managerId?: string }) {
  const [alertCount, setAlertCount] = useState(0);

  useEffect(() => {
    const updateCount = () => {
      let alerts: TeamHealthAlert[] = [];
      if (role === 'manager' && managerId) {
        alerts = TeamHealthService.getManagerAlerts(managerId);
      } else if (role === 'admin') {
        alerts = TeamHealthService.getAdminAlerts();
      }
      setAlertCount(alerts.length);
    };

    updateCount();
    const interval = setInterval(updateCount, 30000);
    return () => clearInterval(interval);
  }, [role, managerId]);

  if (alertCount === 0) return null;

  return (
    <div className="relative">
      <Heart className="text-red-400" size={20} />
      <div className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
        {alertCount > 9 ? '9+' : alertCount}
      </div>
    </div>
  );
}

// src/components/ui/TeamHealthPulse.tsx - Team health monitoring component
import React, { useState, useEffect } from 'react';
import { Heart, ThumbsUp, AlertTriangle, TrendingUp, X, CheckCircle } from 'lucide-react';
import { useApp } from '../../contexts';
import { supabase } from '../../services/supabaseClient';

interface PulseQuestion {
  id: string;
  question_id: string;
  question_text: string;
  type: 'scale' | 'boolean' | 'choice';
  options?: string[];
  category: 'satisfaction' | 'workload' | 'support' | 'engagement';
  is_active: boolean;
  sort_order: number;
}

interface TeamHealthPulseProps {
  onComplete?: (response: any) => void;
  showRandomQuestion?: boolean;
}

export default function TeamHealthPulse({ onComplete, showRandomQuestion = false }: TeamHealthPulseProps) {
  const { user } = useApp();
  const [currentQuestion, setCurrentQuestion] = useState<PulseQuestion | null>(null);
  const [showPulse, setShowPulse] = useState(false);
  const [hasAnsweredToday, setHasAnsweredToday] = useState(false);
  const [questions, setQuestions] = useState<PulseQuestion[]>([]);

  // Load questions from database
  const loadQuestions = async () => {
    try {
      const { data, error } = await supabase
        .from('pulse_questions')
        .select('*')
        .eq('is_active', true)
        .order('sort_order');

      if (error) throw error;
      
      console.log('Loaded pulse questions:', data);
      setQuestions(data || []);
    } catch (error) {
      console.error('Error loading pulse questions:', error);
      // Fallback to hardcoded questions if database fails
      const fallbackQuestions = [
        {
          id: '1',
          question_id: 'provide_value',
          question_text: 'Do you feel you provide value to the company?',
          category: 'satisfaction' as const,
          type: 'scale' as const,
          is_active: true,
          sort_order: 1
        },
        {
          id: '2', 
          question_id: 'happy_to_start_work',
          question_text: 'Were you happy to start work today?',
          category: 'satisfaction' as const,
          type: 'scale' as const,
          is_active: true,
          sort_order: 2
        }
      ];
      setQuestions(fallbackQuestions);
    }
  };

  useEffect(() => {
    if (user?.id) {
      loadQuestions();
    }
  }, [user?.id]);

  useEffect(() => {
    if (!user?.id || questions.length === 0) return;

    // Check if user has answered recently (within 52 minutes)
    const lastAnsweredTime = localStorage.getItem(`pulse_last_answered_time_${user?.id}`);
    const now = new Date().getTime();
    const fiftyTwoMinutesInMs = 52 * 60 * 1000; // 52 minutes in milliseconds
    
    if (lastAnsweredTime && (now - parseInt(lastAnsweredTime)) < fiftyTwoMinutesInMs) {
      setHasAnsweredToday(true);
      return;
    }

    // ALWAYS show question automatically every 52 minutes (no manual trigger needed)
    if (!hasAnsweredToday && questions.length > 0) {
      const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
      setCurrentQuestion(randomQuestion);
      setShowPulse(true);
    }
  }, [user?.id, hasAnsweredToday, questions]);

  const handleResponse = async (response: any) => {
    if (!currentQuestion || !user) return;

    const pulseResponse = {
      questionId: currentQuestion.question_id,
      question: currentQuestion.question_text,
      response,
      timestamp: new Date().toISOString(),
      userId: user.id,
      category: currentQuestion.category
    };

    try {
      // Import TeamHealthService dynamically
      const { TeamHealthService } = await import('../../services/TeamHealthService');
      
      // Get user info for alert creation
      const { supabase } = await import('../../services/supabaseClient');
      const { data: employee } = await supabase
        .from('employees')
        .select('name, manager_id, department')
        .eq('user_id', user.id)
        .single();
      
      // Save response with user context for alert creation
      await TeamHealthService.savePulseResponse(
        pulseResponse, 
        employee?.name || user.email?.split('@')[0] || 'Unknown',
        employee?.manager_id,
        employee?.department
      );
      
      // Mark as answered recently (store timestamp)
      localStorage.setItem(`pulse_last_answered_time_${user.id}`, new Date().getTime().toString());
      
      if (onComplete) {
        onComplete(pulseResponse);
      }

      setHasAnsweredToday(true);
      setShowPulse(false);
    } catch (error) {
      console.error('Error saving pulse response:', error);
    }
  };

  const renderQuestionInput = () => {
    if (!currentQuestion) return null;

    switch (currentQuestion.type) {
      case 'scale':
        return (
          <div className="space-y-4">
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-400">Strongly Disagree</span>
              <span className="text-sm text-gray-400">Strongly Agree</span>
            </div>
            <div className="flex space-x-2">
              {[1, 2, 3, 4, 5].map(value => (
                <button
                  key={value}
                  onClick={() => handleResponse(value)}
                  className="w-12 h-12 rounded-full border-2 border-cyan-500 hover:bg-cyan-500 hover:text-white transition-colors flex items-center justify-center font-semibold"
                >
                  {value}
                </button>
              ))}
            </div>
          </div>
        );


      default:
        return null;
    }
  };

  const PulseIcon = () => {
    const category = currentQuestion?.category;
    switch (category) {
      case 'satisfaction': return <Heart className="text-red-400" size={24} />;
      case 'workload': return <TrendingUp className="text-yellow-400" size={24} />;
      case 'support': return <ThumbsUp className="text-blue-400" size={24} />;
      case 'engagement': return <CheckCircle className="text-green-400" size={24} />;
      default: return <Heart className="text-cyan-400" size={24} />;
    }
  };

  if (!showPulse || hasAnsweredToday || !currentQuestion) {
    return null;
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-gray-800 rounded-lg p-6 max-w-md w-full border border-gray-600 shadow-2xl">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-3">
            <PulseIcon />
            <h3 className="text-lg font-semibold text-white">Team Health Pulse</h3>
          </div>
          <button
            onClick={() => setShowPulse(false)}
            className="text-gray-400 hover:text-white transition-colors"
          >
            <X size={20} />
          </button>
        </div>

        <div className="mb-6">
          <p className="text-white text-lg mb-4">{currentQuestion.question_text}</p>
          {renderQuestionInput()}
        </div>

        <div className="text-xs text-gray-500 text-center">
          Your response helps us improve our work environment for everyone
        </div>
      </div>
    </div>
  );
}

// Persistent dashboard org health widget
export function OrgHealthWidget() {
  const { user } = useApp();
  const [currentQuestion, setCurrentQuestion] = useState<PulseQuestion | null>(null);
  const [hasAnswered, setHasAnswered] = useState(false);
  const [isNewQuestion, setIsNewQuestion] = useState(false);
  const [questions, setQuestions] = useState<PulseQuestion[]>([]);

  // Load questions from database
  const loadQuestions = async () => {
    try {
      const { data, error } = await supabase
        .from('pulse_questions')
        .select('*')
        .eq('is_active', true)
        .order('sort_order');

      if (error) throw error;
      setQuestions(data || []);
    } catch (error) {
      console.error('Error loading pulse questions for OrgHealthWidget:', error);
      // Fallback to hardcoded questions if database fails
      const fallbackQuestions = [
        {
          id: '1',
          question_id: 'provide_value',
          question_text: 'Do you feel you provide value to the company?',
          category: 'satisfaction' as const,
          type: 'scale' as const,
          is_active: true,
          sort_order: 1
        },
        {
          id: '2', 
          question_id: 'workplace_satisfaction',
          question_text: 'How satisfied are you with your workplace today?',
          category: 'satisfaction' as const,
          type: 'scale' as const,
          is_active: true,
          sort_order: 2
        }
      ];
      setQuestions(fallbackQuestions);
    }
  };

  useEffect(() => {
    if (user?.id) {
      loadQuestions();
    }
  }, [user?.id]);

  useEffect(() => {
    if (!user?.id || questions.length === 0) return;

    const checkForNewQuestion = () => {
      const lastAnsweredTime = localStorage.getItem(`pulse_last_answered_time_${user.id}`);
      const lastQuestionId = localStorage.getItem(`pulse_last_question_id_${user.id}`);
      const now = new Date().getTime();
      
      // Random interval between 10 minutes and 52 minutes (gamified randomness)
      const minInterval = 10 * 60 * 1000; // 10 minutes
      const maxInterval = 52 * 60 * 1000; // 52 minutes
      const randomInterval = Math.random() * (maxInterval - minInterval) + minInterval;
      
      if (!lastAnsweredTime || (now - parseInt(lastAnsweredTime)) > randomInterval) {
        // Time for a new question
        const availableQuestions = questions.filter(q => q.question_id !== lastQuestionId);
        const randomQuestion = availableQuestions[Math.floor(Math.random() * availableQuestions.length)];
        
        setCurrentQuestion(randomQuestion);
        setHasAnswered(false);
        setIsNewQuestion(true);
        localStorage.setItem(`pulse_current_question_${user.id}`, JSON.stringify(randomQuestion));
      } else {
        // Check if there's a current unanswered question
        const storedQuestion = localStorage.getItem(`pulse_current_question_${user.id}`);
        const storedAnswered = localStorage.getItem(`pulse_question_answered_${user.id}`);
        
        if (storedQuestion) {
          setCurrentQuestion(JSON.parse(storedQuestion));
          setHasAnswered(storedAnswered === 'true');
          setIsNewQuestion(false);
        }
      }
    };

    checkForNewQuestion();
    
    // Check for new questions every minute
    const interval = setInterval(checkForNewQuestion, 60000);
    return () => clearInterval(interval);
  }, [user?.id, questions]);

  const handleResponse = async (response: any) => {
    if (!currentQuestion || !user) return;

    const pulseResponse = {
      questionId: currentQuestion.question_id,
      question: currentQuestion.question_text,
      response,
      timestamp: new Date().toISOString(),
      userId: user.id,
      category: currentQuestion.category
    };

    try {
      // Import TeamHealthService dynamically
      const { TeamHealthService } = await import('../../services/TeamHealthService');
      
      // Get user info for alert creation
      const { supabase } = await import('../../services/supabaseClient');
      const { data: employee } = await supabase
        .from('employees')
        .select('name, manager_id, department')
        .eq('user_id', user.id)
        .single();
      
      // Save response with user context
      await TeamHealthService.savePulseResponse(
        pulseResponse, 
        employee?.name || user.email?.split('@')[0] || 'Unknown',
        employee?.manager_id,
        employee?.department
      );
      
      // Mark as answered
      localStorage.setItem(`pulse_last_answered_time_${user.id}`, new Date().getTime().toString());
      localStorage.setItem(`pulse_last_question_id_${user.id}`, currentQuestion.question_id);
      localStorage.setItem(`pulse_question_answered_${user.id}`, 'true');
      
      setHasAnswered(true);
      setIsNewQuestion(false);
      
    } catch (error) {
      console.error('Error saving pulse response:', error);
    }
  };

  if (!currentQuestion) {
    return (
      <div className="bg-gray-800/50 border border-gray-600/50 rounded-lg p-4">
        <div className="flex items-center space-x-3">
          <Heart className="text-gray-500" size={20} />
          <div>
            <h4 className="text-gray-400 font-medium">Org Health Pulse</h4>
            <p className="text-gray-500 text-sm">
              {questions.length === 0 ? 'Setting up questions...' : 'Loading next question...'}
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`rounded-lg p-4 transition-all duration-500 ${
      hasAnswered 
        ? 'bg-green-900/30 border border-green-500/40' 
        : isNewQuestion 
          ? 'bg-gradient-to-r from-cyan-900/40 to-blue-900/40 border border-cyan-400/50 shadow-lg shadow-cyan-500/20' 
          : 'bg-cyan-900/20 border border-cyan-500/30'
    }`}>
      <div className="flex items-start space-x-3">
        {hasAnswered ? (
          <CheckCircle className="text-green-400 animate-pulse" size={20} />
        ) : (
          <Heart className={`${isNewQuestion ? 'text-cyan-300 animate-bounce' : 'text-cyan-400'}`} size={20} />
        )}
        <div className="flex-1">
          <div className="flex items-center justify-between mb-3">
            <h4 className={`font-medium ${
              hasAnswered ? 'text-green-200' : 'text-cyan-200'
            }`}>
              Org Health Pulse {isNewQuestion && !hasAnswered && ' NEW!'}
            </h4>
            {isNewQuestion && !hasAnswered && (
              <div className="w-2 h-2 bg-cyan-400 rounded-full animate-ping" />
            )}
          </div>
          
          <p className={`text-sm mb-4 ${
            hasAnswered ? 'text-green-300' : 'text-white'
          }`}>
            {currentQuestion.question_text}
          </p>
          
          {hasAnswered ? (
            <div className="flex items-center space-x-2">
              <CheckCircle className="text-green-400" size={16} />
              <span className="text-green-300 text-sm font-medium">
                Thanks for your feedback! 
              </span>
            </div>
          ) : (
            <div className="space-y-3">
              <div className="flex justify-between items-center text-xs text-gray-400">
                <span>Strongly Disagree</span>
                <span>Strongly Agree</span>
              </div>
              <div className="flex space-x-2">
                {[1, 2, 3, 4, 5].map(value => (
                  <button
                    key={value}
                    onClick={() => handleResponse(value)}
                    className="w-10 h-10 rounded-full border-2 border-cyan-500 hover:bg-cyan-500 hover:text-white transition-all duration-200 flex items-center justify-center font-semibold text-sm hover:scale-110"
                  >
                    {value}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { AuthService } from '../services';

// Action types
const APP_ACTIONS = {
  SET_USER: 'SET_USER',
  SET_USER_DATA: 'SET_USER_DATA',
  SET_USER_DATA_LOADING: 'SET_USER_DATA_LOADING',
  SET_ACTIVE_PAGE: 'SET_ACTIVE_PAGE',
  OPEN_MODAL: 'OPEN_MODAL',
  CLOSE_MODAL: 'CLOSE_MODAL',
  RESET_USER_STATE: 'RESET_USER_STATE'
};

// Initial state
const initialState = {
  user: null,
  userRole: null,
  userName: '',
  userDataLoading: true,
  activePage: { name: 'Dashboard', props: {} },
  modal: { isOpen: false, name: null, props: {} }
};

// Reducer
const appReducer = (state, action) => {
  switch (action.type) {
    case APP_ACTIONS.SET_USER:
      return { ...state, user: action.payload };
    
    case APP_ACTIONS.SET_USER_DATA:
      return { 
        ...state, 
        userRole: action.payload.role, 
        userName: action.payload.name 
      };
    
    case APP_ACTIONS.SET_USER_DATA_LOADING:
      return { ...state, userDataLoading: action.payload };
    
    case APP_ACTIONS.SET_ACTIVE_PAGE:
      return { ...state, activePage: action.payload };
    
    case APP_ACTIONS.OPEN_MODAL:
      return { 
        ...state, 
        modal: { 
          isOpen: true, 
          name: action.payload.name, 
          props: action.payload.props || {} 
        } 
      };
    
    case APP_ACTIONS.CLOSE_MODAL:
      return { 
        ...state, 
        modal: { isOpen: false, name: null, props: {} } 
      };
    
    case APP_ACTIONS.RESET_USER_STATE:
      return {
        ...state,
        user: null,
        userRole: null,
        userName: '',
        userDataLoading: false,
        activePage: { name: 'Dashboard', props: {} },
        modal: { isOpen: false, name: null, props: {} }
      };
    
    default:
      return state;
  }
};

// Create context
const AppContext = createContext();

// Context provider component
export const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(appReducer, initialState);

  // Fetch user role and name after authentication
  const fetchUserData = async (user) => {
    if (!user) {
      dispatch({ type: APP_ACTIONS.SET_USER_DATA_LOADING, payload: false });
      return;
    }

    try {
      if (process.env.NODE_ENV === 'development') {
        console.log(' Fetching user data for authenticated user');
      }
      
      const { role, name } = await AuthService.getUserRole(user.email);

      if (process.env.NODE_ENV === 'development') {
        console.log(' User data loaded successfully');
      }
      dispatch({ type: APP_ACTIONS.SET_USER_DATA, payload: { role, name } });

    } catch (error) {
      console.error(' Error in fetchUserData:', error);
      // Set defaults if there's an error
      dispatch({ 
        type: APP_ACTIONS.SET_USER_DATA, 
        payload: { role: 'employee', name: user.email.split('@')[0] } 
      });
    } finally {
      dispatch({ type: APP_ACTIONS.SET_USER_DATA_LOADING, payload: false });
    }
  };

  // Initialize auth state
  useEffect(() => {
    if (process.env.NODE_ENV === 'development') {
      console.log(' App starting...');
    }
    
    AuthService.getSession().then((session) => {
      if (process.env.NODE_ENV === 'development') {
        console.log(' Session check:', session?.user ? 'Active session' : 'No session');
      }
      dispatch({ type: APP_ACTIONS.SET_USER, payload: session?.user ?? null });
    });

    const { data: { subscription } } = AuthService.onAuthStateChange((event, session) => {
      if (process.env.NODE_ENV === 'development') {
        console.log(' Auth changed:', event, session?.user?.email || 'No user');
      }
      dispatch({ type: APP_ACTIONS.SET_USER, payload: session?.user ?? null });
      
      // Reset user data when auth changes
      if (!session?.user) {
        dispatch({ type: APP_ACTIONS.RESET_USER_STATE });
      }
    });

    return () => subscription.unsubscribe();
  }, []);

  // Fetch user data when user changes
  useEffect(() => {
    if (state.user) {
      dispatch({ type: APP_ACTIONS.SET_USER_DATA_LOADING, payload: true });
      fetchUserData(state.user);
    }
  }, [state.user]);

  // Action creators
  const actions = {
    setActivePage: (page) => dispatch({ type: APP_ACTIONS.SET_ACTIVE_PAGE, payload: page }),
    openModal: (name, props = {}) => dispatch({ type: APP_ACTIONS.OPEN_MODAL, payload: { name, props } }),
    closeModal: () => dispatch({ type: APP_ACTIONS.CLOSE_MODAL }),
    signOut: async () => {
      try {
        await AuthService.signOut();
        dispatch({ type: APP_ACTIONS.RESET_USER_STATE });
      } catch (error) {
        console.error('Error signing out:', error);
      }
    }
  };

  const value = {
    ...state,
    ...actions
  };

  return (
    <AppContext.Provider value={value}>
      {children}
    </AppContext.Provider>
  );
};

// Custom hook to use the app context
export const useApp = () => {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error('useApp must be used within an AppProvider');
  }
  return context;
};

