export const isActiveReview = (assessment) => {
  const statusInfo = getStatusDisplay(assessment);
  return statusInfo.isActive;
};

export const filterActiveReviews = (assessments) => {
  return assessments.filter(assessment => isActiveReview(assessment));
};

export const filterCompletedReviews = (assessments) => {
  return assessments.filter(assessment => !isActiveReview(assessment));
};

export const getAssessmentProgress = (assessment) => {
  const status = assessment.self_assessment_status || assessment.status;
  
  switch (status) {
    case 'not_started':
      return { percentage: 0, step: 'Not Started' };
    case 'in_progress':
      return { percentage: 25, step: 'In Progress' };
    case 'employee_complete':
      return { percentage: 50, step: 'Submitted' };
    case 'manager_complete':
      return { percentage: 75, step: 'Manager Review Complete' };
    case 'pending_admin_approval':
      return { percentage: 85, step: 'Pending Admin Approval' };
    case 'finalized':
      return { percentage: 100, step: 'Finalized' };
    default:
      return { percentage: 0, step: 'Unknown' };
  }
};

/**
 * Calculates a numerical score (1-5) from an assessment
 * FOR INDIVIDUAL ASSESSMENT TRENDS ONLY - uses GWC and satisfaction
 * NOTE: Department/Company satisfaction uses popup survey responses, not this function
 */
export const calculateAssessmentScore = (assessment) => {
  if (!assessment) return null;
  
  const scores = [];
  
  // Overall satisfaction (already 1-5 scale)
  if (assessment.overall_satisfaction) {
    const satisfaction = typeof assessment.overall_satisfaction === 'string' 
      ? parseInt(assessment.overall_satisfaction) 
      : assessment.overall_satisfaction;
    if (satisfaction >= 1 && satisfaction <= 5) {
      scores.push(satisfaction);
    }
  }
  
  // GWC fields: convert boolean to 1-5 scale (FOR INDIVIDUAL ASSESSMENT TRENDS ONLY)
  // true = 5 (excellent), false = 1 (poor), null/undefined = 3 (neutral)
  const gwcFields = ['gwc_gets_it', 'gwc_wants_it', 'gwc_capacity'];
  gwcFields.forEach(field => {
    if (assessment[field] === true) {
      scores.push(5);
    } else if (assessment[field] === false) {
      scores.push(1);
    } else if (assessment[field] !== undefined && assessment[field] !== null) {
      scores.push(3); // neutral for any other defined value
    }
  });
  
  // If no scores available, return null
  if (scores.length === 0) return null;
  
  // Calculate average and round to 1 decimal place
  const average = scores.reduce((sum, score) => sum + score, 0) / scores.length;
  return Math.round(average * 10) / 10;
};

/**
 * Gets historical scores for trend analysis
 * Groups assessments by cycle and calculates scores
 */
export const getAssessmentTrends = (assessments) => {
  if (!assessments || assessments.length === 0) return [];
  
  return assessments
    .map(assessment => ({
      cycleName: assessment.cycle_name,
      cycleEndDate: assessment.cycle_end_date,
      selfScore: calculateAssessmentScore(assessment),
      managerScore: assessment.manager_overall_rating ? 
        (typeof assessment.manager_overall_rating === 'string' ? 
          parseInt(assessment.manager_overall_rating) : 
          assessment.manager_overall_rating) : null,
      isComplete: assessment.employee_acknowledged_at !== null || assessment.employee_acknowledgment === true
    }))
    .filter(trend => trend.selfScore !== null || trend.managerScore !== null)
    .sort((a, b) => new Date(a.cycleEndDate) - new Date(b.cycleEndDate));
};

// Utility to create database function
import { supabase } from '../services/supabaseClient';

export const createManagerReviewFunction = async () => {
  const sqlQuery = `
    CREATE OR REPLACE FUNCTION public.get_assessment_for_manager_review(
        p_assessment_id bigint
    ) RETURNS TABLE (
        id bigint,
        employee_id uuid,
        employee_name text,
        employee_email text,
        employee_job_title text,
        review_cycle_id bigint,
        cycle_name text,
        cycle_status text,
        due_date date,
        self_assessment_status text,
        manager_review_status text,
        self_assessment_data jsonb,
        manager_review_data jsonb,
        value_passionate_examples text,
        value_driven_examples text,
        value_resilient_examples text,
        value_responsive_examples text,
        gwc_gets_it boolean,
        gwc_gets_it_feedback text,
        gwc_wants_it boolean,
        gwc_wants_it_feedback text,
        gwc_capacity boolean,
        gwc_capacity_feedback text,
        manager_performance_rating text,
        manager_summary_comments text,
        employee_submitted_at timestamp with time zone,
        manager_reviewed_at timestamp with time zone,
        created_at timestamp with time zone,
        updated_at timestamp with time zone
    ) 
    LANGUAGE plpgsql 
    SECURITY DEFINER 
    AS $$
    DECLARE
        v_current_user_id uuid;
        v_manager_employee_id uuid;
        v_assessment_employee_manager_id uuid;
    BEGIN
        -- Get the current user's ID
        v_current_user_id := auth.uid();
        
        -- Get the current user's employee record
        SELECT id INTO v_manager_employee_id
        FROM employees 
        WHERE user_id = v_current_user_id;
        
        IF v_manager_employee_id IS NULL THEN
            RAISE EXCEPTION 'Manager employee record not found';
        END IF;
        
        -- Get the manager_id of the employee whose assessment we're trying to access
        SELECT e.manager_id INTO v_assessment_employee_manager_id
        FROM assessments a
        JOIN employees e ON a.employee_id = e.id
        WHERE a.id = p_assessment_id;
        
        -- Check if the current user is the manager of the employee being assessed
        -- or if they are an admin
        IF v_assessment_employee_manager_id != v_manager_employee_id THEN
            -- Check if user is admin
            DECLARE
                v_user_role text;
            BEGIN
                SELECT role INTO v_user_role FROM auth.users WHERE id = v_current_user_id;
                IF v_user_role != 'admin' THEN
                    RAISE EXCEPTION 'Access denied: You can only review assessments for your direct reports';
                END IF;
            END;
        END IF;
        
        -- Return the assessment data with employee and cycle information
        RETURN QUERY
        SELECT 
            a.id,
            a.employee_id,
            e.name as employee_name,
            e.email as employee_email,
            e.job_title as employee_job_title,
            a.review_cycle_id,
            rc.name as cycle_name,
            rc.status as cycle_status,
            a.due_date,
            a.self_assessment_status,
            a.manager_review_status,
            a.self_assessment_data,
            a.manager_review_data,
            a.value_passionate_examples,
            a.value_driven_examples,
            a.value_resilient_examples,
            a.value_responsive_examples,
            a.gwc_gets_it,
            a.gwc_gets_it_feedback,
            a.gwc_wants_it,
            a.gwc_wants_it_feedback,
            a.gwc_capacity,
            a.gwc_capacity_feedback,
            a.manager_performance_rating,
            a.manager_summary_comments,
            a.employee_submitted_at,
            a.manager_reviewed_at,
            a.created_at,
            a.updated_at
        FROM assessments a
        JOIN employees e ON a.employee_id = e.id
        JOIN review_cycles rc ON a.review_cycle_id = rc.id
        WHERE a.id = p_assessment_id;
    END;
    $$;

    -- Grant execute permissions
    GRANT EXECUTE ON FUNCTION public.get_assessment_for_manager_review(bigint) TO authenticated;
  `;

  try {
    const { data, error } = await supabase.rpc('exec_sql', { sql: sqlQuery });
    if (error) {
      console.error('Error creating function:', error);
      throw error;
    }
    console.log('Function created successfully');
    return data;
  } catch (error) {
    console.error('Failed to create function:', error);
    // Try using raw SQL query approach
    try {
      const { data, error: rawError } = await supabase.from('_supabase_migrations').select('*').limit(0);
      console.log('Database connection test:', { data, rawError });
    } catch (e) {
      console.log('Cannot create function, using fallback approach');
    }
    throw error;
  }
};

// csrfProtection.js - CSRF protection utilities for secure form submissions

import { supabase } from '../services/supabaseClient';

/**
 * CSRF Protection utility class
 */
class CSRFProtection {
  constructor() {
    this.tokenKey = '_csrf_token';
    this.headerName = 'X-CSRF-Token';
  }

  /**
   * Generate a secure random token
   */
  generateToken() {
    const array = new Uint8Array(32);
    crypto.getRandomValues(array);
    return Array.from(array, byte => byte.toString(16).padStart(2, '0')).join('');
  }

  /**
   * Store CSRF token in session storage
   */
  storeToken(token) {
    try {
      sessionStorage.setItem(this.tokenKey, token);
      return true;
    } catch (error) {
      console.error('Failed to store CSRF token:', error);
      return false;
    }
  }

  /**
   * Get stored CSRF token
   */
  getStoredToken() {
    try {
      return sessionStorage.getItem(this.tokenKey);
    } catch (error) {
      console.error('Failed to retrieve CSRF token:', error);
      return null;
    }
  }

  /**
   * Initialize CSRF protection (call on app start)
   */
  initialize() {
    let token = this.getStoredToken();
    if (!token) {
      token = this.generateToken();
      this.storeToken(token);
    }
    return token;
  }

  /**
   * Create protected form data with CSRF token
   */
  createProtectedFormData(formData) {
    const token = this.getStoredToken();
    if (!token) {
      throw new Error('CSRF token not available');
    }

    return {
      ...formData,
      _csrf_token: token,
      _timestamp: Date.now(),
      _nonce: this.generateToken().substring(0, 16)
    };
  }

  /**
   * Validate form submission timing (prevent replay attacks)
   */
  validateTimestamp(timestamp, maxAgeMs = 300000) { // 5 minutes default
    if (!timestamp) return false;
    const now = Date.now();
    const age = now - timestamp;
    return age >= 0 && age <= maxAgeMs;
  }

  /**
   * Create secure headers for API requests
   */
  createSecureHeaders() {
    const token = this.getStoredToken();
    if (!token) {
      throw new Error('CSRF token not available');
    }

    return {
      [this.headerName]: token,
      'Content-Type': 'application/json',
      'X-Requested-With': 'XMLHttpRequest'
    };
  }

  /**
   * Secure RPC call wrapper with CSRF protection
   */
  async secureRPC(functionName, params = {}) {
    const token = this.getStoredToken();
    if (!token) {
      throw new Error('CSRF token not available');
    }

    // Add security metadata to params
    const secureParams = {
      ...params,
      _csrf_token: token,
      _timestamp: Date.now(),
      _nonce: this.generateToken().substring(0, 16)
    };

    try {
      const { data, error } = await supabase.rpc(functionName, secureParams);
      
      if (error) {
        throw error;
      }

      return data;
    } catch (error) {
      console.error(`Secure RPC call failed (${functionName}):`, error);
      throw error;
    }
  }

  /**
   * Validate form data before submission
   */
  validateFormData(formData) {
    const errors = [];

    // Check for CSRF token
    if (!formData._csrf_token) {
      errors.push('Security token missing');
    } else if (formData._csrf_token !== this.getStoredToken()) {
      errors.push('Invalid security token');
    }

    // Check timestamp
    if (!formData._timestamp) {
      errors.push('Timestamp missing');
    } else if (!this.validateTimestamp(formData._timestamp)) {
      errors.push('Request expired');
    }

    // Check for required nonce
    if (!formData._nonce) {
      errors.push('Security nonce missing');
    }

    return {
      isValid: errors.length === 0,
      errors
    };
  }

  /**
   * Clear stored tokens (call on logout)
   */
  clearTokens() {
    try {
      sessionStorage.removeItem(this.tokenKey);
    } catch (error) {
      console.error('Failed to clear CSRF token:', error);
    }
  }

  /**
   * Refresh token (call periodically or after sensitive operations)
   */
  refreshToken() {
    const newToken = this.generateToken();
    this.storeToken(newToken);
    return newToken;
  }
}

// Create singleton instance
const csrfProtection = new CSRFProtection();

// Initialize on module load
csrfProtection.initialize();

export default csrfProtection;

/**
 * Hook for React components to use CSRF protection
 */
export const useCSRFProtection = () => {
  const createProtectedData = (formData) => {
    return csrfProtection.createProtectedFormData(formData);
  };

  const secureSubmit = async (functionName, formData) => {
    const protectedData = createProtectedData(formData);
    return await csrfProtection.secureRPC(functionName, protectedData);
  };

  const getSecureHeaders = () => {
    return csrfProtection.createSecureHeaders();
  };

  return {
    createProtectedData,
    secureSubmit,
    getSecureHeaders,
    refreshToken: () => csrfProtection.refreshToken(),
    clearTokens: () => csrfProtection.clearTokens()
  };
};

/**
 * Higher-order component for CSRF protection
 */
export const withCSRFProtection = (WrappedComponent) => {
  return function CSRFProtectedComponent(props) {
    const csrfProps = useCSRFProtection();
    return <WrappedComponent {...props} csrf={csrfProps} />;
  };
};

// Date-related utility functions

export const formatDate = (dateString, options = {}) => {
  if (!dateString) return 'N/A';
  
  const defaultOptions = {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  };
  
  const formatOptions = { ...defaultOptions, ...options };
  
  try {
    return new Date(dateString).toLocaleDateString(undefined, formatOptions);
  } catch (error) {
    console.error('Error formatting date:', error);
    return 'Invalid Date';
  }
};

export const formatDateTime = (dateString) => {
  if (!dateString) return 'N/A';
  
  try {
    return new Date(dateString).toLocaleString();
  } catch (error) {
    console.error('Error formatting date time:', error);
    return 'Invalid Date';
  }
};

export const getRelativeTime = (dateString) => {
  if (!dateString) return 'N/A';
  
  try {
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now - date;
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffMinutes = Math.floor(diffMs / (1000 * 60));
    
    if (diffDays > 0) {
      return `${diffDays} day${diffDays > 1 ? 's' : ''} ago`;
    } else if (diffHours > 0) {
      return `${diffHours} hour${diffHours > 1 ? 's' : ''} ago`;
    } else if (diffMinutes > 0) {
      return `${diffMinutes} minute${diffMinutes > 1 ? 's' : ''} ago`;
    } else {
      return 'Just now';
    }
  } catch (error) {
    console.error('Error calculating relative time:', error);
    return 'Unknown time';
  }
};

export const isDateInPast = (dateString) => {
  if (!dateString) return false;
  try {
    return new Date(dateString) < new Date();
  } catch (error) {
    return false;
  }
};

export const isDateInFuture = (dateString) => {
  if (!dateString) return false;
  try {
    return new Date(dateString) > new Date();
  } catch (error) {
    return false;
  }
};

